INSERT INTO `prescript` VALUES (58232, '11439', '大安汤', '麻黄（去根节，汤煮，掠去沫，焙）4两，防风（去叉）1两半，芎1两，羌活（去芦头）1两，桔梗（去芦头，锉，炒）1两，柴胡（去苗）1两，赤箭1两，白鲜皮1两，蔓荆实（去皮）1两，独活（去芦头）1两半，前胡（去芦头）1两半，甘草（炙，锉）半两，人参2两，松花2两，石膏（碎，研）3两。', '每服5钱匕，水1盏半，加薄荷5叶，煎至8分，去滓温服，不拘时候。', '《圣济总录》卷十三', '风邪伤人，寒热时作，头痛烦躁，周身疼痛，颈项拘急。', '-', '-', '柴胡', '[\'麻黄\', \'根节\', \'掠去\', \'防风\', \'两半\', \'羌活\', \'芦头\', \'桔梗\', \'芦头\', \'柴胡\', \'赤箭\', \'白鲜皮\', \'两蔓\', \'荆实\', \'去皮\', \'独活\', \'芦头\', \'两半\', \'胡去\', \'芦头\', \'两半\', \'甘草\', \'半两\', \'人参\', \'松花\', \'石膏\', \'碎研\']');
INSERT INTO `prescript` VALUES (58233, '11432', '柴胡饮', '柴胡（去苗）半两，赤芍药半两，麦门冬（去心，焙）半两，人参半两，黄耆（微炒，锉）半两，甘草（炙）半两，生地黄1两半（研，绞取汁）。', '每服3钱匕，以水1盏，入地黄汁1分，同煎至6分，去滓温服，日2次。', '《圣济总录》卷一五四', '妊娠阻病，头疼，四肢羸弱，不思食饮，唯思眠睡。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'芍药\', \'半两\', \'麦门冬\', \'半两\', \'人参\', \'半两\', \'黄耆\', \'半两\', \'甘草\', \'半两\', \'生地黄\', \'两半\', \'研绞取\']');
