INSERT INTO `prescript` VALUES (58235, '11416', '黄耆白术汤', '细辛3分，吴茱萸5分，川芎5分，柴胡1钱，升麻1钱，当归身1钱5分，黄柏2钱（酒洗），炙甘草2钱，羌活2钱，五味子3钱，白术5钱，人参5钱，黄耆1两。', '黄耆白术散（《普济方》卷三二八）。如腹中痛不快，加炙甘草1钱；汗出不止，加黄柏1钱。', '《兰室秘藏》卷中', '妇人四肢沉重，自汗，上至头，际头而还，恶风，头痛躁热。', '-', '-', '柴胡', '[\'细辛\', \'吴茱萸\', \'川芎\', \'柴胡\', \'升麻\', \'当归\', \'身钱分\', \'黄柏\', \'钱酒\', \'甘草\', \'羌活\', \'五味子\', \'白术\', \'人参\', \'黄耆\']');
