INSERT INTO `prescript` VALUES (58238, '11385', '大腹木香汤', '大腹（锉）2两，木香（锉）2两，半夏（汤洗7遍，焙）2两，枳壳（去瓤，麸炒）1两，白术（锉）1两，前胡（去芦头）1两，白芷（锉）1两，桂（去粗皮）1两，陈橘皮（汤浸，去白，焙）1两，延胡索半两，当归（切，焙）半两，甘草（炙，锉）半两，旋覆花半两，柴胡（去苗）半两，芍药半两，干姜（炮）3分，人参3分。', '每服3钱匕，水1盏，加生姜3片，大枣3枚（擘破），同煎至6分，去滓，稍热食前服。', '《圣济总录》卷四十七', '胃寒肠热，腹胀泄利。', '-', '-', '柴胡', '[\'大腹\', \'木香\', \'半夏\', \'洗遍\', \'枳壳\', \'白术\', \'两前\', \'胡去\', \'芦头\', \'白芷\', \'两桂去\', \'粗皮\', \'橘皮\', \'汤浸\', \'延胡索\', \'半两\', \'当归\', \'切焙\', \'半两\', \'甘草\', \'半两\', \'旋覆花\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'芍药\', \'半两\', \'干姜\', \'炮分\', \'人参\']');
INSERT INTO `prescript` VALUES (58239, '11378', '柴胡六味地黄汤', '熟地5钱，淮山2钱，柴胡1钱5分，萸肉1钱5分，茯苓1钱5分，丹皮1钱5分，泽泻（盐水炒）1钱。', '-', '《不知医必要》卷一', '久疟热未退，口干唇焦。', '-', '-', '柴胡', '[\'熟地\', \'淮山\', \'柴胡\', \'分萸\', \'钱分\', \'茯苓\', \'钱分\', \'丹皮\', \'钱分\', \'泽泻\', \'盐水\']');
INSERT INTO `prescript` VALUES (58240, '11326', '荆防五苓饮', '荆芥7分，防风1钱，泽泻1钱，柴胡8分，木通3分，甘草3分，赤茯苓1钱5分，猪苓1钱5分。', '加生姜3片，空心服。虚者，加人参3分。', '《穷乡便方》', '夏日疫病。', '-', '-', '柴胡', '[\'荆芥\', \'防风\', \'泽泻\', \'柴胡\', \'木通\', \'甘草\', \'分赤\', \'茯苓\', \'钱分\', \'猪苓\', \'钱分\']');
INSERT INTO `prescript` VALUES (58241, '11319', '调荣养卫汤', '人参8分，黄耆8分，当归8分，羌活8分，防风8分，白术8分，陈皮8分，柴胡1钱，地黄1钱，甘草3分，细辛3分，川芎7分。', '调营养气汤（《赤水玄珠》卷十八）。元气不足者，加升麻少许；口渴，加天花粉、知母；喘嗽，加杏仁，去升麻；汗不止，加芍药，去升麻，细辛；胸中饱闷，加枳壳、桔梗，去生地黄、甘草，黄耆；痰盛者，加瓜蒌仁、贝母、去防风、细辛；腹痛，去耆、术，加芍药和之。', '《伤寒六书》卷四', '劳力感寒症。因内伤气血，外感寒邪，致头疼身热，恶寒微渴，濈然汗出，身作痛，脚腿酸疼，无力沉倦，脉空浮而无力。', '-', '-', '柴胡', '[\'人参\', \'分黄\', \'耆分\', \'当归\', \'羌活\', \'防风\', \'白术\', \'陈皮分\', \'柴胡\', \'地黄\', \'甘草\', \'细辛\', \'川芎\']');
INSERT INTO `prescript` VALUES (58242, '11314', '大鳖甲丸', '鳖甲1枚重2两（去裙襕，醋炙），柴胡（去苗）1两，大黄（湿纸裹煨）1两，熟干地黄（焙）1两，乌梅（去核，炒）1两，桃仁（汤浸，去皮尖、双仁，炒）1两，干姜（炮）半两，槟榔（锉）半两，木香半两，人参半两，白茯苓（去黑皮）半两，芎半两，桂（去粗皮）半两，紫菀（去苗土）半两，芍药半两，牛膝（酒浸，切，焙）半两，知母（焙）半两，京三棱（炮，锉）半两，五味子半两，白术半两，黄连（去须）半两，厚朴（去粗皮，姜汁炙）半两，黄芩（去黑心）半两，陈橘皮（汤浸，去白，炒）半两，枳壳（去瓤，麸炒）半两，当归（切，焙）半两。', '每服20丸至30丸，温酒送下，1日3次。', '《圣济总录》卷九十一', '虚劳积聚，心腹胀满，喘促气逆，面色萎黄，痰嗽心忪，不思饮食。', '-', '-', '柴胡', '[\'鳖甲\', \'两去\', \'柴胡\', \'大黄\', \'湿纸\', \'两熟\', \'地黄\', \'乌梅\', \'去核\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\', \'干姜\', \'半两\', \'槟榔\', \'半两\', \'木香\', \'半两\', \'人参\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'紫菀\', \'苗土\', \'半两\', \'芍药\', \'半两\', \'牛膝\', \'浸切\', \'半两\', \'知母\', \'半两\', \'三棱\', \'半两\', \'五味子\', \'半两\', \'白术\', \'半两\', \'黄连\', \'半两\', \'厚朴\', \'粗皮\', \'姜汁\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'橘皮\', \'汤浸\', \'半两\', \'枳壳\', \'半两\', \'当归\', \'切焙\', \'半两\']');
INSERT INTO `prescript` VALUES (58243, '11281', '大黄丸', '大黄（破如米豆，熬令黑）1升，柴胡1升，朴消1升，芎5两，干姜1升，蜀椒2两，茯苓（如鸡子大）1枚。', '服十日下血，二十日下长虫及清黄汁，三十日病除，五十日肥白。', '《千金》卷二', '带下、百病、无子。', '-', '-', '柴胡', '[\'大黄\', \'破如米\', \'黑升\', \'柴胡\', \'升朴\', \'消升\', \'干姜\', \'升蜀椒\', \'茯苓\', \'鸡子\', \'大枚\']');
INSERT INTO `prescript` VALUES (58244, '11269', '柴胡鳖甲汤', '柴胡（去苗）1两，鳖甲（去裙襕，醋炙）1两，赤茯苓（去黑皮）1两，黄芩（去黑心）3分，知母（焙）3分，桑根白皮（锉）3分，甘草（炙）半两。', '每服5钱匕，水1盏半，加生姜半分（拍碎），煎至7分，去滓温服，不拘时候。', '《圣济总录》卷二十三', '伤寒过经，潮热不解，或时作寒如疟状。', '-', '-', '柴胡', '[\'柴胡\', \'鳖甲\', \'茯苓\', \'黑皮\', \'黄芩\', \'黑心\', \'知母\', \'焙分\', \'桑根\', \'白皮\', \'锉分\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58245, '11256', '调中益气汤', '黄耆1钱，人参（去芦）半钱，甘草（炙）半钱，陈皮2分，五味子7粒，芍药3分，白术5分，当归5分，升麻2分，柴胡2分。', '上（口父）咀，作1服。水2盏，煎至1盏，去滓，食前温服。如下元阴火蒸蒸发，燥热者，加生地黄2分；如咳嗽，加5味子10粒；腹中气不转运者，更加陈皮3分、木香2分；身体沉重，虽小便数多，加茯苓2钱、苍术1钱、泽泻半钱，黄柏3分；如胃气不和，加汤洗姜制半夏5分；痰厥头疼，加半夏；如夏月，须加白芍药3分，以补肺气不足；如春夏腹疼，尤宜加芍药；恶热燥渴而腹疼者，更加白芍药半钱；严寒腹疼，加中桂2钱；如冬月腹疼，不可用芍药，以太寒故也，只加干姜2分，或加半夏4分（姜制）。', '《东垣试效方》卷一', '因饥饱劳役，损伤脾胃，元气不足。其脉弦或洪缓而沉，按之无力，中之下，时得一涩。其证身体沉重，四肢倦懒，百节烦疼，胸满短气，膈咽不通，心烦不安，耳鸣耳聋，目有瘀肉，热壅如火，视物昏花，口中沃沫，饮食失味，怠堕嗜卧，忽肥忽瘦，溺色变，或清利而数，或上饮下便，或夏月飧泄，腹中虚痛，不思饮食。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'芦半钱\', \'甘草\', \'炙半钱\', \'陈皮分\', \'五味子\', \'芍药\', \'白术\', \'当归\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58246, '11248', '顾耳汤', '柴胡2钱，白芍2两，金银花2两，熟地2两，当归1两，天花粉5钱，生甘草3钱。', '若十日之后此方救之亦可生，然脾胃一坏恐难救。', '《洞天奥旨》卷五', '耳前初发恶疽。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'金银花\', \'熟地\', \'当归\', \'两天\', \'花粉\', \'生甘草\']');
INSERT INTO `prescript` VALUES (58247, '11243', '柴胡半夏汤', '柴胡8两，人参3两，半夏2两半（洗），甘草3两（炙），黄芩3两，白术2两，麦门冬3两（去心）。', '柴胡饮子（《御药院方》卷五）。', '《活人书》卷十七', '消痰利膈除烦。主痰热头痛，手足烦热，荣卫不调，肢节拘倦，身体疼痛，嗜卧少力，饮食无味，及五饮，痰癖。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'半夏\', \'两半\', \'甘草\', \'黄芩\', \'白术\', \'麦门冬\', \'两去\']');
INSERT INTO `prescript` VALUES (58248, '11223', '柴胡汤', '柴胡（去苗）半两，鳖甲（去裙襕，醋炙）半两，枳壳（去瓤，麸炒）半两，人参半两，乌梅肉（炒）半两，白茯苓（去黑皮）半两，桂（去粗皮）1分，白术（锉）1分，款冬花1分，紫菀（去土）1分，桔梗（炒）1分，甘草（炙）1分，槟榔（大者，锉）1枚。', '每服3钱匕，水1盏，加生姜2片，青蒿少许，同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷八十九', '虚劳。阳气外虚，腠理不密，荣卫发泄，盗汗不止，骨节热痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'鳖甲\', \'半两\', \'枳壳\', \'半两\', \'人参\', \'半两\', \'乌梅\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'桂去\', \'粗皮分\', \'白术\', \'锉分\', \'款冬花\', \'紫菀\', \'土分\', \'桔梗\', \'炒分\', \'甘草\', \'炙分\', \'槟榔\', \'大者\']');
INSERT INTO `prescript` VALUES (58249, '11222', '柴胡散', '柴胡1两半（去苗），桔梗3分（去芦头），槟榔3分，半夏3分（汤洗7遍去滑），诃黎勒皮3分，赤茯苓3分，陈橘皮（汤浸，去白瓤，焙）半两，桂心半两。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，稍热服，不拘时候。', '《圣惠》卷五十', '膈气全不思食，或食即欲呕，咽中噎塞，食难稍下。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'去苗\', \'桔梗\', \'分去\', \'芦头\', \'槟榔\', \'半夏\', \'分汤\', \'洗遍\', \'滑诃黎勒\', \'皮分\', \'茯苓\', \'分陈\', \'橘皮\', \'汤浸\', \'半两\', \'桂心\', \'半两\']');
INSERT INTO `prescript` VALUES (58250, '11211', '黄耆膏子煎丸', '人参1两半，白术1两半，柴胡1两，黄芩1两，白芷半两，知母半两，甘草（炙）半两，鳖甲1个（半手指大，酥炙）。', '每服30-50丸，空心以百沸汤送下。', '《元戎》', '除烦解劳，去肺热。主上焦热，咳衄，心热惊悸；脾胃热，口甘，吐血；肝胆热，泣出口苦；肾热，神志不定；上而酒毒，膈热消渴，下而血滞，五淋血崩。气虚，呼吸少气，懒言语，无力动作，目无睛光，面色(白光)白。', '-', '-', '柴胡', '[\'人参\', \'两半\', \'白术\', \'两半\', \'柴胡\', \'黄芩\', \'白芷\', \'半两\', \'知母\', \'半两\', \'甘草\', \'半两\', \'鳖甲\', \'手指\']');
INSERT INTO `prescript` VALUES (58251, '11171', '黄连散', '黄连（去须）半两，柴胡1两（去苗），前胡（去芦头）1两。', '每服1钱匕，温酒调下，每日3次。', '《圣济总录》卷四十三', '心热汗出，及虚热盗汗。', '-', '-', '柴胡', '[\'黄连\', \'半两\', \'柴胡\', \'两去\', \'苗前\', \'胡去\', \'芦头\']');
INSERT INTO `prescript` VALUES (58252, '11170', '柴胡散', '柴胡（去苗）1两，大黄（锉，炒）1两，赤芍药1两，槟榔（锉）1两，枳实（麸炒，去瓤）1两，半夏半两（姜汁浸令透，焙）。', '每服2钱匕，浓煎苦橡根汤调下；米饮亦得。', '《圣济总录》卷二十九', '伤寒狐惑，神思昏闷，大便难，肌肤热。', '-', '-', '柴胡', '[\'柴胡\', \'大黄\', \'芍药\', \'槟榔\', \'枳实\', \'半夏\', \'半两\', \'姜汁\', \'浸令\']');
INSERT INTO `prescript` VALUES (58253, '11099', '柴胡汤', '柴胡4两，白术2两，芍药（一方作紫葳），甘草2两，苁蓉1两，芎藭2两，麦门冬2两，干地黄5两，大枣30枚，生姜6两。', '上(口父)咀。以水1斗，煮取3升，分4服，日3夜1。中间进糜粥，7日更服1剂。', '《千金》卷二', '曾伤6月胎者。', '勿食生冷及坚硬之物。', '-', '柴胡', '[\'柴胡\', \'白术\', \'芍药\', \'一方\', \'紫葳\', \'甘草\', \'苁蓉\', \'麦门冬\', \'两干\', \'地黄\', \'大枣\', \'生姜\']');
INSERT INTO `prescript` VALUES (58254, '11092', '柴胡散', '柴胡（去苗）1两，人参（去芦头）1两，犀角屑1两，黄芩1两，麦门冬（去心）1两，甘草半两（炙微赤，锉），半夏半两（汤洗7遍，去滑）。', '每服5钱，以水1大盏，加生姜半分，大枣3枚，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气3日，头痛壮热，心神烦壅，胸膈不利。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'芦头\', \'犀角\', \'黄芩\', \'麦门冬\', \'甘草\', \'半两\', \'炙微\', \'半夏\', \'半两\', \'洗遍\']');
INSERT INTO `prescript` VALUES (58255, '11091', '柴胡当归汤', '柴胡2钱5分，黄芩1钱5分，半夏1钱5分，人参1钱5分，甘草5分，生姜5片，枣子2枚，生地黄1钱。', '上(口父)咀。用水煎8分，食前热服；外用大豆甘草汤。', '《杏苑》卷八', '茎上湿痒作疮，及注干疮。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'半夏\', \'钱分\', \'人参\', \'钱分\', \'甘草\', \'生姜\', \'枣子\', \'生地黄\']');
INSERT INTO `prescript` VALUES (58256, '11060', '柴胡散', '柴胡1两（去苗），大黄1两，朴消1两，甘草半两，枳壳1两（去瓤）。', '每服3大钱，水2盏，煎至6分，温服，日只2服，候大小便通，即自然汗出。', '《博济》卷一', '伤寒日数过多，心中气闷，或发疼痛，狂言不定，狂躁不得眠，大小便不通。', '不可多服。', '-', '柴胡', '[\'柴胡\', \'两去\', \'大黄\', \'两朴\', \'消两\', \'甘草\', \'半两\', \'枳壳\', \'两去\']');
INSERT INTO `prescript` VALUES (58257, '11044', '柴胡汤', '柴胡（去苗）2两，白术2两，牡蛎（烧令透）2两，桑根白皮（炙黄）半两，知母（锉，焙）半两，木通（锉）半两，甘草（炙）半两，鳖甲（去裙襕，一醋炙黄）1两半。', '每服3钱匕，水1盏，加生姜半分（拍碎），竹叶3片，煎至6分，去滓，空心温服，夜卧再服。', '《圣济总录》卷八十七', '热劳。潮热盗汗，羸瘦减食。', '-', '-', '柴胡', '[\'柴胡\', \'白术\', \'牡蛎\', \'烧令\', \'两桑根\', \'白皮\', \'半两\', \'知母\', \'半两\', \'木通\', \'半两\', \'甘草\', \'半两\', \'鳖甲\', \'一醋\', \'两半\']');
INSERT INTO `prescript` VALUES (58258, '11018', '柴胡汤', '柴胡（去苗）1两半，当归（切，焙）1两半，麦门冬（去心，焙）1两半，半夏（汤洗去滑，焙）1两半，人参1两，白茯苓（去黑皮）1两，防风（去叉）1两，细辛（去苗叶）1两，厚朴（去粗皮，生姜汁炙，锉）1两，陈橘皮（去白，炒）1两，甘草（炙，锉）1两，杏仁（去皮尖双仁，炒）1两，大腹（锉）1两，黄耆（锉）2两。', '每服5钱匕，水1盏半，加生姜5片，同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷八十七', '男子妇人急劳。咳嗽上气，饮食减少，痰涎壅盛，手足酸痛，唇口干燥，心虚惊悸，气乏羸劣。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'当归\', \'切焙\', \'两半\', \'麦门冬\', \'两半\', \'半夏\', \'汤洗\', \'两半\', \'人参\', \'白茯苓\', \'黑皮\', \'防风\', \'细辛\', \'苗叶\', \'厚朴\', \'粗皮生\', \'姜汁\', \'橘皮\', \'甘草\', \'杏仁\', \'皮尖\', \'双仁\', \'两大腹\', \'黄耆\']');
INSERT INTO `prescript` VALUES (58259, '11008', '柴胡散', '柴胡1两（去苗），石膏1两（碎），麻黄1两（去根节，掠去沫，焙）。', '每服3钱匕，水1盏，加豉30粒，葱白2寸，同煎6分，去滓，热服，并3服，不拘时候。汗出效。', '《普济方》卷一四八', '时气1日至3日，头痛壮热，心神烦闷。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'石膏\', \'两碎\', \'麻黄\', \'两去\', \'根节\', \'掠去\']');
INSERT INTO `prescript` VALUES (58260, '10965', '黄耆汤', '黄耆（锉，炒）1两，人参1两，白术（炒）1两，桂（去粗皮）1两，赤茯苓（去黑皮）1两，附子（炮裂，去皮脐）1两，麻黄（去节）1两，柴胡（去苗）1两，半夏（汤洗去滑，焙）1两，甘草（炙，锉）1两，桔梗（锉，炒）1两。', '每服5钱匕，以水1盏半，加生姜5片，煎取8分，去滓温服。', '《圣济总录》卷九十二', '气极虚寒，皮毛枯燥，津液不通。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'两桂去\', \'粗皮\', \'茯苓\', \'黑皮\', \'附子\', \'炮裂\', \'去皮\', \'麻黄\', \'去节\', \'柴胡\', \'半夏\', \'汤洗\', \'甘草\', \'桔梗\']');
INSERT INTO `prescript` VALUES (58261, '10955', '柴胡通塞汤', '柴胡3两，黄芩3两，橘皮3两，泽泻3两，羚羊角3两，生地黄1升，香豉1升（别盛），栀子4两，石膏6两，芒消2两。', '上(口父)咀。以水1斗，煮取3升，去滓纳芒消，分3服。', '《千金》卷二十', '下焦热，大小便不通。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'橘皮\', \'泽泻\', \'羚羊角\', \'生地黄\', \'升香豉\', \'升别\', \'栀子\', \'石膏\', \'两芒\', \'消两\']');
INSERT INTO `prescript` VALUES (58262, '10944', '柴胡散', '柴胡（去苗）1两，川升麻1两，黄芩1两，黄连（去须）1两，栀子仁1两，车前子1两，决明子1两，防风（去芦头）1两，羚羊角屑1两，马牙消1两，甘草（炙微赤，锉）1两，玄参1两。', '每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝脏风热，眼中生疮肿痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两川\', \'升麻\', \'黄芩\', \'黄连\', \'栀子\', \'车前子\', \'决明子\', \'防风\', \'芦头\', \'羚羊角\', \'两马牙\', \'消两\', \'甘草\', \'炙微\', \'玄参\']');
INSERT INTO `prescript` VALUES (58263, '10941', '柴胡散', '柴胡2两（去苗），乌梅肉2两（微炒），甘草1两（炙微赤，锉），麦门冬1两半（去心）。', '柴胡汤（《圣济总录》卷五十九）。', '《圣惠》卷五十三', '暴渴，心神烦闷，口舌干焦。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'乌梅\', \'两微\', \'甘草\', \'两炙微\', \'麦门冬\', \'两半\', \'去心\']');
INSERT INTO `prescript` VALUES (58264, '10913', '柴胡散', '柴胡（去芦）1两，地骨皮（去木）1两，玄参1两，羚羊角（镑）1两，甘菊花（去枝梗）1两，赤芍药1两，黄芩1两，甘草（炙）半两。', '上(口父)咀。每服4钱，水1盏半，加生姜5片，煎至8分，去滓温服，不拘时候。', '《医方类聚》卷十引《济生》', '肝气实热，头痛目眩，眼目赤痛，胸中烦闷，梦寐惊恐，肢节不利。目纵横赤脉，沙涩，眵膜多泪，怕日羞明者。', '-', '-', '柴胡', '[\'柴胡\', \'去芦\', \'地骨皮\', \'玄参\', \'羚羊角\', \'甘菊花\', \'枝梗\', \'两赤\', \'芍药\', \'黄芩\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58265, '10908', '救苦化坚汤', '黄耆1钱，人参3分，炙甘草5分，真漏芦1钱，升麻1钱，葛根5分，连翘1钱，牡丹皮3分，当归身3分，生地黄3分，熟地黄3分，白芍药3分，肉桂2分，柴胡8分，黍粘子3分，羌活1钱，独活5分，防风5分，昆布2分，京三棱（煨）2分，广荗（煨）3分，益智仁2分，大麦芽面1钱，神曲末（炒黄色）2分，黄连（去须）3分，黄柏（炒）3分，厚朴3钱2分（姜制）。', '救苦胜灵汤（《准绳·疡医》卷三）、消瘰化坚汤（《医林篡要》卷十）。本方方名，《济生拔萃》本作“救苦胜灵丹”；改为丸剂，名“救苦化坚丸”（见《杏苑》）。如气短不调及喘者，加人参剂量；如夏月，倍白芍药，冬寒则不可用；如有烦躁者，去肉桂；如疮不在少阳经，去柴胡；无肿者，不用黍粘子；如疮不坚硬，不用京三棱、广荗；如无唾多，吐沫，吐食，去益智仁；如有热，或腿脚无力，或躁烦欲去衣，宜加用黄柏，无则不用；如无腹胀，不用厚朴；如气不顺，加橘皮，甚者加木香少许；如只在阳明分为瘰疬者，去柴胡、黍粘子；如在少阳分，为马刀扶瘿者', '《兰室秘藏》（人卫本）卷下', '瘰疬，马刀挟瘿。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'甘草\', \'分真\', \'漏芦\', \'升麻\', \'葛根\', \'连翘\', \'牡丹皮\', \'当归\', \'身分\', \'生地黄\', \'熟地黄\', \'分白\', \'芍药\', \'肉桂\', \'柴胡\', \'粘子分\', \'羌活\', \'独活\', \'防风\', \'昆布\', \'分京\', \'三棱\', \'分广\', \'益智仁\', \'大麦芽\', \'面钱\', \'神曲\', \'黄色\', \'黄连\', \'黄柏\', \'炒分\', \'厚朴\', \'钱分\', \'姜制\']');
INSERT INTO `prescript` VALUES (58266, '10900', '桂心散', '桂（去粗皮）5两，柴胡（去苗）6两，青橘皮（去白，焙）1两，桃仁（汤浸，去皮尖双仁，炒）5两，紫葛（去心，微炙）3两，山茱萸3两，益智（去皮）3两，知母（锉，焙）3两，芎藭3两，当归（炙，锉）3两，五味子3两，豮猪肚1具（切，焙）。', '每服3钱匕，空心陈米饮调下。', '《圣济总录》卷八十七', '冷劳。脏腑虚弱，心腹胀满，四肢羸瘦，困乏无力，不思饮食。', '-', '-', '柴胡', '[\'桂去\', \'粗皮\', \'柴胡\', \'去苗\', \'两青\', \'橘皮\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\', \'紫葛去\', \'心微\', \'山茱萸\', \'益智\', \'去皮\', \'知母\', \'当归\', \'五味子\', \'猪肚\', \'具切\']');
INSERT INTO `prescript` VALUES (58267, '10879', '黄耆汤', '黄耆3钱（蜜炙），当归3钱，川芎3钱，白芍药3钱，生干地黄3钱，虾蟆3钱（去足，炙焦），鳖甲（醋炙焦）3钱，人参2钱，白茯苓2钱，橘皮2钱，半夏曲2钱，柴胡2钱，使君子2钱（略煨），甘草（炙）2钱。', '每服2钱，加生姜、大枣煎，食前服。', '《直指小儿》卷三', '小儿疳劳，咳嗽不定，虚汗骨蒸，渴而复泻，乳食迟进。', '-', '-', '柴胡', '[\'黄耆\', \'钱蜜\', \'当归\', \'川芎\', \'钱白\', \'芍药\', \'钱生\', \'地黄\', \'虾蟆\', \'鳖甲\', \'焦钱\', \'人参\', \'白茯苓\', \'橘皮\', \'半夏\', \'曲钱\', \'柴胡\', \'使君子\', \'钱略\', \'甘草\']');
INSERT INTO `prescript` VALUES (58268, '10874', '柴胡散', '柴胡2两，枳壳半两（麸炒），黄芩1两，赤芍药1两，半夏1两（洗去滑），人参1两。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至5分，去滓热服，不拘时候。', '《医方类聚》卷五十三引《神巧万全方》', '阳明病，外证身热汗出而不恶寒，但恶热，脉迟，发热头眩，小便难，欲作谷疸，胁下坚满，大便秘而呕，口燥；中风，其脉浮大，短气心痛，鼻干，嗜卧，不得汗，一身悉黄，有潮热而哕，身前后肿，刺之虽小愈，外若不解；伤寒3日，少阳受病，口苦干燥，目眩，若已吐下，发汗，谵语；少阳中风，两耳无所闻，目赤，胸中满而烦，不可吐下，吐下则悸而惊；少阴病，胁下坚满，干呕不能饮食，往来寒热，若未吐下，其脉沉紧，恶寒而蜷，时时自烦，不欲厚衣，利清水，色青者，心下必痛，口干燥；伤寒6日，阳脉涩，阴脉弦，当腹中急痛，胸满烦惊，小便不利，谵语', '-', '-', '柴胡', '[\'柴胡\', \'枳壳\', \'半两\', \'黄芩\', \'两赤\', \'芍药\', \'半夏\', \'两洗\', \'人参\']');
INSERT INTO `prescript` VALUES (58269, '10859', '柴胡散', '柴胡（去芦）1两，半夏（汤洗）1分，桂心（去粗皮）2钱，白芍药1钱，甘草（炙）1钱半。', '加生姜7片，大枣1个，水煎温服。寒热欲退，便止此药。', '《岭南卫生方》卷中', '寒热。伤寒后发疟。', '-', '-', '柴胡', '[\'柴胡\', \'去芦\', \'半夏\', \'汤洗\', \'分桂心\', \'粗皮\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (58270, '10854', '荆芥散', '荆芥穗1两，人参1两，白术1两，当归（切细，焙）1两，黄耆1两，芍药1两，桂（去皮）1两，柴胡（去苗）2两，甘草（炙）半两。', '每服5钱，水2盏，煎至1盏，去滓温服。', '《全生指迷方》卷二', '荣卫虚弱，外为风邪相乘，翕翕发热，淅淅恶寒，无有时度，肢节如解，手足酸痛，头目昏晕，久不治成劳气者。', '-', '-', '柴胡', '[\'荆芥\', \'人参\', \'白术\', \'当归\', \'切细\', \'黄耆\', \'芍药\', \'两桂\', \'去皮\', \'柴胡\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58271, '10833', '川大黄丸', '川大黄（锉，微炒）3分，黄芩3分，黄连（去须）3分，当归3分，赤茯苓3分，黄耆（锉）3分，生干地黄3分，赤芍药3分，柴胡（去苗）3分，栀子仁半两。', '每服20丸，以温水送下，不拘时候。', '《圣惠》卷二十七', '虚劳骨热，心神烦躁，大小便难，四肢疼痛。', '-', '-', '柴胡', '[\'大黄\', \'锉微\', \'炒分\', \'黄芩\', \'黄连\', \'当归\', \'分赤\', \'茯苓\', \'分黄耆\', \'分生\', \'地黄\', \'分赤\', \'芍药\', \'柴胡\', \'苗分\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (58272, '10822', '柴胡汤', '柴胡（去苗）2两，竹茹2两，桔梗（炒）2两，紫菀（去土）2两，知母（炒）2两，贝母（去心，炒）2两，诃黎勒皮1两。', '每服5钱匕，水1盏，生地黄汁半盏，乌梅1个（拍碎），煎至8分，去滓温服，日再夜1。', '《圣济总录》卷四十九', '肺痿。久嗽不已，四肢烦热，颊赤咽燥。', '-', '-', '柴胡', '[\'柴胡\', \'竹茹\', \'桔梗\', \'紫菀\', \'知母\', \'贝母\', \'两诃黎勒皮\']');
INSERT INTO `prescript` VALUES (58273, '10802', '救苦汤', '桂枝1钱5分，连翘1钱5分（去隔），红花1钱5分，细辛1钱5分，归尾1钱5分，甘草1钱5分，苍术7分（泔浸，焙），胆草7分，羌活1钱，黄芩1钱，麻黄1钱，柴胡1钱，防风1钱，藁本1钱，黄柏1钱，黄连5分，生地1钱，知母（炒）1钱，白芍2钱。', '食远服。', '《松峰说疫》卷三', '时疫赤眼，两目突然红肿疼痛。', '-', '-', '柴胡', '[\'桂枝\', \'钱分\', \'连翘\', \'钱分\', \'花钱\', \'细辛\', \'钱分\', \'归尾\', \'钱分\', \'甘草\', \'钱分\', \'苍术\', \'分泔浸\', \'胆草分\', \'羌活\', \'黄芩\', \'麻黄\', \'柴胡\', \'防风\', \'藁本\', \'黄柏\', \'黄连\', \'生地\', \'知母\', \'白芍\']');
