INSERT INTO `prescript` VALUES (58299, '10238', '大黄丸', '川大黄1两（锉，微炒），桃仁1两（汤浸，去皮尖双仁，麸炒微黄），干漆1两（捣碎，炒令烟出），赤茯苓3分，甜葶苈3分（隔纸炒令紫色），牛膝1两（去苗），牡丹3分，水蛭半两（炒令黄），芎半两，桂心半两，柴胡3分（去苗），牡蒿3分，人参半两（去芦头），当归半两（锉，微炒），干姜1分（炮裂，锉），虻虫半两（微炒令黄，去翅足），川椒1两（去目及闭口者，微炒去汗），(庶虫)虫半两（炒令微黄），吴茱萸1分（汤浸7遍，焙干，微炒）生地黄1两。', '每服10丸，食前以温酒送下。', '《圣惠》卷七十九', '产后恶血凝滞，月水不通。', '-', '-', '柴胡', '[\'大黄\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'干漆\', \'捣碎\', \'炒令\', \'烟出\', \'茯苓\', \'分甜\', \'分隔\', \'炒令\', \'紫色\', \'牛膝\', \'两去\', \'牡丹\', \'水蛭\', \'半两\', \'炒令\', \'黄芎\', \'半两\', \'桂心\', \'半两\', \'柴胡\', \'分去\', \'人参\', \'半两\', \'芦头\', \'当归\', \'半两\', \'干姜\', \'分炮裂\', \'半两\', \'炒令\', \'黄去\', \'足川椒\', \'两去\', \'目及\', \'闭口\', \'虫虫\', \'半两\', \'炒令\', \'微黄\', \'吴茱萸\', \'分汤\', \'浸遍\', \'焙干\', \'生地黄\']');
INSERT INTO `prescript` VALUES (58300, '10219', '调肝和血丸', '当归8钱，白芍5钱，柴胡3钱（醋炙），香附4钱，薄荷3钱，丹皮4钱，栀子3钱（炒），郁金3钱，大黄4钱（炭），犀角1钱半，生地6钱，青皮2钱。', '-', '《慈禧光绪医方选议》', '养血调肝，凉血和血。主肝郁血虚，热在血分者。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'柴胡\', \'香附\', \'薄荷\', \'丹皮\', \'栀子\', \'炒郁\', \'金钱\', \'大黄\', \'钱炭\', \'犀角\', \'半生\', \'青皮\']');
