INSERT INTO `prescript` VALUES (58305, '10140', '菊花汤', '甘菊花半两（择），地骨皮半两（去土），升麻半两，防风半两（去叉），黄连半两（去须），赤茯苓（去黑皮）半两，萎蕤1两，柴胡1两（去苗），木通（锉）1两。', '每服5钱匕，水2盏半，加竹叶7片，煎至1盏，去滓，入芒消末1钱匕，食后、临卧温服。如腹脏易利，即少用芒消。', '《圣济总录》卷一○七', '肝风邪热冲眼，色赤痛痒不定。', '-', '-', '柴胡', '[\'甘菊花\', \'半两\', \'地骨皮\', \'半两\', \'升麻\', \'半两\', \'防风\', \'半两\', \'黄连\', \'半两\', \'茯苓\', \'黑皮\', \'半两\', \'萎蕤\', \'柴胡\', \'两去\', \'苗木\']');
