INSERT INTO `prescript` VALUES (58315, '9979', '柴胡清肝散', '柴胡、黄芩（炒）5分，人参1钱，山栀（炒）1钱，川芎1钱，连翘8分，桔梗8分，甘草5分。', '柴胡清肝汤（《医学入门》卷八）、柴胡清肝饮（《审视瑶函》卷四）。', '《校注妇人良方》卷二十四', '清肝解郁。主肝胆热盛，头昏目眩，乍寒乍热，或寒热往来，口中味酸，或耳前后肿痛，或发疮疡，或患乳痈，脉弦数。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'炒分\', \'人参\', \'钱山\', \'川芎\', \'连翘\', \'桔梗\', \'甘草\']');
INSERT INTO `prescript` VALUES (58316, '9968', '大黄散', '川大黄1两（锉碎，微炒），泽泻1两，黄芩1两，栀子仁1两，柴胡1两（去苗），羚羊角屑1两，木香（石膏）2两，甘草1两（炙微赤，锉），木通1两（锉）。', '每服4钱，以水1中盏，加淡竹叶2-7片，煎至6分，去滓，食前温服。', '《圣惠》卷二十六', '心劳热，口疮，心烦腹满，小肠不利。', '忌炙、热面，生果。', '-', '柴胡', '[\'大黄\', \'泽泻\', \'黄芩\', \'栀子\', \'柴胡\', \'两去\', \'羚羊角\', \'木香\', \'石膏\', \'甘草\', \'两炙微\', \'木通\']');
INSERT INTO `prescript` VALUES (58317, '9964', '柴胡散', '柴胡3分（去苗），鳖甲1两半（涂醋炙微黄，去裙襕），知母1两，桑根白皮1两半（锉），旋覆花3分，甘草3分（炙微赤，锉），天灵盖1两半（涂酥炙微黄）。', '每服4钱，以童便1中盏，煎至5分，去滓，加地黄汁半合，更煎1-2沸，温服，不拘时候。', '《圣惠》卷十四', '伤寒后发疟，寒热不定，四肢无力，心烦头痛，不思饮食。', '-', '-', '柴胡', '[\'柴胡\', \'分去\', \'鳖甲\', \'两半\', \'涂醋\', \'微黄\', \'知母\', \'两桑根\', \'白皮\', \'两半\', \'旋覆花\', \'甘草\', \'分炙微\', \'天灵盖\', \'两半\', \'微黄\']');
INSERT INTO `prescript` VALUES (58318, '9930', '柴胡汤', '柴胡（去苗）1两，桃仁（去皮尖双仁，炒）半两，天门冬（去心）3分，麦门冬（去心，焙）1两，甘草（炙）1两，白茯苓（去黑皮）1两，山芋1两，黄耆（锉）1两，阿胶（炙令燥）1两，人参1两。', '每服3钱匕，水1盏，煎至6分，去滓温服，不拘时候。', '《圣济总录》卷一五六', '妊娠咳嗽，胸满气急，减食。', '-', '-', '柴胡', '[\'柴胡\', \'桃仁\', \'皮尖\', \'双仁\', \'半两\', \'天门冬\', \'心分\', \'麦门冬\', \'甘草\', \'白茯苓\', \'黑皮\', \'山芋\', \'两黄耆\', \'阿胶\', \'炙令燥\', \'人参\']');
INSERT INTO `prescript` VALUES (58319, '9919', '慢白汤', '西党参9g，全当归9g，生白术9g，生黄耆15g，怀山药15g，云茯苓15g，熟枣仁15g，制首乌15g，银柴胡3g，炒白芍6g，大红枣6枚。', '骨蒸潮热，去生白术、当归、红枣，加生龟版、生鳖甲各24g，地骨皮、青蒿梗各9g；盗汗，去当归、银柴胡，加粉龙骨15g，牡蛎24g（先煎），麦冬12g，五味子3g。口干，去当归、白术、大枣，加北沙参9g，冬青子15g，粉丹皮6g；肝脾肿大，去生黄耆，加炙鳖甲24g（先煎），大丹参9g，粉丹皮6g颈部腋下瘰疬，去党参、黄耆、当归、白术、怀山药、红枣，加夏枯草12g，黑元参12g，川贝母9g，天花粉12g，生牡蛎24g，粉丹皮6g；鼻衄，去党参、黄耆、当归、白术、山药、银柴胡、大红枣，加焦山栀、肥知母各9g，侧', '方出《中医临证撮要》，名见《古今名方》', '补气血，益脾肾。主慢性白血病。头昏耳鸣，心悸气短，纳食不香，面色萎黄，浮肿，腰酸腰痛，疲乏无力，潮热，腹胀，大便时结时溏，舌苔淡薄，或薄白，脉象细濡涩，或沉微迟。', '-', '-', '柴胡', '[\'党参\', \'当归\', \'白术\', \'生黄耆\', \'山药\', \'茯苓\', \'枣仁\', \'首乌\', \'银柴胡\', \'白芍\', \'红枣\']');
INSERT INTO `prescript` VALUES (58320, '9903', '麻黄汤', '麻黄（去根节，煎掠去沫，焙）半两，羌活（去芦头）半两，牡丹（去心）半两，独活（去芦头）半两，栀子（去皮）半两，柴胡（去苗）半两，桔梗（锉，炒）半两，升麻半两，荆芥穗半两，大黄（锉，炒）半两，半夏（洗去滑，焙）半两，木香半两，知母（焙）半两，黄芩（去黑心）半两。', '麻黄羌活汤（《普济方》卷一九八）。', '《圣济总录》卷三十四', '温疟初发，身热，头痛不可忍，临醒时即寒栗战动。', '-', '-', '柴胡', '[\'麻黄\', \'根节\', \'掠去\', \'半两\', \'羌活\', \'芦头\', \'半两\', \'牡丹\', \'去心\', \'半两\', \'独活\', \'芦头\', \'半两\', \'栀子\', \'去皮\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'桔梗\', \'半两\', \'升麻\', \'半两\', \'荆芥\', \'半两\', \'大黄\', \'半两\', \'半夏\', \'洗去\', \'半两\', \'木香\', \'半两\', \'知母\', \'半两\', \'黄芩\', \'黑心\', \'半两\']');
INSERT INTO `prescript` VALUES (58321, '9853', '调脾除湿汤', '升麻2钱，柴胡2钱，防风2钱，麦芽2钱，苍术1钱2分，陈皮1钱2分，猪苓1钱2分，泽泻1钱2分，半夏1钱2分，木通8分，羌活8分。', '水煎，温服。', '《玉案》卷五', '湿气伤脾，久泻不止，中气下陷，小便黄赤，腹微作胀。', '-', '-', '柴胡', '[\'升麻\', \'柴胡\', \'防风\', \'麦芽\', \'苍术\', \'钱分\', \'陈皮\', \'钱分\', \'猪苓\', \'钱分\', \'泽泻\', \'钱分\', \'半夏\', \'钱分\', \'木通\', \'羌活\']');
