INSERT INTO `prescript` VALUES (58323, '9843', '黄芩汤', '黄芩3分，石膏3分，赤茯苓（去黑皮）半两，甘草（锉）半两，葛根（锉）半两，五加皮（锉）半两，麻黄（去根节）半两，柴胡（去苗）1两。', '每服3钱匕，水1盏，加生姜半分（切），煎至8分，去滓，食后温服。', '《圣济总录》卷六十', '胃中热盛，食已如饥，唇燥口干。', '-', '-', '柴胡', '[\'黄芩\', \'石膏\', \'分赤\', \'茯苓\', \'黑皮\', \'半两\', \'甘草\', \'半两\', \'葛根\', \'半两\', \'五加皮\', \'半两\', \'麻黄\', \'根节\', \'半两\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58324, '9842', '柴胡散', '柴胡2两（去苗），紫葳1两，白术1两，甘草半两（炙微赤，锉），麦门冬1两（去心），熟干地黄1两，芎藭1两，肉苁蓉1两（汤浸1宿，刮去皱皮，炙令干）。', '每服4钱，以水1大盏，加大枣3枚，煎至5分，去滓，食前温服。', '《圣惠》卷七十六', '曾伤六月胎。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'紫葳\', \'白术\', \'甘草\', \'半两\', \'炙微\', \'麦门冬\', \'两去\', \'心熟\', \'地黄\', \'肉苁蓉\', \'两汤\', \'浸宿\', \'刮去\', \'皱皮炙令\']');
INSERT INTO `prescript` VALUES (58325, '9822', '大黄散', '川大黄半两（锉碎，微炒），黄连半两（去须），麦门冬1两（去心），栀子仁半两（锉），柴胡1两（去苗），甘草半两（炙微赤，锉）。', '每服5钱，以水1大盏，加豉少半合，葱白2茎，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十六', '时气头痛，壮热不解，心神烦躁。', '-', '-', '柴胡', '[\'大黄\', \'半两\', \'黄连\', \'半两\', \'麦门冬\', \'两去\', \'栀子\', \'半两\', \'柴胡\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58326, '9816', '桂心丸', '桂心3分，川乌头1两（炮裂，去皮脐），柴胡1两（去苗），赤芍药3分，槟榔3分，木香半两，桃仁1两（汤浸，去皮尖双仁，麸炒微黄），当归3分（锉，微炒）。', '每服20丸，以粥饮送下，不拘时候。', '《圣惠》卷四十九', '痃气急痛，木思饮食，肌体瘦弱。', '-', '-', '柴胡', '[\'桂心\', \'分川\', \'乌头\', \'两炮裂\', \'去皮\', \'柴胡\', \'两去\', \'芍药\', \'槟榔\', \'木香\', \'半两\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'当归\']');
INSERT INTO `prescript` VALUES (58327, '9780', '胡蜣螂散', '胡蜣螂2个（去翅足，微炒），赤芍药1分，柴胡半两（去苗），熊胆半分（细研），鳖甲1分（涂醋，炙令黄，去裙襕），川大黄1分（锉碎，微炒），枳壳1分（麸炒微黄，去瓤），赤茯苓1分，紫菀1分（洗去苗土），甘草1分（炙微赤，锉），人参1分（去芦头），生姜半分（切，烧灰），麝香1钱（细研），蛇黄1分（细研），牛黄1分（细研）。', '每服半钱，以温水调下，1日3服。', '《圣惠》卷八十八', '小儿骨热，黄瘦不食，多卧。', '-', '-', '柴胡', '[\'蜣螂\', \'足微\', \'芍药\', \'柴胡\', \'半两\', \'去苗\', \'熊胆\', \'半分\', \'细研\', \'鳖甲\', \'分涂\', \'炙令\', \'黄去\', \'大黄\', \'枳壳\', \'分麸\', \'微黄\', \'茯苓\', \'紫菀\', \'分洗\', \'苗土\', \'甘草\', \'分炙微\', \'人参\', \'分去\', \'芦头\', \'生姜\', \'半分\', \'烧灰\', \'麝香\', \'钱细\', \'研蛇\', \'黄分\', \'细研\', \'牛黄\', \'分细研\']');
INSERT INTO `prescript` VALUES (58328, '9762', '柴胡汤', '柴胡（去苗）3分，赤茯苓（去黑皮）3分，桔梗（炒）1两，木通（锉）1两，芍药半两，鳖甲（去裙襕，醋蘸，慢火炙令黄色）半两，郁李仁（汤浸，去皮）半两。', '柴胡饮（《原书卷七十三）。', '《圣济总录》卷七十二', '症癖气胀，腹痛，胁肋胀满，不思食饮。', '-', '-', '柴胡', '[\'柴胡\', \'苗分\', \'茯苓\', \'黑皮\', \'桔梗\', \'木通\', \'芍药\', \'半两\', \'鳖甲\', \'慢火\', \'炙令\', \'黄色\', \'半两\', \'郁李仁\', \'汤浸\', \'去皮\', \'半两\']');
