INSERT INTO `prescript` VALUES (58330, '9743', '柴胡散', '柴胡根1两，半夏5钱（洗）。', '加生姜，水煎服。心下痞，加枳实1钱；有里证，加大黄，初1服1钱，次2钱，又3钱，邪尽则止。', '《保命集》卷中', '伤寒，往来寒热而呕。', '-', '-', '柴胡', '[\'柴胡\', \'根两\', \'半夏\']');
INSERT INTO `prescript` VALUES (58331, '9739', '麻黄散', '麻黄2两（去根节），川大黄1两（锉碎，微炒），葛根1两（锉），甘草半两（炙微赤，锉），桂心1两，柴胡1两（去苗），赤芍药1两。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓热服，不拘时候。衣盖取汗。', '《圣惠》卷十七', '热病2日，头痛壮热，肢节不利。', '-', '-', '柴胡', '[\'麻黄\', \'两去\', \'根节川\', \'大黄\', \'葛根\', \'甘草\', \'半两\', \'炙微\', \'桂心\', \'柴胡\', \'两去\', \'芍药\']');
INSERT INTO `prescript` VALUES (58332, '9720', '理劳神功散', '秦艽1钱，续断1钱，杜仲1钱，香附7分，当归8分，骨碎补1钱，陈皮7分，甘草3分，五加皮8分，金毛脊8分，柴胡8分，葛根8分。', '加生姜、大枣。若发热，加柴胡7分，干葛8分；若咳嗽，加白前、桔梗6分；若久嗽，加紫菀、百部8分；若腰痛，加破故纸1钱；若骨蒸夜热，加地骨皮、青蒿、鳖甲8分；若胸满，加砂仁、木香6分。', '《不居集》上集卷十', '伤筋动骨，劳苦太过，损气耗血，而邪有不能外出者。', '-', '-', '柴胡', '[\'秦艽\', \'续断\', \'杜仲\', \'香附\', \'当归\', \'骨碎补\', \'陈皮分\', \'甘草\', \'五加皮\', \'金毛\', \'脊分\', \'柴胡\', \'葛根\']');
INSERT INTO `prescript` VALUES (58333, '9717', '救左汤', '熟地1两，白芍5钱，柴胡3分，花粉1钱5分。', '水煎服。', '《惠直堂方》卷一', '中风后，左手不仁，或目不识人。', '-', '-', '柴胡', '[\'熟地\', \'白芍\', \'柴胡\', \'花粉\', \'钱分\']');
INSERT INTO `prescript` VALUES (58334, '9708', '羚羊角散', '羚羊角屑1两，地骨皮3分，黄芩3分，麦门冬1两半（去心，焙），秦艽半两（去苗），柴胡半两（去苗），栀子仁半两，车前子3分，葳蕤半两，川升麻半两，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十二', '丹石毒上攻眼目，涩痛瞳赤，心神烦躁，唇口干燥。', '-', '-', '柴胡', '[\'羚羊角\', \'地骨皮\', \'黄芩\', \'麦门冬\', \'两半\', \'秦艽\', \'半两\', \'去苗\', \'柴胡\', \'半两\', \'去苗\', \'栀子\', \'半两\', \'车前子\', \'葳蕤\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58335, '9707', '宽缓汤', '柴胡2钱，茯苓2钱，当归3钱，白芍5钱，甘草1钱，苏叶1钱，黄芩1钱，竹叶30片。', '水煎服。', '《辨证录》卷五', '少阳之气不和，胸中胀急，烦闷不安，上不能食，下不能出，大小便窘迫之极。', '-', '-', '柴胡', '[\'柴胡\', \'茯苓\', \'当归\', \'白芍\', \'甘草\', \'苏叶\', \'黄芩\', \'钱竹\', \'叶片\']');
