INSERT INTO `prescript` VALUES (58338, '9667', '柴胡散', '柴胡3分（去苗），地骨皮3分，玄参半两，黄芩3分，石膏1两，甘菊花3分，甘草半两（炙微赤，锉），羌活半两，防风半两（去芦头），川朴消1两半。', '每服5钱，以水1大盏，加竹叶3-7片，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒热毒气攻眼，障翳赤涩疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'分去\', \'地骨皮\', \'玄参\', \'半两\', \'黄芩\', \'石膏\', \'甘菊花\', \'甘草\', \'半两\', \'炙微\', \'羌活\', \'半两\', \'防风\', \'半两\', \'芦头川\', \'朴消\', \'两半\']');
