INSERT INTO `prescript` VALUES (58340, '9598', '蚕鳖散', '川芎1钱，当归1钱5分，红花4分，羌活6分，防风8分，白僵蚕1钱2分，土鳖虫7个（捣碎），穿山甲3大片（酒炙），柴胡7分，生甘草4分。', '水、酒各半，煎8分服。疮在下部，加牛膝1钱。', '《洞天奥旨》卷十三', '破伤风疮。', '-', '-', '柴胡', '[\'川芎\', \'当归\', \'钱分\', \'红花\', \'羌活\', \'防风\', \'白僵蚕\', \'钱分\', \'土鳖虫\', \'捣碎\', \'穿山甲\', \'大片\', \'柴胡\', \'生甘草\']');
INSERT INTO `prescript` VALUES (58341, '9594', '黄耆生地汤', '麻黄2钱（先煎去沫方入），黄耆1钱，白术5分，人参5分，柴胡5分，防风5分，生地5分，羌活1钱，黄柏1钱，甘草3分，杏仁3个。', '临卧服。', '《嵩崖尊生》卷十', '风湿。阳气不升，四肢倦怠，走注身痛，阴室中汗出，懒语。', '-', '-', '柴胡', '[\'麻黄\', \'钱先\', \'方入\', \'黄耆\', \'白术\', \'人参\', \'柴胡\', \'防风\', \'生地\', \'羌活\', \'黄柏\', \'甘草\', \'杏仁\']');
INSERT INTO `prescript` VALUES (58342, '9574', '大茱萸丸', '吴茱萸半升，细辛半两，芍药半两，柴胡半两，旋覆花半两，黄芩半两，紫菀半两，人参半两，白术半两，茯苓半两，干姜半两，桂心半两，附子（炮）半两，甘草（炙）半两，半夏（洗）半两，当归半两，（1方有前胡、干地黄、蜀椒，无柴胡、黄芩、桂心）。', '每次3丸，食前服，1日3次。不知稍加。', '《外台》卷七引《范汪方》', '心腹寒疝，胸中有逆气，时上抢心痛，烦渴，不得卧，面目恶风，悸掉，惕惕时惊，不欲饮食而呕，变发寒热。', '忌生葱、羊肉、饧、酢物、桃、李、雀肉、猪肉、生菜、海藻、菘菜。', '-', '柴胡', '[\'吴茱萸\', \'半升\', \'细辛\', \'半两\', \'芍药\', \'半两\', \'柴胡\', \'半两\', \'旋覆花\', \'半两\', \'黄芩\', \'半两\', \'紫菀\', \'半两\', \'人参\', \'半两\', \'白术\', \'半两\', \'茯苓\', \'半两\', \'干姜\', \'半两\', \'桂心\', \'半两\', \'附子\', \'半两\', \'甘草\', \'半两\', \'半夏\', \'半两\', \'当归\', \'两方\', \'有前\', \'胡干\', \'地黄\', \'蜀椒\', \'柴胡\', \'黄芩\', \'桂心\']');
