INSERT INTO `prescript` VALUES (58347, '9496', '黄药子散', '黄药子1两，当归1两，芍药1两，生地黄1两，黄芩1两，人参1两，白术1两，知母1两，石膏1两，川芎1分，桔梗1分，甘草1两，紫菀1分半，槐花子1分半，柴胡1分半。', '每服3钱，水1盏，煎至7分，滤汁温服，食前但1服。', '《宜明论》卷十一', '月事不止，烦渴闷乱，心腹急痛，肢体困倦，不美饮食。', '-', '-', '柴胡', '[\'黄药子\', \'当归\', \'芍药\', \'生地黄\', \'黄芩\', \'人参\', \'白术\', \'知母\', \'石膏\', \'川芎\', \'桔梗\', \'甘草\', \'紫菀\', \'分半\', \'槐花\', \'子分\', \'柴胡\', \'分半\']');
