INSERT INTO `prescript` VALUES (58349, '9466', '荆芥汤', '荆芥穗1钱，牡丹皮（去心）1钱，川芎1钱，蔓荆子1钱，柴胡（去毛）1钱，羌活1钱，鳖甲（醋炙）1钱，天灵盖（酥炙，炒黑色）1钱，沉香1钱，甘草1钱，当归1钱，连翘子半两，秦艽3铢。', '每服2钱，水1盏，先煎令沸后，入药末，一搅便泻出，食后、临卧时和滓服。一方，去滓温服。', '《普济方》卷十五引《圣惠》', '一切男子肝脏壅热，血气皆滞，有所劳伤，肝家自感风劳之气，筋脉羸弱，目视昏暗，欲睡还觉，常多瞋怒，头旋目晕，面色青，浑身痿瘁，口中多涎，小便黄赤。', '-', '-', '柴胡', '[\'荆芥\', \'牡丹皮\', \'心钱\', \'川芎\', \'蔓荆子\', \'柴胡\', \'毛钱\', \'羌活\', \'鳖甲\', \'天灵盖\', \'黑色\', \'沉香\', \'甘草\', \'当归\', \'连翘\', \'半两\', \'秦艽\']');
INSERT INTO `prescript` VALUES (58350, '9414', '柴胡桂枝鳖甲汤', '柴胡3钱，鳖甲3钱（醋炙），甘草2钱，桂枝3钱，半夏3钱，芍药3钱，茯苓3钱。', '煎大半杯，温服。胃寒，加干姜、川椒、附子。', '《四圣心源》卷六', '胃胆上逆，痛在心胸。', '-', '-', '柴胡', '[\'柴胡\', \'鳖甲\', \'甘草\', \'桂枝\', \'半夏\', \'芍药\', \'茯苓\']');
INSERT INTO `prescript` VALUES (58351, '9391', '柴胡散', '柴胡1两（去苗），知母2两，黄连1两（去须），甘草3分（炙微赤，锉），百合2两，秦艽1两（去苗），栝楼根1两。', '百合柴胡汤（《圣济总录》卷二十九）。', '《圣惠》卷十三', '伤寒百合病久不愈，欲成痨。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'知母\', \'黄连\', \'两去\', \'甘草\', \'分炙微\', \'百合\', \'秦艽\', \'两去\', \'栝楼\', \'根两\']');
INSERT INTO `prescript` VALUES (58352, '9375', '黄耆柴胡汤', '黄耆2钱，柴胡梢1钱，羌活5分，连翘1钱2分，肉桂3分，土瓜根3分，黄柏3分（酒洗），生地3分，当归尾7钱5分。', '酒、水各半煎，热服。', '《疡科选粹》卷二', '大腿近膝股肉生附骨疽，不辨肉色，温肿木硬，痛势甚大，其脉弦细，按之洪缓略有力者。', '-', '-', '柴胡', '[\'黄耆\', \'柴胡\', \'羌活\', \'连翘\', \'钱分\', \'肉桂\', \'分土瓜\', \'根分\', \'黄柏\', \'分酒\', \'生地\', \'当归\', \'尾钱分\']');
INSERT INTO `prescript` VALUES (58353, '9372', '柴胡芍药汤', '柴胡2两，赤芍药2分，人参2分，甘草2分，半夏6钱。', '柴胡去芩加芍汤（《医级》卷七）。', '《鸡峰》卷五', '伤寒温疫，身体壮热，头痛项强，腰背四肢烦疼，胁下牢满，干呕哕逆，不能饮食；及妇人经水方来适断，热入血室，寒热如疟，谵言妄语。少阳寒热腹痛。', '-', '-', '柴胡', '[\'柴胡\', \'两赤\', \'芍药\', \'人参\', \'甘草\', \'半夏\']');
INSERT INTO `prescript` VALUES (58354, '9339', '黄连救苦汤', '黄连1钱，升麻1钱，葛根1钱，柴胡1钱，赤芍1钱，川芎1钱，归尾1钱，连翘1钱，桔梗1钱，黄芩1钱，羌活1钱，防风1钱，金银花1钱，甘草节1钱。', '水2碗，煎8分，临服入酒1杯，食后服。', '《外科正宗》卷三', '脑疽、发鬓、发颐及天行时毒，初起憎寒壮热，头面耳项俱肿。对口疽，初起寒热发肿。', '-', '-', '柴胡', '[\'黄连\', \'升麻\', \'葛根\', \'柴胡\', \'赤芍\', \'川芎\', \'钱归尾\', \'连翘\', \'桔梗\', \'黄芩\', \'羌活\', \'防风\', \'金银\', \'花钱\', \'甘草\', \'节钱\']');
INSERT INTO `prescript` VALUES (58355, '9324', '调肝饮', '当归1钱5分，川芎1钱5分，乌药1钱5分，玄胡索1钱5分，青皮1钱5分，柴胡1钱，槟榔1钱，广木香1钱，桃仁（去皮尖）1钱。', '水煎，热服。', '《玉案》卷五', '季肋痛连小腹。', '-', '-', '柴胡', '[\'当归\', \'钱分\', \'川芎\', \'钱分\', \'乌药\', \'钱分\', \'胡索\', \'钱分\', \'青皮\', \'钱分\', \'柴胡\', \'槟榔\', \'钱广\', \'木香\', \'桃仁\', \'皮尖\']');
INSERT INTO `prescript` VALUES (58356, '9321', '黄耆肉桂柴胡酒煎汤', '黄耆2钱，当归梢2钱，柴胡1钱5分，黍粘子1钱（炒），连翘1钱，肉桂1钱，升麻7分，炙甘草5分，黄柏5分。', '内托黄耆酒煎汤（《东垣试效方》卷三）、内托酒煎汤（《医学入门》卷八）、黄耆柴胡汤（《杏苑》卷八）、内托酒煎散（《外科大成》卷二）。本方方名，《医学正传》引作“托里黄耆汤”；《疡科选粹》引作“柴胡黍粘子汤”。', '《兰室秘藏》卷下', '附骨痈，坚硬漫肿，不辨肉色，行步作痛，按之大痛。', '-', '-', '柴胡', '[\'黄耆\', \'当归\', \'柴胡\', \'钱分\', \'粘子\', \'连翘\', \'肉桂\', \'升麻\', \'甘草\', \'黄柏\']');
INSERT INTO `prescript` VALUES (58357, '9313', '柴胡散', '柴胡1两半（去苗），黄芩3分，麦门冬1两（去心，焙），半夏半两（汤洗7遍去滑），枳壳1两（麸炒令黄，去瓤），枇杷叶3分（拭去毛，炙微黄），甘草半两（炙微赤，锉），人参半两（去芦头）。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十一', '伤寒，干呕不止，心胸烦躁，四肢热。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'去苗\', \'黄芩\', \'麦门冬\', \'两去\', \'半夏\', \'半两\', \'洗遍\', \'枳壳\', \'两麸\', \'炒令\', \'黄去\', \'枇杷叶\', \'拭去\', \'微黄\', \'甘草\', \'半两\', \'炙微\', \'人参\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (58358, '9281', '解郁和肝丸', '当归23两，栀子（炒）23两，黄芩5两，茯苓5两，橘皮5两，枳壳（炒）5两，厚朴（炙）5两，郁金5两，木香5两，香附（炙）17两，川芎9两2钱5分，白芍15两，神曲（炒）15两，山楂15两，砂仁3两2钱5分，青皮（炒）6两，甘草2两5钱，法半夏9两2钱5分，苍术（炒）9两2钱5分，柴胡3两2钱5分。', '每服2钱，日服2次，温开水送下。', '《北京市中药成方选集》', '舒肝开郁，顺气消胀。主肝郁不舒，气逆胸满，两胁膨胀，胃痛恶心。', '-', '-', '柴胡', '[\'当归\', \'栀子\', \'黄芩\', \'茯苓\', \'橘皮\', \'枳壳\', \'厚朴\', \'郁金\', \'木香\', \'香附\', \'川芎\', \'两钱分\', \'白芍\', \'神曲\', \'山楂\', \'砂仁\', \'两钱分\', \'青皮\', \'甘草\', \'两钱法\', \'半夏\', \'两钱分\', \'苍术\', \'两钱分\', \'柴胡\', \'两钱分\']');
INSERT INTO `prescript` VALUES (58359, '9280', '柴胡三白汤', '柴胡3钱，人参1钱5分，半夏1钱，白芍药1钱，白术1钱，白茯苓1钱，甘草5分，黄连8分，生姜5片。', '加大枣2枚，水煎，食前服。', '《杏苑》卷三', '身热，泄泻，烦渴。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'钱分\', \'半夏\', \'钱白\', \'芍药\', \'白术\', \'白茯苓\', \'甘草\', \'黄连\', \'生姜\']');
INSERT INTO `prescript` VALUES (58360, '9258', '柴胡地骨皮汤', '柴胡（去苗）1两，地骨皮1两，桔梗（炒）1两，甘草（炙）半两。', '每服1钱匕，水1小盏，煎至5分，去滓，食后、临卧温服。', '《圣济总录》卷一六八', '小儿潮热，饮食不为肌肉，黄瘁，夜卧不安，时有虚汗。', '-', '-', '柴胡', '[\'柴胡\', \'地骨皮\', \'桔梗\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58361, '9221', '柴胡汤', '甘草（炙）2两，柴胡2两，麻黄2两（去节，煎去沫），大枣12枚（擘），食茱萸1升。', '以水6升，煮取3升，适寒温服1升，每日3次。食噫醋，春秋冬夏，去茱萸。加枸杞1斤；六月，加小麦1斤，石膏3两；秋，去石膏，加甘草1两；九月，去麻黄，加干姜1两；十月，加芎藭3分。', '《外台》卷三十三引《古今录验》', '除热下气。主妊娠不欲食，或吐，或食噫醋。', '忌海藻、菘莱。', '-', '柴胡', '[\'甘草\', \'柴胡\', \'麻黄\', \'两去\', \'大枣\', \'食茱萸\']');
INSERT INTO `prescript` VALUES (58362, '9194', '调经补真汤', '独活2分，干姜（炮）2分，藁本2分，防风2分，苍术2分，麻黄（不去节）5分，炙甘草5分，人参（去芦）5分，当归身5分，白术5分，生黄芩5分，升麻5分，黄耆7分，良姜1钱，泽泻1钱，羌活1钱，柴胡4钱，杏仁2个，桂枝少许，白葵花7朵（去萼）。', '调经固真汤（《东垣试效方》卷四）。', '《兰室秘藏》卷中', '冬后一月，白带再来，阴户中寒。', '-', '-', '柴胡', '[\'独活\', \'干姜\', \'炮分\', \'藁本\', \'防风\', \'苍术\', \'麻黄\', \'不去\', \'节分\', \'甘草\', \'人参\', \'芦分\', \'当归\', \'身分\', \'白术\', \'黄芩\', \'升麻\', \'分黄耆\', \'分良\', \'姜钱\', \'泽泻\', \'羌活\', \'柴胡\', \'杏仁\', \'桂枝\', \'少许\', \'葵花\']');
INSERT INTO `prescript` VALUES (58363, '9181', '柴胡散', '柴胡（去苗）1两，白茯苓1两，陈橘皮（汤浸，去白瓢，焙）1两，知母1两，桔梗（去芦头）1两，黄耆（锉）1两，百合2两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十三', '伤寒百合病，羸瘦，不食少力。', '-', '-', '柴胡', '[\'柴胡\', \'白茯苓\', \'两陈\', \'橘皮\', \'汤浸\', \'白瓢\', \'知母\', \'桔梗\', \'芦头\', \'黄耆\', \'百合\']');
INSERT INTO `prescript` VALUES (58364, '9165', '茯神散', '茯神1两，羚羊角屑1两，石膏2两，防风1两（去芦头），赤芍药1两，人参1两（去芦头），柴胡1两半（去苗），天门冬1两（去心），桃仁1两半（汤浸，去皮尖双仁，麸炒微黄），独活1两，郁李仁1两（汤浸，去皮，微炒），生干地黄1两，枳壳1两（麸炒微黄，去瓤），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人血风劳气，头疼目赤，胸背气壅，四肢疼痛，心烦惊悸，少欲饮食。', '-', '-', '柴胡', '[\'茯神\', \'羚羊角\', \'石膏\', \'防风\', \'两去\', \'芦头\', \'芍药\', \'人参\', \'两去\', \'芦头\', \'柴胡\', \'两半\', \'去苗\', \'天门冬\', \'两去\', \'桃仁\', \'两半\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'独活\', \'郁李仁\', \'两汤\', \'去皮\', \'生干\', \'地黄\', \'枳壳\', \'两麸\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58365, '9154', '柴胡消肿汤', '黍粘子（炒）5钱，黄连5钱，归尾1两，甘草1两，花粉1钱，黄耆1钱5分，黄芩2钱，柴胡2钱，连翘3钱，红花5分，玄参1钱。', '上(口父)咀。每服5钱，水2钟，煎8分，食后热服。', '《外科启玄》卷十二', '马刀疮肿盛。', '忌酒、湿面、房劳、气怒。', '-', '柴胡', '[\'粘子\', \'黄连\', \'钱归尾\', \'甘草\', \'花粉\', \'黄耆\', \'钱分\', \'黄芩\', \'柴胡\', \'连翘\', \'红花\', \'玄参\']');
