INSERT INTO `prescript` VALUES (58368, '9133', '麻黄散', '麻黄2两（去根节），知母1两，赤芍药1两半，葛根1两，黄芩半两，甘草半两（炙微赤，锉），白药半两，栀子仁半两，细辛半两，柴胡半两（去苗），石膏2两。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓，稍热服，不拘时候。衣盖取汗。', '《圣惠》卷十七', '热病3日，头疼壮热。', '-', '-', '柴胡', '[\'麻黄\', \'两去\', \'根节\', \'知母\', \'两赤\', \'芍药\', \'两半\', \'葛根\', \'黄芩\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'白药\', \'半两\', \'栀子\', \'半两\', \'细辛\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'石膏\']');
INSERT INTO `prescript` VALUES (58369, '9126', '柴胡散', '柴胡（去苗）1两，蛇衔1两，黄连（去须）3分，芒消（研）3分，细辛（去苗叶）半两，竹叶（焙）半两。', '每服2钱匕，水1盏，煎至6分，去滓，食后、临卧温服。', '《圣济总录》卷一○六', '风邪攻目，目睛疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两蛇\', \'黄连\', \'分芒\', \'消研分\', \'细辛\', \'苗叶\', \'半两\', \'竹叶\', \'半两\']');
INSERT INTO `prescript` VALUES (58370, '9125', '大腹子散', '大腹子1两，木香1两，当归半两（锉，微炒），芎半两，瞿麦半两，柴胡1两（去苗）。', '每服4钱，用水1中盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒后，真气尚虚，因合阴阳，致小腹拘急，便溺涩痛。', '-', '-', '柴胡', '[\'大腹子\', \'木香\', \'当归\', \'半两\', \'半两\', \'瞿麦\', \'半两\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (58371, '9092', '麻黄苍术汤', '麻黄8钱，苍术5钱，黄耆1钱5分，草豆蔻6分，柴胡5分，羌活5分，生甘草4分，当归梢4分，防风4分，炙甘草3分，黄芩3分，五味子9个。', '上（口父）咀，分作2服。以水2盏，煎至1盏，临卧稍热服。', '《兰室秘藏》卷下', '秋冬每夜五更嗽，连声不绝，乃至天晓日高方缓，口苦，两胁下痛，心下痞闷，卧而多惊，筋挛，肢节疼痛，痰唾涎沫，日晚神昏呵欠，不进饮食。', '-', '-', '柴胡', '[\'麻黄\', \'苍术\', \'黄耆\', \'钱分\', \'草豆蔻\', \'柴胡\', \'羌活\', \'生甘草\', \'当归\', \'防风\', \'甘草\', \'黄芩\', \'五味子\']');
INSERT INTO `prescript` VALUES (58372, '9078', '藁本汤', '藁本（去苗土，锉）1分，羚羊角（镑）1两，防风（去叉）1两，芎半两，菊花（去萼，爁）半两，细辛（去苗叶）半两，白术半两，人参半两，柴胡（去苗）半两，白蒺藜（微炒）半两，山栀子仁半两，白茯苓（去黑皮）半两，甘草（炙）1分，黄芩（去黑心）1分。', '每服1钱匕，水7分，入青竹叶5片，同煎至4分，去滓澄清，放温细呷，食后日再。', '《圣济总录》卷一八○', '小儿脑热，鼻干无涕。', '-', '-', '柴胡', '[\'藁本\', \'苗土\', \'锉分\', \'羚羊角\', \'防风\', \'两芎\', \'半两\', \'菊花\', \'半两\', \'细辛\', \'苗叶\', \'半两\', \'白术\', \'半两\', \'人参\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'蒺藜\', \'半两\', \'栀子\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'甘草\', \'炙分\', \'黄芩\', \'黑心\']');
INSERT INTO `prescript` VALUES (58373, '9051', '解怒补肝汤', '白芍1两，当归5钱，泽泻1钱，柴胡1钱，荆芥1钱，甘草1钱，枳壳3分，丹皮3钱，天花粉2钱。', '水煎服。', '《辨证录》卷十', '肝血少，少逢拂意之事，便觉怒气填胸，不能自遣，嗔恼不已。', '-', '-', '柴胡', '[\'白芍\', \'当归\', \'泽泻\', \'柴胡\', \'荆芥\', \'甘草\', \'枳壳\', \'丹皮\', \'天花粉\']');
INSERT INTO `prescript` VALUES (58374, '9006', '麻黄羌活散', '麻黄（去根）1分，羌活1分，牡丹皮（去心）1分，独活1分，山栀1分，柴胡（去苗）1分，桔梗（去芦）1分，升麻1分，荆芥穗1分，大黄1分，知母1分，黄芩1分，半夏4铢，牵牛半两。', '每服3钱，水1盏，生姜2片，同煎至7分，临发壮热时，和滓吃。须是初发热时服，仍是食后吃，发了已寒，即更不要吃，待第二发热时吃也。', '《续易简方》卷三', '温疟，初发浑身大热，头痛不可忍，临醒时即寒栗战动，逡巡便醒。', '-', '-', '柴胡', '[\'麻黄\', \'根分\', \'羌活\', \'牡丹皮\', \'心分\', \'独活\', \'分山\', \'栀分\', \'柴胡\', \'苗分\', \'桔梗\', \'芦分\', \'升麻\', \'荆芥\', \'穗分\', \'大黄\', \'知母\', \'黄芩\', \'半夏\', \'牵牛\', \'半两\']');
INSERT INTO `prescript` VALUES (58375, '9005', '柴胡汤', '柴胡（去苗）2两，黄芩（去黑心）1两，芎藭2两，芍药2两，大黄（锉碎，炒香）2两，石膏5两，羚羊角（镑）2两，茯神（去木）2两。', '每服5钱匕，以水1盏半，加竹叶7片，煎至1盏，去滓，投芒消半字，放温，空心顿饮。以利为度；如利频，去芒消，以粥止之。', '《圣济总录》卷一○五', '积年风毒瘀热，目赤时痛。', '如秋月肝弱时，不宜泻肝，宜加补药，即不用此方。', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'黑心\', \'芍药\', \'大黄\', \'石膏\', \'羚羊角\', \'茯神\']');
INSERT INTO `prescript` VALUES (58376, '8998', '桔梗散', '桔梗3分（去芦头），当归3分，苍术3分（微炒），诃黎勒3分（煨，用皮），芎藭3分，柴胡3两（去苗），鳖甲1两（涂醋炙微黄，去裙襕），川大黄1两（锉碎，微炒），赤芍药1两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，食前温服。', '《圣惠》卷三十一', '骨蒸痃癖，胁下妨痛，渐加羸劣，不欲饮食。', '忌苋菜。', '-', '柴胡', '[\'桔梗\', \'分去\', \'芦头\', \'当归\', \'苍术\', \'分微\', \'炒诃黎勒分\', \'柴胡\', \'两去\', \'鳖甲\', \'两涂\', \'微黄\', \'大黄\', \'芍药\']');
INSERT INTO `prescript` VALUES (58377, '8971', '鳖甲白术散', '鳖甲（醋炙）1两，常山1两，白芍药1两，柴胡（去苗）1两，白术2两，牡蛎半两（火煅）。', '上(口父)咀，每服5钱，水2盏，生姜5片，煎至1盏，去滓温服，不拘时候。', '《杨氏家藏方》卷三', '久疟。寒热相等，汗多，腰脊重痛。', '-', '-', '柴胡', '[\'鳖甲\', \'常山\', \'两白\', \'芍药\', \'柴胡\', \'白术\', \'牡蛎\', \'半两\']');
INSERT INTO `prescript` VALUES (58378, '8954', '柴胡散', '柴胡（去苗）1两，地骨皮半两，赤茯苓（去黑皮）半两，枳壳（去瓤，麸炒）半两，旋覆花半两。', '每服2钱匕，生姜汤调下，日3次，不拘时候。', '《圣济总录》卷一一七', '口中干燥，心膈痰壅。', '-', '-', '柴胡', '[\'柴胡\', \'地骨皮\', \'半两\', \'茯苓\', \'黑皮\', \'半两\', \'枳壳\', \'半两\', \'旋覆花\', \'半两\']');
INSERT INTO `prescript` VALUES (58379, '8934', '柴胡饮', '北柴胡（去芦，净洗）1两，人参（去芦）1两，当归（酒洗）1两，黄芩1两，赤芍药1两，甘草（炙）1两，大黄（生用）半两，桔梗（去芦，锉，炒）半两，北五味子（去梗）半两，半夏（汤煮透，去滑）半两。', '每服2钱，水1盏，加乌梅1个，生姜2片，煎7分，温服，不拘时候。', '《准绳·幼科》卷八', '小儿骨蒸疳气，五心烦热，日晡转盛，口干无味，渴多身瘦，胸满痰紧，小便黄色，食减神昏。', '-', '-', '柴胡', '[\'柴胡\', \'芦净\', \'人参\', \'去芦\', \'当归\', \'酒洗\', \'黄芩\', \'两赤\', \'芍药\', \'甘草\', \'大黄\', \'生用\', \'半两\', \'桔梗\', \'去芦\', \'半两\', \'五味子\', \'半两\', \'半夏\', \'透去\', \'半两\']');
INSERT INTO `prescript` VALUES (58380, '8904', '桂香汤', '桂（去粗皮）1两，芍药1两，木香1两，柴胡（去苗）1两，芎藭3分，鳖甲（去裙襕，醋炙）3分，干姜（炮）3分，吴茱萸（汤浸，焙干，炒）3分，常山3分。', '每服3钱匕，水1盏半，煎至8分，去滓温服，不拘时候。', '《圣济总录》卷一○○', '尸注发歇无时，心腹切痛。', '-', '-', '柴胡', '[\'桂去\', \'粗皮\', \'芍药\', \'木香\', \'柴胡\', \'鳖甲\', \'炙分\', \'干姜\', \'炮分\', \'吴茱萸\', \'汤浸\', \'焙干\', \'炒分\', \'常山\']');
INSERT INTO `prescript` VALUES (58381, '8892', '救婴丹', '人参1钱，茯苓3钱，柴胡3分，白芍2钱，神曲5分，砂仁1粒，炮姜3分。', '水煎服。', '《辨证录》卷七', '小儿偶感风邪，发热身颤，手背反张。', '-', '-', '柴胡', '[\'人参\', \'茯苓\', \'柴胡\', \'白芍\', \'神曲\', \'砂仁\', \'粒炮\', \'姜分\']');
INSERT INTO `prescript` VALUES (58382, '8874', '鳖甲汤', '鳖甲（去裙襕，酒浸，炙）1两半，柴胡（去苗）1两，人参1两1分，白术1两1分，知母（焙）1两1分，半夏（汤洗去滑，炒）1两1分，桔梗（炒）1两1分，紫菀（去苗土）1两1分，桑根白皮（炙，锉）1两半，芎3分，芍药（炒）1两1分，当归（切，焙）1两半，秦艽（去苗土）1两半，地骨皮1两半，甘草（炙）3分，桂（去粗皮）3分，虎头骨（酒浸，炙）1两，生干地黄（焙）1两半，赤茯苓（去黑皮）1两半，槟榔（煨）1两半，附子（炮裂，去皮脐）1两半。', '每服3钱匕，水1盏，加生姜1枣大（拍碎），煎至7分，去滓，空腹、食前温服，每日3次。如有寒热，小便赤涩者，更加葱白1茎，豉40粒。', '《圣济总录》卷九十', '五劳咳嗽，咯唾脓血，言语声嘎，日渐羸劣，不欲饮食。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'柴胡\', \'人参\', \'两分\', \'白术\', \'两分\', \'知母\', \'两分\', \'半夏\', \'汤洗\', \'两分\', \'桔梗\', \'两分\', \'紫菀\', \'苗土\', \'两分\', \'桑根\', \'白皮\', \'两半\', \'芍药\', \'两分\', \'当归\', \'切焙\', \'两半\', \'秦艽\', \'苗土\', \'两半\', \'地骨皮\', \'两半\', \'甘草\', \'分桂去\', \'粗皮\', \'分虎\', \'头骨\', \'酒浸\', \'两生\', \'地黄\', \'两半\', \'茯苓\', \'黑皮\', \'两半\', \'槟榔\', \'两半\', \'附子\', \'炮裂\', \'去皮\', \'两半\']');
INSERT INTO `prescript` VALUES (58383, '8853', '柴胡地黄汤', '柴胡3钱，黄芩3钱，甘草2钱，芍药3钱，丹皮3钱，地黄3钱。', '水煎大半杯，温服。表未解，加苏叶、生姜。', '《四圣心源》卷十', '热入血室，胸胁痞满，状如结胸，语言谵妄，神识不清。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'甘草\', \'芍药\', \'丹皮\', \'地黄\']');
INSERT INTO `prescript` VALUES (58384, '8831', '柴胡丸', '柴胡（去苗土）1两半，枳壳（麸炒，去瓤）1两半，白术3分，白茯苓（去黑皮）1两，丹参（去根，炙）2两，黄耆（锉）2两。', '每服30丸，空腹以粥送下，日3次。', '《圣济总录》卷五十', '大肠虚，腹痛不能久立，或腹中虚鸣。', '-', '-', '柴胡', '[\'柴胡\', \'苗土\', \'两半\', \'枳壳\', \'两半\', \'白术\', \'白茯苓\', \'黑皮\', \'丹参\', \'根炙两\', \'黄耆\']');
INSERT INTO `prescript` VALUES (58385, '8823', '黄耆丸', '黄耆1分（锉），麦门冬1分（去心，焙），柴胡半两（去苗），赤茯苓1分，白术1分，子芩1分，鳖甲半两（涂醋，炙令黄，去裙襕），甘草1分（炙微赤，锉）。', '每服5丸，以粥饮送下，日3-4次。', '《圣惠》卷八十四', '小儿往来寒热，多汗心烦，小便赤黄，不欲饮食，四肢羸瘦。', '-', '-', '柴胡', '[\'黄耆\', \'麦门冬\', \'分去\', \'柴胡\', \'半两\', \'茯苓\', \'白术\', \'分子\', \'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'黄去\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58386, '8816', '葱豉汤', '葱白8茎，香豉2合，蓝叶3分，柴胡1两（去苗），川升麻半两，犀角屑半两，赤芍药半两，甘草半两（炙微赤）。', '以水5大盏，煎至2盏半，去滓，分5次温服。未效，再合服之。', '《圣惠》卷三十八', '五石忽发动，体热烦疼，心躁闷乱。', '-', '-', '柴胡', '[\'葱白\', \'香豉\', \'合蓝叶分\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'半两\', \'犀角\', \'半两\', \'芍药\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58387, '8810', '救顽汤', '当归1两，黄耆1两，白术1两，生甘草3钱，熟地1两，山茱萸5钱，麦冬1两，柴胡1两，茯苓5钱，半夏2钱，防风1钱，连翘1钱，附子1片。', '水煎服。', '《辨证录》卷十三', '久生恶疮，或在手足，或在头面，经年不愈，臭腐不堪。', '-', '-', '柴胡', '[\'当归\', \'两黄耆\', \'白术\', \'生甘草\', \'熟地\', \'山茱萸\', \'麦冬\', \'柴胡\', \'茯苓\', \'半夏\', \'防风\', \'连翘\', \'附子\']');
INSERT INTO `prescript` VALUES (58388, '8704', '柴胡消痈汤', '柴胡10～20g，夏枯草10g，紫地丁10g，三颗针10g，黄芩5～10g，白芍5～10g，芒消5～10g，野菊花5g，郁金3～5g，大黄3～5g，法半夏3～5g，甘草3～5g。', '热重，重用柴胡、黄芩，选加紫地丁、野菊花、黄连；大热、大渴、脉洪大者，加石膏、知母、天花粉、鲜芦根；湿重，重用茵陈、郁金、金钱草、栀子、大黄；痛重，加玄胡、川楝子；呕吐，加半夏、竹茹、生姜；驱蛔，加苦楝根皮、槟榔、使君子；瘀血，加丹参、川芎、红花；气阴欲脱，加独参汤、生脉散。', '《古今名方》', '清热解毒，通里泻火。主胆道感染，痈疡型（脓毒型）。', '-', '-', '柴胡', '[\'柴胡\', \'夏枯草\', \'地丁\', \'三颗针\', \'黄芩\', \'白芍\', \'芒消\', \'野菊花\', \'郁金\', \'大黄\', \'半夏\', \'甘草\']');
INSERT INTO `prescript` VALUES (58389, '8680', '柴胡养阴汤', '柴胡4钱，当归2钱，陈皮2钱，知母1钱。', '-', '《明医指掌》卷四', '养营解表。主阴分虚，邪气盛，无汗而疟。久疟不解，脉弦数。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'陈皮\', \'知母\']');
INSERT INTO `prescript` VALUES (58390, '8662', '断下丸', '杞子1两，覆盆子1两，车前子1两（炒），煅龙骨1两，煅牡蛎1两，党参1两，茯苓1两，淮山药1两，杜仲1两（酒炒），柴胡1两，赤石脂1两，生地黄1两，棉花子仁2两。', '每服2-3钱，白术泡汤送下。', '《医方简义》卷五', '赤白带下。', '-', '-', '柴胡', '[\'杞子\', \'覆盆子\', \'车前子\', \'龙骨\', \'牡蛎\', \'党参\', \'茯苓\', \'两淮\', \'山药\', \'杜仲\', \'两酒\', \'柴胡\', \'赤石脂\', \'生地黄\', \'棉花子\']');
INSERT INTO `prescript` VALUES (58391, '8652', '柴胡知母饮', '柴胡（去苗）1两，知母（焙）半两，芦根（锉）3分，槟榔1两，陈橘皮半两，人参半两。', '每服3钱匕，水1盏，加生姜5片，同煎至6分，去滓热服。', '《圣济总录》卷六十三', '呕吐不下食，头痛身热。', '-', '-', '柴胡', '[\'柴胡\', \'知母\', \'半两\', \'芦根\', \'锉分\', \'槟榔\', \'两陈\', \'橘皮\', \'半两\', \'人参\', \'半两\']');
INSERT INTO `prescript` VALUES (58392, '8608', '柴胡散', '柴胡1两（去苗），人参1两（去芦头），甘草半两（炙微赤，锉），紫苏茎叶半两，木通3分（锉），大腹皮半两（锉），陈橘皮半两（汤浸，去白瓤，微炒）。', '每服4钱，以水1大盏，煎至5分，去滓，食前温服。', '《圣惠》卷七十六', '妊娠3-4月，气壅恶食，呕哕，肢节烦疼，或脚膝虚肿。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'人参\', \'两去\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'紫苏\', \'半两\', \'木通\', \'大腹皮\', \'半两\', \'橘皮\', \'半两\', \'汤浸\', \'白瓤微\']');
