INSERT INTO `prescript` VALUES (58413, '8291', '柴胡青黛汤', '柴胡5分，青黛5分，香附1钱，川芎1钱，青皮8分，黄连8分（酒炒），栀子8分（姜汁炒黑），甘草8分。', '水2钟，煎1钟，温服。', '《杂病广要》引《粹言》', '泻肝火。主怒气伤肝。', '-', '-', '柴胡', '[\'柴胡\', \'分青黛分\', \'香附\', \'川芎\', \'青皮\', \'黄连\', \'分酒\', \'栀子\', \'姜汁\', \'甘草\']');
INSERT INTO `prescript` VALUES (58414, '8261', '柴胡羌活汤', '柴胡1钱，羌活1钱5分，紫苏1钱5分，淡豆豉3钱，制半夏2钱，青皮1钱5分，草蔻仁1钱（研），生甘草5分。', '煨姜3钱，大枣3枚为引，水煎服。胸口腹胀，加槟榔1钱5分，炒枳壳1钱5分；恶心，加藿香1钱5分；口渴，加葛根3钱；内热甚，加青蒿3钱；大便结，加瓜蒌仁3钱；作泻，加赤芍5钱，车前子3钱；虚人1服轻，即去羌活、紫苏，再1服。', '《慈航集》卷下', '因感风而得风疟，初病，恶风自汗，烦躁头痛，先热后寒。', '-', '-', '柴胡', '[\'柴胡\', \'羌活\', \'钱分\', \'紫苏\', \'钱分\', \'淡豆豉\', \'钱制\', \'半夏\', \'青皮\', \'钱分\', \'草蔻仁\', \'钱研\', \'生甘草\']');
INSERT INTO `prescript` VALUES (58415, '8238', '理气平肝散', '乌药1钱半，香附1钱半，青皮1钱，枳壳1钱，芍药1钱，川芎1钱，柴胡1钱，木香1钱，甘草4分。', '加生姜3片，水煎服，木香磨水2匙加入。', '《赤水玄珠》卷十四', '七情所伤发痉。', '-', '-', '柴胡', '[\'乌药\', \'钱半\', \'香附\', \'钱半\', \'青皮\', \'枳壳\', \'芍药\', \'川芎\', \'柴胡\', \'木香\', \'甘草\']');
INSERT INTO `prescript` VALUES (58416, '8232', '柴胡汤', '柴胡（去苗）半两，升麻半两，黄芩（去黑心）半两，甘草（炙赤）半两，大黄（锉，炒令香）半两，石膏（碎）3分，钩藤1分，蚱蝉（去翅足，微炙）1枚，蛇蜕皮2寸（炙令黄色）。', '每服1钱匕，水1小盏，煎至半盏，去滓，加竹沥半合，更煎1-2沸，半月至1月儿，斟酌与之；稍大，以意增加。', '《圣济总录》卷一七一', '未满月及出月儿壮热发痫。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'升麻\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'甘草\', \'半两\', \'大黄\', \'令香\', \'半两\', \'石膏\', \'碎分\', \'钩藤\', \'蚱蝉\', \'微炙枚\', \'蜕皮\', \'寸炙令\', \'黄色\']');
INSERT INTO `prescript` VALUES (58417, '8221', '柴胡散', '柴胡（去芦）2两，鳖甲2两（醋浸1宿，炙令黄），甘草1两，知母1两，秦艽1两半。', '柴胡汤（《圣济总录》卷八十九）。', '《博济》卷二', '荣卫不顺，体热黄瘦，筋骨疼痛，多困少力，饮食进退。虚劳羸瘦，盗开。', '-', '-', '柴胡', '[\'柴胡\', \'去芦\', \'鳖甲\', \'两醋\', \'浸宿炙令\', \'甘草\', \'知母\', \'秦艽\', \'两半\']');
