INSERT INTO `prescript` VALUES (58419, '8216', '柴胡散', '柴胡1两（去苗），黄芩半两，人参半两（去芦头），赤芍药半两，甘草半两（炙微赤，锉），犀角屑半两，半夏半两（汤洗7遍去滑），麦门冬半两（去心）。', '每服4钱，以水1中盏，加生姜半两，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠伤寒，外证未去，身体重，发热恶寒，肢节烦疼，微呕，心下支满。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'黄芩\', \'半两\', \'人参\', \'半两\', \'芦头\', \'芍药\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'犀角\', \'半两\', \'半夏\', \'半两\', \'洗遍\', \'麦门冬\', \'半两\', \'去心\']');
