INSERT INTO `prescript` VALUES (58426, '8147', '羚羊角汤', '羚羊角屑1两，鳖甲1两（去裙襕，醋炙），当归1两（切，焙），芍药1两，桂1两（去粗皮），牡丹皮1两，陈橘皮1两（去白，焙），芎1两，防风1两（去叉），白茯苓1两（去黑皮），草豆蔻1两（去皮），独活1两（去芦头），甘草1两（炙），人参1两，白术1两，白芷1两，天麻1两，麻黄1两（去根节），蒲黄1两，柴胡1两（去苗），益智1两，厚朴1两（去粗皮，生姜汁炙），干荷叶1两，延胡索1两。', '每服3钱匕，以水1盏，加生姜3片，同煎至7分，去滓，空心、食前温服。', '《圣济总录》卷一五○', '妇人血风劳气，每至晚即壮热恶寒，肢节酸痛，腹胀，饮食无味，日渐羸瘦。', '-', '-', '柴胡', '[\'羚羊角\', \'鳖甲\', \'两去\', \'当归\', \'两切\', \'芍药\', \'两桂\', \'两去\', \'粗皮\', \'牡丹皮\', \'两陈\', \'橘皮\', \'两去\', \'防风\', \'两去\', \'白茯苓\', \'两去\', \'黑皮\', \'草豆蔻\', \'去皮\', \'独活\', \'两去\', \'芦头\', \'甘草\', \'人参\', \'白术\', \'白芷\', \'天麻\', \'麻黄\', \'两去\', \'根节\', \'蒲黄\', \'柴胡\', \'两去\', \'益智\', \'厚朴\', \'两去\', \'粗皮生\', \'姜汁\', \'炙干\', \'荷叶\', \'延胡索\']');
INSERT INTO `prescript` VALUES (58427, '8111', '黄连解毒汤', '黄连2钱，黄芩2钱，黄柏2钱，栀子2钱，柴胡2钱，连翘2钱。', '水煎，温服。', '《回春》卷二', '伤寒大热不止，烦躁干呕，口渴喘满，阳厥极深，蓄热内甚，及汗吐下后，寒凉不能退其热者。嗜酒不育，脉六部洪大，重按则觉微细无力者。', '-', '-', '柴胡', '[\'黄连\', \'黄芩\', \'黄柏\', \'栀子\', \'柴胡\', \'连翘\']');
INSERT INTO `prescript` VALUES (58428, '8097', '减味鳖甲煎丸', '鳖甲2两4钱，柴胡1两2钱，黄芩6钱，人参2钱，半夏2钱，甘草2钱，桂枝6钱，芍药1两，丹皮1两，桃仁4钱，阿胶6钱，大黄6钱，干姜6钱，葶苈2钱。', '空腹服7丸，每日3次。', '《医学摘粹》', '久疟不愈，结为症瘕，名曰疟母。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'黄芩\', \'人参\', \'半夏\', \'甘草\', \'桂枝\', \'芍药\', \'丹皮\', \'桃仁\', \'阿胶\', \'大黄\', \'干姜\']');
INSERT INTO `prescript` VALUES (58429, '8096', '葶苈煎', '葶苈（纸上炒）3分，防己1两半，泽漆叶1两1分，郁李仁（去皮尖，炒）1两1分，赤茯苓（去黑皮）3两，泽泻3两，杏仁（汤浸，去皮尖双仁，炒，研如膏）3两，柴胡（去苗）2两。', '2岁儿每服半钱匕，温水调下。渐加之，更随儿大小加减。', '《圣济总录》卷一七四', '小儿肿满，服药不退。', '-', '-', '柴胡', '[\'葶苈纸\', \'炒分\', \'防己\', \'两半\', \'泽漆\', \'两分\', \'郁李仁\', \'皮尖\', \'两分\', \'茯苓\', \'黑皮\', \'泽泻\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'研如膏\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58430, '8060', '理肺泻心汤', '茺蔚子4钱，当归3钱，赤芍3钱，川芎2钱，黄芩3钱，木贼2钱，川黄连2钱，枳壳2钱，桔梗3钱，柴胡1钱半，薄荷2钱，甘草1钱，蝉蜕1钱半。', '水煎服。', '《眼科临症笔记》', '阴阳翳症。', '-', '-', '柴胡', '[\'茺蔚子\', \'当归\', \'赤芍\', \'川芎\', \'黄芩\', \'木贼\', \'钱川\', \'黄连\', \'枳壳\', \'桔梗\', \'柴胡\', \'钱半\', \'薄荷\', \'甘草\', \'蝉蜕\', \'钱半\']');
INSERT INTO `prescript` VALUES (58431, '8042', '柴胡当归汤', '柴胡（去苗）1两，当归（切，焙）1两，防风（去叉）1两，白芷1两，附子（炮裂，去皮脐）1两，白术1两，牡丹皮1两，桂（去粗皮）1两，天仙藤1两，秦艽（去苗土）1两，桔梗（炒）1两，芍药1两，人参1两，麻黄（去根节）1两，木香1两，知母（切，焙）半两，甘草（炙，锉）半两。', '每服3钱匕，水1盏，加生姜3片，大枣1枚（擘），同煎至7分，去滓，空心温服。', '《圣济总录》卷八十八', '虚劳寒热，日渐羸瘦，行步艰难，饮食不进，状如疟疾。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'切焙\', \'防风\', \'白芷\', \'附子\', \'炮裂\', \'去皮\', \'白术\', \'牡丹皮\', \'两桂去\', \'粗皮\', \'天仙藤\', \'秦艽\', \'苗土\', \'桔梗\', \'芍药\', \'人参\', \'麻黄\', \'根节\', \'木香\', \'知母\', \'切焙\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58432, '8034', '柴胡下热汤', '柴胡3两，黄芩3两，泽泻3两，升麻3两，芒消3两，玄参6两，淡竹叶（切）1升，生地黄（切）1升，干姜2两。', '以水9升，煮取3升，去滓，下芒消，平旦分3服。', '《外台》卷十六引《删繁方》', '肝劳。热闷，关格不通，精神不守，气逆上胸，热炎炎不止。', '忌芜荑。', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'泽泻\', \'升麻\', \'两芒\', \'消两\', \'玄参\', \'淡竹叶\', \'切升\', \'生地黄\', \'切升\', \'干姜\']');
INSERT INTO `prescript` VALUES (58433, '8022', '柴胡石膏散', '赤芍药50两，柴胡（去苗）50两，前胡（去苗）50两，石膏（煅）50两，干葛50两，升麻25两，黄芩37两，桑白皮37两，荆芥穗（去土）37两。', '柴胡升麻汤（原书同卷宝庆新增方）、柴胡石膏汤（《奇效良方》卷十）。', '《局方》卷二', '外感风热、少阳、阳明合病，壮热恶风，头痛身疼，鼻塞咽干，口渴心烦，咳嗽自汗，脉浮缓。时行瘟疫，心胸如满，寒热往来，痰实咳嗽，涕唾稠粘。伤风，及阳气郁遏，元气下陷。春夏感冒，声重声哑，甚至痰壅气喘，咳嗽咽干，自汗。', '-', '-', '柴胡', '[\'芍药\', \'柴胡\', \'去苗\', \'两前\', \'胡去\', \'石膏\', \'两干葛\', \'升麻\', \'黄芩\', \'桑白皮\', \'荆芥\']');
INSERT INTO `prescript` VALUES (58434, '8020', '柴胡丸', '柴胡（去苗）1两，鳖甲（醋多，去裙襕）2两，厚朴（去粗皮，生姜汁炙，焙）1两，山栀子仁1两，常山1两，知母（切，焙）1两，秦艽（去苗土）1两，黄芩（去黑心）1两，白术1两，槟榔（锉）1两，桔梗（炒）1两，芍药1两，枳壳（去瓤，麸炒）1两，白茯苓（去黑皮）1两，贝母（去心）1两，人参1两，熟干地黄（焙）1两，前胡（去芦头）1两，防风（去叉）1两，紫菀（去苗土）1两，麻黄（去根节）1两，黄耆（细锉）1两，陈橘皮（去白，麸炒）1两，桂（去粗皮）1两，京三棱（炮，锉）3两。', '每服30丸，以温酒送下，空心日午、夜卧服。', '《圣济总录》卷八十八', '虚劳寒热，羸瘦食减，肢体困倦。', '-', '-', '柴胡', '[\'柴胡\', \'鳖甲\', \'厚朴\', \'粗皮生\', \'姜汁\', \'两山\', \'栀子\', \'常山\', \'知母\', \'切焙\', \'秦艽\', \'苗土\', \'黄芩\', \'黑心\', \'白术\', \'槟榔\', \'桔梗\', \'芍药\', \'枳壳\', \'白茯苓\', \'黑皮\', \'贝母\', \'人参\', \'两熟\', \'地黄\', \'两前\', \'胡去\', \'芦头\', \'防风\', \'紫菀\', \'苗土\', \'麻黄\', \'根节\', \'黄耆\', \'橘皮\', \'白麸\', \'两桂去\', \'粗皮\', \'两京\', \'三棱\']');
INSERT INTO `prescript` VALUES (58435, '8018', '柴胡疏肝散', '柴胡2钱，橘皮（醋炒）2钱，川芎（童便浸，切）1钱半，芍药1钱半，枳壳（炒）1钱半，甘草（炙）5分，香附（醋炒）1钱半，山栀（姜汁炒黑）1钱，煨姜1片。', '水煎，食前温服。吐血，加童便半盏。', '《张氏医道》卷十四', '解郁调肝。主怒火伤肝，胁痛，血菀于上。呕血，脉弦数者。', '-', '-', '柴胡', '[\'柴胡\', \'橘皮\', \'川芎\', \'童便\', \'浸切\', \'芍药\', \'钱半\', \'枳壳\', \'甘草\', \'炙分\', \'香附\', \'半山\', \'姜汁\', \'黑钱\', \'姜片\']');
INSERT INTO `prescript` VALUES (58436, '8011', '调中益气汤', '黄耆1钱，人参5分，苍术5分，陈皮5分，升麻3分，炙甘草3分，柴胡3分，黄柏3分，木香3分，当归5分，白术5分，白芍5分。', '-', '《嵩崖尊生》卷六', '病后耳鸣耳聋。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'苍术\', \'陈皮分\', \'升麻\', \'甘草\', \'柴胡\', \'黄柏\', \'木香\', \'当归\', \'白术\', \'白芍\']');
INSERT INTO `prescript` VALUES (58437, '7982', '柴胡疏肝散', '柴胡2钱，陈皮（醋炒）2钱，川芎1钱半，芍药1钱半，枳壳（麸炒）1钱半，甘草（炙）5分，香附1钱半。', '柴胡舒肝散（《验方新编》卷五）、柴胡疏肝汤（《不知医必要》卷二）。', '《准绳·类方》卷四引《统旨》', '疏肝理气。主因怒气郁而胁痛，寒热往来，痛而胀闷，不得俯仰，喜太息，脉弦。现用于神经官能症、中耳炎等。胁肋疼痛。肝实胁痛，不得转侧，喜太息。', '-', '-', '柴胡', '[\'柴胡\', \'陈皮\', \'川芎\', \'钱半\', \'芍药\', \'钱半\', \'枳壳\', \'甘草\', \'炙分\', \'香附\', \'钱半\']');
INSERT INTO `prescript` VALUES (58438, '7970', '羚羊角汤', '羚羊角1两（镑），决明子1两（炒），犀角1两（镑），石膏1两，地骨皮1两，玄参半两，细辛半两（去苗叶），黄芩（去黑心）半两，防风3分（去叉），芎3分，柴胡3分（去苗），升麻3分。', '每服5钱匕，以水1盏半，加竹叶10片，煎至7分，去滓，入芒消末半钱匕，食后、临卧温服，每日2次。', '《圣济总录》卷一○四', '风毒目赤肿痛，肝肺风热。', '-', '-', '柴胡', '[\'羚羊角\', \'两镑\', \'决明子\', \'犀角\', \'两镑\', \'石膏\', \'地骨皮\', \'玄参\', \'半两\', \'细辛\', \'半两\', \'苗叶\', \'黄芩\', \'黑心\', \'半两\', \'防风\', \'分去\', \'柴胡\', \'分去\', \'升麻\']');
INSERT INTO `prescript` VALUES (58439, '7947', '柴胡石膏汤', '柴胡（去苗）1两，石膏（碎）1两，赤茯苓（去黑皮）1两，白术1两，萎蕤1两，羚羊角（镑）1两，栀子仁1分，桑根白皮（锉）3分。', '每服5钱匕，水1盏半，煎至7分，去滓温服。', '《圣济总录》卷二十三', '伤寒10日以上，潮热不退。', '-', '-', '柴胡', '[\'柴胡\', \'石膏\', \'茯苓\', \'黑皮\', \'白术\', \'萎蕤\', \'羚羊角\', \'栀子\', \'仁分\', \'桑根\', \'白皮\', \'锉分\']');
INSERT INTO `prescript` VALUES (58440, '7925', '草豆蔻散', '草豆蔻半两（去皮），青橘皮半两（汤浸，去白瓤，焙），人参1两（去芦头），桂心半两，附子3分（炮裂，去皮脐），白茯苓3分，白术半两，当归半两（锉，微炒），枳实半两（麸炒微黄），厚朴1两半（去粗皮，涂生姜汁，炙令香熟），芎藭半两，柴胡半两（去苗），桔梗1两（去芦头），白芍药半两，黄耆半两（锉）。', '每服2钱，以水1中盏，加生姜半分、大枣3个，煎至6分，去滓，稍热服，不拘时候。', '《圣惠》卷五', '脾胃气久虚，四肢无力，腑脏虚损，不欲饮食，日加羸瘦，体虚颤掉。', '忌生冷、油腻、湿面、猪犬肉。', '-', '柴胡', '[\'草豆蔻\', \'半两\', \'去皮\', \'橘皮\', \'半两\', \'汤浸\', \'人参\', \'两去\', \'芦头\', \'桂心\', \'半两\', \'附子\', \'分炮裂\', \'去皮\', \'白茯苓\', \'白术\', \'半两\', \'当归\', \'半两\', \'枳实\', \'半两\', \'微黄\', \'厚朴\', \'两半\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'桔梗\', \'两去\', \'芦头\', \'芍药\', \'半两\', \'黄耆\', \'半两\']');
INSERT INTO `prescript` VALUES (58441, '7924', '黄芩汤', '黄芩（去黑心）2两，黄连（去须）2两，木通（锉）2两，柴胡（去苗）2两，赤芍药2两，地骨皮1两半，山栀子仁1两半，萎蕤2两半，大黄（蒸过，切，炒）2两半，甘草（炙，锉）2两半，石膏6两半。', '每服3钱匕，水1盏，煎取7分，去滓，入朴消半钱匕，食后良久温服，1日2次。', '《圣济总录》卷一○八', '白膜晕赤侵黑睛生翳，横冲瞳人，成丁翳痛。', '-', '-', '柴胡', '[\'黄芩\', \'黑心\', \'黄连\', \'木通\', \'柴胡\', \'芍药\', \'地骨皮\', \'半山\', \'栀子\', \'两半\', \'萎蕤\', \'两半\', \'大黄\', \'蒸过切\', \'两半\', \'甘草\', \'两半\', \'石膏\', \'两半\']');
INSERT INTO `prescript` VALUES (58442, '7910', '大黄散', '川大黄1两（锉碎，微炒），柴胡3分（去苗），川升麻3分，黄芩3分，枳壳3分（麸炒微黄，去瓤），赤芍药3分，栀子仁3分，石膏1两半，知母3分，杏仁3分（汤浸，去皮尖双仁，麸炒微黄）。', '每服1钱，以水1小盏，加青竹叶2-7片，煎至5分，去滓，量儿大小，分减服之。', '《圣惠》卷八十二', '小儿周岁至3岁壮热。', '-', '-', '柴胡', '[\'大黄\', \'柴胡\', \'分去\', \'苗川\', \'升麻\', \'黄芩\', \'枳壳\', \'分麸\', \'微黄\', \'芍药\', \'栀子\', \'仁分\', \'石膏\', \'两半\', \'知母\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (58443, '7903', '柴胡枳壳汤', '柴胡7分，枳壳7分，赤芍7分，大黄1钱，甘草4分。', '-', '《嵩崖尊生》卷十五', '小儿出疹，腹胀气促。', '-', '-', '柴胡', '[\'柴胡\', \'枳壳\', \'赤芍\', \'大黄\', \'甘草\']');
INSERT INTO `prescript` VALUES (58444, '7889', '积疝方', '山楂（炒）1两，茴香（炒）3钱，柴胡（炒）3钱，牡丹皮1钱。', '本方方名，《医统》引作“积疝丸”。', '《丹溪心法》卷四', '疝痛。', '-', '-', '柴胡', '[\'山楂\', \'茴香\', \'柴胡\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (58445, '7831', '柴平汤', '银柴胡2钱，黄芩1钱5分，人参（去芦）1钱，半夏（汤泡7次）1钱，甘草5分，陈皮1钱2分，苍术（泔浸）1钱半，厚朴（姜制）1钱。', '柴平饮（《证治宝鉴》卷二）、柴平煎（《中医大辞典·方剂分册》）。发于午前为阳，属气虚，加白术（土炒）8分，白茯苓（去皮）7分；发于午后为阴，属血虚，加当归（酒浸）9分，川芎7分；发于午前，延及午后，此气血两虚，上4味俱加；食积，加神曲（炒）8分，麦芽（炒）7分，山楂1钱，枳实（麸炒）1钱。', '《增补内经拾遗》卷三引《宦邸便方》', '痎疟，湿疟，食疟；春嗽。[br]发时一身尽痛，手足沉重，寒多热少，脉濡。小儿饮食无节，复受风暑之气，寒热交作，胸腹胀满，痞闷不通，面黄恶食，症轻者。', '-', '-', '柴胡', '[\'银柴胡\', \'黄芩\', \'钱分\', \'人参\', \'芦钱\', \'半夏\', \'汤泡次\', \'甘草\', \'陈皮\', \'钱分\', \'苍术\', \'泔浸\', \'厚朴\', \'制钱\']');
INSERT INTO `prescript` VALUES (58446, '7827', '蓝青散', '蓝青5钱，知母5钱，甘草5钱，杏仁（去皮尖）5钱，黄芩4钱，升麻4钱，柴胡4钱，寒水石4钱，石膏4钱，山栀4钱，赤芍药4钱，羚羊角3钱。', '每次3钱，水煎服。', '《直指》卷二十四', '一切丹毒赤肿。', '-', '-', '柴胡', '[\'蓝青\', \'知母\', \'甘草\', \'杏仁\', \'皮尖\', \'黄芩\', \'升麻\', \'柴胡\', \'寒水石\', \'石膏\', \'钱山\', \'栀钱\', \'芍药\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (58447, '7803', '柴胡散', '柴胡1两半（去苗），五味子1两，桔梗1两（去芦头），熟干黄1两，白茯苓1两，麦门冬1两（去心），紫菀1两（洗，去苗土），人参1两（去芦头），地骨皮1两，黄耆1两（锉），甘草3分（炙微赤，锉），桂心1两，牡蛎粉1两，半夏3分（汤浸7遍去滑），白术1两。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十九', '虚劳寒热，夜卧盗汗，四肢无力，吃食口苦，上气咳嗽。', '-', '-', '柴胡', '[\'柴胡\', \'两半\', \'去苗\', \'五味子\', \'桔梗\', \'两去\', \'芦头\', \'白茯苓\', \'麦门冬\', \'两去\', \'紫菀\', \'两洗\', \'苗土\', \'人参\', \'两去\', \'芦头\', \'地骨皮\', \'两黄耆\', \'甘草\', \'分炙微\', \'桂心\', \'牡蛎\', \'半夏\', \'分汤\', \'浸遍\', \'白术\']');
INSERT INTO `prescript` VALUES (58448, '7780', '柴胡枳壳汤', '柴胡1钱半，枳壳1钱，黄芩1钱，山栀1钱，知母1钱，麦门冬1钱，干葛1钱，大青2钱，生地2钱，石膏2钱，升麻8分，甘草4分。', '水煎，温服。大便闭甚，加大黄。', '《医学入门》卷四', '孕妇伤寒，邪传于里，口渴烦热，腹满便闭，谵语，或发斑，昼夜不安。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'枳壳\', \'黄芩\', \'钱山\', \'栀钱\', \'知母\', \'麦门冬\', \'干葛钱\', \'大青钱\', \'生地\', \'石膏\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (58449, '7766', '柴胡加桂汤', '柴胡3钱，黄芩2钱，桂枝2钱，半夏1钱，甘草4分。', '加生姜3片，大枣2枚，水煎，温服。', '《医学入门》卷四', '半表里症，盗汗，身热不欲去衣；及不满不硬，但心下妨闷，谓之支结,少阳疟往来寒热。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'桂枝\', \'半夏\', \'甘草\']');
INSERT INTO `prescript` VALUES (58450, '7758', '黄耆散', '黄耆1两（锉），枳壳1两（麸炒微黄，去瓤），鳖甲1两（涂醋，炙令黄，去裙襕），柴胡1两（去苗），麦门冬3分（去心），赤茯苓1两，赤芍药3分，桑根白皮3分（锉），五味子半两，紫苏茎叶3分，半夏半两（汤洗7遍，去滑），木通3分（锉），诃黎勒皮1两，甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十九', '虚劳羸瘦，上焦壅滞，每唾稠粘，不思饮食。', '-', '-', '柴胡', '[\'黄耆\', \'枳壳\', \'两麸\', \'微黄\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'柴胡\', \'两去\', \'麦门冬\', \'分去\', \'茯苓\', \'两赤\', \'芍药\', \'分桑根\', \'白皮\', \'五味子\', \'半两\', \'紫苏\', \'叶分\', \'半夏\', \'半两\', \'洗遍\', \'木通\', \'黎勒皮\', \'甘草\', \'半两\', \'炙微\']');
