INSERT INTO `prescript` VALUES (58456, '7638', '黄芩汤', '黄芩（去黑心）1两，茵陈蒿1两，升麻1两，栀子仁半两，柴胡（去苗）半两，龙胆半两，犀角（镑）1两。', '每服5钱匕，用水1盏半，煎至1盏，去滓，加生地黄汁1合，搅令匀，不拘时候温服。', '《圣济总录》卷二十八', '内消折热。主伤寒发黄，或先服利药未愈者。', '-', '-', '柴胡', '[\'黄芩\', \'黑心\', \'茵陈蒿\', \'升麻\', \'栀子\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'龙胆\', \'半两\', \'犀角\']');
INSERT INTO `prescript` VALUES (58457, '7600', '柴胡饮', '柴胡（去苗）半两，升麻半两，茯神（去木）半两，芍药半两，犀角（镑）半两，百合半两，地骨皮半两，麦门冬（去心，焙）半两，黄芩（去黑心）半两，人参半两，鳖甲（去裙襕，醋炙）1两，石膏1两，甘草（炙，锉）1分。', '每服5钱匕，水1盏半，加生姜半分（拍碎），竹叶3-7片，同煎至7分，去滓，食后温服，日2次。', '《圣济总录》卷三十一', '伤寒后骨节疼痛，咳嗽不能食，口舌干燥，乍寒乍热，唇口生疮。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'升麻\', \'半两\', \'茯神\', \'半两\', \'芍药\', \'半两\', \'犀角\', \'半两\', \'百合\', \'半两\', \'地骨皮\', \'半两\', \'麦门冬\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'人参\', \'半两\', \'鳖甲\', \'石膏\', \'甘草\']');
INSERT INTO `prescript` VALUES (58458, '7582', '蓝叶散', '蓝叶1两，黄芩1分，犀角屑1分，川大黄（锉碎，微炒）1分，柴胡（去苗）1分，栀子仁1分，川升麻1分半，石膏1分半，甘草半分（炙微赤，锉）。', '升麻黄芩汤（《圣济总录》卷一八二）。', '《圣惠》卷九十一', '小儿月内发一切丹。', '-', '-', '柴胡', '[\'蓝叶\', \'黄芩\', \'犀角\', \'分川\', \'大黄\', \'碎微\', \'炒分\', \'柴胡\', \'苗分\', \'栀子\', \'仁分川\', \'升麻\', \'分半\', \'石膏\', \'分半\', \'甘草\', \'半分\', \'炙微\']');
