INSERT INTO `prescript` VALUES (58463, '7488', '狼毒丸', '雄黄（生），狼毒，肉桂（去粗皮，不见火）4钱，大附子（炮，去皮脐）1两2钱，汉椒（去目，炒出汗）1两2钱，干漆（炒烟绝）1两2钱，甘遂（生用）1两2钱，当归半两（去芦），芫花（醋炒）1两，川大黄（生）1两半，槟榔（生用）1两半，大戟（生）1两，桃仁（连皮炒）1两，茱萸（生）1两，厚朴（去皮，姜制）1两，干姜（炮，洗）1两，枳壳（生，去瓤）1两，犀角（生用）1两，鳖甲（炙）1两4钱，银川柴胡（生用）1两4钱。', '方中雄黄、狼毒用量原缺。', '《魏氏家藏方》卷九', '腹胀水肿。', '-', '-', '柴胡', '[\'雄黄\', \'狼毒\', \'肉桂\', \'粗皮\', \'不见\', \'火钱\', \'附子\', \'去皮\', \'两钱汉椒\', \'出汗\', \'干漆\', \'炒烟绝\', \'甘遂\', \'生用\', \'当归\', \'半两\', \'去芦\', \'芫花\', \'两川\', \'大黄\', \'两半\', \'槟榔\', \'生用\', \'两半\', \'大戟\', \'生两\', \'桃仁\', \'连皮\', \'茱萸\', \'生两\', \'厚朴\', \'去皮\', \'姜制\', \'干姜\', \'炮洗\', \'枳壳\', \'生去\', \'犀角\', \'生用\', \'鳖甲\', \'银川\', \'柴胡\', \'生用\']');
INSERT INTO `prescript` VALUES (58464, '7486', '调中丸', '人参半两，白术半两，鳖甲半两，柴胡半两，茯苓半两，三棱半两，当归半两，陈皮半两。', '每服30-50丸，米饮送下。', '《鸡峰》卷十二', '化癖进食长肌。主荣卫不和，脾虚多病，肌体清瘦，或发寒热，面色痿黄。', '-', '-', '柴胡', '[\'人参\', \'半两\', \'白术\', \'半两\', \'鳖甲\', \'半两\', \'柴胡\', \'半两\', \'茯苓\', \'半两\', \'三棱\', \'半两\', \'当归\', \'半两\', \'陈皮\', \'半两\']');
