INSERT INTO `prescript` VALUES (58466, '7473', '鳖甲汤', '鳖甲（去裙襕，醋炙）1两，柴胡（去苗）1两，甘草（炙，锉）1两，半夏（生姜半两同捣，作饼子，晒干，如此3次）1两，楝实（麸炒，去核）1两，黄耆（锉）1两，赤芍药1两，秦艽（去苗土）半两，人参半两，白术半两，白茯苓（去黑皮）半两，桔梗（炒）半两，知母（焙）半两，枳壳（去瓤，麸炒）半两，熟干地黄（焙）半两，地骨皮半两，草豆蔻（去皮）半两，常山半两，乌梅（取肉）半两，木香1分。', '每服3钱匕，水1盏，加生姜2片，大枣1个（劈破），同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷八十八', '虚劳。潮热，肌瘦咳嗽，骨节酸疼，面红颊赤。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'甘草\', \'半夏\', \'生姜\', \'半两\', \'同捣作\', \'饼子\', \'晒干\', \'如此\', \'实麸\', \'去核\', \'两黄耆\', \'芍药\', \'秦艽\', \'苗土\', \'半两\', \'人参\', \'半两\', \'白术\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'桔梗\', \'半两\', \'知母\', \'半两\', \'枳壳\', \'半两\', \'熟干\', \'地黄\', \'半两\', \'地骨皮\', \'半两\', \'草豆蔻\', \'去皮\', \'半两\', \'常山\', \'半两\', \'乌梅\', \'取肉\', \'半两\', \'木香\']');
INSERT INTO `prescript` VALUES (58467, '7438', '黄耆丸', '黄耆3两（锉），防风3两（去叉），丹参3两（去苗土），白术3两，白茯苓3两（去黑皮），芎3两，枳壳3两（去瓤，麸炒），山栀子3两（去皮），蒺藜子3两（炒，去角），赤芍药3两，知母3两（焙），地骨皮3两，黄芩3两（去黑心），柴胡3两（去苗），苦参3两，生干地黄（焙）3两。', '每服20丸，空心、日午、夜卧以温水送下．', '《圣济总录》卷十八', '大风癞。面上生疮，身多盗汗，腹痛。', '-', '-', '柴胡', '[\'黄耆\', \'防风\', \'两去\', \'丹参\', \'两去\', \'苗土\', \'白术\', \'白茯苓\', \'两去\', \'黑皮\', \'枳壳\', \'两去\', \'炒山\', \'栀子\', \'去皮\', \'蒺藜\', \'芍药\', \'知母\', \'两焙\', \'地骨皮\', \'黄芩\', \'两去\', \'黑心\', \'柴胡\', \'两去\', \'苦参\', \'两生\', \'地黄\']');
INSERT INTO `prescript` VALUES (58468, '7434', '大黄散', '川大黄1两（锉，微炒），当归1两（锉，微炒），白术1两，枳壳1两（麸炒微黄，去瓤），柴胡1两半（去苗），鳖甲1两（涂醋，炙令黄，去裙襕）。', '每服3钱，以水1中盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷四十九', '痃癖坚急，气连心肋相引痛。', '-', '-', '柴胡', '[\'大黄\', \'当归\', \'白术\', \'枳壳\', \'两麸\', \'微黄\', \'柴胡\', \'两半\', \'去苗\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\']');
