INSERT INTO `prescript` VALUES (58470, '7305', '柴胡饮', '柴胡（去苗）半两，白术1分半，赤茯苓（去黑皮）1分半，鳖甲（去裙澜，醋炙）1分半，知母（切，焙）1分，犀角屑1分，枳壳（去瓤，麸炒）1分半。', '每服3钱匕，水1盏，煎至半盏，去滓温服，早晨、日午、夜卧各1服。', '《圣济总录》卷八十八', '虚劳咳嗽，气喘颊赤，心忪烦躁，两胁胀闷，肌瘦少力，不思饮食。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'白术\', \'分半赤\', \'茯苓\', \'黑皮\', \'分半\', \'鳖甲\', \'分半\', \'知母\', \'切焙分\', \'犀角\', \'枳壳\', \'分半\']');
INSERT INTO `prescript` VALUES (58471, '7285', '理郁升陷汤', '生黄耆6钱，知母3钱，当归身3钱，桂枝尖1钱半，柴胡1钱半，乳香（不去油）3钱，没药（不去油）3钱。', '胁下撑胀，或兼疼者，加龙骨、牡蛎各5钱；少腹下坠者，加升麻1钱。', '《衷中参西》上册', '胸中大气下陷，又兼气分郁结，经络湮淤者。', '-', '-', '柴胡', '[\'生黄耆\', \'知母\', \'当归\', \'身钱\', \'桂枝\', \'尖钱\', \'柴胡\', \'钱半\', \'乳香\', \'不去\', \'油钱\', \'没药\', \'不去\', \'油钱\']');
INSERT INTO `prescript` VALUES (58472, '7275', '麻仁丸', '大麻仁2两，川大黄1两（锉碎，微炒），枳壳1两（麸炒微黄，去瓤），赤芍药1两，郁李仁1两（汤浸，去皮尖，微炒），木香半两，槟榔1两，柴胡1两（去苗）。', '每服30丸，食前以生姜汤送下。', '《圣惠》卷二十九', '虚劳气壅，大便秘涩，四肢烦疼。', '-', '-', '柴胡', '[\'麻仁\', \'两川\', \'大黄\', \'枳壳\', \'两麸\', \'微黄\', \'芍药\', \'郁李仁\', \'两汤\', \'浸去\', \'皮尖\', \'木香\', \'半两\', \'槟榔\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (58473, '7263', '鳖甲汤', '鳖甲（去裙襕，酥炙）1两，柴胡（去苗）1两，附子（炮裂，去皮脐）1两，白茯苓（去黑皮）1两，芍药1两，沉香半两，黄耆半两，桔梗半两，人参半两，芎半两，桂（去粗皮）半两，木香半两，黄芩（去黑心）半两，五味子半两，半夏（汤洗7遍，去滑，焙）半两，防风（去叉）半两，枳壳（去瓤，麸炒）半两，当归（切，焙）半两，麻黄（去根节，汤煮掠去沫，焙）半两，羌活（去芦头）半两，秦艽（去苗土）半两，槟榔1个，甘草（炙）1两半（锉），陈橘皮（汤浸，去白，焙）1分。', '每服3钱匕，水1盏，加生姜2片，大枣1个（劈破），同煎至7分，去滓温服。', '《圣济总录》卷八十九', '男子。妇人五劳七伤。四肢无力，手足疼痛，饮食无味。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'附子\', \'炮裂\', \'去皮\', \'白茯苓\', \'黑皮\', \'芍药\', \'沉香\', \'半两\', \'黄耆\', \'半两\', \'桔梗\', \'半两\', \'人参\', \'半两\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'木香\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'五味子\', \'半两\', \'半夏\', \'洗遍\', \'半两\', \'防风\', \'半两\', \'枳壳\', \'半两\', \'当归\', \'切焙\', \'半两\', \'麻黄\', \'根节\', \'掠去\', \'半两\', \'羌活\', \'芦头\', \'半两\', \'秦艽\', \'苗土\', \'半两\', \'槟榔\', \'甘草\', \'两半\', \'橘皮\', \'汤浸\', \'白焙分\']');
INSERT INTO `prescript` VALUES (58474, '7234', '调经养血汤', '大熟地12g，当归身15g，阿胶珠12g，丹参30g，炒白芍18g，柴胡6g，陈皮9g，香附9g，炒杜仲12g，川续断12g，桑寄生30g，甘草3g。', '-', '《临证医案医方》', '养血调经。主月经不调，色淡量少，或经期提前错后，少腹隐痛，得按则减，腰酸疼痛，舌质淡，苔薄白，脉沉细。', '-', '-', '柴胡', '[\'熟地\', \'当归\', \'阿胶\', \'丹参\', \'白芍\', \'柴胡\', \'陈皮\', \'香附\', \'杜仲\', \'续断\', \'桑寄生\', \'甘草\']');
INSERT INTO `prescript` VALUES (58475, '7216', '鳖甲汤', '鳖甲（醋炙黄）1两，桑根白皮（锉）1两，甘草（炙，锉）1两，贝母（去心，炒）1两，麦门冬（去心，焙）1两，白术1两，人参1两，陈橘皮（去白，焙）1两，胡黄连半两，知母（焙）半两，麻黄（去根节）半两，黄芩（去黑心）半两，百部半两，紫菀（去苗土）半两，山栀子仁半两，栝楼根半两，柴胡（去苗）半两，款冬花半两。', '每服3钱匕，水1盏半，煎至8分，加杏仁7个（去皮尖），再同煎至6分，去滓，临卧温服，如嗽止，不用杏仁，只用生姜少许同煎。', '《圣济总录》卷九十', '虚劳。烦躁，羸瘦发热，面红颊赤，咳吐脓血。', '-', '-', '柴胡', '[\'鳖甲\', \'两桑根\', \'白皮\', \'甘草\', \'贝母\', \'麦门冬\', \'白术\', \'人参\', \'两陈\', \'橘皮\', \'胡黄连\', \'半两\', \'知母\', \'半两\', \'麻黄\', \'根节\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'百部\', \'半两\', \'紫菀\', \'苗土\', \'半两\', \'栀子\', \'半两\', \'栝楼\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'款冬花\', \'半两\']');
INSERT INTO `prescript` VALUES (58476, '7215', '羚羊角饮子', '羚羊角1钱半，青防风1钱半，甜桔梗8分，西羌活1钱半，肉桂1钱半（去皮），软柴胡8分，制大黄3钱。', '上水煎，去滓，入地黄酒1杯，温服。', '《医略六书》卷三十', '产后刚痉，脉紧数大者。', '-', '-', '柴胡', '[\'羚羊角\', \'钱半青\', \'防风\', \'钱半甜\', \'桔梗\', \'分西\', \'羌活\', \'钱半\', \'肉桂\', \'钱半\', \'去皮\', \'柴胡\', \'分制\', \'大黄\']');
INSERT INTO `prescript` VALUES (58477, '7187', '调中散', '柴胡1分，前胡（各去毛）1分，桔梗1分，贝母（去心）1分，牡丹皮（去心）1分，黄芩1分，麻黄1分，枳壳（只用青）4铢，栀子4铢，升麻半两，甘草1分。', '每服3钱，水1盏，煎两3沸，去滓，食后临卧任意服。', '《普济方》卷一三二', '伤寒吃转药，病热已减，然大腑馀热出后，终未快，精神昏闷。', '-', '-', '柴胡', '[\'柴胡\', \'分前\', \'胡各去\', \'毛分\', \'桔梗\', \'贝母\', \'心分\', \'牡丹皮\', \'心分\', \'黄芩\', \'麻黄\', \'枳壳\', \'只用\', \'青铢\', \'栀子\', \'升麻\', \'半两\', \'甘草\']');
INSERT INTO `prescript` VALUES (58478, '7170', '柴胡煮散', '柴胡（去苗）1两，人参1两，白茯苓（去黑皮）1两，当归（切，焙）1两，桔梗（锉，炒）1两，青橘皮（去白，炒）1两，芍药1两，芎藭1两，麦门冬（去心，焙）1两，白术1两，升麻1两，桑根白皮（锉）1两，甘草（炙，锉）1两。', '柴胡人参汤（原书卷一七九）。', '《圣济总录》卷八十八', '虚劳潮热，肢节烦疼，肌肤枯燥，面赤咽干。小儿骨热盗汗，肌瘦减食。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'白茯苓\', \'黑皮\', \'当归\', \'切焙\', \'桔梗\', \'两青\', \'橘皮\', \'芍药\', \'麦门冬\', \'白术\', \'升麻\', \'两桑根\', \'白皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (58479, '7144', '大黄散', '川大黄（锉碎，微炒）1两，栀子仁1两，犀角屑1两，麦门冬（去心）1两，黄连（去须）1两，地骨皮1两，甘草（炙微赤，锉）1两，黄芩1两，柴胡（去苗）1两，白鲜皮1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十六', '时气余热不退，发渴躁闷。', '-', '-', '柴胡', '[\'大黄\', \'栀子\', \'犀角\', \'麦门冬\', \'黄连\', \'地骨皮\', \'甘草\', \'炙微\', \'黄芩\', \'柴胡\', \'白鲜皮\']');
INSERT INTO `prescript` VALUES (58480, '7138', '鳖甲散', '鳖甲2两（涂醋，炙令黄，去裙襕），五味子1两，槟榔1两，赤茯苓1两半，桔梗1两（去芦头），陈橘皮1两（汤浸，去白瓤，焙），桂心1两，白术1两半，柴胡1两（去苗），甘草1两（炙微赤，锉），半夏3分（汤洗7遍，去滑）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，食前温服。', '《圣惠》卷二十六', '肝劳。虚寒胁痛，胀满气急，昏不思饮食。', '忌饴糖、羊肉、苋菜。', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'五味子\', \'槟榔\', \'两赤\', \'茯苓\', \'两半\', \'桔梗\', \'两去\', \'芦头\', \'橘皮\', \'两汤\', \'浸去\', \'焙桂心\', \'白术\', \'两半\', \'柴胡\', \'两去\', \'甘草\', \'两炙微\', \'半夏\', \'分汤\', \'洗遍\']');
INSERT INTO `prescript` VALUES (58481, '7133', '槟榔散', '槟榔半两，枳壳1分（麸炒微黄，去瓤），赤芍药1分，柴胡1分（去芦头），知母1分，人参1分（去芦头），地骨皮1分，甘草1分（炙微赤，锉），川大黄半两（锉碎，微炒）。', '每服1钱，以水1小盏，煎至5分，去滓，放温，量儿大小，分减服之。', '《圣惠》卷八十八', '小儿腹内痞结，壮热不能乳食，心胸烦壅。', '-', '-', '柴胡', '[\'槟榔\', \'半两\', \'枳壳\', \'分麸\', \'微黄\', \'芍药\', \'柴胡\', \'分去\', \'芦头\', \'知母\', \'人参\', \'分去\', \'芦头\', \'地骨皮\', \'甘草\', \'分炙微\', \'锉川\', \'大黄\', \'半两\']');
INSERT INTO `prescript` VALUES (58482, '7095', '柴胡疏厥煎', '柴胡2钱，前胡2钱，当归2钱，赤芍8分，琥珀（研，冲）8分，左牡蛎4钱，砂仁末5分，枳壳1钱。', '加灯草1丸，金箔1张，拌水煎服。', '《医方简义》卷三', '阳气暴张而为煎厥。', '-', '-', '柴胡', '[\'柴胡\', \'钱前\', \'胡钱\', \'当归\', \'赤芍\', \'琥珀\', \'研冲分\', \'牡蛎\', \'砂仁\', \'末分\', \'枳壳\']');
INSERT INTO `prescript` VALUES (58483, '7030', '柴胡饮', '柴胡（去苗）3分，葛根（锉）3分，芦根（锉）3分，地骨皮3分，百合（干者）3分，桑根白皮（锉）3分，知母（切，焙）3分，萎蕤3分，贝母（去心，炒），茅根（锉），犀角（镑），甘草（炙，锉），木通（锉）半两。', '每服4钱匕，水1盏，加生地黄半分，同煎至7分，去滓，食后温服，日3次。', '《圣济总录》卷五十八', '消渴，上焦虚热，心中烦躁。', '-', '-', '柴胡', '[\'柴胡\', \'苗分\', \'葛根\', \'锉分\', \'芦根\', \'锉分\', \'地骨皮\', \'百合\', \'干者\', \'分桑根\', \'白皮\', \'锉分\', \'知母\', \'切焙分\', \'萎蕤\', \'贝母\', \'茅根\', \'犀角\', \'甘草\', \'木通\', \'半两\']');
INSERT INTO `prescript` VALUES (58484, '7013', '黄芩汤', '黄芩（去黑心）1两，黄耆（锉）1两，柴胡（去苗）1两，秦艽（去土）1两，赤茯苓（去黑皮）1两，人参1两，栀子仁1两，甘草（炙，锉），升麻半两，地骨皮半两。', '每服3钱匕，水1盏，煎至6分，去滓，食后温服。', '《圣济总录》卷四十八', '肺脏热实，涕唾稠粘，喉咽不利。', '-', '-', '柴胡', '[\'黄芩\', \'黑心\', \'两黄耆\', \'柴胡\', \'秦艽\', \'茯苓\', \'黑皮\', \'人参\', \'栀子\', \'甘草\', \'升麻\', \'半两\', \'地骨皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58485, '7010', '柴胡饮', '柴胡（去苗）2两，桑根白皮（锉）1两，防风（去叉）1两，芍药1两，玄参1两，黄芩（去黑心）1两，甘草（炙，锉）1两。', '每服半两，水3盏，加生姜5片，煎至2盏，去滓，日午、临卧分温2服。', '《圣济总录》卷四十三', '心热多汗，骨蒸盗汗，咳嗽，五心烦热。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两桑根\', \'白皮\', \'防风\', \'芍药\', \'玄参\', \'黄芩\', \'黑心\', \'甘草\']');
INSERT INTO `prescript` VALUES (58486, '6997', '柴胡饮', '柴胡（去苗）半两，常山3分，甘草（生）半两，附子（炮裂，去皮脐）半两，干姜（炮裂）1分。', '每用5钱匕，酒1盏半，煎至1盏，去滓，分2服，空心未发时1服，食后再服。', '《圣济总录》卷三十五', '久疟不愈，将成骨蒸劳，寒热无时。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'常山\', \'甘草\', \'半两\', \'附子\', \'炮裂\', \'去皮\', \'半两\', \'干姜\', \'炮裂\']');
INSERT INTO `prescript` VALUES (58487, '6984', '常山饮', '常山1两，鳖甲（去裙襕，醋炙）1两，知母（焙）3分，白头翁3分，甘草（炙，锉）3分，柴胡（去苗）3分，青蒿1握，桃枝1握，柳枝1握，桂（去粗皮）半两。', '每服4钱匕，酒1盏半，入葱白、薤白各3寸，切，浸1宿，煎取8分，去滓，空心温服，欲发时再服。', '《圣济总录》卷三十五', '一切疟疾，经年不愈。', '-', '-', '柴胡', '[\'常山\', \'鳖甲\', \'知母\', \'焙分\', \'白头翁\', \'甘草\', \'柴胡\', \'苗分\', \'青蒿\', \'握桃枝握\', \'柳枝\', \'握桂去\', \'粗皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58488, '6975', '解肌地骨皮汤', '地骨皮1两，人参1两，柴胡（去苗）1两，栀子（去皮）1两，甘草（生锉）1两。', '地骨皮汤（《普济方》卷四十三）。', '《圣济总录》卷五十四', '中焦热结，唇焦面赤，或时烦躁，四肢拘倦。', '-', '-', '柴胡', '[\'地骨皮\', \'人参\', \'柴胡\', \'栀子\', \'去皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (58489, '6965', '柴陈五苓散', '白术1钱半，茯苓1钱半，猪苓1钱半，泽泻2钱2分，肉桂1钱半，茵陈3钱，车前子1钱，木通1钱半，柴胡1钱半。', '灯草为引，水煎服。酒疸，加干葛。', '《会约》卷十一', '湿热发黄，小水赤黑，烦渴发热。', '-', '-', '柴胡', '[\'白术\', \'钱半\', \'茯苓\', \'钱半\', \'猪苓\', \'钱半\', \'泽泻\', \'钱分\', \'肉桂\', \'钱半\', \'茵陈\', \'车前子\', \'木通\', \'钱半\', \'柴胡\', \'钱半\']');
INSERT INTO `prescript` VALUES (58490, '6958', '款肺散', '麻黄2两（去根节，炒），贝母1两，桑白皮1两（炒，锉），柴胡1两半（去芦，炒），杏仁1两（去皮尖，炒），糯米1两，款冬花1两（去尘，炒）。', '方中柴胡用量原缺，据《普济方》补。', '《博济》卷三', '寒湿相交，咳嗽不止，胸膈闷乱，痰涎并多。', '-', '-', '柴胡', '[\'麻黄\', \'两去\', \'根节\', \'贝母\', \'桑白皮\', \'柴胡\', \'两半\', \'去芦\', \'杏仁\', \'两去\', \'皮尖\', \'糯米\', \'款冬花\', \'两去\']');
INSERT INTO `prescript` VALUES (58491, '6940', '鳖甲汤', '鳖甲（去裙襕，醋炙令黄）3两，秦艽（去苗土）2两，紫菀（去土）1两半，柴胡（去苗）3两，诃黎勒皮（煨）1两半，牡蛎（煅）1两，麻黄（去根节）1两，犀角（镑）1两，知母（切，焙）1两，升麻1两，甘草（炙）1两，栀子仁1两，槟榔（锉）1两，木香1两，当归（切，焙）1两，桔梗（炒）1两，桑根白皮（锉）1两，大黄（炒）1两，黄连（去须）1两，桃仁（炒）1两，人参1两，桂（去粗皮）1两，葳蕤1两，芎1两。', '每服3钱匕，水1盏，大枣1个（劈），乌梅1个，生姜3片，同煎3-5沸，去滓稍热服，不拘时候。', '《圣济总录》卷九十三', '男子、女人骨蒸热劳，皮肉消瘦，面色萎黄，不思饮食，夜多咳嗽，涕唾稠粘，骨节疼痛，憎寒壮热，心腹气胀，坐卧不安，发如疟状；女人血风劳；一切劳疾。', '-', '-', '柴胡', '[\'鳖甲\', \'炙令\', \'秦艽\', \'苗土\', \'紫菀\', \'两半\', \'柴胡\', \'去苗\', \'两诃黎勒皮\', \'两半\', \'牡蛎\', \'麻黄\', \'根节\', \'犀角\', \'知母\', \'切焙\', \'升麻\', \'甘草\', \'栀子\', \'槟榔\', \'木香\', \'当归\', \'切焙\', \'桔梗\', \'两桑根\', \'白皮\', \'大黄\', \'黄连\', \'桃仁\', \'人参\', \'两桂去\', \'粗皮\', \'葳蕤\', \'两芎\']');
INSERT INTO `prescript` VALUES (58492, '6939', '柴胡煎丸', '童便7升，甘草1尺2寸，雄鼠粪49粒，桃枝1握（长3寸），柳枝1握（长3寸），豉心半斤，糯米1合，葱1握，薤白1握。', '每服30丸，食前以温酒送下；温水下亦得。', '《圣惠》卷三十一', '骨蒸劳。颊赤口干，心神烦闷，体瘦，发渴，寒热。', '-', '-', '柴胡', '[\'童便\', \'甘草\', \'尺寸\', \'雄鼠\', \'粪粒\', \'桃枝握\', \'长寸\', \'柳枝\', \'长寸豉\', \'半斤\', \'糯米\', \'合葱握\', \'薤白\']');
INSERT INTO `prescript` VALUES (58493, '6936', '解肌汤', '羌活、柴胡、葛根、防风、荆芥（份两量儿大小）。', '风盛抽掣，加天麻、钩藤；痰涎壅盛，加橘红、半夏；鼻塞，加葱白；指冷，加生姜；吐乳，加麦芽。【方论】羌活太阳，柴胡少阳，葛根阳明，荆、防随所应而至，以开腠理者也。此1方为发表之总剂也。', '《小儿诸热辨》', '小儿风搐初起而轻者。', '-', '-', '柴胡', '[\'羌活\', \'柴胡\', \'葛根\', \'防风\', \'荆芥\', \'两量\', \'大小\']');
INSERT INTO `prescript` VALUES (58494, '6927', '羚羊羌活汤', '羚羊角屑1两半，羌活1两半（去芦头），黄芩1两半（去黑心），人参1两半，附子1两半（炮裂，去皮脐），泽泻1两半，山茱萸1两半，秦艽1两半（去苗土），决明子1两半（微炒），车前子1两半，青葙子1两半，甘草（微炙）1两，黄耆（锉）2两，柴胡（去苗）2两半。', '每服5钱匕，以水1盏半，煎至8分，去滓温服，不拘时候，每日2次。', '《圣济总录》卷一○二', '肝肾虚，眼见黑花，或似蝇翅。', '-', '-', '柴胡', '[\'羚羊角\', \'两半\', \'羌活\', \'两半\', \'芦头\', \'黄芩\', \'两半\', \'黑心\', \'人参\', \'两半\', \'附子\', \'两半\', \'炮裂\', \'去皮\', \'泽泻\', \'两半\', \'山茱萸\', \'两半\', \'秦艽\', \'两半\', \'苗土\', \'决明子\', \'两半\', \'车前子\', \'两半\', \'青葙子\', \'两半\', \'甘草\', \'微炙两\', \'黄耆\', \'柴胡\', \'去苗\', \'两半\']');
INSERT INTO `prescript` VALUES (58495, '6911', '干地黄汤', '干地黄1两，大黄1两，黄连1两，黄芩1两，柴胡（去芦）1两，白芍药1两，甘草（炙）1两。', '每服抄4钱匕，以水1盏半，煎至7分，去滓温服。取溏利汗出解。', '《活人书》卷十九', '妇人伤寒愈后，犹有余热不去。', '-', '-', '柴胡', '[\'地黄\', \'大黄\', \'黄连\', \'黄芩\', \'柴胡\', \'去芦\', \'两白\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (58496, '6898', '寄奴汤', '白术1两，茯苓3钱，肉桂1钱，柴胡1钱，刘寄奴2钱。', '水煎服。', '《辨证录》卷二', '小便艰涩，道涩如淋，而下身生疼，时而升上有如疝气，此为风寒湿邪入于小肠而成痹。', '-', '-', '柴胡', '[\'白术\', \'茯苓\', \'肉桂\', \'柴胡\', \'刘寄奴\']');
INSERT INTO `prescript` VALUES (58497, '6893', '柴胡枳芎汤', '柴胡2钱，黄芩1钱，人参1钱，白芍药1钱，半夏1钱，枳壳1钱，甘草5分，川芎1钱，枣子2枚。', '上(口父)咀。加生姜5片，水煎，食远温服。左疼甚，加青皮6分；气血不和，加乌药8分。', '《杏苑》卷六', '两胁疼痛，身热者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'人参\', \'钱白\', \'芍药\', \'半夏\', \'枳壳\', \'甘草\', \'川芎\', \'枣子\']');
INSERT INTO `prescript` VALUES (58498, '6830', '鳖甲散', '鳖甲1两（涂醋炙令黄，去裙襕），人参3分（去芦头），枳壳3分（麸炒微黄，去瓤），紫菀3分（洗去苗土），柴胡1两（去苗），露蜂房半两（微炙），槟榔半两，桔梗半两（去芦头），五味子半两，杏仁半两（汤浸，去皮尖双仁，麸炒微黄），赤茯苓半两，甘草半两（炙微赤，锉）。', '每服4钱，以童便1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十六', '肺劳。发歇寒热，痰嗽喘促，坐卧不得。', '忌猪肉、苋菜。', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'人参\', \'分去\', \'芦头\', \'枳壳\', \'分麸\', \'微黄\', \'紫菀\', \'分洗\', \'苗土\', \'柴胡\', \'两去\', \'露蜂房\', \'半两\', \'槟榔\', \'半两\', \'桔梗\', \'半两\', \'芦头\', \'五味子\', \'半两\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'茯苓\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58499, '6828', '大黄除热汤', '大黄3两，黄芩3两，柴胡1两，芒消（别研）1两，甘草1两。', '每服5钱，水1盏半，煎1盏，去滓；入芒消1钱匕，再煎2-3沸，温服。', '《普济方》卷一一九引《指南方》', '阳实发热。', '-', '-', '柴胡', '[\'大黄\', \'黄芩\', \'柴胡\', \'两芒\', \'消别\', \'研两\', \'甘草\']');
INSERT INTO `prescript` VALUES (58500, '6804', '柴胡汤', '麻黄2两（陈者，去节），柴胡3两，黄芩3两，甘草2两（炙），干葛2两，石膏5两（碎，绵裹），葱白根（切）1升（勿令有青处，青即热，白即冷。一作桑根皮），豉7合（绵裹，3沸出之）。', '以水9升，宿渍药，明旦先煮麻黄令沸，掠去上沫，然后并诸药煮取1升7合，分3服，服别相去三食顷。良久覆取汗。汗出以粉拭之。恶寒多，加桂心1两。', '《外台》卷三引《救急方》', '天行热气，头痛，骨肉酸痛，壮热，已进豉尿汤，病不除者。', '忌海藻、菘菜。', '-', '柴胡', '[\'麻黄\', \'陈者\', \'柴胡\', \'黄芩\', \'甘草\', \'两炙干葛\', \'石膏\', \'两碎\', \'绵裹\', \'葱白\', \'根切\', \'升勿令\', \'青处\', \'青即\', \'热白\', \'一作\', \'桑根\', \'皮豉\', \'合绵裹\', \'沸出\']');
INSERT INTO `prescript` VALUES (58501, '6795', '救肝开郁汤', '白芍2两，柴胡1钱，甘草1钱，白芥子3钱，白术5钱，当归5钱，陈皮2钱，茯苓5钱。', '水煎服。', '《石室秘录》卷六', '气郁不能言。', '-', '-', '柴胡', '[\'白芍\', \'柴胡\', \'甘草\', \'白芥子\', \'白术\', \'当归\', \'陈皮\', \'茯苓\']');
INSERT INTO `prescript` VALUES (58502, '6748', '黄耆丸', '黄耆半两（薄切，用蜜炒黄色），人参半两，柴胡半两（去苗，洗净），薯蓣半两，赤茯苓半两，黄芩1分（小紧者），生犀末1分。', '以麦门冬熟水磨下。', '《幼幼新书》卷二十引《庄氏家传》', '壮气补虚。主小儿因患体虚，时复发热，不思饮食，或多惊悸。', '-', '-', '柴胡', '[\'黄耆\', \'半两\', \'薄切\', \'用蜜\', \'黄色\', \'人参\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'洗净\', \'薯蓣\', \'半两\', \'茯苓\', \'半两\', \'黄芩\', \'分小\', \'紧者\', \'生犀末分\']');
INSERT INTO `prescript` VALUES (58503, '6740', '柴胡桂枝干姜汤', '柴胡3钱，甘草2钱，人参1钱，茯苓3钱，桂枝3钱，干姜3钱。', '煎大半杯，热服，覆衣。', '《四圣心源》卷七', '牡疟。寒多热少，或但寒不热。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'人参\', \'茯苓\', \'桂枝\', \'干姜\']');
INSERT INTO `prescript` VALUES (58504, '6726', '黄耆汤', '黄耆（细锉）1两，柴胡（去苗）1两，鳖甲（去裙襕，醋炙）1两，肉豆蔻（炮，去壳）1两，白芷1两，秦艽（去苗土）1两，桂（去粗皮）1两，桔梗（炒）1两，麦门冬（去心，焙）半两，当归（切，焙）半两，白茯苓（去黑皮）半两，人参半两，枳壳（去瓤，麸炒）半两，甘草（炙，锉）半两，熟干地黄（焙）半两，海桐皮（锉）半两，芍药半两，木香半两，酸枣仁（炒）半两，沉香（锉）半两，荆芥穗半两，槟榔（锉）半两。', '每服3钱匕，水1盏，加生姜3片，同煎至7分，去滓温服，空心、日午、近夜各1次。', '《圣济总录》卷八十八', '虚劳寒热，周身疼痛，咳嗽痰壅。', '-', '-', '柴胡', '[\'黄耆\', \'柴胡\', \'鳖甲\', \'肉豆蔻\', \'炮去\', \'白芷\', \'秦艽\', \'苗土\', \'两桂去\', \'粗皮\', \'桔梗\', \'麦门冬\', \'半两\', \'当归\', \'切焙\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'人参\', \'半两\', \'枳壳\', \'半两\', \'甘草\', \'半两\', \'熟干\', \'地黄\', \'半两\', \'海桐皮\', \'半两\', \'芍药\', \'半两\', \'木香\', \'半两\', \'酸枣仁\', \'半两\', \'沉香\', \'半两\', \'荆芥\', \'半两\', \'槟榔\', \'半两\']');
INSERT INTO `prescript` VALUES (58505, '6724', '调卫饮', '广木香8分，木通8分，枳壳8分，当归1钱，穿山甲1钱，漏芦1钱，柴胡1钱，甘草3分。', '水酒各1钟，食后煎服。', '《玉案》卷五', '产后发寒，皆缘乳汁不行，以致多寒。', '-', '-', '柴胡', '[\'木香\', \'木通\', \'枳壳\', \'当归\', \'穿山甲\', \'漏芦\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (58506, '6703', '羚羊角汤', '羚羊角1两（镑），地骨皮1两，秦艽1两（洗去苗土），麦门冬1两（去心，焙），枳壳1两（去瓤，麸炒），大黄1两（锉），柴胡1两（去苗），白茯苓1两（去皮），芍药1两，桑根白皮1两（锉），黄耆1两（薄切），人参1两，鳖甲（醋炙，去裙襕）1两。', '每服3钱匕，以水1盏，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷四十三', '心热汗出，及骨蒸烦躁盗汗，食不生肌。', '-', '-', '柴胡', '[\'羚羊角\', \'两镑\', \'地骨皮\', \'秦艽\', \'两洗\', \'苗土\', \'麦门冬\', \'两去\', \'枳壳\', \'两去\', \'大黄\', \'柴胡\', \'两去\', \'白茯苓\', \'去皮\', \'芍药\', \'两桑根\', \'白皮\', \'黄耆\', \'两薄切\', \'人参\', \'鳖甲\']');
INSERT INTO `prescript` VALUES (58507, '6697', '柴胡煎', '柴胡（去苗）3分，升麻3分，栀子仁3分，芍药3分，钓藤1分，凝水石（研）1两，黄芩（去黑心）1两，知母（切，焙）1两，生葛汁1合，甘草（炙）1分，蜜2合，淡竹叶（细锉）3握，杏仁（汤浸，去皮尖双仁，炒，别研）半两。', '百日儿每服如绿豆大，1岁儿如杏仁大，温浆水化破服，每日3次。', '《圣济总录》卷一七一', '小儿频惊，壮热欲作痫。', '-', '-', '柴胡', '[\'柴胡\', \'苗分\', \'升麻\', \'栀子\', \'仁分\', \'芍药\', \'分钓藤分\', \'凝水\', \'石研\', \'黄芩\', \'黑心\', \'知母\', \'切焙\', \'两生\', \'葛汁合\', \'甘草\', \'炙分\', \'蜜合\', \'淡竹叶\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'别研\', \'半两\']');
INSERT INTO `prescript` VALUES (58508, '6680', '大腹皮散', '大腹皮1两（锉），麻黄1两半（去根节），紫苏茎叶1两半，陈橘皮1两半（汤浸去白瓤，焙），杏仁2两（汤浸，去皮尖双仁，麸炒微黄），赤茯苓1两，柴胡1两（去苗），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，入生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十六', '久咳嗽上气，坐卧不得，咽喉不利，胸中满闷。', '-', '-', '柴胡', '[\'大腹皮\', \'麻黄\', \'两半\', \'根节\', \'紫苏\', \'两半\', \'橘皮\', \'两半\', \'汤浸\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'茯苓\', \'柴胡\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58509, '6650', '黄芩荆芥汤', '柴胡1钱5分，黄芩1钱5分，荆芥1钱5分，牛蒡子4分，连翘4分，瞿麦4分，车前子4分，赤芍药4分，滑石4分，栀子4分，木通4分，当归4分，防风4分，蝉蜕5分，甘草1钱5分。', '加竹叶10片，灯芯10寸，水2钟，煎8分，不拘时候，频频服。', '《良朋汇集》卷四', '小儿感冒发热，痰壅风热，丹毒疼痛，颈项有核，腮赤痈疖，眼目赤肿，口舌生疮，咽喉疼痛，小便淋沥，胎毒痘疹，一切余毒。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'荆芥\', \'钱分\', \'牛蒡子\', \'连翘\', \'瞿麦分\', \'车前子\', \'分赤\', \'芍药\', \'滑石\', \'栀子\', \'木通\', \'当归\', \'防风\', \'蝉蜕\', \'甘草\', \'钱分\']');
INSERT INTO `prescript` VALUES (58510, '6624', '黄耆鳖甲散', '人参1两6钱半，肉桂1两6钱半（去粗皮），苦梗1两6钱半，生干地黄（洗，焙干）3两3钱，半夏（煮）2两半，紫菀2两半（去芦），知母2两半，赤芍药2两半，黄耆2两半，甘草2两半（爁），桑白皮2两半，天门冬5两（去心，焙），鳖甲（去裙，醋炙）5两，秦艽3两3钱（去芦），白茯苓3两3钱（焙），地骨皮3两3钱（去土），柴胡（去芦）3两3钱。', '黄耆鳖甲汤（《医学入门》卷八）、黄耆鳖甲煎（《金匮翼》卷三）。', '《局方》卷五（吴直阁增诸家名方）', '虚劳客热，肌肉消瘦，四肢倦怠，五心烦热，口燥咽干，颊赤心忡，日晚潮热，夜有盗汗，胸胁不利，减食多渴，咳唾稠粘，时有脓血。男女虚热，身瘦，五心烦热，四肢怠惰，咳嗽咽干，自汗食少。', '-', '-', '柴胡', '[\'人参\', \'肉桂\', \'半去\', \'粗皮苦\', \'半生\', \'地黄\', \'焙干\', \'半夏\', \'两半\', \'紫菀\', \'两半\', \'去芦\', \'知母\', \'两半\', \'芍药\', \'两半\', \'黄耆\', \'两半\', \'甘草\', \'两半\', \'桑白皮\', \'两半\', \'天门冬\', \'两去\', \'鳖甲\', \'秦艽\', \'白茯苓\', \'地骨皮\', \'柴胡\', \'去芦\']');
INSERT INTO `prescript` VALUES (58511, '6612', '柴胡防归汤', '柴胡1钱，人参1钱，当归3钱，川芎1钱半，半夏8分，陈皮8分，防风8分，甘草5分。', '加生姜、大枣，水煎服。', '《医学入门》卷四', '产后发热，不因难产伤力及亡血过多、恶露未尽、无子蒸乳四症，系外感风寒表症，脉实，挟食积瘀血者。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'当归\', \'川芎\', \'钱半\', \'半夏\', \'陈皮分\', \'防风\', \'甘草\']');
INSERT INTO `prescript` VALUES (58512, '6608', '鳖甲丸', '鳖甲（醋炙，去裙襕）1两，黄耆（锉）1两，柴胡（去苗）1两，枳壳（去瓤，麸炒）半两，白术半两，人参半两，木香1分。', '2岁儿每服10丸，温粥米饮送下，每日2次。', '《圣济总录》卷一六八', '小儿潮热，烦渴引饮，胁腹满胀，羸瘦多汗。', '-', '-', '柴胡', '[\'鳖甲\', \'黄耆\', \'柴胡\', \'枳壳\', \'半两\', \'白术\', \'半两\', \'人参\', \'半两\', \'木香\']');
INSERT INTO `prescript` VALUES (58513, '6578', '救坏汤', '人参5钱，茯苓5钱，柴胡1钱，白芍1钱，玄参5钱，麦冬5钱，白芥子2钱，当归5钱，陈皮5分。', '水煎服。', '《石室秘录》卷六', '伤寒巳汗吐下，而仍身热如火之坏症。', '-', '-', '柴胡', '[\'人参\', \'茯苓\', \'柴胡\', \'白芍\', \'玄参\', \'麦冬\', \'白芥子\', \'当归\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (58514, '6488', '丁香散', '丁香3分，半夏半两（汤洗7遍去滑），白术3分，前胡3分（去芦头），桂心3分，人参3分（去芦头），枇杷叶半两（去毛，炙微黄），厚朴3分（去粗皮，涂生姜汁，炙令香熟），柴胡1两（去苗），白茯苓3分，陈橘皮3分（汤浸，去白瓤，焙），诃黎勒1两（煨用皮），甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，大枣3个，煎至6分，去滓稍热服，不拘时候。', '《圣惠》卷二十八', '气劳，脾胃久弱，呕逆不纳饮食，四肢羸瘦，渐加乏力。', '-', '-', '柴胡', '[\'丁香\', \'半夏\', \'半两\', \'洗遍\', \'白术\', \'分前\', \'胡分\', \'芦头\', \'桂心分\', \'人参\', \'分去\', \'芦头\', \'枇杷叶\', \'半两\', \'微黄\', \'厚朴\', \'分去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'柴胡\', \'两去\', \'白茯苓\', \'分陈\', \'橘皮\', \'分汤\', \'浸去\', \'焙诃黎勒\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58515, '6485', '荆防牛蒡汤', '荆芥1钱，防风1钱，牛蒡子（炒，研）1钱，金银花1钱，陈皮1钱，花粉1钱，黄芩1钱，蒲公英1钱，连翘（去心）1钱，皂刺1钱，柴胡5分，香附子5分，甘草（生）5分。', '上用水2钟，煎至8分，食远服。', '《金鉴》卷六十六', '外吹乳初起。因乳母肝胃气浊，更兼子吮乳睡熟，鼻孔凉气袭入乳房，与热乳凝结，以致乳房肿痛，寒热往来，烦躁口渴者。', '-', '-', '柴胡', '[\'荆芥\', \'防风\', \'牛蒡子\', \'研钱\', \'金银\', \'花钱\', \'陈皮\', \'花粉\', \'黄芩\', \'蒲公英\', \'连翘\', \'心钱\', \'柴胡\', \'香附子\', \'甘草\', \'生分\']');
INSERT INTO `prescript` VALUES (58516, '6453', '救脉汤', '甘草5分，苏木5分，陈皮5分，升麻1钱，柴胡1钱，苍术1钱，当归梢2钱，熟地黄2钱，白芍药2钱，黄耆2钱，人参2钱。', '人参救肺散（原书同卷）、救肺饮（《脉因证治》卷上）、人参救肺汤（《医学纲目》卷十七）、救脉散（《医统》卷四十二）。', '《兰室秘藏》卷中', '吐血、衄血。', '-', '-', '柴胡', '[\'甘草\', \'苏木\', \'陈皮分\', \'升麻\', \'柴胡\', \'苍术\', \'当归\', \'熟地黄\', \'钱白\', \'芍药\', \'黄耆\', \'人参\']');
INSERT INTO `prescript` VALUES (58517, '6445', '解围汤', '人参5钱，熟地1两，山茱萸5钱，当归1两，茯神5钱，生枣仁5钱，柴胡1钱，白芍1两，远志2钱，半夏2钱，玄参3钱，菖蒲1钱。', '水煎服。2剂寒热减半，躁颤亦减半；再服2剂，前症顿愈；再服2剂，不再发。', '《辨证录》卷六', '止肾热，散心寒，舒肝郁。主心肾不交，寒热时止时发，1日4-5次以为常，热来时躁不可当，寒来时颤不能已。', '-', '-', '柴胡', '[\'人参\', \'熟地\', \'山茱萸\', \'当归\', \'茯神\', \'钱生\', \'枣仁\', \'柴胡\', \'白芍\', \'远志\', \'半夏\', \'玄参\', \'菖蒲\']');
INSERT INTO `prescript` VALUES (58518, '6428', '羚羊角散', '羚羊角屑3分，柴胡1两（去苗），赤芍药3分，黄芩3分，石膏2两，芎3分，川芒消3分，枳壳1两（麸炒微黄，去瓤），川大黄3分（锉碎，微炒）。', '每服3钱，以水1中盏，加竹叶2-7片，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝壅风热，眼眉骨连头疼痛，心神烦躁，大小便难。', '-', '-', '柴胡', '[\'羚羊角\', \'柴胡\', \'两去\', \'芍药\', \'黄芩\', \'石膏\', \'两芎\', \'分川芒\', \'消分\', \'枳壳\', \'两麸\', \'微黄\', \'大黄\']');
INSERT INTO `prescript` VALUES (58519, '6410', '解肌散', '麻黄2分（去根节），杏仁半两（汤浸，去皮尖双仁，麸炒微黄），赤芍药半两，贝母半两（煨微黄），石膏1两（细研），柴胡半两（去苗），葛根半两（锉），甘草半两（炙微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓，不拘时候温服。', '《圣惠》卷八十四', '小儿时气壮热，头疼咳嗽，不能食。', '-', '-', '柴胡', '[\'麻黄\', \'分去\', \'根节\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'芍药\', \'半两\', \'贝母\', \'半两\', \'微黄\', \'石膏\', \'两细研\', \'柴胡\', \'半两\', \'去苗\', \'葛根\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58520, '6393', '解肌汤', '恒山2两，柴胡4两，知母1两，甘草1两，青蒿子1两，桃枝1两，桂、生姜2两，乌梅12枚，葱白3寸，薤白3寸，柳枝1握，鳖甲（醋炙，去裙襕）2两。', '方中桂用量原缺。不用葱、薤亦良。', '《普济方》卷一四二', '伤寒往来寒热，有时肢节烦疼者；久疟。', '-', '-', '柴胡', '[\'恒山\', \'柴胡\', \'知母\', \'甘草\', \'青蒿\', \'两桃枝\', \'两桂\', \'生姜\', \'乌梅\', \'葱白\', \'薤白\', \'柳枝\', \'鳖甲\']');
INSERT INTO `prescript` VALUES (58521, '6341', '解肌散', '葛根1两（锉），柴胡1两（去苗），麻黄3分（去根节），赤芍药半两，黄芩半两，甘草半两（炙微赤，锉），桂心半两，石膏2两。', '每服5钱，以水1大盏，入生姜半分，煎至5分，去滓，不拘时候温服之。', '《圣惠》卷十六', '时气5-7日，头痛，余热不解。', '-', '-', '柴胡', '[\'葛根\', \'柴胡\', \'两去\', \'麻黄\', \'分去\', \'根节\', \'芍药\', \'半两\', \'黄芩\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'桂心\', \'半两\', \'石膏\']');
INSERT INTO `prescript` VALUES (58522, '6339', '黄芩汤', '黄芩（去黑心）1两，柴胡（去苗）1两，葛根（锉）3分，赤芍药3分，甘草（炙）半两，石膏（碎）2两。', '每服3钱匕，水1盏煎至7分，去滓温服，不拘时候。', '《圣济总录》卷四十七', '胃气实热，口舌干燥，头痛烦渴。', '-', '-', '柴胡', '[\'黄芩\', \'黑心\', \'柴胡\', \'葛根\', \'锉分\', \'芍药\', \'甘草\', \'半两\', \'石膏\']');
INSERT INTO `prescript` VALUES (58523, '6335', '柴胡煎', '柴胡（去苗）1两，知母（焙）1两，木通1两半，淡竹叶100片，瞿麦穗1两，连翘1两，防己2两，大黄（生）2两（细锉），生麦门冬汁3合（汤成下），生藕汁3合（汤成下），甜消4两（汤成下）。', '以水3升，煮取1升半，去滓，下甜消令散，次入麦门冬、藕汁等，共分为8服，空腹2服。以溏利为度，余药食后缓缓服之。', '《圣济总录》卷一三一', '热毒痈肿，血不散，初觉憎寒干渴，四肢烦闷。', '-', '-', '柴胡', '[\'柴胡\', \'知母\', \'木通\', \'两半\', \'淡竹叶\', \'麦穗\', \'连翘\', \'防己\', \'大黄\', \'生两细\', \'锉生\', \'麦门冬\', \'汁合\', \'汤成\', \'下生\', \'合汤\', \'成下\', \'甜消\', \'两汤\', \'成下\']');
INSERT INTO `prescript` VALUES (58524, '6329', '大黄丸', '大黄（锉，炒）3分，黄芩（去黑心）3分，黄连（去须）3分，当归（切，焙）3分，赤茯苓（去黑皮）3分，黄耆（锉）3分，生干地黄（焙）3分，赤芍药3分，柴胡（去苗）3分，栀子仁半两。', '每服20丸，温水送下，不拘时候。', '《圣济总录》卷九十二', '虚劳骨蒸，心神烦躁，大小便难，四肢疼痛。', '-', '-', '柴胡', '[\'大黄\', \'炒分\', \'黄芩\', \'黑心\', \'黄连\', \'当归\', \'切焙分\', \'茯苓\', \'黑皮\', \'分黄耆\', \'分生\', \'地黄\', \'焙分\', \'芍药\', \'柴胡\', \'苗分\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (58525, '6322', '黄耆散', '黄耆1两半（锉），生干地黄1两，赤芍药半两，川大黄1两半（锉碎，微炒），赤茯苓1两，知母1两，柴胡1两（去苗），川升麻1两，当归半两，木通1两（锉），甘草半两（生锉），羚羊角屑1两。', '每服4钱，以水1中盏，加小麦100粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十一', '痈肿。体热烦渴，肢节拘急，肩背疼痛。', '-', '-', '柴胡', '[\'黄耆\', \'两半\', \'锉生\', \'地黄\', \'两赤\', \'芍药\', \'半两\', \'大黄\', \'两半\', \'茯苓\', \'知母\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'当归\', \'半两\', \'木通\', \'甘草\', \'半两\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (58526, '6307', '调气汤', '代赭石18g（布包），旋复花6g（布包），牛膝9g，丹参15g，瓜蒌15g，郁金9g，白芍9g，柴胡6g，陈皮9g，枳壳9g，苏梗6g，桔梗6g，木香6g。', '-', '《临证医案医方》', '理气、活血、解郁。主食管狭窄，食管憩室。吞咽梗阻不利，胸膈痞满，有时疼痛或逆气，舌苔白腻，脉弦。', '-', '-', '柴胡', '[\'代赭石\', \'布包\', \'旋复花\', \'布包\', \'牛膝\', \'丹参\', \'郁金\', \'白芍\', \'柴胡\', \'陈皮\', \'枳壳\', \'苏梗\', \'桔梗\', \'木香\']');
INSERT INTO `prescript` VALUES (58527, '6292', '调卫养荣汤', '陈皮1钱，白术1钱，当归1钱，生地黄1钱，沙参1钱，麦门冬1钱，牡丹皮8分，地骨皮8分，桔梗5分，柴胡稍5分，谷芽1钱，甘草4分。', '上加莲子、生姜、大枣，水煎，早、晚服。痰中带血，加侧柏枝；烦躁口干者，加炒山栀，倍麦门冬；胁下胀疼，加青皮、川芎；胸膈满闷，加黄连（姜炒）、枳实，去麦冬、地骨皮；夜出盗汗，加黄连、黄耆，去柴胡、桔梗；大便闭结，加桃仁，倍当归；咳嗽不已，加瓜蒌、阿胶；头眩头痛，加天麻、川芎；小水不利，加木通、茯苓；烦渴不利，加滑石、倍麦门冬。', '《医统》卷八十二引《集验方》', '妇人室女月经不调，或先或后，或经闭不通，憎寒壮热，口苦无味，饮食少思，连声咳嗽，烦躁头眩，渐成痨证者。', '-', '-', '柴胡', '[\'陈皮\', \'白术\', \'当归\', \'生地黄\', \'沙参\', \'麦门冬\', \'牡丹皮\', \'地骨皮\', \'桔梗\', \'柴胡\', \'稍分\', \'谷芽\', \'甘草\']');
INSERT INTO `prescript` VALUES (58528, '6282', '柴胡煎丸', '柴胡1两半（去苗），犀角屑1两，知母1两，胡黄连1两，桔梗（去芦头）1两，川升麻1两，地骨皮1两，黄芩1两，诃黎勒皮1两，栝楼1枚，鳖甲2两（涂醋炙令微黄，去裙襕），甘草3分（炙微赤，锉），赤茯苓3分，人参3分（去芦头）。', '每服20丸，食后煎乌梅、小便送下。', '《圣惠》卷二十七', '虚劳骨热，肢节烦疼，心膈躁闷。', '忌苋菜。', '-', '柴胡', '[\'柴胡\', \'两半\', \'去苗\', \'犀角\', \'知母\', \'胡黄连\', \'桔梗\', \'芦头\', \'两川\', \'升麻\', \'地骨皮\', \'黄芩\', \'两诃黎勒皮\', \'栝楼\', \'鳖甲\', \'两涂\', \'炙令\', \'微黄\', \'甘草\', \'分炙微\', \'茯苓\', \'人参\', \'分去\', \'芦头\']');
INSERT INTO `prescript` VALUES (58529, '6270', '麻黄散', '麻黄1两（去根节），桂心半两，川大黄3分（锉碎，微炒），茵陈1两，细辛半两，柴胡半两（去苗），甘草半两（炙微赤，锉），栝楼根1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病4日，毒气内攻，身体疼痛，壮热头重，烦渴不止。', '-', '-', '柴胡', '[\'麻黄\', \'两去\', \'根节\', \'桂心\', \'半两\', \'大黄\', \'茵陈\', \'细辛\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'甘草\', \'半两\', \'炙微\', \'栝楼\', \'根两\']');
