INSERT INTO `prescript` VALUES (58531, '6243', '碧霞丹', '炉甘石（制）2两，黄丹（炒）2两，铜绿8钱，海螵蛸5钱，归尾1钱，没药1钱，血竭1钱，白丁香1钱，硼砂1钱，牙硝1钱，乳香1钱，青盐1钱，轻粉1钱，雄黄1钱，元明粉1钱，胆矾1钱，明矾1钱，朱砂1钱，熊胆1分，冰片3分，麝1分，黄连1两，川芎3钱，当归3钱，赤芍3钱，生地3钱，柴胡3钱，龙胆草3钱，蕤仁3钱，杏仁3钱，蝉蜕3钱，菊花3钱，黄柏3钱，五倍3钱，羌活3钱，防风3钱，木贼草3钱。', '临用热水化洗。', '《理瀹》', '目内外障。', '-', '-', '柴胡', '[\'炉甘石\', \'制两\', \'黄丹\', \'铜绿\', \'海螵蛸\', \'钱归尾\', \'没药\', \'血竭\', \'钱白\', \'丁香\', \'硼砂\', \'钱牙硝钱\', \'乳香\', \'青盐\', \'轻粉\', \'雄黄\', \'元明粉\', \'胆矾\', \'明矾\', \'朱砂\', \'熊胆\', \'冰片\', \'麝分\', \'黄连\', \'川芎\', \'当归\', \'赤芍\', \'生地\', \'柴胡\', \'龙胆草\', \'蕤仁\', \'杏仁\', \'蝉蜕\', \'菊花\', \'黄柏\', \'五倍\', \'羌活\', \'防风\', \'木贼\', \'草钱\']');
INSERT INTO `prescript` VALUES (58532, '6240', '大腹皮散', '大腹皮2两（锉），柴胡2（1）两（去苗），诃黎勒皮1两，枳壳1两（麸炒微黄，去瓤），川大黄2两（锉碎；微炒），羚羊角屑3分，川朴消2两，甘草半两（炙微赤，锉）。', '本方方名，《普济方》引作“大腹皮汤”。', '《圣惠》卷六', '大肠实热，肠胀不通，热气上冲，口内生疮。', '-', '-', '柴胡', '[\'大腹皮\', \'柴胡\', \'两去\', \'苗诃黎勒皮\', \'枳壳\', \'两麸\', \'微黄\', \'大黄\', \'羚羊角\', \'分川朴\', \'消两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58533, '6228', '大腹皮散', '大腹皮1两（锉），赤茯苓3分，枳实3分（麸炒微黄），柴胡3分（去苗），桑根白皮3分（锉），人参1两（去芦头）。', '每服4钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病，肺壅气喘，膈中不利。', '-', '-', '柴胡', '[\'大腹皮\', \'茯苓\', \'枳实\', \'分麸\', \'微黄\', \'柴胡\', \'分去\', \'苗桑根\', \'白皮\', \'人参\', \'两去\', \'芦头\']');
INSERT INTO `prescript` VALUES (58534, '6225', '脯鸡糁', '黄雌鸡1只（去毛头足肠胃，洗净，以小麦2合，以水5升煮鸡半熟，即取出鸡，去骨），蜀椒（去目并闭口，炒汗出，取末）1钱，柴胡（去苗）2钱，干姜末半钱，粳米3合。', '上先取水再煮鸡及米令烂，入葱、薤、椒、姜、柴胡末等，次又入五味、盐、酱，煮取熟，任意食之。', '《圣济总录》卷一九○', '产后心虚忪悸，遍身疼痛。', '-', '-', '柴胡', '[\'黄雌鸡\', \'毛头\', \'肠胃\', \'洗净\', \'小麦\', \'合以\', \'水升\', \'半熟\', \'取出\', \'鸡去\', \'闭口\', \'炒汗\', \'出取\', \'柴胡\', \'苗钱\', \'干姜\', \'末半钱\', \'粳米\']');
INSERT INTO `prescript` VALUES (58535, '6224', '鳖甲柴胡汤', '鳖甲（醋炙，去裙襕）1两，柴胡（去苗）1两，乌梅（去核）1两，人参1两，半夏（汤洗7遍，去滑）3分，陈橘皮（汤浸，去白，焙）3分，独活（去芦头）3分，芎3分，附子（炮裂，去皮脐）3分，芍药（炒）3分，桂（去粗皮）半两，酸枣仁半两，甘草（炙，锉）半两，黄耆半两。', '每服5钱匕，水1盏半，加生姜5片，煎取7分，去滓温服。', '《圣济总录》卷八十九', '风虚劳倦，四肢拘急，不思饮食，遍身疼痛。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'乌梅\', \'去核\', \'人参\', \'半夏\', \'洗遍\', \'滑分\', \'橘皮\', \'汤浸\', \'白焙分\', \'独活\', \'芦头\', \'分芎\', \'附子\', \'炮裂\', \'去皮\', \'脐分\', \'芍药\', \'分桂去\', \'粗皮\', \'半两\', \'酸枣仁\', \'半两\', \'甘草\', \'半两\', \'黄耆\', \'半两\']');
INSERT INTO `prescript` VALUES (58536, '6194', '露宿汤', '常山（炒）1两，秦艽（去苗土）半两，栀子仁半两，柴胡（去苗）半两，青蒿子半两，槟榔（生锉）半两，甘草（炙，锉）半两，桂（去粗皮）1分。', '每服3钱匕，水3盏，入乌梅2个，黑豆20粒，煎至1盏半，去滓，露1宿，五更初再温服。其滓再入水2盏，煎至1盏，去滓，当夜临卧时服。', '《圣济总录》卷三十七', '岭南瘴气，头疼体痛，寒热往来，胸满腹胀，烦渴呕逆，咳嗽多痰，心躁狂言，大便难，小便赤。', '-', '-', '柴胡', '[\'常山\', \'秦艽\', \'苗土\', \'半两\', \'栀子\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'青蒿\', \'半两\', \'槟榔\', \'半两\', \'甘草\', \'半两\', \'桂去\', \'粗皮分\']');
INSERT INTO `prescript` VALUES (58537, '6162', '大腹皮饮', '大腹皮（锉）3分，人参1两，赤茯苓（去黑皮）1两，当归（切、焙）1两半，枳壳（去瓤，麸炒）1两半，柴胡（去苗）1两半，白术1两半。', '每服3钱匕，水1盏，加生姜3片，大枣1枚（擘），煎至7分，去滓温服，1日3次。', '《圣济总录》卷一五五', '妇人妊娠，心腹疼痛，及两胁肋内妨闷，呕逆恶心不止，饮食不下，体倦，四肢少力；或时发气胀，喘息粗大，胎不安稳。', '-', '-', '柴胡', '[\'大腹皮\', \'锉分\', \'人参\', \'两赤\', \'茯苓\', \'黑皮\', \'当归\', \'切焙\', \'两半\', \'枳壳\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'白术\', \'两半\']');
INSERT INTO `prescript` VALUES (58538, '6156', '麻黄白术汤', '青皮（去腐）1分，酒黄连1分，酒黄柏2分，橘红2分，甘草（炙，末）2分，升麻2分，黄耆3分，人参3分，桂枝3分，白术3分，厚朴3分，柴胡3分，苍术3分，猪苓3分，吴茱萸4分，白茯苓4分，泽泻4分，白豆蔻5分，炒曲5分，麻黄（不去节）5钱，杏仁4个。', '麻黄白术散（《东垣试效方》卷七）。', '《兰室秘藏》卷下', '大便不通，五日一遍，小便黄赤，浑身肿，面上及腹尤甚，色黄，麻木，身重如山，沉困无力，四肢痿软，不能举动，喘促唾清水，吐哕，痰唾白沫如胶，时躁热发欲去衣，须臾而过则振寒，项额有时如冰，额寒尤甚，头旋眼黑，目中溜火，冷泪，鼻不闻香臭，少腹急痛，当脐有时动气，按之坚硬而痛。', '-', '-', '柴胡', '[\'青皮\', \'腐分\', \'黄连\', \'分酒\', \'黄柏\', \'橘红\', \'甘草\', \'炙末分\', \'升麻\', \'分黄\', \'耆分\', \'人参\', \'桂枝\', \'白术\', \'厚朴\', \'柴胡\', \'苍术\', \'猪苓\', \'吴茱萸\', \'白茯苓\', \'泽泻\', \'白豆蔻\', \'曲分\', \'麻黄\', \'不去\', \'节钱\', \'杏仁\']');
INSERT INTO `prescript` VALUES (58539, '6145', '解郁活血汤', '当归2钱，白芍3钱，柴胡2钱，茯苓3钱，薄荷1钱，丹皮2钱，山栀仁2钱，白术3钱，泽兰叶4钱，郁金2钱，甘草1钱。', '水煎服。', '《中医妇科治疗学》', '舒郁行气活血。主经闭气郁证。肝郁气滞，经闭不行，面色青黄，精神抑郁，烦躁性急，头晕耳鸣，胸胁作胀，食少嗳气，舌尖红，苔微黄而燥，脉弦数或弦紧。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'柴胡\', \'茯苓\', \'薄荷\', \'丹皮\', \'钱山\', \'栀仁钱\', \'白术\', \'泽兰\', \'叶钱郁\', \'金钱\', \'甘草\']');
INSERT INTO `prescript` VALUES (58540, '6132', '大柴胡汤', '柴胡8两，半夏（汤洗）8两，生姜4两，知母2两，芍药2两，大黄2两，萎蕤2两，甘草（炙）3两，人参3两。', '方中甘草用量原缺。《外台》引《集验方》无人参。', '《外台》卷一引《范汪方》', '伤寒7-8日不解，默默烦闷，腹中有干粪，谵语。', '忌海藻、菘菜、羊肉、饧。', '-', '柴胡', '[\'柴胡\', \'半夏\', \'汤洗\', \'生姜\', \'知母\', \'芍药\', \'大黄\', \'萎蕤\', \'甘草\', \'人参\']');
INSERT INTO `prescript` VALUES (58541, '6118', '羚羊角汤', '羚羊角1两（镑），荠苨1两，独活1两（去芦头），防风1两（去叉），柴胡1两（去苗），芎1两，枳壳1两（去瓤，麸炒），人参1两，甘草1两（炙，锉），白术1两。', '每服2钱匕，以水1盏，加薄荷5叶，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷十三', '中风寒热，头痛昏倦。', '-', '-', '柴胡', '[\'羚羊角\', \'两镑\', \'独活\', \'两去\', \'芦头\', \'防风\', \'两去\', \'柴胡\', \'两去\', \'苗芎\', \'枳壳\', \'两去\', \'人参\', \'甘草\', \'白术\']');
INSERT INTO `prescript` VALUES (58542, '6105', '羚羊角汤', '羚羊角3分（镑），黄芩3分（去黑心），柴胡3分（去苗），升麻3分，甘草（生锉）1两。', '本方方名，《普济方》引作“羚羊角散”。', '《圣济总录》卷一○九', '心肺风热，冲目生胬肉。', '-', '-', '柴胡', '[\'羚羊角\', \'分镑\', \'黄芩\', \'黑心\', \'柴胡\', \'分去\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (58543, '6087', '黄连汤', '黄连（去须）1两，大青1两，山栀子仁1两，茵陈蒿1两，柴胡（去苗）1两，地骨皮1两，人参1两，黄芩（去黑心）1两，芒消1两，大黄（细锉炒）2两。', '每服5钱匕，水1盏半，煎至8分，去滓温服，不拘时候。', '《圣济总录》卷六十', '黄疸，遍身面目皆黄。', '-', '-', '柴胡', '[\'黄连\', \'两大青\', \'两山\', \'栀子\', \'茵陈蒿\', \'柴胡\', \'地骨皮\', \'人参\', \'黄芩\', \'黑心\', \'两芒\', \'消两\', \'大黄\']');
INSERT INTO `prescript` VALUES (58544, '6081', '柴胡汤', '柴胡（去苗）1两，人参1两，黄芩（去黑心）1两，犀角（镑）3分，朴消3分，茯神（去木）3分，甘草（炙，锉）半两。', '每服5钱匕，水1盏半，煎至7分，去滓温服。', '《圣济总录》卷二十三', '伤寒热实，烦躁谵语。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'黄芩\', \'黑心\', \'犀角\', \'分朴\', \'消分\', \'茯神\', \'木分\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58545, '6047', '黄连猪肚丸', '黄连3两（去须），人参1两（去芦头），赤茯苓1两，黄耆1两（锉），木香半两，鳖甲1两半（涂醋，炙令黄，去裙襕），柴胡1两（去苗），地骨皮半两，桃仁1两半（汤浸，去皮尖双仁，麸炒微黄）。', '猪肚丸（《妇人良方》卷六）。', '《圣惠》卷七十', '妇人热劳羸瘦。', '-', '-', '柴胡', '[\'黄连\', \'两去\', \'人参\', \'两去\', \'芦头\', \'茯苓\', \'两黄耆\', \'木香\', \'半两\', \'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'黄去\', \'柴胡\', \'两去\', \'地骨皮\', \'半两\', \'桃仁\', \'两半\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (58546, '6037', '葛根散', '葛根半两（锉），黄芩半两，川大黄1两（锉碎，微炒），柴胡半两（去苗），甘草1分（炙微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓温服，不拘时候。以稍利为度。', '《圣惠》卷八十四', '小儿时气烦渴，腹中痞实。', '-', '-', '柴胡', '[\'葛根\', \'半两\', \'黄芩\', \'半两\', \'大黄\', \'柴胡\', \'半两\', \'去苗\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58547, '6033', '葶苈丸', '半夏（洗）5分，厚朴（炙）5分，石膏5分，青皮5分，当归身7分，白豆蔻仁1钱，缩砂1钱，茵陈（酒制）1钱，干葛1钱，炙甘草3钱，羌活3钱，黄芩（一半酒洗，一半炒）3钱，苦葶苈（酒洗，炒）3钱，人参3钱，柴胡3钱，独活3钱。', '每服2钱，临卧用一口汤送下。', '《兰室秘藏》卷上', '心下痞，胸中不利。', '-', '-', '柴胡', '[\'半夏\', \'洗分\', \'厚朴\', \'炙分\', \'石膏\', \'青皮\', \'当归\', \'身分\', \'白豆蔻\', \'仁钱\', \'缩砂\', \'茵陈\', \'制钱\', \'干葛钱\', \'甘草\', \'羌活\', \'黄芩\', \'一半\', \'酒洗\', \'一半\', \'炒钱苦\', \'人参\', \'柴胡\', \'独活\']');
INSERT INTO `prescript` VALUES (58548, '6032', '柴胡汤', '生地4钱，白芷3钱，天冬1钱，青蒿6钱，柴胡1两，玄参3钱，生耆3钱，连翘3钱，百合5钱，知母3钱。', '水煎服。', '《验方新编》卷十五', '中蛊。额焦口腥，神昏性躁，目见邪鬼形，耳闻邪鬼声，如犯大罪，如见恶役持练锁至，如有刀兵、健卒追赶，常思自尽。', '宜戒荤盐，俟毒净自愈，愈后不用戒鱼虾等物。', '-', '柴胡', '[\'生地\', \'白芷\', \'天冬\', \'青蒿\', \'柴胡\', \'玄参\', \'钱生\', \'连翘\', \'百合\', \'知母\']');
INSERT INTO `prescript` VALUES (58549, '6006', '感冒清热冲剂', '荆芥穗200g，薄荷60g，防风100g，柴胡100g，紫苏叶60g，葛根100g，桔梗60g，苦杏仁80g，白芷60g，苦地丁200g，芦根160g。', '每次1g，开水冲服，1日2次。', '《中国药典》2015版一部', '疏风散寒，解表清热。主风寒感冒，头痛发热，恶寒身痛，鼻流清涕，咳嗽咽干。', '-', '-', '柴胡', '[\'荆芥\', \'薄荷\', \'防风\', \'柴胡\', \'紫苏叶\', \'葛根\', \'桔梗\', \'苦杏仁\', \'白芷\', \'苦地丁\', \'芦根\']');
INSERT INTO `prescript` VALUES (58550, '5998', '琥珀散', '琥珀3分（细研），白术3分，当归3分（锉碎，微炒），柴胡1两（去苗），延胡索半两，红花子半两，牡丹半两，木香半两，桂心半两，桃仁3分（汤浸，去皮尖双仁，麸炒黄），鳖甲1两（涂醋，炙令黄，去裙襕），赤芍药2分。', '每服4钱，以水1中盏，入生姜半分，煎至6分，去滓，每于食前稍热服。', '《圣惠》卷七十', '妇人血风劳气，腑腹疼痛，经脉不调，渐加羸瘦。', '-', '-', '柴胡', '[\'琥珀\', \'分细研\', \'白术\', \'当归\', \'柴胡\', \'两去\', \'延胡索\', \'半两\', \'红花\', \'半两\', \'牡丹\', \'半两\', \'木香\', \'半两\', \'桂心\', \'半两\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'芍药\']');
INSERT INTO `prescript` VALUES (58551, '5984', '鳖甲汤', '鳖甲（汤浸，刮令净，醋炙）1两，白术1两，常山1两，桂（去皮）1两，柴胡（去苗）1两，牡蛎半两（火煅赤）。', '每服5钱，水2盏，煎至1盏，去滓温服。', '《全生指迷方》卷二', '疟疾寒热等。', '-', '-', '柴胡', '[\'鳖甲\', \'汤浸\', \'刮令净\', \'白术\', \'常山\', \'两桂\', \'去皮\', \'柴胡\', \'牡蛎\', \'半两\']');
INSERT INTO `prescript` VALUES (58552, '5970', '解毒清肝汤', '银花15g，柴胡6g，归尾6g，酒黄芩9g，秦皮3g，赤芍9g，车前子9g，防风1.5g。', '-', '《张皆春眼科证治》', '清热解毒，泻火除风。主凝脂翳初起，风热偏盛，胞睑微浮，白睛赤脉纵横，羞明流泪，结眵粘稠，风轮出现肥厚星点，或凝脂成片。', '-', '-', '柴胡', '[\'银花\', \'柴胡\', \'归尾酒\', \'黄芩\', \'秦皮\', \'赤芍\', \'车前子\', \'防风\']');
INSERT INTO `prescript` VALUES (58553, '5965', '羚羊角饮子', '羚羊角1两，羌活1两，黑参1两，细辛1两，桔梗1两，黄芩1两，柴胡1两，车前子1两半，茺蔚子1两半，防风1两。', '黑风羚羊饮（《金鉴》卷七十七）。本方用法，原作丸剂，与方名不符，据《金鉴》改。', '《秘传眼科龙木论》卷二', '黑风内障。', '-', '-', '柴胡', '[\'羚羊角\', \'羌活\', \'两黑\', \'参两\', \'细辛\', \'桔梗\', \'黄芩\', \'柴胡\', \'车前子\', \'两半\', \'茺蔚子\', \'两半\', \'防风\']');
INSERT INTO `prescript` VALUES (58554, '5963', '葵子汤', '葵子2升，生姜6两，甘草2两，芍药4两，白术3两，柴胡3两，大枣20枚，厚朴2两。', '上(口父)咀。以水9升，煮取3升，分3服，1日3次。10日1剂。一方用乌雌鸡1只，煮水以煎药。', '《千金》卷二', '曾伤八月胎者，预服之。', '忌海藻、菘菜、桃、李、雀肉等。', '-', '柴胡', '[\'葵子\', \'生姜\', \'甘草\', \'芍药\', \'白术\', \'柴胡\', \'大枣\', \'厚朴\']');
INSERT INTO `prescript` VALUES (58555, '5945', '鳖甲丸', '鳖甲（涂醋，炙令黄，去裙襕）1两，贝母（煨微黄）1两，人参（去芦头）1两，木香1两，柴胡（去苗）1两，桔梗（去芦头）1两，五味子1两，甘草半两（炙微赤，锉）。', '每服30丸，以糯米粥饮送下，不拘时候。', '《圣惠》卷七十四', '妊娠咳嗽，羸瘦，不能下食。', '-', '-', '柴胡', '[\'鳖甲\', \'涂醋\', \'炙令\', \'黄去\', \'贝母\', \'微黄\', \'人参\', \'芦头\', \'木香\', \'柴胡\', \'桔梗\', \'芦头\', \'五味子\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58556, '5915', '马兜铃汤', '马兜铃1分，杏仁1分（去皮尖双仁，炒黄），柴胡1分（去苗），贝母1分（炒，去心），桔梗1分（锉，炒），紫菀1分（去苗土），麻黄1分（去根节，汤煮，掠去沫，焙），麦门冬1分（去心，焙），大腹皮1分，大黄3铢，羌活半两。', '每服4钱匕，水1盏半，加生姜3片，同煮1-2沸，去滓温服。', '《圣济总录》卷二十四', '伤寒热病发咳，坐卧喘急不安，其脉右手寸关洪大浮数。', '-', '-', '柴胡', '[\'马兜铃\', \'杏仁\', \'皮尖\', \'双仁\', \'柴胡\', \'分去\', \'贝母\', \'桔梗\', \'紫菀\', \'苗土\', \'麻黄\', \'分去\', \'根节\', \'掠去\', \'麦门冬\', \'分去\', \'大腹皮\', \'大黄\', \'羌活\', \'半两\']');
INSERT INTO `prescript` VALUES (58557, '5895', '黄龙汤', '柴胡1两，黄芩1分半，人参1分半，甘草（炙）1分半。', '小柴胡汤（《普济方》卷三三九）。', '《活人书》卷十九', '妊妇寒热头痛，嘿嘿不欲饮食，胁下痛，呕逆痰气；及产后伤风，热入胞宫，寒热如疟；并经水适来适断，病后劳复，余热不解。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'分半\', \'人参\', \'分半\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (58558, '5858', '大黄散', '川大黄2两（锉碎，微炒），木香半两，柴胡1两（去苗），赤芍药3分，诃黎勒3分（用皮），枳实半两（麸炒微黄），甘草半两（炙微赤，锉），桃仁1两（汤浸，去皮尖双仁，麸炒微黄），鳖甲1两（涂醋，炙微黄，去裙襕）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，食前温服。', '《圣惠》卷三十一', '骨蒸痃癖，胁下妨闷，肢节疼痛。', '-', '-', '柴胡', '[\'大黄\', \'木香\', \'半两\', \'柴胡\', \'两去\', \'芍药\', \'分诃黎勒\', \'分用\', \'枳实\', \'半两\', \'微黄\', \'甘草\', \'半两\', \'炙微\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'鳖甲\', \'两涂\', \'微黄\']');
INSERT INTO `prescript` VALUES (58559, '5852', '柴胡煎', '柴胡（去苗）1两半，麻黄（去根节，汤煮，掠去沫）1两半，甘草（炙）1两半，木通（锉）1两半，紫菀（去苗土）1两半，五味子1两半，大青干1两半，百合1两半，款冬花3分，蓝叶3分，人参3分，赤茯苓（去黑皮）3分，大黄（锉，炒）半两，酥1斤，蜜（炼熟，去白沫）1升。', '5-6岁儿每服1钱匕，食后温熟水调下，日3次。', '《圣济总录》卷一八○', '小儿脑热肺壅，鼻干无涕，喘息不得。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'麻黄\', \'根节\', \'掠去\', \'两半\', \'甘草\', \'两半\', \'木通\', \'两半\', \'紫菀\', \'苗土\', \'两半\', \'五味子\', \'两半\', \'大青干\', \'两半\', \'百合\', \'两半\', \'款冬花\', \'分蓝叶分\', \'人参\', \'分赤\', \'茯苓\', \'黑皮\', \'大黄\', \'半两\', \'酥斤\', \'蜜炼\', \'白沫\']');
INSERT INTO `prescript` VALUES (58560, '5842', '黄鸡煎丸', '黄连（去须）2两，鹤虱半两，芜荑仁半两，秦艽1两（去苗土），柴胡1两（去苗），知母1两（焙），使君子（去皮）1两。', '每服10丸，量儿大小加减，空心、临卧麦门冬熟水送下；疳瘦骨热，15岁以上，温酒送下。', '《圣济总录》卷一六八', '小儿潮热，肌瘦盗汗。', '-', '-', '柴胡', '[\'黄连\', \'鹤虱\', \'半两\', \'芜荑仁\', \'半两\', \'秦艽\', \'两去\', \'苗土\', \'柴胡\', \'两去\', \'知母\', \'两焙\', \'使君子\', \'去皮\']');
INSERT INTO `prescript` VALUES (58561, '5797', '黄芩散', '黄芩1两，栀子仁1两，犀角屑1两，赤芍药1两，柴胡（去苗）1两，枳壳（麸炒微黄，去瓤，焙）1两，槟榔1两。', '每服5钱，以水1中盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气5日，心腹壅闷，骨节疼痛，背膊烦热，不下饮食。', '-', '-', '柴胡', '[\'黄芩\', \'栀子\', \'犀角\', \'芍药\', \'柴胡\', \'枳壳\', \'微黄\', \'槟榔\']');
INSERT INTO `prescript` VALUES (58562, '5746', '蛤蚧散', '蛤蚧1对（雌雄头尾全者，不得有蛀蚛，水洗净，焙干），枇杷叶（拭去毛）3分，柴胡（去苗）半两，紫菀（净洗，焙干）3两，贝母（去心，炒）1两，人参半两，鹿角胶（炙燥）3分。', '每用梨1颗，去皮，细切，净器研之，生绢滤自然汁于银器内，用药末半钱匕，入梨汁中，以慢火熬3-5沸取出，食后、临卧服。去枕仰卧一饭顷。', '《圣济总录》卷六十五', '咳嗽，咽嗌不利。', '-', '-', '柴胡', '[\'蛤蚧\', \'雌雄\', \'头尾\', \'全者\', \'不得\', \'洗净\', \'焙干\', \'枇杷叶\', \'拭去\', \'毛分\', \'柴胡\', \'去苗\', \'半两\', \'紫菀\', \'净洗\', \'焙干\', \'贝母\', \'人参\', \'半两\', \'鹿角胶\', \'炙燥\']');
INSERT INTO `prescript` VALUES (58563, '5732', '鳖甲汤', '鳖甲（去裙襕，醋炙）1两，羌活（去芦头）1两，防风（去叉）1两，芎1两，熟干地黄（焙）1两，人参1两，附子（炮裂，去皮脐）1两，白茯苓（去黑皮）1两，芍药1两，柴胡（去苗）1两，木香半两，桂（去粗皮）半两。', '每服3钱匕，水1盏，生姜3片，大枣1个（劈破），煎至7分，去滓，空心、日午、临卧温服。', '《圣济总录》卷一五○', '妇人风虚劳冷，头目昏眩，肢体酸痛，脐腹冷疼，饮食不化，经水不匀。', '-', '-', '柴胡', '[\'鳖甲\', \'羌活\', \'芦头\', \'防风\', \'两芎\', \'两熟\', \'地黄\', \'人参\', \'附子\', \'炮裂\', \'去皮\', \'白茯苓\', \'黑皮\', \'芍药\', \'柴胡\', \'木香\', \'半两\', \'桂去\', \'粗皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58564, '5678', '解毒济生汤', '川芎1钱，当归1钱，黄柏1钱，知母1钱，天花粉1钱，金银花1钱，麦门冬1钱，远志1钱，柴胡1钱，黄芩1钱，犀角1钱，茯神1钱，甘草5分，红花5分，升麻（手指加）5分，牛膝（足指加）5分。', '水2钟，煎8分，临服入童便1杯，随病上下服。', '《外科正宗》卷二', '脱疽初起，恶寒体倦，发热作渴，或肿或紫，或麻或痛，四肢倦怠，心志恍惚不宁者。', '-', '-', '柴胡', '[\'川芎\', \'当归\', \'黄柏\', \'知母\', \'天花粉\', \'金银\', \'花钱\', \'麦门冬\', \'远志\', \'柴胡\', \'黄芩\', \'犀角\', \'茯神\', \'甘草\', \'红花\', \'升麻\', \'手指\', \'牛膝\', \'足指\']');
INSERT INTO `prescript` VALUES (58565, '5673', '黄龙汤', '黄耆1钱5分（蜜多），当归1钱，白芍1钱，白术（蜜炙）1钱，苍术（米泔浸）1钱，陈皮1钱，生地黄3钱，甘草（炙）3钱，熟地黄5钱，柴胡2钱。', '水煎服。', '《竹林女科》卷一', '妇人劳役，脾胃虚损，漏下不止，其色鲜红，气短气逆，自汗不止，身体发热，大便泄泻，四肢无力，不思饮食。', '-', '-', '柴胡', '[\'黄耆\', \'分蜜\', \'当归\', \'白芍\', \'白术\', \'蜜炙钱\', \'苍术\', \'泔浸\', \'陈皮\', \'生地黄\', \'甘草\', \'熟地黄\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58566, '5647', '麻黄汤', '麻黄（去根节）3分，乌梅肉（炒）3分，秦艽（去苗土）3分，柴胡（去苗）3分，甘草（炙）3分，麦门冬（去心，焙）3分，犀角（镑）3分，青蒿子1两半，常山1两。', '每用5钱匕，以水1盏半，加桃柳枝心各7枚（锉细）、豉50粒，煎至1盏，入朴消少许，更煎1-2沸，去滓，分2次服，早晨及卧时温服之。', '《圣济总录》卷三十四', '诸疟疾，先热后寒，头痛，四肢烦倦。', '-', '-', '柴胡', '[\'麻黄\', \'根节\', \'乌梅\', \'炒分\', \'秦艽\', \'苗土\', \'柴胡\', \'苗分\', \'甘草\', \'炙分\', \'麦门冬\', \'心焙分\', \'犀角\', \'青蒿\', \'两半\', \'常山\']');
INSERT INTO `prescript` VALUES (58567, '5634', '黄连散', '黄连1两（去须），知母2两，鳖甲2两（涂醋，炙令黄，去裙襕），麦门冬3分（去心），龙胆半两（去芦头），甘草半两（炙微赤，锉），柴胡1两半（去苗），白术3分，地骨皮3分，木通1两（锉），黄芩3分，犀角屑3分。', '每服4钱，以水1中盏，加生姜半分，淡竹叶3-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人骨蒸劳热，四体昏沉，背膊疼痛，面色萎黄，渐渐无力。', '-', '-', '柴胡', '[\'黄连\', \'两去\', \'须知\', \'母两\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'麦门冬\', \'分去\', \'龙胆\', \'半两\', \'芦头\', \'甘草\', \'半两\', \'炙微\', \'柴胡\', \'两半\', \'去苗\', \'白术\', \'地骨皮\', \'木通\', \'黄芩\', \'犀角\']');
INSERT INTO `prescript` VALUES (58568, '5631', '葶苈丸', '葶苈子（慢火炒，别捣为膏）1两半，枳壳（去瓤，麸炒）3分，桂（去粗皮）3分，羚羊角（镑）3分，白茯苓（去黑皮）3分，柴胡（去苗）3分，鳖甲（去裙襕，醋浸炙）3分，防风（去叉）3分，菟丝子（酒浸两宿，焙干，炒，别捣）3分，牛膝（去苗）3分，安息香3分，陈橘皮（汤浸，去白，焙）1两。', '每服30丸，空腹酒送下。', '《圣济总录》卷五十八', '消渴下冷，小便浓白如泔，呕逆不下食。', '-', '-', '柴胡', '[\'葶苈子\', \'慢火\', \'炒别\', \'两半\', \'枳壳\', \'分桂去\', \'粗皮分\', \'羚羊角\', \'白茯苓\', \'黑皮\', \'柴胡\', \'苗分\', \'鳖甲\', \'浸炙分\', \'防风\', \'去叉分\', \'菟丝子\', \'酒浸\', \'两宿\', \'焙干\', \'炒别\', \'捣分\', \'牛膝\', \'苗分\', \'安息香\', \'分陈\', \'橘皮\', \'汤浸\']');
INSERT INTO `prescript` VALUES (58569, '5614', '解郁软坚汤', '全当归10g，赤芍药10g，正川芎5g，北柴胡5g，川郁金6g，白蒺藜10g，漂昆布10g，净海藻10g，制香附6g，酒青皮5g，山慈菇5g，蒲公英13g，鹿角霜15g（先煎）。', '水煎服，每日1剂。', '《千家妙方》卷下引李聪甫方', '疏肝解郁，和血软坚。主肝郁结滞之乳腺小叶增生。两乳肿块坚硬，推之不移，皮色如常，隐隐作痛，经前肿块变大，经后复小。', '-', '-', '柴胡', '[\'当归\', \'芍药\', \'川芎\', \'柴胡\', \'郁金\', \'蒺藜\', \'昆布\', \'海藻\', \'香附\', \'青皮\', \'山慈菇\', \'蒲公英\', \'鹿角霜\']');
INSERT INTO `prescript` VALUES (58570, '5604', '槟苏败毒散', '人参1钱，羌活1钱，独活1钱，前胡1钱，柴胡1钱，桔梗1钱，枳壳（麸炒）1钱，茯苓1钱，川芎1钱，甘草1钱，槟榔、紫苏。', '水煎服。', '《校注妇人良方》卷二十四', '疮疡焮痛，寒热，或拘急头痛。', '-', '-', '柴胡', '[\'人参\', \'羌活\', \'独活\', \'钱前\', \'胡钱\', \'柴胡\', \'桔梗\', \'枳壳\', \'茯苓\', \'川芎\', \'甘草\', \'槟榔\', \'紫苏\']');
INSERT INTO `prescript` VALUES (58571, '5558', '蓝青汤', '干蓝青2两，凝水石（碎）1两半，石膏（研）1两半，山栀子仁1两半，柴胡（去苗）半两，犀角（镑）半两，黄芩（去黑心）半两，杏仁（汤浸，去皮尖双仁，炒）半两，甘草（炙，锉）半两，赤芍药半两，羚羊角（镑）半两，葛根（锉）半两，知母1两。', '每次1钱匕，水半盏，煎至4分，下蜜半钱，竹沥少许，再煎至3分，去滓，空心、日晚分温2服。', '《圣济总录》卷一八二', '小儿丹毒，赤肿壮热，百治不愈者。', '-', '-', '柴胡', '[\'蓝青\', \'两凝水\', \'石碎\', \'两半\', \'石膏\', \'研两\', \'半山\', \'栀子\', \'两半\', \'柴胡\', \'去苗\', \'半两\', \'犀角\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'半两\', \'甘草\', \'半两\', \'芍药\', \'半两\', \'羚羊角\', \'半两\', \'葛根\', \'半两\', \'知母\']');
INSERT INTO `prescript` VALUES (58572, '5555', '理疝至奇汤', '沙参1两，白芍3钱，柴胡1钱，橘核1钱，肉桂1钱，榖树叶（3月3日，或5月5日，采取阴干，要择如云版式者）4钱。', '水煎服。', '《外科医镜》', '小肠疝气。', '-', '-', '柴胡', '[\'沙参\', \'白芍\', \'柴胡\', \'橘核\', \'肉桂\', \'树叶\', \'采取\', \'阴干\', \'要择\', \'如云\', \'版式\']');
INSERT INTO `prescript` VALUES (58573, '5529', '槟榔丸', '槟榔1两，枳壳3分（麸炒微黄，去瓤），桔梗3分（去芦头），人参3分（去芦头），白术半两，桂心半两，柴胡2两（去苗），陈橘皮3分（汤浸，去白瓤，焙），川大黄1两（锉碎，微炒），芎半两，草豆蔻1两（去皮）。', '每服30丸，以温酒送下，不拘时候。', '《圣惠》卷四十九', '痃癖气发，即两胁急满，四肢烦闷，不能食。', '-', '-', '柴胡', '[\'槟榔\', \'枳壳\', \'分麸\', \'微黄\', \'桔梗\', \'分去\', \'芦头\', \'人参\', \'分去\', \'芦头\', \'白术\', \'半两\', \'桂心\', \'半两\', \'柴胡\', \'两去\', \'橘皮\', \'分汤\', \'浸去\', \'焙川\', \'大黄\', \'半两\', \'草豆蔻\', \'去皮\']');
INSERT INTO `prescript` VALUES (58574, '5514', '柴胡汤', '柴胡（去苗）1两，麻黄（去根节，汤煮，掠去沫）1两。', '用童便5盏，同煎至2盏，去滓，分温2服。出汗即愈。', '《圣济总录》卷八十八', '虚劳发热，肢体烦疼。', '-', '-', '柴胡', '[\'柴胡\', \'麻黄\', \'根节\', \'掠去\']');
INSERT INTO `prescript` VALUES (58575, '5507', '救苦丹', '麻黄（去根节，洗净晒干）4两（研极细末，温水浸，用细布取汁，余滓再捣再浸再取汁，必要洁净细腻为上），甘草（炙，去皮净，研极细末）4两（凉水浸，照前取汁），赤芍1两（研极细末，温水浸，照前取汁），升麻（微炒）1两（研极细末，凉水浸取汁），朱砂1两5钱（研极细，水飞），雄黄1两5钱（研极细，水飞），当归身2两（研极细，温酒浸取汁），人参（去芦尽，研极细末）1两（温酒浸取汁），柴胡（去芦，研极细末）1两（温水浸取汁），细辛5钱（研极细末，温水浸取汁），枳实（去心，研极细末）5钱（温水浸取汁）。', '每服1丸，雄黄5分（研极细），新汲水半盏，调雄黄连药送下。用厚被盖暖处，香烧3寸，汗出即愈。春、夏，加石膏5钱（水飞）；秋、冬，加桂皮5钱（研极细末，温水浸取汁）。', '《万氏家抄方》卷一引通真子方', '大人、小儿感冒伤寒。', '-', '-', '柴胡', '[\'麻黄\', \'根节\', \'洗净\', \'晒干\', \'两研极\', \'细末温\', \'水浸\', \'细布\', \'取汁\', \'余滓\', \'取汁\', \'必要\', \'洁净\', \'细腻\', \'甘草\', \'去皮\', \'研极\', \'细末\', \'凉水\', \'浸照\', \'取汁\', \'赤芍\', \'两研极\', \'细末温\', \'水浸\', \'照前\', \'取汁\', \'升麻\', \'研极\', \'细末\', \'凉水\', \'浸取\', \'朱砂\', \'研极\', \'细水\', \'雄黄\', \'研极\', \'细水\', \'当归\', \'身两\', \'研极细\', \'温酒\', \'浸取\', \'人参\', \'芦尽\', \'研极\', \'细末\', \'温酒\', \'浸取\', \'柴胡\', \'去芦\', \'研极\', \'细末\', \'温水\', \'浸取\', \'细辛\', \'研极\', \'细末\', \'温水\', \'浸取\', \'枳实\', \'去心\', \'研极\', \'细末\', \'温水\', \'浸取\']');
INSERT INTO `prescript` VALUES (58576, '5491', '干姜丸', '干姜1两，芎1两，茯苓1两，消石1两，杏仁1两，水蛭1两，虻虫1两，桃仁1两，蛴螬1两，(庶虫)虫1两，柴胡2两，芍药2两，人参2两，大黄2两，蜀椒2两，当归2两。', '每服3丸，空心饮送下。不知，加至10丸。', '《千金》卷四', '妇人寒热羸瘦，酸消怠情，胸中支满，肩背脊重痛，腹里坚满积聚；或痛不可忍，引腰小腹痛，四肢烦疼，手足厥逆，寒至肘膝；或烦满，手足虚热，意欲投水中，百节尽痛，心下常苦悬痛，时寒时热，恶心，涎唾喜出，每爱咸酸甜苦之物，身体或如鸡皮，月经不通，大小便苦难，食不生肌。', '-', '-', '柴胡', '[\'干姜\', \'两芎\', \'茯苓\', \'消石\', \'杏仁\', \'水蛭\', \'桃仁\', \'蛴螬\', \'两庶\', \'虫虫\', \'柴胡\', \'芍药\', \'人参\', \'大黄\', \'两蜀椒\', \'当归\']');
INSERT INTO `prescript` VALUES (58577, '5472', '羚羊角散', '羚羊角屑1分，犀角屑1分，麦门冬半两（去心），百合半两，柴胡半两（去苗），地骨皮半两，木通半两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒已得汗，热不除，发渴，朝暮烦热。', '-', '-', '柴胡', '[\'羚羊角\', \'犀角\', \'麦门冬\', \'半两\', \'去心\', \'百合\', \'半两\', \'柴胡\', \'半两\', \'去苗\', \'地骨皮\', \'半两\', \'木通\', \'半两\']');
INSERT INTO `prescript` VALUES (58578, '5465', '羚羊角散', '羚羊角屑3分，黄芩3分，麦门冬3分（去心），人参3分（去芦头），赤芍药3分，木通（锉）3分，柴胡1两（去苗），黄耆半两（锉），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠烦躁，体热口干，肢节疼痛，少思饮食。', '-', '-', '柴胡', '[\'羚羊角\', \'黄芩\', \'麦门冬\', \'分去\', \'人参\', \'分去\', \'芦头\', \'芍药\', \'木通\', \'锉分\', \'柴胡\', \'两去\', \'黄耆\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58579, '5456', '柴胡汤', '当归1钱2分，白芍1钱，柴胡1钱，黄芩1钱，熟地3分，甘草3分，半夏（制）7分，川芎7分，人参5分，麦冬（去心）5分，生姜3片。', '水煎，空心热服。不睡，加枣仁（炒）；呕吐，加砂仁7分，白术6分（蜜炙），香附7分（制）；咳嗽，加杏仁6分（去皮尖），五味子5分，苏叶、桔梗各7分。', '《叶氏女科》卷一', '妇女17-18岁，因脾胃虚弱，气血不行，而致经脉不通，或阻隔半年百日，面色青黄，饮食不思，或作寒热，头痛眩晕，腹中结块，烦闷呕吐，或作膨胀。', '-', '-', '柴胡', '[\'当归\', \'钱分\', \'白芍\', \'柴胡\', \'黄芩\', \'熟地\', \'甘草\', \'半夏\', \'制分\', \'川芎\', \'人参\', \'麦冬\', \'心分\', \'生姜\']');
INSERT INTO `prescript` VALUES (58580, '5444', '解毒活血汤', '连翘3钱，柴胡2钱，葛根2钱，生地5钱，赤芍3钱，红花5钱，桃仁8钱，川朴1钱（后下），当归1钱半，甘草2钱，苏木2两。', '水煎服。轻证初起，每三点钟服1次，危证初起，两点钟服1次，或合数剂熬膏，连连服之。或热，或渴，或出汗，或吐血，加生石膏1两，鲜芦根汁1杯，和药膏服，并多服羚羊角，犀角所磨之汁；孕妇加桑寄生1两，黄芩1两，略减桃仁、红花。', '《衷中参西》中册', '鼠疫。脉道阻滞，形容惨淡，神气模糊，恶核痛甚者。', '-', '-', '柴胡', '[\'连翘\', \'柴胡\', \'葛根\', \'生地\', \'赤芍\', \'钱红\', \'花钱\', \'桃仁\', \'钱川朴\', \'当归\', \'钱半\', \'甘草\', \'苏木\']');
INSERT INTO `prescript` VALUES (58581, '5427', '常山汤', '常山1两，鳖甲（去裙襕，醋炙）1两，柴胡（去苗）1两，甘草（炙锉）1两，石膏（研）1两，人参1两，牵牛子（炒）1两，干漆（炒令烟出）1两，陈橘皮（去白焙干）1两，大黄（锉，炒）1两，当归（切，焙）1两。', '每服3钱匕，水1盏，入小麦、竹叶，煎至7分，去滓，食后温服。', '《圣济总录》卷八十八', '虚劳潮热，饥瘦减食，烦躁颊赤，夜多盗汗。', '-', '-', '柴胡', '[\'常山\', \'鳖甲\', \'柴胡\', \'甘草\', \'石膏\', \'研两\', \'人参\', \'牵牛子\', \'干漆\', \'炒令\', \'烟出\', \'橘皮\', \'焙干\', \'大黄\', \'当归\', \'切焙\']');
INSERT INTO `prescript` VALUES (58582, '5380', '鳖甲汤', '鳖甲（去裙襕，醋炙黄）1两，荆三棱（锉）1两，大腹（锉）1两，芍药1两，当归（切，焙）1两半，柴胡（去苗）1两半，生干地黄（焙）1两半，桂（去粗皮）3分，生姜（切片，炒）3分。', '每服3钱匕，水1盏，加木香末半钱，同煎至7分，去滓，空心温服，每日2次。', '《圣济总录》卷七十一', '伏梁积气，心下如臂，痞痛不消，小便不利。', '-', '-', '柴胡', '[\'鳖甲\', \'荆三棱\', \'两大腹\', \'芍药\', \'当归\', \'切焙\', \'两半\', \'柴胡\', \'半生\', \'地黄\', \'两半\', \'桂去\', \'粗皮\', \'分生\', \'姜切片\', \'炒分\']');
INSERT INTO `prescript` VALUES (58583, '5349', '大柴胡汤', '柴胡3两，黄芩3两，芍药3两，甘草（炙）3两，半夏2两半（汤洗7次），大黄2两，枳实1两（麸炒，去瓤）。', '每服5钱，水2盏，加生姜10片，大枣2个，同煎至1盏，去滓温服。', '《普济方》卷四十四引《指南方》', '疏利风热。主头痛。痰嗽，腹胀，及里证未解。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'芍药\', \'甘草\', \'半夏\', \'两半\', \'洗次\', \'大黄\', \'枳实\', \'两麸\']');
INSERT INTO `prescript` VALUES (58584, '5279', '救败散', '当归5钱，麦冬5钱，人参5钱，白芍5钱，柴胡5分，甘草5分，北五味10粒，神曲3分。', '水煎服。', '《辨证录》卷一', '冬月伤寒5-6日，吐泻后又加大汗，气喘不得卧，发厥者。', '-', '-', '柴胡', '[\'当归\', \'麦冬\', \'人参\', \'白芍\', \'柴胡\', \'甘草\', \'分北\', \'五味\', \'神曲\']');
INSERT INTO `prescript` VALUES (58585, '5271', '鳖甲汤', '鳖甲（九肋者，童便浸半日，醋炙，去裙襕）2两，柴胡（去苗）1两，胡黄连1两，木香1两，人参1两，白茯苓（去黑皮）1两，桔梗（炒）1两，槟榔（锉）1两，犀角（镑）1两，大黄（锉，炙）1两，枳壳（去瓤，麸炒）1两，白术1两。', '每服3钱匕，水1盏，加生姜、甘草各少许，同煎至7分，去滓分服。', '《圣济总录》卷八十七', '气劳。或冷或热，不思饮食，多睡少起，四肢沉重。', '-', '-', '柴胡', '[\'鳖甲\', \'九肋者\', \'童便\', \'半日\', \'柴胡\', \'胡黄连\', \'木香\', \'人参\', \'白茯苓\', \'黑皮\', \'桔梗\', \'槟榔\', \'犀角\', \'大黄\', \'枳壳\', \'白术\']');
INSERT INTO `prescript` VALUES (58586, '5260', '柴胡散', '柴胡1两（去苗），人参1两（去芦头），甘草1两（炙微赤，锉），麻黄1两半（去根节），黄芩1两，赤芍药1两，桂心1两，石膏2两，葛根1两（锉）。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓，稍热服，不拘时候。以衣盖取汗。如人行5里，未汗再服。', '《圣惠》卷九', '伤寒2日，头项四肢烦热疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'人参\', \'两去\', \'芦头\', \'甘草\', \'两炙微\', \'麻黄\', \'两半\', \'根节\', \'黄芩\', \'两赤\', \'芍药\', \'两桂心\', \'石膏\', \'葛根\']');
INSERT INTO `prescript` VALUES (58587, '5215', '麻黄葛根汤', '麻黄（去根节）1两半，葛根（锉）1两，柴胡（去苗）1两，芍药3分。', '每服5钱匕，以水1盏半，加豉100粒、开口椒17粒、连须葱白3寸，薄荷叶20叶，同煎至8分，去滓热服。服后葱豉汤1盏投之，衣覆取汗，汗未快再服。', '《圣济总录》卷二十一', '发汗。主伤寒初得1-2日。', '-', '-', '柴胡', '[\'麻黄\', \'根节\', \'两半\', \'葛根\', \'柴胡\', \'芍药\']');
INSERT INTO `prescript` VALUES (58588, '5180', '解劳散', '白芍药1两半，柴胡（去苗）1两，鳖甲（醋浸，炙黄）1两，枳壳（去瓤，麸炒）1两，甘草半两（炙），赤茯苓（去皮）半两。', '上(口父)咀。每服5钱，水1盏半，入生姜3片，枣子1枚，煎至7分，食后温服。', '《杨氏家藏方》卷十', '虚劳，积气坚硬，噎塞，胸胁引背彻痛。', '-', '-', '柴胡', '[\'芍药\', \'两半\', \'柴胡\', \'鳖甲\', \'枳壳\', \'甘草\', \'半两\', \'茯苓\', \'去皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58589, '5175', '大柴胡汤', '柴胡2两（去苗），黄芩1两，赤芍药1两，半夏（汤洗7遍去滑）1两，枳实（麸炒令黄）1两，槟榔1两，白术1两，赤茯苓1两。', '每服4钱，以水1中盏，加生姜半分，大枣3枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十一', '伤寒2-3日，心中悸，呕吐不止，心急郁郁微烦者，尚未解。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'黄芩\', \'两赤\', \'芍药\', \'半夏\', \'洗遍\', \'枳实\', \'炒令\', \'槟榔\', \'白术\', \'两赤\', \'茯苓\']');
INSERT INTO `prescript` VALUES (58590, '5153', '黄连散', '黄连1两（去须），大青1两，栀子仁1两，茵陈1两，柴胡1两（去苗），地骨皮1两，黄芩1两，川芒消1两，川大黄2两（锉碎，微炒），甘草1两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十八', '黄疸，遍身面目悉黄。', '-', '-', '柴胡', '[\'黄连\', \'两去\', \'大青\', \'栀子\', \'茵陈\', \'柴胡\', \'两去\', \'地骨皮\', \'黄芩\', \'两川芒\', \'消两川\', \'大黄\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (58591, '5131', '干姜丸', '干姜半两（炮裂，锉），皂荚1两（去黑皮，涂酥，炙令黄焦，去子），菖蒲3分，桂心3分，川乌头半两（炮裂，去皮脐），柴胡3分（去苗），人参3分（去芦头），黄连3分（去须），赤茯苓3分，吴茱萸半两（汤浸7遍，焙干，微炒），川椒3分（去目及闭口者，微炒去汗），厚朴2两（去粗皮，涂生姜汁，炙令香熟）。', '每服20丸，食前温酒送下。', '《圣惠》卷四十八', '积聚，心腹胀满，食少。', '-', '-', '柴胡', '[\'干姜\', \'半两\', \'炮裂\', \'皂荚\', \'两去\', \'黑皮\', \'炙令\', \'黄焦\', \'去子\', \'菖蒲\', \'分桂心\', \'分川\', \'乌头\', \'半两\', \'炮裂\', \'去皮\', \'柴胡\', \'分去\', \'人参\', \'分去\', \'芦头\', \'黄连\', \'分去\', \'茯苓\', \'吴茱萸\', \'半两\', \'浸遍\', \'焙干\', \'炒川椒\', \'分去\', \'目及\', \'闭口\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\']');
