INSERT INTO `prescript` VALUES (58612, '4526', '葛根散', '葛根3分（锉），石膏1两，柴胡1两（去苗），川升麻3分，知母3分，栀子仁半两，甘草3分（炙微赤，锉），川大黄1两（锉碎，微炒）。', '每服4钱，以水1中盏，入生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷九', '伤寒6日，心躁烦渴，肢节解痛，小腹急满，阴缩。', '-', '-', '柴胡', '[\'葛根\', \'石膏\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'知母\', \'栀子\', \'半两\', \'甘草\', \'分炙微\', \'锉川\', \'大黄\']');
INSERT INTO `prescript` VALUES (58613, '4521', '黄耆地骨皮散', '黄耆1钱2分，地骨皮8分，柴胡8分，赤芍药8分，生地黄8分，麦门冬8分，当归8分，赤茯苓8分，黄芩8分，人参1钱，甘草（炙）5分。', '上（口父）咀。用生姜5片，水煎，食前服。', '《杏苑》卷八', '劳热羸瘦，四肢烦疼，心躁口干，不欲饮食。', '-', '-', '柴胡', '[\'黄耆\', \'钱分\', \'地骨皮\', \'柴胡\', \'分赤\', \'芍药\', \'生地黄\', \'麦门冬\', \'当归\', \'分赤\', \'茯苓\', \'黄芩\', \'人参\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (58614, '4508', '黄芩鳖甲汤', '黄芩半两（去黑心），柴胡半两（去苗），山栀子仁半两，乌梅（去核）半两，鳖甲（去裙襕，醋炙）1两，甘草（炙）1分。', '每服5钱匕，用童子小便1盏半，入桃、柳尖各7个，生姜1枣大，拍碎，豉半合，同浸1宿，平旦煎至7分，去滓，入生地黄汁1合，搅匀，更煎1沸，温服，不拘时候。', '《圣济总录》卷三十三', '伤寒后变成劳疟，久不愈。', '-', '-', '柴胡', '[\'黄芩\', \'半两\', \'黑心\', \'柴胡\', \'半两\', \'苗山\', \'栀子\', \'半两\', \'乌梅\', \'去核\', \'半两\', \'鳖甲\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (58615, '4500', '救汗回生汤', '人参3两，当归2两，柴胡2钱，白芍1两，陈皮5分，甘草1钱，麦冬5钱。', '水煎服。2剂后减去柴胡，将此方减去10分之6，渐渐调理。', '《辨证录》卷一', '冬月伤寒5-6日，吐泻后又加发汗，气喘不得卧，发厥者。', '-', '-', '柴胡', '[\'人参\', \'当归\', \'柴胡\', \'白芍\', \'陈皮分\', \'甘草\', \'麦冬\']');
INSERT INTO `prescript` VALUES (58616, '4487', '柴胡汤', '柴胡（去苗）1两半，赤茯苓（去黑皮）1两半，枳壳（去瓤，麸炒）3两，白术1两，地骨皮1两，葛根（锉）2两，甘草（炙，锉）1两，木通（锉）2两，麦门冬（去心，焙）1两。', '每服5钱匕，水1盏半，加生姜1枣大（切碎）。煎至1盏，去滓，空腹温服，1日2次。', '《圣济总录》卷九十一', '虚劳发热，心中烦闷，面黄口干，腹中虚满，腰背急痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'两半\', \'茯苓\', \'黑皮\', \'两半\', \'枳壳\', \'白术\', \'地骨皮\', \'葛根\', \'甘草\', \'木通\', \'麦门冬\']');
INSERT INTO `prescript` VALUES (58617, '4480', '菊花饮', '生地1钱5分，当归1钱，柴胡1钱，花粉1钱，黄连1钱，天冬1钱，麦冬1钱，菊花2钱，甘草5分。', '水煎服。', '《慈幼新书》卷六', '痘疮。', '-', '-', '柴胡', '[\'生地\', \'钱分\', \'当归\', \'柴胡\', \'花粉\', \'黄连\', \'天冬\', \'麦冬\', \'菊花\', \'甘草\']');
INSERT INTO `prescript` VALUES (58618, '4477', '调经末子', '当归1两，川芎5钱，白芍3钱，苁蓉3钱，五灵脂（炒）3钱，延胡（炒）3钱，白术3钱，苍术3钱，白芷3钱，乌药3钱，茴香3钱，陈皮3钱，半夏3钱，柴胡2钱，黄连（同吴萸炒）1钱。', '醋或酒炒，熨心腹脐下，并缚脐，如冷再炒，每日用之，以调为度。先期者加条芩，丹皮，地骨皮各2钱；后期者，加官桂、干姜、艾各2钱；干血痨，加桃仁、红花、大黄、生姜、红枣；血瘕，再加马鞭草。', '《理瀹》。+', '月经不论前后多少，痛或不痛。', '-', '-', '柴胡', '[\'当归\', \'川芎\', \'白芍\', \'苁蓉\', \'五灵脂\', \'炒钱\', \'延胡\', \'白术\', \'苍术\', \'白芷\', \'乌药\', \'茴香\', \'陈皮\', \'半夏\', \'柴胡\', \'黄连\', \'吴萸\']');
INSERT INTO `prescript` VALUES (58619, '4471', '羚羊角散', '羚羊角屑3分，茯神3分，车前子3分，甘菊花3分，决明子3分，防风3分（去芦头），羌活3分，赤芍药3分，蔓荆子3分，黄芩3分，川升麻3分，栀子仁3分，麦门冬3分（去心），柴胡3分（去苗），枳壳1两（麸炒微黄，去瓤），甘草3分（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分。去滓，食后温服。', '《圣惠》卷三十二', '肝肾久积风热，两眼赤痛，上焦壅滞，头重心烦，四肢不利。', '忌炙煿、热面、油腻。', '-', '柴胡', '[\'羚羊角\', \'茯神\', \'车前子\', \'甘菊花\', \'决明子\', \'防风\', \'分去\', \'芦头\', \'羌活\', \'分赤\', \'芍药\', \'蔓荆子\', \'黄芩\', \'分川\', \'升麻\', \'栀子\', \'仁分\', \'麦门冬\', \'分去\', \'柴胡\', \'分去\', \'枳壳\', \'两麸\', \'微黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58620, '4461', '黄芩散', '黄芩1两，人参（去芦头）1两，甘草（炙微赤，锉）1两，麦门冬1两，柴胡（去苗）1两，葛根（锉）1两，桂心半两。', '每服3钱，以水1中盏，加生姜半分，大枣3枚，煎至5分，去滓温服，木拘时候。', '《圣惠》卷十', '伤寒。吐下后，内外有热，烦渴不止。', '-', '-', '柴胡', '[\'黄芩\', \'人参\', \'芦头\', \'甘草\', \'炙微\', \'麦门冬\', \'柴胡\', \'葛根\', \'两桂心\', \'半两\']');
INSERT INTO `prescript` VALUES (58621, '4451', '菊花明目饮', '菊花18g，黄芩12g，柴胡6g，龙胆草3g，知母9g，玄参9g，赤芍9g，牡丹皮9g，防风3g，青葙子6g。', '黄液上冲者，可加玄明粉3g，酒大黄6g。', '《张皆春眼科证治》', '头痛目痛严重，抱轮红赤，黄仁纹理模糊，神水混浊，瞳神缩小。', '-', '-', '柴胡', '[\'菊花\', \'黄芩\', \'柴胡\', \'龙胆草\', \'知母\', \'玄参\', \'赤芍\', \'牡丹皮\', \'防风\', \'青葙子\']');
INSERT INTO `prescript` VALUES (58622, '4435', '黄疸汤', '茵陈30g，山栀9g，金银花15g，连翘15g，败酱草15g，板蓝根15g，赤芍9g，白芍9g，柴胡6g，神曲15g，苏梗6g，桔梗6g，大豆黄卷15g。', '-', '《临证医案医方》', '清热利湿退黄。主急性黄疸型肝炎（阳黄）。巩膜黄染，周身皮肤发黄，小便黄赤，舌苔黄腻，脉弦数。', '-', '-', '柴胡', '[\'茵陈\', \'金银花\', \'连翘\', \'败酱草\', \'板蓝根\', \'赤芍\', \'白芍\', \'柴胡\', \'神曲\', \'苏梗\', \'桔梗\', \'大豆黄卷\']');
INSERT INTO `prescript` VALUES (58623, '4419', '黄耆鳖甲汤', '黄耆1两，鳖甲1两（去裙襕，醋炙），秦艽1两（去苗土），柴胡1两（去苗），当归1两（切，焙），知母（切，焙）1两，人参半两，芎半两，羌活（去芦头），赤茯苓半两（去黑皮），黄芩半两（去黑心），紫菀半两（去土），甘草半两（炙），芍药半两，桑根白皮半两，白鲜皮半两，款冬花半两，陈橘皮半两（汤浸，去白，焙），贝母半两（去心，炒），木香半两，桂半两（去粗皮），附子（炮裂，去皮脐）半两，丁香1分。', '鳖甲汤（《普济方》卷二二九）。', '《圣济总录》卷八十七', '风劳。四肢倦怠，百节酸疼，饮食减少，行履不得，涕唾稠粘，多困少力，面色萎黄，小便赤涩。', '-', '-', '柴胡', '[\'黄耆\', \'鳖甲\', \'两去\', \'秦艽\', \'两去\', \'苗土\', \'柴胡\', \'两去\', \'当归\', \'两切\', \'知母\', \'切焙\', \'人参\', \'半两\', \'半两\', \'羌活\', \'芦头\', \'茯苓\', \'半两\', \'黑皮\', \'黄芩\', \'半两\', \'黑心\', \'紫菀\', \'半两\', \'甘草\', \'半两\', \'芍药\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'白鲜皮\', \'半两\', \'款冬花\', \'半两\', \'橘皮\', \'半两\', \'汤浸\', \'贝母\', \'半两\', \'木香\', \'半两\', \'半两\', \'粗皮\', \'附子\', \'炮裂\', \'去皮\', \'半两\', \'丁香\']');
INSERT INTO `prescript` VALUES (58624, '4409', '调中和气饮', '大黄1钱，石膏1钱，槟榔8分，枳壳（麸炒）8分，黄芩8分，知母8分，黄连6分，黄柏5分，柴胡3分。', '调中和气散（《女科秘要》卷二）。', '《叶氏女科》卷二', '胎气攻心。妊娠过食辛热，毒物热积胎中，以致胎儿不安，手足乱动，上攻心胞，母多痛苦。', '-', '-', '柴胡', '[\'大黄\', \'石膏\', \'槟榔\', \'枳壳\', \'炒分\', \'黄芩\', \'知母\', \'黄连\', \'黄柏\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58625, '4408', '解热汤', '柴胡1钱，白术1钱，枳壳1钱，白茯苓1钱（锉）。', '上药分作2剂，每剂用童子小便、水、酒共1升，煎至半升，去滓，分2次温服。以效为度。', '《普济方》卷二二八', '饮酒虚劳生热。', '-', '-', '柴胡', '[\'柴胡\', \'白术\', \'枳壳\', \'白茯苓\']');
INSERT INTO `prescript` VALUES (58626, '4391', '葛根汤', '葛根（锉）1两，柴胡（去苗）1两，麻黄（去根节，煎掠去沫，焙）3分，芍药半两，黄芩（去黑心）半两，甘草（炙，锉）半两，桂（去粗皮）半两。', '每服5钱匕，以水1盏半，入枣2枚（擘），煎至6分，去滓，不拘时候温服。', '《圣济总录》卷三十一', '伤寒及天行后，头痛，余热不解。', '-', '-', '柴胡', '[\'葛根\', \'柴胡\', \'麻黄\', \'根节\', \'掠去\', \'焙分\', \'芍药\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'甘草\', \'半两\', \'桂去\', \'粗皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58627, '4362', '大黄散', '川大黄1两（锉碎，微炒），柴胡1两，秦艽1两半（去苗），石膏2两，麻黄1两（去根节），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至5分，去滓，不拘时候稍热频服。汗出为度。', '《圣惠》卷十七', '热病3日，发汗未解，头痛口干，心胸烦闷。', '-', '-', '柴胡', '[\'大黄\', \'柴胡\', \'秦艽\', \'两半\', \'去苗\', \'石膏\', \'麻黄\', \'两去\', \'根节\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58628, '4340', '鳖甲猪肚丸', '鳖甲（去裙襕，醋炙）1两，柴胡（去苗）1两，木香1两，青蒿1两，生干地黄（焙）1两，黄连（去须）2两，青橘皮（去白，炒）半两。', '每服15-20丸，食前、日午、临卧温水送下。', '《普济方》卷二三○引《博济》', '虚劳潮热，唇红颊赤，气粗口干，睡多盗汗，大小肠秘涩，饮食减少。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'木香\', \'青蒿\', \'两生\', \'地黄\', \'黄连\', \'两青\', \'橘皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58629, '4339', '锉散', '天仙藤10斤，青蒿子（炒）10斤，桑白皮（炒）10斤，香附子（炒）10斤，荆芥穗10斤，前胡（生姜汁制，炒）10斤，柴胡10斤，桔梗10斤，麻黄（去根节）10斤，苍术（炒）10斤，干葛10斤，陈橘皮10斤，茴香（炒）2斤半，秦艽2斤半，川芎2斤半，白芍药2斤半，藁本2斤半，黄耆2斤半，半夏（为粗末，姜汁炙）2斤半，川羌活2斤半，甘草（炒）5斤，肉桂5斤，白芷（炒）5斤，厚朴（去粗皮，姜汁炒）5斤。', '每服3大钱，水1盏半，加生姜、乌梅、大枣，煎至7分，去滓温服。并两滓作1服煎。', '《局方》卷二（绍兴续添方）', '壮筋骨，轻健肢体，进饮食。主五劳七伤，感冷冒寒，气弱体虚，多倦少力。', '-', '-', '柴胡', '[\'天仙藤\', \'青蒿\', \'炒斤\', \'桑白皮\', \'炒斤\', \'香附子\', \'炒斤\', \'荆芥\', \'穗斤\', \'胡生\', \'姜汁\', \'炒斤\', \'柴胡\', \'桔梗\', \'麻黄\', \'根节斤\', \'苍术\', \'炒斤\', \'干葛斤\', \'橘皮\', \'茴香\', \'斤半\', \'秦艽\', \'斤半\', \'川芎\', \'斤半\', \'芍药\', \'斤半\', \'藁本\', \'斤半\', \'黄耆\', \'斤半\', \'半夏\', \'粗末\', \'姜汁\', \'斤半\', \'羌活\', \'斤半\', \'甘草\', \'炒斤\', \'肉桂\', \'白芷\', \'炒斤\', \'厚朴\', \'粗皮\', \'姜汁\', \'炒斤\']');
INSERT INTO `prescript` VALUES (58630, '4297', '鳖甲散', '鳖甲1两半（涂醋，炙令微黄，去裙襕），柴胡2两半（去苗），京三棱1两（炮，锉），当归1两半，赤芍药1两，人参1两（去芦头），白术1两，陈橘皮3分（汤浸，去白瓤，焙），大腹皮半两。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，食前稍热服。', '《圣惠》卷二十八', '虚劳。肩背疼闷，心腹胀痛，肠胃虚鸣，脐下拘急。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'微黄\', \'柴胡\', \'两半\', \'苗京\', \'三棱\', \'两炮\', \'当归\', \'两半\', \'芍药\', \'人参\', \'两去\', \'芦头\', \'白术\', \'两陈\', \'橘皮\', \'分汤\', \'浸去\', \'大腹皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58631, '4193', '截疟立验汤', '陈皮1钱，半夏1钱半，茯苓1钱，甘草1钱，青皮7分，白豆蔻（去壳，微炒，研）1钱2分，柴胡1钱5分，桂枝1钱2分，苏叶1钱，生姜2钱，知母1钱2分，黄芩1钱5分。', '上水煎，首1次轻煎，于疟未发前三时服；次煎加常山（酒炒）1钱4分，草果仁（或面或饭包煨，捣碎）8分，槟榔8分，多水久煎服，但要于疟未发前一时服。若早则药力过，迟则疟已来矣。能如法服，则疟立止。疟若寒重，桂枝、生姜、苏叶3味各加5分，若热重，知母、黄芩2味各加5分。', '《会约》卷十', '疟疾。', '凡男女大小及孕妇俱宜。', '-', '柴胡', '[\'陈皮\', \'半夏\', \'钱半\', \'茯苓\', \'甘草\', \'青皮\', \'白豆蔻\', \'研钱分\', \'柴胡\', \'钱分\', \'桂枝\', \'钱分\', \'苏叶\', \'生姜\', \'知母\', \'钱分\', \'黄芩\', \'钱分\']');
INSERT INTO `prescript` VALUES (58632, '4168', '凉血散火汤', '生地2钱（切片），丹皮8分，赤芍8分，黄芩8分，防风8分，荆芥8分，归尾8分，蝉蜕6分，柴胡8分，车前子1钱。', '头痛恶风或发热，加羌活8分；眼痛不可忍，口渴，加川连8分（酒炒）；肿不消，红不退，加红花4分。', '《程松崖先生眼科》', '火盛而致眼白珠尽红，肿痛生眵，流泪羞明。', '-', '-', '柴胡', '[\'生地\', \'切片\', \'丹皮\', \'赤芍\', \'黄芩\', \'防风\', \'荆芥\', \'分归尾分\', \'蝉蜕\', \'柴胡\', \'车前子\']');
INSERT INTO `prescript` VALUES (58633, '4128', '马鞭草散', '马鞭草（去粗梗）2两，荆芥穗2两，北柴胡2两，乌梅肉2两，枳壳1两，白术1两，羌活1两，白芍药1两，秦艽半两，天台乌药半两，麻黄半两，木香半两，当归1两，川乌（炮）1两，甘草1两。', '每服2钱，水1盏，加生姜2片，大枣1个，葱白2寸，煎至7分，日午、临卧温服。常服无忌。', '《妇人良方》卷五', '血风攻透，肢体疼痛，或觉搔痒，或觉麻痹，作寒作热，饮食减味。', '有孕莫服。', '-', '柴胡', '[\'马鞭草\', \'荆芥\', \'两北\', \'柴胡\', \'乌梅\', \'枳壳\', \'白术\', \'羌活\', \'两白\', \'芍药\', \'秦艽\', \'半两\', \'天台乌药\', \'半两\', \'麻黄\', \'半两\', \'木香\', \'半两\', \'当归\', \'川乌\', \'甘草\']');
INSERT INTO `prescript` VALUES (58634, '4117', '鳖甲饮子', '鳖甲半两（涂醋，炙令微黄，去裙襕），柴胡1两（去苗），人参1分（去芦头），甘草半分（炙微赤，锉），豉1合，白术1分，雄鼠粪1个（微炒，别研）。', '每服以水2大盏，加生姜2分，葱白3茎，煎至1盏，去滓，入研了鼠粪搅匀，分2次温服，不拘时候。', '《圣惠》卷十八', '热病后体气尚虚，用力太早，遂生寒热，四肢乏力。', '-', '-', '柴胡', '[\'鳖甲\', \'半两\', \'涂醋\', \'炙令\', \'微黄\', \'柴胡\', \'两去\', \'人参\', \'分去\', \'芦头\', \'甘草\', \'半分\', \'炙微\', \'豉合\', \'白术\', \'分雄鼠\', \'粪个\', \'别研\']');
