INSERT INTO `prescript` VALUES (58640, '3974', '羚羊角散', '羚羊角屑1两，栀子仁半两，决明子1两，芎1两，羌活1两，石膏1两，柴胡1两（去苗），黄芩1两，人参1两（去芦头），川大黄1两（锉碎，微炒）。', '每服5钱，以水1大盏，加竹叶3-7片，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒热毒攻眼，赤涩昏暗疼痛。', '-', '-', '柴胡', '[\'羚羊角\', \'栀子\', \'半两\', \'决明子\', \'两芎\', \'羌活\', \'石膏\', \'柴胡\', \'两去\', \'黄芩\', \'人参\', \'两去\', \'芦头川\', \'大黄\']');
