INSERT INTO `prescript` VALUES (58664, '3401', '蛤蚧丸', '蛤蚧2对（涂酥，炙），人参1两半，芸苔子1两半，桔梗（炒）1两半，知母（焙）1两半，紫苏茎叶1两半，猪牙皂荚（酥炙）1两半，鳖甲（去裙襕，醋炙）1两半，槟榔（锉）1两半，白前1两半，柴胡（去苗）2两，防己1两半，杏仁（汤去皮尖双仁，炒）1两半，羚羊角（镑）1两半，郁李仁（炒，去皮）1两半，紫菀（去苗土）1两半，猪苓（去黑皮）1两半，甜葶苈（隔纸炒）半两。', '每服10-15丸，食后煎人参汤送下，1日3次。', '《圣济总录》卷六十六', '咳嗽唾脓血，及肺痿羸瘦，涎涕稠粘。', '-', '-', '柴胡', '[\'蛤蚧\', \'对涂\', \'人参\', \'两半\', \'苔子\', \'两半\', \'桔梗\', \'两半\', \'知母\', \'两半\', \'紫苏\', \'两半\', \'猪牙皂\', \'两半\', \'鳖甲\', \'两半\', \'槟榔\', \'两半\', \'两半\', \'柴胡\', \'防己\', \'两半\', \'杏仁\', \'汤去\', \'皮尖\', \'双仁\', \'两半\', \'羚羊角\', \'两半\', \'郁李仁\', \'去皮\', \'两半\', \'紫菀\', \'苗土\', \'两半\', \'猪苓\', \'黑皮\', \'两半\', \'甜葶\', \'隔纸\', \'半两\']');
INSERT INTO `prescript` VALUES (58665, '3380', '截疟煎', '常山1钱，槟榔3钱，柴胡7分，白术1钱，当归7分，陈皮5分，甘草3分，茯苓7分，黄耆1钱，人参5分。', '水2钟，煎至8分服。截后以柴苓汤加青皮饮调理脾胃，消导。', '《医林绳墨大全》卷一', '疟疾。', '-', '-', '柴胡', '[\'常山\', \'槟榔\', \'柴胡\', \'白术\', \'当归\', \'陈皮分\', \'甘草\', \'茯苓\', \'分黄耆\', \'人参\']');
INSERT INTO `prescript` VALUES (58666, '3305', '麻黄汤', '麻黄（去节）3两，杏仁（去皮尖双仁，研）3两，紫菀3两，柴胡4两，橘皮4两。', '以水6升，煮取2升半，去滓，分3次服。1剂不愈，频服3剂。', '《普济方》卷一五九引《集验良方》', '久患气嗽，发时奔喘，坐卧不得，并喉里呀呷，声气欲绝。', '-', '-', '柴胡', '[\'麻黄\', \'去节\', \'杏仁\', \'皮尖\', \'双仁研\', \'紫菀\', \'柴胡\', \'橘皮\']');
INSERT INTO `prescript` VALUES (58667, '3269', '阑尾炎汤', '大黄15g（后下），丹皮9g，冬瓜子18g，桃仁9g，元明粉9g（冲），丹参30g，杭白芍24g，柴胡6g，金银花30g，连翘30g，败酱草15g，薏苡仁18g。', '-', '《临证医案医方》', '消炎止痛，活血通便。主急性阑尾炎，尚未化脓，右下腹剧痛，反跳痛，甚则发热，呕吐，舌苔厚腻，脉洪数。', '-', '-', '柴胡', '[\'大黄\', \'丹皮\', \'冬瓜子\', \'桃仁\', \'元明粉\', \'丹参\', \'白芍\', \'柴胡\', \'金银花\', \'连翘\', \'败酱草\', \'薏苡仁\']');
INSERT INTO `prescript` VALUES (58668, '3243', '鳖甲汤', '鳖甲（去裙襕，醋炙）3两，柴胡（去苗）3两，桔梗（炒）1两半，甘草（炙黄）1两半，秦艽（去苗土）1两，青蒿子2两（用童便浸1宿，焙干，微炒）。', '每服3钱匕，水1盏，加乌梅1个（拍破），同煎至6分，去滓，食后温服。', '《圣济总录》卷九十三', '男子、妇人骨蒸劳气，肌体羸瘦，四肢无力，颊赤面黄，五心烦热，困倦心忪，或多盗汗，腹胁有块，不欲饮食。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'桔梗\', \'两半\', \'甘草\', \'两半\', \'秦艽\', \'苗土\', \'青蒿\', \'两用\', \'童便\', \'浸宿\', \'焙干\']');
INSERT INTO `prescript` VALUES (58669, '3222', '鳖甲饮', '九肋鳖甲（新瓦上焙，以醋淬数次至黄色，研为末，再用醋拌炙干）2钱，山楂肉（去子）3钱，麦冬5钱，薏苡仁（炒）5钱，白茯苓2钱，橘红2钱5分，知母1钱2分，干葛1钱5分，白芍1钱5分，柴胡2钱，贝母3钱，淡竹叶1钱，炙甘草7分。', '加生姜，水煎服。渴者，加熟石膏少许；不渴者，去麦冬、知母。', '《麻科活人》卷四', '清凉，健脾，开胃。主麻后成疟疾者。', '-', '-', '柴胡', '[\'鳖甲\', \'新瓦上\', \'数次\', \'黄色\', \'研为\', \'山楂\', \'去子\', \'麦冬\', \'薏苡仁\', \'白茯苓\', \'橘红\', \'钱分\', \'知母\', \'钱分\', \'干葛钱分\', \'白芍\', \'钱分\', \'柴胡\', \'贝母\', \'淡竹叶\', \'甘草\']');
INSERT INTO `prescript` VALUES (58670, '3218', '鳖甲丸', '鳖甲（涂酥，炙令黄，去裙襕）1两，酸枣仁（微炒）1两，羌活1两，黄耆1两，麦门冬（去心，焙）1两，柴胡1两，白茯苓1两，人参（去芦头）1两，牛膝（去苗）1两，知母1两，五味子1两，白芍药1两。', '每服30丸，温水送下，不拘时候。', '《圣惠》卷二十七', '虚劳羸瘦，四肢烦热疼痛，吃食减少，不得睡卧。', '-', '-', '柴胡', '[\'鳖甲\', \'炙令\', \'黄去\', \'酸枣仁\', \'羌活\', \'两黄耆\', \'麦门冬\', \'柴胡\', \'白茯苓\', \'人参\', \'芦头\', \'牛膝\', \'知母\', \'五味子\', \'两白\', \'芍药\']');
INSERT INTO `prescript` VALUES (58671, '3210', '静待汤', '白芍3钱，当归3钱，茯苓5钱，柴胡5分，甘草1钱，白芥子1钱，丹皮2钱，枣仁1钱。', '水煎服。', '《石室秘录》卷一', '拂逆之症，火郁不得舒，躁急，不可一刻停留。', '-', '-', '柴胡', '[\'白芍\', \'当归\', \'茯苓\', \'柴胡\', \'甘草\', \'白芥子\', \'丹皮\', \'枣仁\']');
INSERT INTO `prescript` VALUES (58672, '3209', '大柴胡汤', '柴胡2两（去苗），枳壳半两（麸炒微黄），黄芩2两，赤芍药1两，半夏1两（汤洗7次）。', '此方比大柴胡汤，无大黄、枳实，用枳壳；比小柴胡汤，无甘草、人参，多枳壳、芍药。详之治十一证，皆治大柴胡汤之轻证，小柴胡汤之重证。又治阴病可下证中，度量自得其宜。', '《医方类聚》卷五十四引《通真子伤寒括要》', '阳明病，外证身热汗出，不恶寒，但恶热；阳明病，脉迟，发热头眩，小便难，欲作谷疸；阳明病，胁下坚满，大便秘而呕，口燥者；阳明病中风，其脉浮大，短气心痛，鼻干，嗜卧，不得汗，一身悉黄，小便难，有潮热而哕，耳前后肿，刺之虽小愈，外未解者；少阳病，口苦干燥，目眩者；少阳中风，两耳无所闻，目赤，胸中满而烦者；少阴病，汗则谵语者；少阴病，恶寒而蜷，时时自烦，不欲厚衣者；少阴病，下利清水，色青，心下痛，口干燥者；厥阴病，阳脉涩，阴脉弦，当腹中急痛，先与小建中汤，不瘥者；厥阴下之，胸满烦惊，小便不利，谵语，一身不可转侧者', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'枳壳\', \'半两\', \'微黄\', \'黄芩\', \'两赤\', \'芍药\', \'半夏\', \'两汤\', \'洗次\']');
INSERT INTO `prescript` VALUES (58673, '3184', '鳖甲汤', '鳖甲（去裙襕，醋炙）1两半，当归（切，焙），芍药1两半，柴胡（去苗）1两，秦艽（去苗土）1两，桔梗（炒）1两，知母（切，焙）1两，枳壳（去瓤，麸炒）1两，黄耆（锉）1两，桂（去粗皮）1两，芎1两，前胡（去芦头）1两，人参1两，白茯苓（去黑皮）1两，荆芥穗1两，地骨皮1两，羌活（去芦头）1两。', '每服3钱匕，水1盏，煎7分，去滓温服。', '《圣济总录》卷一五○', '妇人血风劳气。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'当归\', \'切焙\', \'芍药\', \'两半\', \'柴胡\', \'秦艽\', \'苗土\', \'桔梗\', \'知母\', \'切焙\', \'枳壳\', \'黄耆\', \'两桂去\', \'粗皮\', \'两芎\', \'两前\', \'胡去\', \'芦头\', \'人参\', \'白茯苓\', \'黑皮\', \'荆芥\', \'地骨皮\', \'羌活\', \'芦头\']');
INSERT INTO `prescript` VALUES (58674, '3154', '羚羊清肺汤', '羚羊角1钱（镑），黄连1钱，银柴胡1钱，玄参1钱，石膏1钱，川芎1钱，当归身1钱，白芍1钱，生地1钱，蒲黄1钱，地骨皮1钱，山栀1钱，芦荟5分，甘草5分，藕节3个，白茅根4两（捣汁，用水1碗，和绞去滓）。', '羚羊清肺散（《外科大成》卷三）。', '《外科正宗》卷四', '鼻中无故出血不止，及寻常吐血、咳血者。', '-', '-', '柴胡', '[\'羚羊角\', \'黄连\', \'银柴胡\', \'玄参\', \'石膏\', \'川芎\', \'当归\', \'身钱\', \'白芍\', \'生地\', \'黄钱\', \'地骨皮\', \'钱山\', \'栀钱\', \'芦荟\', \'甘草\', \'藕节\', \'白茅根\', \'两捣汁\', \'用水\']');
INSERT INTO `prescript` VALUES (58675, '3122', '鳖甲散', '鳖甲1两（涂醋，炙令黄，去裙襕），枳壳半两（麸炒微黄，去瓤），木香半两，人参3分（去芦头），赤茯苓3分，柴胡3分（去苗），桂心1分，川大黄半两（锉碎，微炒），槟榔半两，京三棱半两（微煨，锉）。', '每服1钱，以水1小盏，煎至5分，去滓温服，每日3次。', '《圣惠》卷八十八', '小儿症瘕。壮热头痛，呕吐腹痛，寒热，头发作穗，及食癖、乳癖、气癖。', '-', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'枳壳\', \'半两\', \'微黄\', \'木香\', \'半两\', \'人参\', \'分去\', \'芦头\', \'茯苓\', \'柴胡\', \'分去\', \'苗桂心\', \'分川\', \'大黄\', \'半两\', \'槟榔\', \'半两\', \'三棱\', \'半两\']');
INSERT INTO `prescript` VALUES (58676, '3095', '橘皮汤', '橘皮3两，麻黄3两，干紫苏2两，柴胡2两，宿姜4两，杏仁4两，石膏8两。', '上(口父)咀。以水9升，煮麻黄2沸，去沫，下诸药，煮取3升，去滓，分3次服。不愈，与2剂。', '《千金》卷十七', '肺热气上，咳息奔喘。', '-', '-', '柴胡', '[\'橘皮\', \'麻黄\', \'两干\', \'紫苏\', \'柴胡\', \'两宿\', \'杏仁\', \'石膏\']');
INSERT INTO `prescript` VALUES (58677, '3093', '鳖甲丸', '鳖甲（去裙襕，醋蘸炙黄色）1两半，木香1两半，乌头（炮裂，去皮脐）1两半，柴胡（去苗）1两半，京三棱（炮，锉）2两，当归（切，焙）2两，桂（去粗皮）2两，厚朴（去粗皮，涂生姜汁炙令微烟出，锉）2两，陈橘皮（汤浸，去白，焙）2两，甘草（炙，锉）半两，槟榔（锉）半两，大黄（锉碎，炒）3两，朴消（研如粉）3两。', '每服10丸，空腹用温酒送下，饮送下亦得。', '《圣济总录》卷七十二', '症癖气块。', '-', '-', '柴胡', '[\'鳖甲\', \'黄色\', \'两半\', \'木香\', \'两半\', \'乌头\', \'炮裂\', \'去皮\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'三棱\', \'当归\', \'切焙\', \'两桂去\', \'粗皮\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'微烟出\', \'橘皮\', \'汤浸\', \'甘草\', \'半两\', \'槟榔\', \'半两\', \'大黄\', \'两朴\', \'消研\', \'如粉\']');
INSERT INTO `prescript` VALUES (58678, '3039', '黄耆汤', '黄耆（锉）3分，桑根白皮（炙，锉）半两，柴胡（去苗）半两，赤芍药（锉，微炒）半两，赤茯苓（去黑皮）半两，陈橘皮（汤浸去白，焙）3分，麦门冬（去心，焙）3分，恶实（微炒）3分，甘草（炙）3分。', '每服3钱匕，水2盏，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷八十', '水气，面体浮肿，咳嗽气促；虚劳，上气喘息，不得安卧，咳唾，面目虚浮，小便不利。', '-', '-', '柴胡', '[\'黄耆\', \'分桑根\', \'白皮\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'芍药\', \'半两\', \'茯苓\', \'黑皮\', \'半两\', \'橘皮\', \'汤浸\', \'白焙分\', \'麦门冬\', \'分恶\', \'实微\', \'炒分\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (58679, '3007', '琥珀散', '滑石1两，白牵牛（头末）1两，斑蝥3钱（去翅足），僵蚕1两，枳壳5钱，赤芍5钱，柴胡5钱，木通7钱，连翘7钱，琥珀2钱，黄芩1两，甘草3钱。', '水煎服。', '《回春》卷八', '瘰疬结核。', '-', '-', '柴胡', '[\'滑石\', \'两白\', \'牵牛\', \'头末\', \'斑蝥\', \'僵蚕\', \'枳壳\', \'赤芍\', \'柴胡\', \'木通\', \'连翘\', \'琥珀\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (58680, '2942', '羚羊角散', '羚羊角屑1两，柴胡1两（去苗），石膏2两，赤芍药1两，车前子1两，川大黄1两（锉碎，微炒），黄芩1两，甘草1两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三', '肝脏壅热，头目不利，胸膈烦躁，体痛。', '-', '-', '柴胡', '[\'羚羊角\', \'柴胡\', \'两去\', \'石膏\', \'两赤\', \'芍药\', \'车前子\', \'两川\', \'大黄\', \'黄芩\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (58681, '2922', '暗治饮', '当归5钱，白芍3钱，茯苓3钱，炒栀子1钱半，柴胡8分，海螵蛸2钱。', '水煎服。', '《外科医镜》', '妇人阴蚀疮。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'茯苓\', \'栀子\', \'钱半\', \'柴胡\', \'海螵蛸\']');
INSERT INTO `prescript` VALUES (58682, '2914', '鳖甲黄连丸', '鳖甲（童便半盏，米醋半盏，慢火上蘸炙至尽色焦黄）半两，黄连（用巴豆7个，去皮膜，用水1盏同煮水尽，去巴豆不用，只使黄连）半两，白术半两，人参（去芦）半两，茯苓半两，甘草（炙）半两，川楝子肉半两，使君子仁半两，木香半两，草豆蔻（炮，去皮）半两，柴胡（去芦）半两，陈皮（去白）半两，草龙胆半两。', '每服10-20丸，米饮送下；如有潮热，体热不解，乌梅汤送下。', '《卫生总微》卷十二', '小儿诸疳羸瘦，发热盗汗，寒热，肚大脚细，不肯进乳食，气粗促急，脾胃不调。', '-', '-', '柴胡', '[\'鳖甲\', \'童便\', \'半盏\', \'米醋\', \'半盏\', \'慢火\', \'尽色\', \'焦黄\', \'半两\', \'黄连\', \'巴豆\', \'皮膜\', \'用水\', \'煮水\', \'尽去\', \'巴豆\', \'不用\', \'黄连\', \'半两\', \'白术\', \'半两\', \'人参\', \'去芦\', \'半两\', \'茯苓\', \'半两\', \'甘草\', \'半两\', \'川楝子\', \'半两\', \'使君子\', \'半两\', \'木香\', \'半两\', \'草豆蔻\', \'去皮\', \'半两\', \'柴胡\', \'去芦\', \'半两\', \'陈皮\', \'半两\', \'龙胆\', \'半两\']');
