INSERT INTO `prescript` VALUES (58694, '2619', '葛根散', '葛根1两（锉），麻黄1两（去根节），桂心3分，甘草3分（炙微赤，锉），赤芍药3分，柴胡1两（去苗），细辛3分，石膏2两，厚朴1两（去粗皮，涂生姜汁，炙微香熟）。', '每服4钱，以水1中盏，入生姜半分，煎至6分，去滓，稍热服，如人行5-7里再服。以微汗为度。', '《圣惠》卷七十八', '产后伤寒3日以前，头痛恶风，烦热。', '-', '-', '柴胡', '[\'葛根\', \'麻黄\', \'两去\', \'根节\', \'桂心分\', \'甘草\', \'分炙微\', \'芍药\', \'柴胡\', \'两去\', \'细辛\', \'石膏\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'微香熟\']');
INSERT INTO `prescript` VALUES (58695, '2593', '槟榔汤', '槟榔3-7枚，细辛1两，半夏1升，生姜8两，大黄3两，紫菀3两，柴胡3两，橘皮2两，甘草2两，紫苏（冬用子）2两，茯苓2两，附子1枚。', '上(口父)咀，以水1斗，煮取3升，分3服，相去如行10里久。消息气力强弱，进2剂后隔10日，更服前桔梗破气丸。若有症结坚实如石，加鳖甲2两，防葵2两；气上，加桑白皮（切）2升，枳实、厚朴各2两。', '《千金》卷十七', '气实若积聚，不得食息。', '-', '-', '柴胡', '[\'槟榔\', \'细辛\', \'半夏\', \'生姜\', \'大黄\', \'紫菀\', \'柴胡\', \'橘皮\', \'甘草\', \'紫苏\', \'冬用子\', \'茯苓\', \'附子\']');
INSERT INTO `prescript` VALUES (58696, '2584', '琥珀丸', '琥珀1两，当归1两，木香1两，川芎1两，防风1两，槟榔1两，三棱（炮）1两2钱半，干姜（炮）1两2钱半，桂心1两2钱半，吴白术（洗）半两，柴胡半两，人参半两，青皮7钱半，吴茱萸（洗，炮）7钱半，全蝎（炒）7钱半，附子（炮）7钱半，草豆蔻7钱半，赤芍药7钱半，柏叶7钱半，白芷7钱半，天麻7钱半，桃仁（去皮尖，麸炒）1两半，败龟甲（醋炙）1两半，鳖甲（醋炙）1两半。', '每日20丸，空心酒下，午前、近晚更进1服。如觉暖，近晚不须服，如腹内块积攻筑，于鳖甲、桃仁、槟榔、三棱各加1倍为妙。', '《准绳·女科》卷二引《博济》', '妇人血风虚劳，上热下冷，或发动即心中烦躁，困乏无力，不美饮食，醋心口疮，月水不调，肌肉黄瘁，腹痛肠鸣，或有气块攻冲，或时作寒热，头旋痰逆，手足麻痹。', '忌生冷、葱、苋菜、毒鱼等物。', '-', '柴胡', '[\'琥珀\', \'当归\', \'木香\', \'川芎\', \'防风\', \'槟榔\', \'三棱\', \'干姜\', \'半桂心\', \'白术\', \'半两\', \'柴胡\', \'半两\', \'人参\', \'半两\', \'青皮\', \'钱半\', \'吴茱萸\', \'洗炮\', \'全蝎\', \'附子\', \'炮钱\', \'草豆蔻\', \'钱半赤\', \'芍药\', \'钱半柏叶\', \'白芷\', \'半天\', \'麻钱\', \'桃仁\', \'皮尖\', \'两半\', \'龟甲\', \'两半\', \'鳖甲\', \'两半\']');
INSERT INTO `prescript` VALUES (58697, '2548', '濡木饮', '白芍1两，熟地5钱，川芎5钱，柴胡5分，香附5分，炒桅子5分，神曲5分，白豆蔻1粒。', '水煎服。', '《辨证录》卷六', '肝燥气郁，两胁胀满，皮肤如虫之咬，干呕而不吐酸。', '-', '-', '柴胡', '[\'白芍\', \'熟地\', \'川芎\', \'柴胡\', \'香附\', \'桅子分\', \'神曲\', \'白豆蔻\']');
INSERT INTO `prescript` VALUES (58698, '2535', '静镇汤', '白芍1钱，黄芩1钱，生地1钱，陈皮1钱，柴胡1钱，甘草8分，防风8分，桂枝8分，紫苏8分，当归1钱半。', '水煎，热服，服3-4剂。如内热，加连翘；如口渴，加葛根；如热甚，加石膏、知母；如寒甚，加生姜。', '《会约》卷十四', '妇人经行时，因冒风寒，以致热入血室而夜不宁者。', '-', '-', '柴胡', '[\'白芍\', \'黄芩\', \'生地\', \'陈皮\', \'柴胡\', \'甘草\', \'防风\', \'桂枝\', \'紫苏\', \'当归\', \'钱半\']');
INSERT INTO `prescript` VALUES (58699, '2512', '橘叶散', '金银花1钱，瓜蒌1钱，青皮1钱，当归1钱，皂针1钱，连翘1钱，橘叶10片，柴胡7分，甘草节3分。', '水煎服。心思不遂者，加远志、贝母。', '《古今医彻》卷三', '乳痈。恶寒发热，乳房红肿。', '-', '-', '柴胡', '[\'金银\', \'花钱\', \'青皮\', \'当归\', \'钱皂针\', \'连翘\', \'钱橘\', \'叶片\', \'柴胡\', \'甘草\', \'节分\']');
INSERT INTO `prescript` VALUES (58700, '2464', '聪耳芦荟丸', '生熟川军5钱，芦荟5钱，青黛5钱，柴胡5钱，龙胆草1两，黄芩1两，山栀1两，当归1两，青皮1两，青木香2钱，杜胆星2钱，当门子5分。', '每服8分至1钱，以麻黄连翘赤小豆汤送下。外用清涤耳毒水灌耳。', '《重订通俗寒伤论》', '苦寒清利，解毒泻火。主黄耳伤寒。两耳红肿黄亮，扪之焮热而痛，两腮红肿痛甚，耳中亦红肿，筑筑然痛，时有黄涎流出，声如蝉噪，两目发黄，身热体痛，恶寒无汗，背脊拘挛串痛，强直难伸，不能转侧，溺短赤涩，脉右濡滞，左浮弦略紧，舌苔白腻带黄，边尖色红，病由风热挟湿温时毒引起的流行性中耳炎。', '-', '-', '柴胡', '[\'生熟\', \'川军\', \'芦荟\', \'青黛钱\', \'柴胡\', \'龙胆草\', \'黄芩\', \'两山\', \'当归\', \'青皮\', \'青木香\', \'杜胆星\', \'门子\']');
INSERT INTO `prescript` VALUES (58701, '2461', '鳖甲地黄汤', '柴胡（去芦）1两，当归（去芦，酒浸）1两，麦门冬（去心）1两，鳖甲（醋炙）1两，石斛（去根）1两，白术1两，熟地黄（酒浸，焙）1两，茯苓（去皮）1两，秦艽（去芦）1两，人参半两，肉桂（不见火）半两，甘草（炙）半两。', '上(口父)咀。每服4钱，水1盏半，加生姜5片，乌梅少许，煎至7分，去滓温服，不拘时候。', '《济生》卷一', '热劳。手足烦，心怔悸；妇人血室有干血，身体赢瘠，饮食不为肌肉。', '虚甚而多汗者，不宜服。', '-', '柴胡', '[\'柴胡\', \'去芦\', \'当归\', \'芦酒\', \'麦门冬\', \'鳖甲\', \'石斛\', \'白术\', \'熟地黄\', \'酒浸\', \'茯苓\', \'去皮\', \'秦艽\', \'去芦\', \'人参\', \'半两\', \'肉桂\', \'不见\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (58702, '2449', '鳖甲丸', '鳖甲（去裙襕，醋炙）2两，厚朴（去粗皮，生姜汁炙，锉）2两，木香半两，青橘皮（汤浸，去白，焙）半两，柴胡（去苗）半两，人参半两，大黄（煨半两，锉）半两，白茯苓（去黑皮）半两。', '每服20丸，食后临卧，米饮送下。', '《圣济总录》卷八十八', '虚劳寒热，喘嗽烦满，夜多虚汗，不思饮食，五心烦热。', '-', '-', '柴胡', '[\'鳖甲\', \'厚朴\', \'粗皮生\', \'姜汁\', \'木香\', \'半两\', \'橘皮\', \'汤浸\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'人参\', \'半两\', \'大黄\', \'半两\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\']');
INSERT INTO `prescript` VALUES (58703, '2419', '瘰疬疏肝丸', '柴胡1两，蒲公英1两，山慈菇1两，漏芦1两，瓜蒌仁1两，象贝1两，橘叶1两，广皮1两，白菊花1两，金银花1两，连翘1两，地丁草1两，茜草1两，生甘草1两，茄蒂1两，制首乌1两，鼠妇1两。', '每服2钱，开水送下，日2次。', '《中药成方配本》引缪仲淳方', '疏肝散郁，通经软坚。主瘰疬，乳岩。', '-', '-', '柴胡', '[\'柴胡\', \'蒲公英\', \'山慈菇\', \'漏芦\', \'两瓜\', \'两象贝\', \'两橘\', \'两广\', \'白菊花\', \'金银花\', \'连翘\', \'两地\', \'丁草\', \'茜草\', \'生甘草\', \'两茄蒂\', \'两制\', \'首乌\', \'两鼠妇\']');
INSERT INTO `prescript` VALUES (58704, '2417', '鳖甲丸', '鳖甲（去裙襕，童便炙黄，为末）2两，柴胡（去苗，为末）2两，秦艽（去土，为末）2两，生薄荷汁（取自然汁）、生青蒿汁（取自然汁）、生地黄汁（取自然汁）、生姜汁（取自然汁）1小盏（银器内熬成煎）。', '每服20丸，食后温熟水送下，每日3次。', '《圣济总录》卷八十七', '热劳，羸瘦盗汗，壮热烦渴。', '-', '-', '柴胡', '[\'鳖甲\', \'童便\', \'黄为\', \'柴胡\', \'苗为\', \'秦艽\', \'土为\', \'两生\', \'薄荷\', \'汁取\', \'自然\', \'汁生\', \'青蒿\', \'汁取\', \'自然\', \'生地黄\', \'汁取\', \'自然\', \'汁生\', \'姜汁\', \'自然\', \'小盏\', \'银器\', \'内熬\']');
INSERT INTO `prescript` VALUES (58705, '2406', '解郁散毒汤', '白芷4钱，白芥子3钱，香附2钱，郁金2钱，柴胡1钱5分，茯苓2钱，蒲公英3钱，陈皮5分，生甘草1钱，白矾1钱，当归3钱，野菊花根2钱，薏苡仁3钱，乳香末1钱。', '水数碗，煎1碗，连服8剂。如已溃者，本方倍加当归，少加附子2分，去郁金、野菊花、白矾、加黄耆3钱，白术5钱。', '《洞天奥旨》卷八', '血胤疮，腋疬。', '-', '-', '柴胡', '[\'白芷\', \'白芥子\', \'香附\', \'钱郁\', \'金钱\', \'柴胡\', \'钱分\', \'茯苓\', \'蒲公英\', \'陈皮分\', \'生甘草\', \'白矾\', \'当归\', \'野菊花\', \'根钱\', \'薏苡仁\', \'乳香\']');
INSERT INTO `prescript` VALUES (58706, '2385', '鳖甲煎丸', '鳖甲（去裙襕，醋炙）2两，柴胡（去苗）2两，甘草（炙，锉）1两，杏仁（去皮尖双仁，炒）1两，桔梗1两，当归（切，焙）1两，地骨皮1两，人参1两，赤芍药1两，木香半两，桂（去粗皮）半两，黄连1分，胡黄连1分，麝香（另研）2钱，酥3两，蜜3两。', '鳖甲柴胡煎丸（《圣济总录》卷一七九）、青蒿鳖甲煎丸（《永类钤方》卷十六）、鳖甲煎（《医部全录》卷三○六）、柴胡煎丸（《普济方》卷三九○）。', '《普济方》卷二三六引《博济》', '补虚劳。主虚劳骨蒸，早晚烦热，寝食不安，五心热闷，百节酸疼，肌瘦盗汗。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'甘草\', \'杏仁\', \'皮尖\', \'双仁\', \'桔梗\', \'当归\', \'切焙\', \'地骨皮\', \'人参\', \'两赤\', \'芍药\', \'木香\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'黄连\', \'胡黄连\', \'麝香\', \'研钱\', \'两蜜\']');
INSERT INTO `prescript` VALUES (58707, '2343', '鳖甲丸', '鳖甲1两（涂醋，炙令黄，去裙襕），人参半两（去芦头），干姜半两（炮裂，锉），白术半两，枳壳半两（麸炒微黄，去瓤），柴胡半两（去苗），当归半两（锉，微炒），赤芍药半两，陈橘皮半两（汤浸，去白瓤，焙），京三棱1两（微煨，锉），川大黄1两（锉碎，微炒），厚朴半两（去粗皮，涂生姜汁炙令香熟）。', '每服7丸，以生姜汤送下，每日3次。', '《圣惠》卷八十八', '小儿痃气，两胁下紧痛，羸瘦。', '-', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'人参\', \'半两\', \'芦头\', \'干姜\', \'半两\', \'炮裂\', \'白术\', \'半两\', \'枳壳\', \'半两\', \'微黄\', \'柴胡\', \'半两\', \'去苗\', \'当归\', \'半两\', \'芍药\', \'半两\', \'橘皮\', \'半两\', \'汤浸\', \'焙京\', \'三棱\', \'两微\', \'锉川\', \'大黄\', \'厚朴\', \'半两\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\']');
INSERT INTO `prescript` VALUES (58708, '2250', '鳗鲡鱼煎', '鳗鲡鱼（寸切，洗净）3斤，附子（炮裂，去皮脐）1两，柴胡（去苗）3分，芎1两，知母（切，焙）1两，贝母（去心）1两，当归（切，焙）1两，鳖甲（去裙襕，醋炙）1两，荆芥穗1两，芜荑仁1分，木香3分，秦艽（去苗土）1两，青蒿子（小便浸1宿，焙）1两，黄耆（锉）1两，木通（锉）1两。', '鳗鲡鱼散（《普济方》卷二三八）。', '《圣济总录》卷一○○', '鬼注传尸，五劳七伤六极。', '-', '-', '柴胡', '[\'鳗鲡\', \'鱼寸切\', \'洗净\', \'附子\', \'炮裂\', \'去皮\', \'柴胡\', \'苗分\', \'知母\', \'切焙\', \'贝母\', \'当归\', \'切焙\', \'鳖甲\', \'荆芥\', \'两芜\', \'仁分\', \'木香\', \'秦艽\', \'苗土\', \'青蒿\', \'小便\', \'浸宿\', \'黄耆\', \'木通\']');
INSERT INTO `prescript` VALUES (58709, '2240', '磁石丸', '磁石（火煅，醋淬，研）1两，附子（炮裂，去皮脐）1两，补骨脂（炒）1两，肉苁蓉（酒浸，去皱皮，焙）1两，桂（去粗皮）1两，续断3分，柴胡（去苗）3分，巴戟天（去心）3分，桃仁（汤浸，去皮尖双仁，麸炒）3分，白茯苓（去黑皮）3分，人参3分，山芋3分，木香3分，厚朴（去粗皮，生姜汁炙）3分，远志（去心）3分，当归（切3分，焙）3分，牛膝（酒浸1宿，切，焙）3分，黄耆（锉）3分，羊肾1对（去筋膜，水煮熟，切，焙），白蒺藜（炒去角）半两，蜀椒（去目并开口者，炒出汗）半两，枳壳（去瓤，麸炒）半两，槟榔1枚（锉）。', '每服20丸，温酒送下，空心、午前各一服。', '《圣济总录》卷五十一', '肾经虚惫，四肢无力，面体少色，恶风寒，手足冷，骨节痛，耳内蝉鸣。', '-', '-', '柴胡', '[\'磁石\', \'研两\', \'附子\', \'炮裂\', \'去皮\', \'补骨脂\', \'肉苁蓉\', \'酒浸\', \'两桂去\', \'粗皮\', \'续断\', \'柴胡\', \'苗分\', \'巴戟天\', \'心分\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'炒分\', \'白茯苓\', \'黑皮\', \'人参\', \'山芋\', \'木香\', \'厚朴\', \'粗皮生\', \'姜汁\', \'炙分\', \'远志\', \'心分\', \'当归\', \'切分\', \'焙分\', \'牛膝\', \'酒浸\', \'宿切\', \'焙分\', \'黄耆\', \'分羊\', \'筋膜\', \'煮熟\', \'切焙白\', \'蒺藜\', \'半两\', \'蜀椒\', \'开口\', \'出汗\', \'半两\', \'枳壳\', \'半两\', \'槟榔\']');
INSERT INTO `prescript` VALUES (58710, '2195', '鳖甲散', '鳖甲1两半（涂醋，炙微黄，去裙襕），柴胡1两半（去苗），干姜半两（炮裂，锉），芎半两，木香3分，川大黄1两（锉碎，微炒），陈橘皮1两（汤浸，去白瓤，焙），诃黎勒2两（煨，用皮），赤茯苓1两，桃仁1两（汤浸，去皮尖双仁，麸炒微黄），京三棱1两（炮裂，锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，食前稍热服。', '《圣惠》卷二十八', '虚劳症瘕，或攻心腹，四肢无力，不思饮食。', '忌苋菜、生冷、湿面。', '-', '柴胡', '[\'鳖甲\', \'两半\', \'涂醋\', \'微黄\', \'柴胡\', \'两半\', \'去苗\', \'干姜\', \'半两\', \'炮裂\', \'半两\', \'木香\', \'分川\', \'大黄\', \'橘皮\', \'两汤\', \'浸去\', \'焙诃黎勒\', \'茯苓\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'三棱\', \'两炮裂\']');
INSERT INTO `prescript` VALUES (58711, '2188', '干地黄丸', '熟干地黄1两，芎3分，白茯苓3分，人参3分（去芦头），当归3分，柴胡半两（去苗），刺蓟半两，桑寄生半两，厚朴1两（去粗皮，涂生姜汁，炙令香熟），龙骨3分，阿胶3分（捣碎，炒令黄燥），白石脂3分，黄耆半两（锉），甘草1分（炙微赤，锉）。', '每服30丸，以清粥饮送下。不拘时候。', '《圣惠》卷七十五', '和气，安养胎脏。主妊娠气血虚弱，胎不长。', '-', '-', '柴胡', '[\'熟干\', \'地黄\', \'两芎\', \'白茯苓\', \'人参\', \'分去\', \'芦头\', \'当归\', \'柴胡\', \'半两\', \'半两\', \'桑寄生\', \'半两\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'龙骨\', \'阿胶\', \'捣碎\', \'炒令\', \'黄燥\', \'白石\', \'脂分\', \'黄耆\', \'半两\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58712, '2185', '淋渫蒴藋汤', '茄秸半两，蒴藋半两，蒺藜子半两，苍耳半两，海桐皮半两，柳木蠹末半两，柴胡半两，茯苓皮半两，水荭半两。', '每用药4两，以水5碗，煎取3碗，去滓，淋渫。', '《御药院方》卷八', '风毒脚气，腿膝痹肿，行履不得，皮骨如小虫行。', '慎避风冷。', '-', '柴胡', '[\'茄秸\', \'半两\', \'半两\', \'蒺藜\', \'半两\', \'苍耳\', \'半两\', \'海桐皮\', \'半两\', \'柳木\', \'半两\', \'柴胡\', \'半两\', \'茯苓皮\', \'半两\', \'水荭\', \'半两\']');
INSERT INTO `prescript` VALUES (58713, '2164', '槟榔汤', '槟榔1两，细辛（去苗叶）1两，半夏（陈者，汤洗7遍，焙干）5两，紫苏2两，甘草（炙，锉）2两，大黄（锉，炒）2两，陈橘皮（汤浸，去白，焙）2两，生姜（切，焙）3两，紫菀（去苗土）3两，柴胡（去苗）3两，附子1枚（炮裂，去皮脐），赤茯苓（去黑皮）4两。', '每服3钱匕，水1盏，煎至7分，去滓温服。若有症瘕癖结，加鳖甲（去裙襕，醋炙）、防葵各2两；上气，加桑根白皮（锉）3两，枳壳（去瓤，麸炒），厚朴（去粗皮，生姜汁炙）各2两。', '《圣济总录》卷七十一', '积聚结实，腹满刺痛，泄利不止。', '-', '-', '柴胡', '[\'槟榔\', \'细辛\', \'苗叶\', \'半夏\', \'陈者\', \'洗遍\', \'焙干\', \'紫苏\', \'甘草\', \'大黄\', \'橘皮\', \'汤浸\', \'生姜\', \'切焙\', \'紫菀\', \'苗土\', \'柴胡\', \'附子\', \'炮裂\', \'去皮\', \'茯苓\', \'黑皮\']');
INSERT INTO `prescript` VALUES (58714, '2108', '黄芩散', '黄芩1两，人参（去芦头）1两，柴胡（去苗）1两，葛根（锉）1两，栀子仁半两，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒7-8日，汗后余热不除。', '-', '-', '柴胡', '[\'黄芩\', \'人参\', \'芦头\', \'柴胡\', \'葛根\', \'栀子\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58715, '2106', '鳖甲汤', '鳖甲（醋炙，去裙襕）1两，当归（锉，焙）3分，桂（去粗皮）半两，生干地黄（焙）1两，芍药3分，虎杖（炒）1两，柴胡（去苗）1两，桃仁（汤去皮尖双仁，炒）1两，牛膝（酒浸，去苗，焙）半两，鬼箭羽3分，大黄（锉，炒）半两，虻虫1分（炒）。', '每服3钱匕，水1盏半，煎至7分，去滓温服，每日1次。', '《圣济总录》卷一五一', '经络壅滞，月水不通，日渐羸瘦，四肢无力。', '-', '-', '柴胡', '[\'鳖甲\', \'当归\', \'分桂去\', \'粗皮\', \'半两\', \'生干\', \'地黄\', \'芍药\', \'虎杖\', \'柴胡\', \'桃仁\', \'汤去\', \'皮尖\', \'双仁\', \'牛膝\', \'酒浸\', \'半两\', \'鬼箭羽\', \'大黄\', \'半两\']');
INSERT INTO `prescript` VALUES (58716, '2092', '羚羊角汤', '羚羊角3分（镑），柴胡3分（去苗），鳖甲3分（去裙襕，醋炙），人参3分，知母半两，淡竹茹半两，黄耆半两，赤茯苓半两（去黑皮），甘草（炙）半两，麦门冬（去心，焙）1两。', '每服5钱匕，以水1盏半，煎至8分，去滓，食后温服，每日2次。', '《圣济总录》卷三十一', '伤寒后，烦热憎寒，口苦不思饮食，日渐羸瘦。', '-', '-', '柴胡', '[\'羚羊角\', \'分镑\', \'柴胡\', \'分去\', \'鳖甲\', \'分去\', \'人参\', \'知母\', \'半两\', \'竹茹\', \'半两\', \'黄耆\', \'半两\', \'茯苓\', \'半两\', \'黑皮\', \'甘草\', \'半两\', \'麦门冬\']');
INSERT INTO `prescript` VALUES (58717, '2051', '鳖甲丸', '鳖甲（去裙襕，醋炙）1两半，杏仁（汤浸，去皮尖双仁，炒）1两半，苦葫芦（用瓤）1两半，天门冬（去心，焙）1两半，巴豆1分（去皮心膜，出油尽），猪牙皂荚（涂酥炙）1两，石菖蒲（微炒）1两，桂（去粗皮）1两，葶苈（隔纸炒）1两，甘遂（微煨）1两，苦参1两，大黄（锉碎，醋炒）1两，柴胡（去苗）1两，当归（切1两，焙）1两，羚羊角（磅）1两，龙骨（烧）3分。', '每服10-15丸，食前温水送下，每日3次。如1-2服后小便利，即减丸数及间日服。', '《圣济总录》卷一五三', '去水，调经血。主妇人水分，肢体肿满不消，因经水不通者。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'两半\', \'葫芦\', \'两半\', \'天门冬\', \'两半\', \'巴豆\', \'去皮\', \'心膜\', \'出油\', \'猪牙皂\', \'荚涂\', \'石菖蒲\', \'两桂去\', \'粗皮\', \'两葶\', \'隔纸\', \'两甘遂\', \'苦参\', \'大黄\', \'柴胡\', \'当归\', \'羚羊角\', \'龙骨\', \'烧分\']');
INSERT INTO `prescript` VALUES (58718, '1968', '解热退烧浸膏', '柴胡60斤，赤芍40斤，甘草20斤，板蓝根60斤，葛根60斤，桔梗60斤，黄芩（去朽）80斤，生石膏200斤，白芷40斤，羌活40斤。', '每服1支，每日3次，热开水冲服。', '《北京市中成药规范》', '清热退烧。主外感发热作冷，头痛口渴，全身倦痛。', '-', '-', '柴胡', '[\'柴胡\', \'赤芍\', \'甘草\', \'板蓝根\', \'葛根\', \'桔梗\', \'黄芩\', \'朽斤\', \'生石膏\', \'白芷\', \'羌活\']');
INSERT INTO `prescript` VALUES (58719, '1941', '川芎散', '川芎（洗）1两，柴胡（去苗，洗）1两，半夏曲半两，甘草（炙）半两，甘菊半两，细辛（去叶）半两，人参（去芦）半两，前胡（去苗，洗）半两，防风（去叉股）半两。', '川芎甘菊散（《杏苑》卷五）。', '《本事》卷四', '风盛膈壅，鼻塞清涕，热气攻眼，下泪多眵，齿间紧急，作偏头疼。', '-', '-', '柴胡', '[\'川芎\', \'柴胡\', \'苗洗\', \'半夏\', \'半两\', \'甘草\', \'半两\', \'甘菊\', \'半两\', \'细辛\', \'半两\', \'人参\', \'去芦\', \'半两\', \'胡去\', \'苗洗\', \'半两\', \'防风\', \'半两\']');
INSERT INTO `prescript` VALUES (58720, '1862', '黄芩散', '黄耆5两，白术5两，前胡5两，枳壳5两，柴胡3两，杏仁3两，人参3两，白茯苓3两，甘草3两，当归3两，半夏3两，黄芩3两，白芍药3两，麦门冬3两，熟干地黄3两。', '每服2大钱，水1盏，煎至7分，去滓，食后临卧温服。', '《鸡峰》卷九', '劳。人不甚虚，有热，胸中烦，手足热，心忪悸，口苦咽干，痰嗽潮热。', '-', '-', '柴胡', '[\'黄耆\', \'白术\', \'两前\', \'胡两\', \'枳壳\', \'柴胡\', \'杏仁\', \'人参\', \'白茯苓\', \'甘草\', \'当归\', \'半夏\', \'黄芩\', \'两白\', \'芍药\', \'麦门冬\', \'两熟\', \'地黄\']');
INSERT INTO `prescript` VALUES (58721, '1850', '黄耆丸', '黄耆1两，麦门冬1两（去心），茯神1两，北柴胡1两，甘草1两，生干地黄1两，酸枣仁3分（炒），郁李仁3分，杏仁3分（去皮尖、双仁，麸炒黄），枸杞子3分，人参3分（去芦），黄芩3分，百合半两，枳壳半两（去瓤，麸炒），赤芍药半两，知母半两，鳖甲2两（制）。', '每服30丸，清粥吞下，不拘时候。', '《妇人良方》卷五', '妇人骨蒸烦热，四肢羸瘦，疼痛口干，心躁不得眠卧。', '-', '-', '柴胡', '[\'黄耆\', \'麦门冬\', \'两去\', \'茯神\', \'两北\', \'柴胡\', \'甘草\', \'两生\', \'地黄\', \'酸枣仁\', \'郁李仁\', \'杏仁\', \'皮尖\', \'双仁麸\', \'枸杞子\', \'人参\', \'分去\', \'黄芩\', \'百合\', \'半两\', \'枳壳\', \'半两\', \'芍药\', \'半两\', \'知母\', \'半两\', \'鳖甲\', \'两制\']');
INSERT INTO `prescript` VALUES (58722, '1823', '马兜铃丸', '马兜铃1两半，柴胡1两半，茯苓1两半，黑参1两，桔梗1两，细辛1两。', '每服10丸，空心茶送下。', '《秘传眼科龙木论》卷五', '眼痒极难忍，外障。', '-', '-', '柴胡', '[\'马兜铃\', \'两半\', \'柴胡\', \'两半\', \'茯苓\', \'两半\', \'黑参\', \'桔梗\', \'细辛\']');
INSERT INTO `prescript` VALUES (58723, '1784', '羚羊角汤', '羚羊角1两（屑），决明子1两，芎1两，羌活1两（去芦头），石膏（碎）1两，柴胡半两（去苗），黄芩半两（去黑心），人参半两。', '每服3钱匕，以水1盏，加竹叶3-7片，煎至6分，去滓温服，早、晚食后各1次。', '《圣济总录》卷三十二', '伤寒后，热毒风壅攻冲，眼目昏暗疼痛。', '-', '-', '柴胡', '[\'羚羊角\', \'两屑\', \'决明子\', \'两芎\', \'羌活\', \'两去\', \'芦头\', \'石膏\', \'柴胡\', \'半两\', \'去苗\', \'黄芩\', \'半两\', \'黑心\', \'人参\', \'半两\']');
INSERT INTO `prescript` VALUES (58724, '1779', '葛根散', '葛根1两（锉），川大黄1两（锉碎，微炒），黄芩1两，麻黄2两（去根节），桂心1两，赤芍药1两，甘草半两（炙微赤，锉），柴胡1两（去苗），栀子仁半两。', '每服4钱，以水1中盏，入生姜半分，煎至5分，去滓，不拘时候稍热服。令有汗为度。', '《圣惠》卷十七', '热病3日，未得汗，壮热烦闷，欲得饮水。', '-', '-', '柴胡', '[\'葛根\', \'锉川\', \'大黄\', \'黄芩\', \'麻黄\', \'两去\', \'根节\', \'桂心\', \'芍药\', \'甘草\', \'半两\', \'炙微\', \'柴胡\', \'两去\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (58725, '1776', '鳖甲丸', '鳖甲1个（九肋者，醋炙），柴胡（酒浸）1两，杏仁（童便浸，炒）1两，甘草（炙）1两，人参半两。', '每服10-15丸，煎生姜汤送下。', '《普济方》卷一六二引《圣惠》', '吐血，咳嗽。', '-', '-', '柴胡', '[\'鳖甲\', \'肋者\', \'柴胡\', \'酒浸\', \'杏仁\', \'童便\', \'甘草\', \'人参\', \'半两\']');
INSERT INTO `prescript` VALUES (58726, '1757', '暗治饮', '黄柏3钱，茯苓5钱，蒲公英3钱，柴胡1钱，白芍5钱，生甘草5钱，龙胆草1钱，豨莶草2钱。', '水煎服。', '《洞天奥旨》卷十二', '袖手疳。生龟头之颈上，皮包于内而外不显。', '-', '-', '柴胡', '[\'黄柏\', \'茯苓\', \'蒲公英\', \'柴胡\', \'白芍\', \'生甘草\', \'龙胆草\', \'豨莶草\']');
INSERT INTO `prescript` VALUES (58727, '1715', '槟榔丸', '槟榔1两，诃黎勒皮1两，柴胡3分（去苗），桂心1两，草豆蔻半两（去皮），木香半两，郁李仁1两（汤浸，去皮，微炒），川大黄1两（锉碎，微炒），吴茱萸半两（汤浸7遍，微炒）。', '每服20丸，食前以生姜汤送下。', '《圣惠》卷五十八', '肠胃冷热不和，大便难秘，食饮不消，心腹妨闷。', '-', '-', '柴胡', '[\'槟榔\', \'两诃黎勒皮\', \'柴胡\', \'分去\', \'苗桂心\', \'草豆蔻\', \'半两\', \'去皮\', \'木香\', \'半两\', \'郁李仁\', \'两汤\', \'去皮\', \'炒川\', \'大黄\', \'吴茱萸\', \'半两\', \'浸遍\']');
INSERT INTO `prescript` VALUES (58728, '1689', '麻黄散', '麻黄1两（去根节），赤芍药1两，甘草1两（炙微赤，锉），葛根1两（锉），柴胡半两（去苗），黄芩1两，石膏2两，麦门冬1两（去心）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠5月或7-8月，卒患时气，烦热口干，心躁头痛，四肢烦疼，不得安卧。', '-', '-', '柴胡', '[\'麻黄\', \'两去\', \'根节\', \'芍药\', \'甘草\', \'两炙微\', \'葛根\', \'柴胡\', \'半两\', \'去苗\', \'黄芩\', \'石膏\', \'麦门冬\', \'两去\']');
INSERT INTO `prescript` VALUES (58729, '1674', '黄金散', '柴胡1钱5分，金银花1两，大力子1钱，肉桂1钱，黄耆5两，归尾3分，黄柏7分，炙甘草5分。', '水、酒各半煎，食前服。', '《洞天奥旨》卷十四', '疮生腿外侧，或因寒湿得附骨痈，于足少阳经分，微侵足阳明经，坚硬漫肿，行步作痛，或不能行。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'金银花\', \'大力\', \'子钱\', \'肉桂\', \'黄耆\', \'两归尾分\', \'黄柏\', \'甘草\']');
INSERT INTO `prescript` VALUES (58730, '1637', '鳖甲散', '肉桂4两，川芎4两，白芷4两，秦艽4两，鳖甲4两（醋炙3次，令赤色），紫菀4两（净洗，焙干），麻黄4两（不去节），羌活4两（一云独活），当归4两（去尾），干姜4两，橘皮4两，苍术1斤（焙），天台乌药7两，紫苏4两（不过火），桔梗3斤半（焙），乌药7两，柴胡7两，川乌半个（炮）。', '《医方类聚》有五味子七两。', '《理伤续断方》', '五劳七伤，四时伤寒风疾，浑身憎寒壮热，骨节烦疼，咳嗽痰涎，酒色伤惫，四肢倦怠；及山岚瘴疟，一切积气，心腹膨胀，呕吐泄泻。', '-', '-', '柴胡', '[\'肉桂\', \'川芎\', \'白芷\', \'秦艽\', \'鳖甲\', \'两醋\', \'炙次\', \'赤色\', \'紫菀\', \'两净\', \'焙干\', \'麻黄\', \'两不去\', \'羌活\', \'两一云\', \'独活\', \'当归\', \'两去\', \'干姜\', \'橘皮\', \'苍术\', \'天台乌药\', \'紫苏\', \'不过\', \'桔梗\', \'斤半\', \'乌药\', \'柴胡\', \'川乌\', \'半个\']');
INSERT INTO `prescript` VALUES (58731, '1615', '鳖甲煮散', '鳖甲（去裙襕，醋炙）半两，羌活（去芦头）1分，柴胡（去苗）1分，黄耆（锉）半两，赤茯苓（去黑皮）1分，生干地黄（焙）1分，木香1分，牛膝（酒浸，切，焙）半两，天灵盖（酥炙）半两，续断半两，菟丝子（酒浸，别捣）半两，芎1两，藿香叶1两，陈橘皮（汤浸，去白，焙）1两，山茱萸1两，麝香（研）1分，独活（去芦头）1分。', '每服40丸，空心盐汤送下。', '《普济方》卷二○○引《护命方》', '劳疟。毛发枯焦，寒热不定，饮食减少，肌肉消瘦，面色青黑，两足无力，非时足冷，小便频数，大便不调，梦遗滑精。', '若大腑秘、骨蒸热勿服。', '-', '柴胡', '[\'鳖甲\', \'半两\', \'羌活\', \'芦头分\', \'柴胡\', \'苗分\', \'黄耆\', \'半两\', \'茯苓\', \'黑皮\', \'分生\', \'地黄\', \'焙分\', \'木香\', \'牛膝\', \'浸切\', \'半两\', \'天灵盖\', \'半两\', \'续断\', \'半两\', \'菟丝子\', \'浸别\', \'半两\', \'藿香\', \'橘皮\', \'汤浸\', \'山茱萸\', \'麝香\', \'研分\', \'独活\', \'芦头分\']');
INSERT INTO `prescript` VALUES (58732, '1602', '鳖甲散', '鳖甲2两（涂醋，炙微黄，去裙襕），厚朴1两（去粗皮，涂生姜汁炙令香熟），木香3分，槟榔3分，神曲2两（捣碎，微炒），京三棱1两（炮，锉），川大黄2两（锉碎，微炒），芎半两，青橘皮3分（汤浸，去白瓤，焙），桃仁1两（汤浸，去皮尖双仁，麸炒微黄），麦糵1两（炒微黄），当归半两，赤芍药1两，桂心3分，柴胡2两半（去苗）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，食前稍热服。', '《圣惠》卷二十八', '虚劳积聚，或心腹疼痛，四肢羸瘦，小便赤，不能饮食。', '忌苋菜、生冷。', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'微黄\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'木香\', \'槟榔\', \'神曲\', \'捣碎\', \'炒京\', \'三棱\', \'两炮\', \'锉川\', \'大黄\', \'半两\', \'橘皮\', \'分汤\', \'浸去\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'微黄\', \'当归\', \'半两\', \'芍药\', \'两桂心分\', \'柴胡\', \'两半\', \'去苗\']');
INSERT INTO `prescript` VALUES (58733, '1601', '磁石汤', '芍药2钱，白术3钱，北柴胡5钱，地黄2钱，地骨皮3钱，甘草3钱。', '本方名磁石汤，但方中无磁石，疑脱。', '《医方类聚》卷二一二引《仙传济阴方》', '妇人经行时，手足受水触血风所致，手足心时时有烦热。', '-', '-', '柴胡', '[\'芍药\', \'白术\', \'钱北\', \'柴胡\', \'地黄\', \'地骨皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (58734, '1578', '解结提金散', '柴胡2两（去芦），川芎5钱，甘草5钱，陈皮5钱（去白），人参1两5钱，桔梗5钱，乳香1两，没药5钱，粟壳10两（去顶，切丝，水泡，晒干，蜜炒黄色）。', '每服3钱，用乌梅3个同煎，用水1大盏，煎至7分，去乌梅，临卧或空心连滓温服，1日1服。', '《医方类聚》卷一五四《寿域神方》', '五劳七伤，久年咳嗽，诸药不效者。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'川芎\', \'甘草\', \'陈皮\', \'人参\', \'桔梗\', \'乳香\', \'没药\', \'钱粟壳\', \'两去\', \'切丝\', \'水泡\', \'晒干\', \'黄色\']');
INSERT INTO `prescript` VALUES (58735, '1571', '鳖甲饮', '当归1钱，秦艽1钱，柴胡1钱，鳖甲3钱（羊酥炙），地骨皮8分，枳实8分，知母8分，乌药8分。', '加灯心3茎，水煎7分，空心服。', '《玉案》卷三', '病后劳复，邪热未除，房劳虚损，一切骨蒸。', '-', '-', '柴胡', '[\'当归\', \'秦艽\', \'柴胡\', \'鳖甲\', \'地骨皮\', \'枳实\', \'知母\', \'乌药\']');
INSERT INTO `prescript` VALUES (58736, '1561', '葶苈丸', '葶苈子（隔纸炒）1两，陈橘皮（汤浸，去白，焙）1两，柴胡（去苗）1两，枣肉（研）1两。', '每服20丸，空腹煎杏仁汤送下，1日2次。', '《圣济总录》卷五十', '肺气喘息，面目浮肿。', '-', '-', '柴胡', '[\'葶苈子\', \'隔纸\', \'橘皮\', \'汤浸\', \'柴胡\', \'去苗\', \'两枣\', \'研两\']');
INSERT INTO `prescript` VALUES (58737, '1554', '解毒饮子', '柴胡8分，紫草6分，防风7分，白芷5分，荆芥7分，鼠粘子1钱，川芎5分，蝉蜕5分，木通5分。', '水煎，热服。', '《张氏医通》卷十五', '小儿痘为风寒所遏而起发迟者。', '-', '-', '柴胡', '[\'柴胡\', \'紫草\', \'防风\', \'白芷\', \'荆芥\', \'分鼠\', \'粘子\', \'川芎\', \'蝉蜕\', \'木通\']');
INSERT INTO `prescript` VALUES (58738, '1525', '解寒汤', '人参5钱，白术1两，附子3分，苍术3钱，川芎2钱，柴胡5分。', '水煎服。2剂汗出而愈。', '《辨证录》卷八', '补二经之虚，兼散其寒邪。主行房之后，阳明与冲脉之气，皆夺其所用，其中空虚，寒邪相犯，邪乘虚而入，舍于二经之间，一时病症，自卯足寒，自酉分方热，至寅初乃休，一日一夜只苏一时。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'附子\', \'苍术\', \'川芎\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58739, '1492', '解郁清肝汤', '柴胡6g，酒黄芩9g，香附9g，青皮3g，银花12g，青黛0.6g，赤芍9g，牡丹皮9g。', '-', '《张皆春眼科证治》', '疏肝解郁，清肝泻火。主风轮激开。因怒气伤肝，气血挟郁火冲逆于上，目珠胀痛，白睛赤丝紫胀，风轮泛高，青睛表层骤起裂痕，且兼胸满胁痛，脉弦有力者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'香附\', \'青皮\', \'银花\', \'青黛\', \'赤芍\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (58740, '1455', '羚羊角汤', '羚羊角半两（镑），大腹半两（并皮子用，锉），柴胡半两（去苗），朴消半两，威蕤3分，石膏1两（碎），桑根白皮（锉）1两。', '每服5钱匕，以水1盏半，煎至8分，去滓温服，不拘时候。', '《圣济总录》卷二十九', '伤寒不发汗十日以上，成狐惑病，腹胀面赤，恶闻食臭。', '-', '-', '柴胡', '[\'羚羊角\', \'半两\', \'大腹\', \'半两\', \'皮子\', \'柴胡\', \'半两\', \'去苗\', \'朴消\', \'半两\', \'威蕤\', \'石膏\', \'两碎\', \'桑根\', \'白皮\']');
INSERT INTO `prescript` VALUES (58741, '1453', '鳖甲散', '鳖甲1两（涂醋，炙微黄，去裙襕），柴胡1两（去苗），知母1两半，赤茯苓3分，款冬花半两，桑根白皮半两（锉），乌梅肉3分（微炒），栀子仁1分，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加葱白2茎，生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒后肺痿劳嗽，涕唾稠粘，骨节烦闷，发歇寒热。', '-', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'微黄\', \'柴胡\', \'两去\', \'知母\', \'两半\', \'茯苓\', \'款冬花\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'乌梅\', \'肉分\', \'栀子\', \'仁分\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58742, '1427', '缓筋汤', '熟地黄1分，生甘草2分，柴胡2分，红花2分，炙甘草2分，苏木2分，独活2分，藁本3分，升麻3分，黄芩3分，草豆蔻仁3分，酒黄柏3分，生地黄3分，当归身3分，麻黄3分，羌活3钱，苍术5分。', '水2大盏，煎至1盏，去滓，食远服。', '《兰室秘藏》卷中', '两目如火肿痛，两足及伏免筋骨痛，膝少力，身重腰痛，夜恶寒，痰嗽，颈项皆急痛，目外眵目丝急，食不下。', '-', '-', '柴胡', '[\'熟地黄\', \'生甘草\', \'柴胡\', \'红花\', \'甘草\', \'苏木\', \'独活\', \'藁本\', \'升麻\', \'黄芩\', \'草豆蔻\', \'仁分\', \'黄柏\', \'生地黄\', \'当归\', \'身分\', \'麻黄\', \'羌活\', \'苍术\']');
INSERT INTO `prescript` VALUES (58743, '1417', '豁痰汤', '柴胡2钱，半夏2钱，茯苓5分，人参5分，甘草5分，紫苏5分，陈皮5分，厚朴5分，南星5分，薄荷叶5分，枳壳5分，羌活5分。', '上药以水2盏，加生姜5片，煎8分，内服，不拘时候。中风者，加独活；胸膈不利者，加枳实；内外无热者，去黄芩。', '《医统》卷四十三引《养生主论》', '一切痰疾。', '-', '-', '柴胡', '[\'柴胡\', \'半夏\', \'茯苓\', \'人参\', \'甘草\', \'紫苏\', \'陈皮分\', \'厚朴\', \'南星\', \'薄荷叶\', \'枳壳\', \'羌活\']');
INSERT INTO `prescript` VALUES (58744, '1416', '鳖甲散', '鳖甲3分（涂醋，炙令黄，去裙襕），川升麻半两，葳蕤1斤，黄连（去须）1斤，当归（锉，微炒）1斤，赤芍药1斤，桂心1斤，犀角屑1斤，贝齿1斤，茯神1斤，秦艽（去苗）1斤，甘草（炙微赤，锉）1斤，柴胡半两（去苗），麻黄半两（去根节），人参半两（去芦头）。', '每服2钱，以粥饮调下，不拘时候。', '《圣惠》卷十三', '伤寒不解，变成狐惑，默默欲睡，卧则不安，咽喉干痛，口内生疮，恶闻食气，时时下痢。', '-', '-', '柴胡', '[\'鳖甲\', \'分涂\', \'炙令\', \'黄去\', \'升麻\', \'半两\', \'葳蕤\', \'黄连\', \'须斤\', \'当归\', \'锉微\', \'炒斤\', \'芍药\', \'斤桂心斤\', \'犀角\', \'贝齿斤\', \'茯神\', \'秦艽\', \'苗斤\', \'甘草\', \'炙微\', \'锉斤\', \'柴胡\', \'半两\', \'去苗\', \'麻黄\', \'半两\', \'根节\', \'人参\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (58745, '1362', '解毒汤', '柴胡（去苗）半两，黄芩（去黑心）1分，荆芥穗1分。', '每服5钱匕，水1盏半，生姜1枣大（拍碎），煎至8分，去滓，入生地黄汁1合，白蜜半匙，更煎3-5沸，热服。', '《圣济总录》卷二十一', '伤寒，初觉烦热，头疼脚痛。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'黄芩\', \'黑心\', \'荆芥\', \'穗分\']');
INSERT INTO `prescript` VALUES (58746, '1334', '鳖甲汤', '鳖甲（去裙襕，酥炙令黄色）1两，天灵盖（酥炙黄色）半两，柴胡（去苗）3分，赤茯苓（去黑皮）3分，贝母（去心）半两，桃仁（去皮尖双仁，麸炒）半两，黄连（去须）半两，麦门冬（去心焙）3分，阿魏（用麸裹，煨令熟）半两，生干地黄3分，槟榔（锉）半两，当归（锉，焙）半两，安息香半两，地骨皮3分，山栀子仁半两，人参半两。', '每服4钱，童便1盏半，葱白5寸，桃、柳枝各7寸，生姜钱大2片，同煎至8分，去滓温服，不拘时候。', '《普济方》卷二三七', '五劳干瘦，及传尸梦寐不祥，日渐消瘦，肌体困倦，骨节疼痛，不思饮食。', '-', '-', '柴胡', '[\'鳖甲\', \'炙令\', \'黄色\', \'天灵盖\', \'黄色\', \'半两\', \'柴胡\', \'苗分\', \'茯苓\', \'黑皮\', \'贝母\', \'去心\', \'半两\', \'桃仁\', \'皮尖\', \'双仁麸\', \'半两\', \'黄连\', \'半两\', \'麦门冬\', \'心焙分\', \'阿魏\', \'令熟\', \'半两\', \'生干\', \'地黄\', \'槟榔\', \'半两\', \'当归\', \'半两\', \'安息香\', \'半两\', \'地骨皮\', \'分山\', \'栀子\', \'半两\', \'人参\', \'半两\']');
INSERT INTO `prescript` VALUES (58747, '1302', '黄耆散', '柴胡1钱，黄耆1钱，赤苓1钱，白术1钱，人参3分半，地骨皮3分半，枳壳3分半，桔梗3分半，桑白3分半，赤芍3分半，生地3分半，麦门冬1钱半，甘草3分。', '加生姜，水煎服。', '《嵩崖尊生》卷十二', '热在筋肉，寅卯时甚，便难转筋。', '-', '-', '柴胡', '[\'柴胡\', \'黄耆\', \'赤苓\', \'白术\', \'人参\', \'分半\', \'地骨皮\', \'分半\', \'枳壳\', \'分半\', \'桔梗\', \'分半桑白\', \'分半\', \'赤芍\', \'半生\', \'地分\', \'麦门冬\', \'钱半\', \'甘草\']');
INSERT INTO `prescript` VALUES (58748, '1278', '鳖甲地黄汤', '鳖甲3钱（生，醋炙），生地5钱，当归2钱，柴胡3分（盐水炒），麦冬3钱（去心），白术1钱半（制），人参6分，茯苓1钱半，石斛3钱，甘草6分，乌梅1钱半。', '水煎，去滓温服。', '《医略六书》卷十九', '虚劳。烦热羸瘦，脉弦濡数者。', '-', '-', '柴胡', '[\'鳖甲\', \'钱生\', \'生地\', \'当归\', \'柴胡\', \'盐水\', \'麦冬\', \'白术\', \'钱半制\', \'人参\', \'茯苓\', \'钱半\', \'石斛\', \'甘草\', \'乌梅\', \'钱半\']');
INSERT INTO `prescript` VALUES (58749, '1242', '鳖甲汤', '鳖甲（去裙襕，醋炙黄）1两，柴胡（去苗）1两，杏仁（汤浸，去皮尖双仁，麸炒）1两，桃仁（汤浸，去皮尖双仁，麸炒）1两，款冬花1两，甘草（炙，锉）1两，贝母（去心）1两，知母（焙）1两，皂荚（去皮子，酥炙）1分。', '每服3钱匕，水1盏，入小麦50粒，乌梅1个，煎至7分，去滓温服。', '《圣济总录》卷八十八', '虚劳。咳嗽，胸满气急，发热羸瘦。', '-', '-', '柴胡', '[\'鳖甲\', \'柴胡\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'款冬花\', \'甘草\', \'贝母\', \'知母\', \'皂荚\', \'皮子\', \'酥炙分\']');
INSERT INTO `prescript` VALUES (58750, '1194', '蓝青散', '蓝青半两，寒水石1两，石膏1两，犀角屑1两，柴胡1两（去苗），知母半两，杏仁半两（浸汤，去皮尖双仁，麸炒微黄），黄芩1两，栀子仁半两，甘草半两（炙微赤，锉），赤芍药3分，羚羊角屑3分。', '蓝青煎（《圣济总录》卷一八二）。', '《圣惠》卷九十一', '小儿一切丹毒，大赤肿，身体壮热如火，已服诸药未损者。', '-', '-', '柴胡', '[\'蓝青\', \'半两\', \'寒水石\', \'石膏\', \'犀角\', \'柴胡\', \'两去\', \'知母\', \'半两\', \'杏仁\', \'半两\', \'浸汤\', \'皮尖\', \'双仁麸\', \'微黄\', \'黄芩\', \'栀子\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'芍药\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (58751, '1187', '鳖甲煎', '雄鳖1个（重1斤者），杏仁（去皮尖双仁，炒），北柴胡4两，贝母（去心）4两，知母4两。', '每服30丸，不拘时候，每日5次。', '《妇人良方》卷五', '妇人骨蒸劳损，或时寒热，肌体倦疼。', '-', '-', '柴胡', '[\'重斤\', \'杏仁\', \'皮尖\', \'双仁\', \'炒北\', \'柴胡\', \'贝母\', \'知母\']');
INSERT INTO `prescript` VALUES (58752, '1172', '豮猪肝方', '豮猪肝1具（水洗，去筋膜令净，切作柳叶片），鳖甲（去裙襕，米醋慢炙）1两，柴胡（去苗）3分，甘草（炙，锉）半两，乌梅肉（炒）半两，人参半两，白术3分，胡黄连1两，干姜（炮）半两，陈橘皮（汤浸，去白，焙）半两，诃黎勒（炮，去核）半两，芜荑（炒）半两。', '空腹食之。如渴，即将药末煎汤服亦效。', '《圣济总录》卷一八九', '冷劳下痢脓血，瘦怯不能食。', '-', '-', '柴胡', '[\'猪肝\', \'水洗\', \'筋膜\', \'令净切\', \'作柳\', \'叶片\', \'鳖甲\', \'米醋\', \'慢炙两\', \'柴胡\', \'苗分\', \'甘草\', \'半两\', \'乌梅\', \'半两\', \'人参\', \'半两\', \'白术\', \'胡黄连\', \'干姜\', \'半两\', \'橘皮\', \'汤浸\', \'半两\', \'黎勒炮\', \'去核\', \'半两\', \'半两\']');
INSERT INTO `prescript` VALUES (58753, '1168', '槟榔散', '槟榔1两，赤芍药半两，枳壳半两（麸炒微黄，去瓤），芎半两，赤茯苓半两，柴胡1两（去苗），木香半两，川大黄1两（锉碎，微炒），当归2分（锉碎，微炒），陈橘皮1两（汤浸，去白瓤，焙），桃仁半两（汤浸，去皮尖双仁，麸炒微黄），甘草1分（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓稍热服，不拘时候。', '《圣惠》卷四十八', '积聚，心腹两胁疼痛。', '-', '-', '柴胡', '[\'槟榔\', \'两赤\', \'芍药\', \'半两\', \'枳壳\', \'半两\', \'微黄\', \'半两\', \'茯苓\', \'半两\', \'柴胡\', \'两去\', \'苗木\', \'半两\', \'大黄\', \'当归\', \'橘皮\', \'两汤\', \'浸去\', \'桃仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (58754, '1141', '解毒泻肝汤', '柴胡9g，龙胆草3g，酒黄芩9g，银花30g，川黄连6g，生地9g，元参9g，赤芍9g，牡丹皮9g。', '-', '《张皆春眼科证治》', '清肝泻火，解毒除风。主花翳白陷。肝胆风热火毒炽盛，症见抱轮红赤，白陷深入风轮内层，色黄带绿，发展迅速者。', '-', '-', '柴胡', '[\'柴胡\', \'龙胆草\', \'黄芩\', \'银花\', \'黄连\', \'生地\', \'元参\', \'赤芍\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (58755, '1137', '琥珀丸', '琥珀（别研）1两，木香1两，禹余粮（煅，醋淬）1两，白术1两，芍药1两，鳖甲（去裙襕，酒浸，炙令香）1两，桂（去粗皮）1两，附子（炮裂，去皮脐）1两，羌活（去芦头）1两，蓬莪术（炮，锉）1两，细辛（去苗叶）1两，牡丹（去心）1两，肉豆蔻（去壳）1两，人参1两，京三棱（炮，锉）1两，黄耆（锉）1两，当归（微焙）1两半，槟榔（锉）1两半，枳壳（去瓤，麸炒）1两半，柴胡（去苗）2两，芎2两，桃仁（汤浸，去皮尖双仁，炒黄色）2两，安息香半两（研）。', '每服20丸，空心温酒送下。', '《圣济总录》卷一五一', '妇人虚冷，月水凝涩不利，腹内疼痛，四肢烦热，皮肤瘾疹，饮食减少。', '-', '-', '柴胡', '[\'琥珀\', \'别研\', \'木香\', \'禹余粮\', \'白术\', \'芍药\', \'鳖甲\', \'浸炙令\', \'两桂去\', \'粗皮\', \'附子\', \'炮裂\', \'去皮\', \'羌活\', \'芦头\', \'两蓬\', \'莪术\', \'细辛\', \'苗叶\', \'牡丹\', \'肉豆蔻\', \'人参\', \'两京\', \'三棱\', \'黄耆\', \'当归\', \'微焙\', \'两半\', \'槟榔\', \'两半\', \'枳壳\', \'两半\', \'柴胡\', \'去苗\', \'两芎\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\', \'黄色\', \'安息香\', \'半两\']');
INSERT INTO `prescript` VALUES (58756, '1116', '棱莪散', '三棱1两，莪术1两，赤芍1两，黄柏1两，大茴8钱，玄胡8钱，槟榔8钱，紫苏8钱，陈皮8钱，青皮5钱，羌活5钱，腹皮5钱，荆芥2钱，桔梗2钱，半夏2钱，黄连2钱，芒消1钱，大黄1钱，防风1钱，柴胡1钱，千里马（即草鞋）2只，姜3片，葱1根。', '上以童便、水各半煎，空心热服。随证加减。若手足伤断，徐徐推正，灯心火纸卷令厚实，杉木皮紧扎自愈。', '《跌损妙方》', '攻逐瘀积，驱散外邪，消肿止痛。主全身性跌打损伤早期，气血未虚而瘀积内蓄，内外闭塞，二便不通，肿痛烦热等实证。', '-', '-', '柴胡', '[\'三棱\', \'莪术\', \'赤芍\', \'黄柏\', \'两大茴钱\', \'胡钱\', \'槟榔\', \'紫苏\', \'陈皮\', \'青皮\', \'羌活\', \'腹皮\', \'荆芥\', \'桔梗\', \'半夏\', \'黄连\', \'钱芒\', \'消钱\', \'大黄\', \'防风\', \'柴胡\', \'千里马\', \'草鞋\', \'姜片\', \'葱根\']');
INSERT INTO `prescript` VALUES (58757, '1080', '解晕神丹', '人参2钱，半夏2钱，茯苓5钱，南星1钱，天麻5分，乌药5分，陈皮5分，菖蒲5分，当归3钱，柴胡1钱。', '水煎服。', '《辨证录》卷五', '补阴以助阳。主阳气虚而不能入于阴血之中，致患阴热之厥，夜间发热，一时厥逆，昏晕如死人状，惟手足温和，喉中痰响，不能出声，手足筋脉多红，饮之水必不吐。', '-', '-', '柴胡', '[\'人参\', \'半夏\', \'茯苓\', \'南星\', \'天麻\', \'乌药\', \'陈皮分\', \'菖蒲\', \'当归\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58758, '1047', '解余汤', '黄芩1钱，柴胡1钱，干葛1钱，前胡6分，枳壳6分，赤芍5分，桔梗5分，连翘5分，甘草3分，薄荷3分，茯苓2钱，半夏2钱，川芎1钱。', '方中茯苓、半夏、川芎用量原缺，据《家庭治病新书》补。', '《观聚方要补》卷一引《孝慈备览》', '伤寒过经不解，发热或潮热，口干舌燥者。', '-', '-', '柴胡', '[\'黄芩\', \'柴胡\', \'干葛钱\', \'胡分\', \'枳壳\', \'赤芍\', \'桔梗\', \'连翘\', \'甘草\', \'薄荷\', \'茯苓\', \'半夏\', \'川芎\']');
INSERT INTO `prescript` VALUES (58759, '1046', '槟榔丸', '槟榔1两半，川大黄2两（锉碎，微炒），白术3分，枳壳3分（麸炒微黄，去瓤），木香半两，柴胡1两（去芦头），鳖甲2两（涂醋炙令黄，去裙襕）。', '每服30丸，食前以暖酒送下。', '《圣惠》卷四十九', '食不消，成症癖，令人四肢干瘦，不欲饮食。', '-', '-', '柴胡', '[\'槟榔\', \'两半\', \'大黄\', \'白术\', \'枳壳\', \'分麸\', \'微黄\', \'木香\', \'半两\', \'柴胡\', \'两去\', \'芦头\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\']');
INSERT INTO `prescript` VALUES (58760, '1045', '鳖甲丸', '鳖甲1个（大者，净洗，去筋膜，面裹外面2-3分厚，上面用纸固济，泥一风炉子，安鳖甲在上面，别入桃仁半斤，去尖皮双仁，研，以米醋4升，无灰酒3升，硇砂3两，同搅拌旋入鳖甲中，煎为膏，取出用盒盛，却将鳖甲去纸泥，炙令黄色），青橘皮（去白，焙）3两，麦糵（炒）3两，沉香（锉）3两，肉豆蔻（去壳）3两，丁香2两，木香2两，槟榔（一半生，一半炒）2两，陈曲（炒）2两，京三棱（煨,锉）2两，大黄（生，锉）2两，厚朴（去粗皮，生姜汁炒令紫）2两，柴胡（去苗）半斤，桂（去粗皮）2两。', '每服20丸，空心米饮送下。', '《圣济总录》卷七十二', '久积症瘕。', '-', '-', '柴胡', '[\'鳖甲\', \'个大者\', \'筋膜\', \'面裹\', \'外面\', \'分厚\', \'上面\', \'用纸\', \'固济\', \'风炉子\', \'鳖甲\', \'上面\', \'别入\', \'桃仁\', \'半斤\', \'尖皮\', \'双仁\', \'研以\', \'米醋\', \'升无灰酒\', \'硇砂\', \'两同\', \'搅拌\', \'旋入\', \'鳖甲\', \'取出\', \'用盒\', \'鳖甲\', \'泥炙令\', \'黄色\', \'橘皮\', \'两麦\', \'沉香\', \'肉豆蔻\', \'丁香\', \'木香\', \'槟榔\', \'一半\', \'一半\', \'陈曲\', \'两京\', \'三棱\', \'大黄\', \'厚朴\', \'粗皮生\', \'姜汁\', \'炒令\', \'柴胡\', \'去苗\', \'半斤\', \'桂去\', \'粗皮\']');
INSERT INTO `prescript` VALUES (58761, '1011', '鳖血丸', '人参半两，川芎1两，芜荑1两，北柴胡1两，使君子21个，胡黄连2两，川黄连2两。', '每服20丸，食前熟水送下。', '《得效》卷十二', '小儿疳痨。', '-', '-', '柴胡', '[\'人参\', \'半两\', \'川芎\', \'两芜\', \'两北\', \'柴胡\', \'使君子\', \'胡黄连\', \'两川\', \'黄连\']');
INSERT INTO `prescript` VALUES (58762, '1005', '丹皮柴胡犀角汤', '丹皮3钱，柴胡3钱，生地3钱，白芍3钱，茯苓3钱，犀角1钱（研汁），炙草2钱。', '水煎，温服。', '《医学金针》卷六', '狂病，喜怒乖常。', '-', '-', '柴胡', '[\'丹皮\', \'柴胡\', \'生地\', \'白芍\', \'茯苓\', \'犀角\', \'研汁\']');
INSERT INTO `prescript` VALUES (58763, '993', '羚羊角丸', '羚羊角（镑）3分，茯神1两（去木），肉苁蓉1两（酒浸，切倍），防风1两（去叉），赤芍药1两，人参1两，柴胡1两（去苗），旋覆花1两，桃仁1两（汤浸，去皮尖双仁，炒），独活1两（去芦头），郁李仁1两（汤去皮，炒），熟干地黄（焙）1两，生干地黄（焙）1两半。', '每服30丸，煎黄耆汤送下。', '《圣济总录》卷一五○', '妇人血风劳气，头痛，胸背气注拘急，筋脉骨节痛，心烦悸，腰腿无力，肌肉瘦悴。', '-', '-', '柴胡', '[\'羚羊角\', \'茯神\', \'两去\', \'肉苁蓉\', \'两酒\', \'浸切\', \'防风\', \'两去\', \'芍药\', \'人参\', \'柴胡\', \'两去\', \'旋覆花\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁\', \'独活\', \'两去\', \'芦头\', \'郁李仁\', \'两汤\', \'去皮\', \'炒熟\', \'地黄\', \'两生\', \'地黄\', \'两半\']');
INSERT INTO `prescript` VALUES (58764, '990', '鳖甲散', '鳖甲（涂醋，炙令黄，去裙襕）1两，柴胡（去苗）1两，川升麻1两，乌悔肉1两，枳实（麸炒微黄）1两，犀角屑1两，黄芩1两，甘草半两（炙微赤，锉）。', '鳖甲犀角汤（《伤寒总病论》卷五）、鳖甲汤（《圣济总录》卷二十一）。', '《圣惠》卷十三', '坏伤寒。经10日以来未解，热在胸膈，烦闷不止。', '-', '-', '柴胡', '[\'鳖甲\', \'涂醋\', \'炙令\', \'黄去\', \'柴胡\', \'去苗\', \'两川\', \'升麻\', \'两乌悔\', \'枳实\', \'微黄\', \'犀角\', \'黄芩\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (58765, '942', '鳖甲散', '鳖甲1两半（涂醋，炙令黄，去裙襕），柴胡1两半（去苗），秦艽半两（去苗），紫菀半两（洗去苗土），桔梗3分（去芦头），麻黄3分（去根节），桃仁1两（汤浸，去皮尖双仁，麸炒微黄），知母3分，半夏半两（汤洗7遍，去滑），桂心半两，陈橘皮半两（汤浸，去白瓤，焙），黄耆3分（锉）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓，不拘时候温服。', '《圣惠》卷十四', '伤寒后夹劳。四肢烦热，骨节疼痛，不思饮食，时时咳嗽，心胸痰壅。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'黄去\', \'柴胡\', \'两半\', \'去苗\', \'秦艽\', \'半两\', \'去苗\', \'紫菀\', \'半两\', \'苗土\', \'桔梗\', \'分去\', \'芦头\', \'麻黄\', \'分去\', \'根节\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'知母\', \'半夏\', \'半两\', \'洗遍\', \'滑桂心\', \'半两\', \'橘皮\', \'半两\', \'汤浸\', \'黄耆\']');
INSERT INTO `prescript` VALUES (58766, '937', '鳖甲丸', '鳖甲1两（涂醋，炙微黄，去裙襕），人参3分（去芦头），赤芍药1两，诃黎勒3分（煨，用皮），枳壳2两（麸炒微黄，去瓤），白术半两，川大黄1两半（锉碎，微炒），柴胡1两（去苗）。', '每服30丸，食前以粥饮送下。', '《圣惠》卷三十一', '骨蒸痃癖，气攻腹胁疼痛，四肢羸瘦少力，不欲饮食。', '-', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'微黄\', \'人参\', \'分去\', \'芦头\', \'芍药\', \'两诃黎勒分\', \'枳壳\', \'两麸\', \'微黄\', \'白术\', \'半两\', \'大黄\', \'两半\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (58767, '918', '葛根汤', '葛根（锉，微炒）半两，麻黄（去节）半两，羌活（去芦头）半两，甘草（炙，锉）半两，枳壳（去瓤，麸炒）半两，杏仁（汤浸，去皮尖双仁，炒）1分，升麻1两，黄芩（去黑心）1两，大黄（锉，炒）1两，柴胡（去苗）3分，芍药3分，钩藤1分，蛇蜕（微炙）3寸，蚱蝉2枚（去翅，微炒），石膏（碎）1两半。', '每服1钱匕，以水半盏，煎至3分，入竹沥少许，更煎1-2沸，去滓，分3次温服。', '《圣济总录》卷一七二', '小儿初生，至百晬前后，惊痫连发不醒，及胎中感风，体冷面青，筋急反张。', '-', '-', '柴胡', '[\'葛根\', \'半两\', \'麻黄\', \'去节\', \'半两\', \'羌活\', \'芦头\', \'半两\', \'甘草\', \'半两\', \'枳壳\', \'半两\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'炒分\', \'升麻\', \'黄芩\', \'黑心\', \'大黄\', \'柴胡\', \'苗分\', \'芍药\', \'钩藤\', \'蛇蜕\', \'微炙寸\', \'蚱蝉\', \'石膏\', \'两半\']');
INSERT INTO `prescript` VALUES (58768, '902', '黄连厚朴汤', '黄连（酒炒）1钱，楂肉1钱，连翘1钱，陈皮1钱，山栀1钱，柴胡5分，厚朴1钱，六一散2钱半。', '加生姜，水煎，二更时热服。', '《风痨臌膈》', '膨胀，独肚腹团团而便坚，脉实大洪数者，乃心脾二经积热，克制金水，而肺胃清气不升，而失下润之化也。', '-', '-', '柴胡', '[\'黄连\', \'连翘\', \'陈皮\', \'钱山\', \'栀钱\', \'柴胡\', \'厚朴\', \'六一散\', \'钱半\']');
INSERT INTO `prescript` VALUES (58769, '854', '鳖甲汤', '鳖甲（去裙襕，醋炙焦）2两，人参2两，柴胡（去苗）2两，当归（切，焙）2两，枳壳（去瓤，麸炒）2两，甘草（炙）半两，桃仁7个（汤浸，去皮尖），白槟榔（煨）2个。', '先用童便2盏，浸药3钱匕，经半日，煎取7分，去滓温服，以愈为度。妇人病状同者，加牛膝半两。', '《圣济总录》卷七十九', '水气。面目浮肿，因虚劳脚气所致。', '-', '-', '柴胡', '[\'鳖甲\', \'人参\', \'柴胡\', \'当归\', \'切焙\', \'枳壳\', \'甘草\', \'半两\', \'桃仁\', \'浸去\', \'皮尖\', \'槟榔\']');
INSERT INTO `prescript` VALUES (58770, '849', '椒姜丸', '蜀椒（去目及闭口，炒出汗）1两1分，干姜（炮）1两，厚朴（去粗皮，涂生姜汁炙）1两，黄芩（去黑心）1两，细辛（去苗叶）1两，芍药1两，桂（去粗皮）1两，桔梗（炒）半两，鸟喙（炮裂，去皮脐）1分，柴胡（去苗）1分，白茯苓（去黑皮）1分，牡丹皮1分。', '每服20丸，温酒或米饮送下，1日3次。', '《圣济总录》卷九十四', '七疝诸寒在脐旁痛，上冲胸中满，少气。', '-', '-', '柴胡', '[\'蜀椒\', \'目及\', \'闭口\', \'出汗\', \'两分\', \'干姜\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'黄芩\', \'黑心\', \'细辛\', \'苗叶\', \'芍药\', \'两桂去\', \'粗皮\', \'桔梗\', \'半两\', \'炮裂\', \'去皮\', \'脐分\', \'柴胡\', \'苗分\', \'白茯苓\', \'黑皮\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (58771, '833', '鳖甲丸', '黄耆1两，柴胡1两，枳壳1两，知母1两，白茯苓1两，沉香1两，人参1两，附子1两，木香1两，升麻1两，肉桂1两，胡黄连1两，杏仁1两，当归1两，常山1两，羌活1两，荆三棱1两，乌梅肉1两，安息香（明者，同胡桃肉细研）1两。', '丈夫妇人15岁已上，每服20-30丸，温酒送下；妇人荆芥酒送下。所煮膏子须吃斟酌多少，勿令剩下，但少些不妨，却别熬酒，若膏剩，恐鳖不全故也。凡服此药恐热，3-5更须服八仙饮子1服解之。', '《普济方》卷三一九', '男子、妇人、童男、室女五劳七伤，传疰，伏连，飞尸，尸注，八极，骨蒸肺痿，黄瘦虚劳无肉，肌肉不生；妇人血蒸，五心烦热，血蒸劳气；室女月闭，黄瘦，气块肠痛，经脉不调，干嗽，咽膈不利，症瘕积块，脸赤口疮。', '-', '-', '柴胡', '[\'黄耆\', \'柴胡\', \'枳壳\', \'知母\', \'白茯苓\', \'沉香\', \'人参\', \'附子\', \'木香\', \'升麻\', \'肉桂\', \'胡黄连\', \'杏仁\', \'当归\', \'常山\', \'羌活\', \'荆三棱\', \'乌梅\', \'安息香\', \'明者\', \'胡桃肉\', \'细研\']');
INSERT INTO `prescript` VALUES (58772, '830', '鳗鲡鱼煎丸', '大白鳗鲡鱼3条（用醋汤洗净，段截后以无灰酒于银锅内慢火煮熟，滤出取肉，细研，再入酒2升，慢火煎成煎），青蒿子1两，桔梗（锉，炒）1两，秦艽（去苗土）1两，柴胡（去苗）1两，知母（焙）1两，甘草（炙，锉）1两，鳖甲（九肋者，去裙襕，醋浸3日后，炙令黄熟）1两，人参1两，附子（炮裂，去皮脐）1两。', '每服20丸，空心、午后、临卧用炒栝楼根酒送下。', '《圣济总录》卷八十六', '肺劳，咳嗽日久。', '-', '-', '柴胡', '[\'大白\', \'鳗鲡\', \'鱼条\', \'洗净\', \'截后\', \'无灰酒\', \'银锅\', \'慢火\', \'煮熟\', \'滤出\', \'取肉\', \'细研\', \'再入\', \'酒升\', \'慢火\', \'煎成\', \'青蒿\', \'桔梗\', \'秦艽\', \'苗土\', \'柴胡\', \'知母\', \'甘草\', \'鳖甲\', \'九肋者\', \'日后\', \'炙令\', \'黄熟\', \'人参\', \'附子\', \'炮裂\', \'去皮\']');
INSERT INTO `prescript` VALUES (58773, '821', '鳖甲散', '鳖甲（去裙襕，醋浸，炙）半两，常山（锉）半两，蜀漆叶半两，乌贼鱼骨（去甲）半两，附子（炮裂，去皮脐）半两，知母（切，焙）1分，蜀椒（去目及闭口，炒出汗）1分，黄耆（锉）3分，柴胡（去苗）3分。', '每服3钱匕，以酒1盏，渍1宿，平旦温服，未发前再1服。', '《圣济总录》卷三十五', '劳疟。久作不已，日致憔悴，势渐危困。', '-', '-', '柴胡', '[\'鳖甲\', \'半两\', \'常山\', \'半两\', \'漆叶\', \'半两\', \'乌贼\', \'鱼骨\', \'去甲\', \'半两\', \'附子\', \'炮裂\', \'去皮\', \'半两\', \'知母\', \'切焙分\', \'目及\', \'闭口\', \'出汗\', \'分黄耆\', \'柴胡\', \'苗分\']');
INSERT INTO `prescript` VALUES (58774, '818', '鳖甲散', '鳖甲2两（涂醋，炙令黄，去裙襕），柴胡2两（去苗），赤芍药1两，甘草1两（炙微赤，锉），赤茯苓1两，枳壳1两（麸炒微黄，去瓤），人参1两（去芦头），地骨皮3分。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '热劳，四肢疼痛，发渴寒热。', '忌苋菜、菘菜、醋物。', '-', '柴胡', '[\'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'柴胡\', \'两去\', \'芍药\', \'甘草\', \'两炙微\', \'茯苓\', \'枳壳\', \'两麸\', \'微黄\', \'人参\', \'两去\', \'芦头\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (58775, '814', '葛根散', '葛根（锉）3分，黄芩（去黑心）3分，甘草（炙）半两，柴胡（去苗）半两，黄连（去须）半两，牛黄（研）半两。', '每服2钱匕，新汲水半盏调下，1日3次。', '《圣济总录》卷八十七', '热劳。心神不宁，肌瘦烦渴。', '-', '-', '柴胡', '[\'葛根\', \'锉分\', \'黄芩\', \'黑心\', \'甘草\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'黄连\', \'半两\', \'牛黄\', \'半两\']');
INSERT INTO `prescript` VALUES (58776, '806', '鳖肉煎丸', '生鳖肉半斤（治如食法），黄芩（去黑心）半两，柴胡（去苗）半两，蜣螂（去翅头足，炒）半两，鼠妇（炒，去足）1分，干姜（炮）1分，大黄（生锉）1分，海藻（洗去咸汁，焙）1分，葶苈子（纸上炒）1分，桂（去粗皮）1分，牡丹皮1分，厚朴（去粗皮，生姜汁炙，锉）1分，紫菀（切，焙）1分，瞿麦（去梗）1分，半夏（汤洗去滑，焙）1分，人参1分，大戟（锉）1分，（庶虫）虫（炒）1分，射干（炮）1分，阿胶（炙燥）1分，桃仁（汤浸，去皮尖双仁，别研）1分，石苇（去毛）1分，赤芍药1分，桑螵蛸（炒）1两。', '每服20丸，未发前温酒送下。', '《圣济总录》卷三十五', '疟久不愈，结为症瘕，名曰疟母。', '-', '-', '柴胡', '[\'鳖肉\', \'半斤\', \'治如\', \'食法\', \'黄芩\', \'黑心\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'蜣螂\', \'头足\', \'半两\', \'鼠妇\', \'足分\', \'干姜\', \'炮分\', \'大黄\', \'海藻\', \'洗去\', \'咸汁\', \'焙分\', \'葶苈子\', \'纸上\', \'分桂去\', \'粗皮分\', \'牡丹皮\', \'厚朴\', \'粗皮生\', \'姜汁\', \'紫菀\', \'切焙分\', \'瞿麦\', \'梗分\', \'半夏\', \'汤洗\', \'焙分\', \'人参\', \'大戟\', \'锉分\', \'虫虫\', \'炒分\', \'射干\', \'炮分\', \'阿胶\', \'炙燥\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁别\', \'研分\', \'石苇\', \'毛分\', \'芍药\', \'桑螵蛸\']');
INSERT INTO `prescript` VALUES (58777, '784', '鳖甲煎丸', '鳖甲3两（水浸3日3夜，去裙，米醋蘸炙令脆，为末），桃仁100个（汤浸，去皮尖，炒黄，细研），硇砂（汤化，去石，煎成霜）3分，上药用酽醋4升，砂盆中慢火熬成膏，更入后药：厚朴（去皮，生姜汁炙）1两，陈皮（去白）1两，神曲（炒）1两，肉桂（去皮）1两，肉豆蔻4个，槟榔2两，柴胡（去苗）半两。', '每服20-30丸，以细切生葱热酒送下。', '《普济方》卷二四九引《卫生家宝》', '小肠气发不可忍，并治淋。', '忌生冷、油腻、湿面。', '-', '柴胡', '[\'鳖甲\', \'水浸\', \'日夜\', \'米醋\', \'令脆\', \'桃仁\', \'浸去\', \'皮尖\', \'黄细研\', \'硇砂\', \'汤化\', \'去石\', \'煎成\', \'霜分\', \'药用\', \'升砂\', \'盆中\', \'慢火\', \'成膏\', \'更入\', \'厚朴\', \'去皮\', \'姜汁\', \'陈皮\', \'神曲\', \'肉桂\', \'去皮\', \'肉豆蔻\', \'槟榔\', \'柴胡\', \'去苗\', \'半两\']');
INSERT INTO `prescript` VALUES (58778, '779', '麻黄散', '麻黄1两（去根节），桂心1两，甘草半两（炙微赤，锉），赤芍药1两，石膏2两，柴胡1两（去苗）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠5月6月，伤寒头疼，壮热，四肢烦疼。', '-', '-', '柴胡', '[\'麻黄\', \'两去\', \'根节\', \'桂心\', \'甘草\', \'半两\', \'炙微\', \'芍药\', \'石膏\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (58779, '706', '鳖甲散', '银柴胡1钱半，胡连1钱，秦艽1钱，鳖甲（醋炙）1钱，地骨1钱，青蒿1钱，知母1钱，甘草5分，当归8两，白芍8两，生地8两。', '嗽，加阿胶、麦冬、五味。', '《嵩崖尊生》卷十一', '骨蒸热。', '-', '-', '柴胡', '[\'银柴胡\', \'钱半\', \'胡连钱\', \'秦艽\', \'鳖甲\', \'地骨\', \'青蒿\', \'知母\', \'甘草\', \'当归\', \'白芍\', \'生地\']');
INSERT INTO `prescript` VALUES (58780, '703', '跌打损伤酒', '柴胡2两，黄芩1两，五灵脂1两，桃仁1两，当归2两，赤芍1两，川芎2两，红花7钱，苏木1两，续断1两，骨碎补1两，三棱7钱，伏水18支，莪术7钱。', '《中药制剂手册》有乳香、无莪术。每次冷饮一至二两，一日二次。外用涂于患处。', '《全国中药成药处方集》（重庆方）', '舒筋活血、止痛。主由跌打损伤引起的瘀血凝滞，肿痛不消，筋络不舒，皮肉青紫。', '气虚血亏者及孕妇忌服。', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'五灵脂\', \'桃仁\', \'当归\', \'赤芍\', \'川芎\', \'两红\', \'花钱\', \'苏木\', \'续断\', \'骨碎补\', \'三棱\', \'伏水\', \'莪术\']');
INSERT INTO `prescript` VALUES (58781, '700', '漏芦汤', '漏芦（去芦头）1两，连翘1两，木通（锉）1两，桂（去粗皮）1两，犀角屑1两，黄芩（去黑心）1两，柴胡（去苗）1两，玄参1两，大黄（锉，炒）1两，知母（焙）1两。', '本方方名，《普济方》引作“知母汤”。', '《圣济总录》卷一二六', '瘰疬初结，时发寒热。', '-', '-', '柴胡', '[\'漏芦\', \'芦头\', \'连翘\', \'木通\', \'两桂去\', \'粗皮\', \'犀角\', \'黄芩\', \'黑心\', \'柴胡\', \'玄参\', \'大黄\', \'知母\']');
INSERT INTO `prescript` VALUES (58782, '656', '葛根汤', '柴胡1钱5分，葛根2钱，党参（生，去芦）1钱5分，防风1钱5分，荆芥1钱5分，甘草6分，生姜2片。', '-', '《不知医必要》卷一', '外感风邪，发热兼渴。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'葛根\', \'党参\', \'生去\', \'芦钱分\', \'防风\', \'钱分\', \'荆芥\', \'钱分\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (58783, '631', '丹砂丸', '丹砂2两（飞研），柴胡（去苗，为末）4两。', '每服10丸，用桃仁、乌梅煎汤送下，1日3次。', '《圣济总录》卷一七七', '小儿骨热，15岁以下骨蒸热劳，遍身如火，日渐黄瘦，夜卧多汗，咳嗽烦渴。', '-', '-', '柴胡', '[\'丹砂\', \'两飞研\', \'柴胡\', \'苗为\']');
INSERT INTO `prescript` VALUES (58784, '512', '鳖甲散', '鳖甲1两半（涂醋，炙令黄，去裙襕），吴茱萸半两（汤浸7遍，焙干，微炒），郁李仁1两（汤浸，去皮，微炒），京三棱1两（炮裂），枳实3分（麸炒微黄），柴胡3分（去苗），桂心3分，槟榔1两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，食前稍热服。', '《圣惠》卷四十八', '伏梁。气横在心下，坚硬妨闷，不能食。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'涂醋\', \'炙令\', \'黄去\', \'吴茱萸\', \'半两\', \'浸遍\', \'焙干\', \'郁李仁\', \'两汤\', \'去皮\', \'炒京\', \'三棱\', \'两炮裂\', \'枳实\', \'分麸\', \'微黄\', \'柴胡\', \'分去\', \'苗桂心分\', \'槟榔\']');
INSERT INTO `prescript` VALUES (58785, '469', '睾丸汤', '白芍2两，小茴香3钱，橘核1钱，柴胡1钱，沙参5钱。', '水煎服。', '《辨证录》卷九', '平肝气。主厥阴受寒，睾丸作痛，两胁胀痛，按之益疼者。', '-', '-', '柴胡', '[\'白芍\', \'小茴香\', \'橘核\', \'柴胡\', \'沙参\']');
INSERT INTO `prescript` VALUES (58786, '466', '鳖甲猪肚丸', '北柴胡1两，黄连7钱，枳实1两半，木香1两半，青皮1两半，九肋鳖甲（醋炙）1两，大虾蟆（干者）1个（炙焦），青藁（干者）7钱。', '每服20-30丸，食后人参汤送下。', '《育婴秘诀》卷四', '小儿病疟，腹中有痞，发热连年不已，欲成疳痨者。', '-', '-', '柴胡', '[\'柴胡\', \'黄连\', \'枳实\', \'两半\', \'木香\', \'两半\', \'青皮\', \'两半\', \'鳖甲\', \'两大\', \'虾蟆\', \'干者\', \'焦青\', \'干者\']');
INSERT INTO `prescript` VALUES (58787, '461', '鳖甲散', '鳖甲1两半（醋炙），常山1两（生），大黄1两（炮），甘草3分（半生半熟），柴胡1两（去芦，焙），石膏1两。', '本方方名，《普济方》引作“小鳖甲散”。', '《博济》卷一', '五心烦热，饮食减少，状似劳气。', '-', '-', '柴胡', '[\'鳖甲\', \'两半\', \'常山\', \'两生\', \'大黄\', \'两炮\', \'甘草\', \'半生半熟\', \'柴胡\', \'两去\', \'芦焙\', \'石膏\']');
INSERT INTO `prescript` VALUES (58788, '446', '解明散', '当归1两，赤芍药1两，黄芩1两，菊花1两，柴胡1两，地骨皮1两，车前子1两，桔梗1两，生地黄1两，栀子1两，连翘1两。', '水煎服。', '《银海精微》卷下', '气旺而血衰，阳多阴少，热邪归于心，致患眼痛而体热者。', '-', '-', '柴胡', '[\'当归\', \'两赤\', \'芍药\', \'黄芩\', \'菊花\', \'柴胡\', \'地骨皮\', \'车前子\', \'桔梗\', \'生地黄\', \'栀子\', \'连翘\']');
INSERT INTO `prescript` VALUES (58789, '412', '蜡杏汤', '柴胡1钱，桔梗1钱，甘草1钱，老鼠刺根2钱，黄芩2钱，茴香（炒）2钱，罂粟壳（去蒂根，蜜炙）2钱，胡椒2钱，乌梅1钱，黄蜡1钱，杏仁1钱，款冬花1钱。', '上以新瓦瓶盛水3-4碗，安药在内密封，慢火煨熟。睡至四更暖服1盏，吃尽用好酒3-4碗，再煨滓服，亦如前。妇人，加当归；男子，加人参；吐血，加阿胶、北五味子。', '《普济方》卷二三五引《医学切问》', '远年近日劳疾。', '-', '-', '柴胡', '[\'柴胡\', \'桔梗\', \'甘草\', \'老鼠\', \'刺根\', \'黄芩\', \'茴香\', \'罂粟壳\', \'去蒂根\', \'蜜炙钱\', \'胡椒\', \'乌梅\', \'黄蜡\', \'杏仁\', \'款冬花\']');
