INSERT INTO `prescript` VALUES (58792, '380', '鳖甲丸', '人参半两，牡丹皮半两，槟榔半两，吴茱萸半两，盐半两，肉豆蔻半两，赤芍药半两，泽泻半两，木香半两，远志半两，缩砂仁半两，枳壳半两，柴胡半两，麻黄半两，麝香半两，乌梅2两，知母1两，升麻1两，甘草1两，鳖甲1两，苁蓉1两，白蔹1两，葳蕤1两，虎骨1两，桃仁1两，羌活1两，防风1两，茯苓1两，附子1两，青蒿1两，秦艽1两，厚朴1两，牛膝1两，半夏1两，桂1两。', '每服30丸，空心温酒送下。', '《鸡峰》卷九', '风劳，肌瘦面黄。', '-', '-', '柴胡', '[\'人参\', \'半两\', \'牡丹皮\', \'半两\', \'槟榔\', \'半两\', \'吴茱萸\', \'半两\', \'半两\', \'肉豆蔻\', \'半两\', \'芍药\', \'半两\', \'泽泻\', \'半两\', \'木香\', \'半两\', \'远志\', \'半两\', \'砂仁\', \'半两\', \'枳壳\', \'半两\', \'柴胡\', \'半两\', \'麻黄\', \'半两\', \'麝香\', \'半两\', \'乌梅\', \'知母\', \'升麻\', \'甘草\', \'鳖甲\', \'苁蓉\', \'白蔹\', \'葳蕤\', \'虎骨\', \'桃仁\', \'羌活\', \'防风\', \'茯苓\', \'附子\', \'青蒿\', \'秦艽\', \'厚朴\', \'牛膝\', \'半夏\', \'两桂\']');
