INSERT INTO `prescript` VALUES (58820, '51639', '麻黄柴胡升麻汤', '麻黄 草豆蔻 益智仁各4.5克 吴茱萸 厚朴各0.6克 当归梢 甘草 柴胡 生黄芩各0.3克 升麻 神曲 苏木各1.5克 全蝎2个 红花少许', '上药锉碎，分二服。用水150毫升，煎至100毫升，空腹时服。微有汗则效。', '《兰室秘藏》卷下', '治小儿寒郁而喘，喉鸣，腹胀肠鸣，鼻流清涕，脉沉数。', '服药后宜避风寒。', '-', '升麻', '[\'麻黄\', \'草豆蔻\', \'益智仁\', \'吴茱萸\', \'厚朴\', \'当归\', \'甘草\', \'柴胡\', \'黄芩\', \'升麻\', \'神曲\', \'苏木\', \'全蝎\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (58821, '51626', '清魂汤', '柴胡 生甘草 酒黄柏各6克 升麻 泽泻各4.5克 当归梢 羌活 麻黄根 汉防己 草龙胆 茯苓各3克 红花少许 五味子20个', '分作二服。用水300毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治两睾丸冷，两大腿内侧出汗，阳痿，阴囊湿痒气秽。', '服药期间忌酒、面、房事。', '-', '升麻', '[\'柴胡\', \'生甘草\', \'黄柏\', \'升麻\', \'泽泻\', \'当归\', \'羌活\', \'麻黄根\', \'防己\', \'龙胆\', \'茯苓\', \'红花\', \'少许\', \'五味子\']');
