INSERT INTO `prescript` VALUES (58840, '51215', '升陷汤', '生黄耆18克 知母9克 柴胡4.5克 桔梗4.5克 升麻3克', '水煎服。\r\n气分虚极下陷者，酌加人参、萸肉。', '《医学衷中参西录》上册', '治胸中大气下陷，气短不足以息，或努力呼吸，有似乎喘，或气息将停，危在顷刻。兼见寒热往来，或咽干作渴，或满闷怔忡，或神昏健忘，脉沉迟微弱。', '-', '-', '升麻', '[\'生黄\', \'耆克\', \'知母\', \'柴胡\', \'桔梗\', \'升麻\']');
INSERT INTO `prescript` VALUES (58841, '51201', '升麻黄芩汤', '升麻 葛根 黄芩 芍药各9克 甘草4.5克（炙）', '每服6克，以水200毫升，煎至120毫升，去滓温服。', '《类证活人书》卷二十', '治小儿伤风有汗，头疼，发热恶寒。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'黄芩\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (58842, '51169', '射干散', '射干 天竺黄（研）马牙消（研）各30克 犀角屑 玄参 川升麻 白矾 白药 黄药 炙甘草各15克', '不拘时，以绵裹1丸，含化咽津。', '《奇效良方》卷六十一', '清热解毒，宣肺利咽。治热毒上攻，悬痈肿痛，咽喉不利，胸中烦热。', '-', '-', '升麻', '[\'射干\', \'天竺黄\', \'研马牙\', \'消研\', \'犀角\', \'玄参\', \'升麻\', \'白矾\', \'白药\', \'黄药\', \'甘草\']');
INSERT INTO `prescript` VALUES (58843, '51167', '龙胆丸', '龙胆草 川升麻 苦楝根皮（焙）防风 赤茯神 芦荟 油发灰各6克 青黛（干）黄连（净）各9克', '每服20丸，薄荷、紫苏泡汤下，食后仍以芦荟末入鼻。', '《直指小儿方》卷三', '治脑疳，脑热如火，满头饼疮。', '-', '-', '升麻', '[\'龙胆草\', \'升麻\', \'苦楝\', \'根皮\', \'防风\', \'茯神\', \'芦荟\', \'油发\', \'青黛干\', \'黄连\']');
INSERT INTO `prescript` VALUES (58844, '51164', '益气止淋汤', '泡参 杜仲 续断各9克 制益智 茯苓各6克 炒前仁 甘草捎各4.5克 升麻2.4克', '水煎服。', '《中医妇科治疗学》', '补气升提。治妊娠数月，小便频数而痛，尿量不减，色白，有时呈淡蓝色，欲解不能，腰部作胀，舌淡苔正常，脉缓无力。', '-', '-', '升麻', '[\'泡参\', \'杜仲\', \'续断\', \'克制\', \'益智\', \'茯苓\', \'前仁\', \'甘草\', \'升麻\']');
INSERT INTO `prescript` VALUES (58845, '51092', '发声散', '升麻 桔梗 川芎 桑白皮各30克 甘草 羌活 马兜铃各15克', '每服3克，水150毫升，入竹茹、薄荷，同煎至90毫升，去滓，食后温服。', '《御药院方》卷九', '主语声不出，胸满短气，涎嗽喘闷，咽喉噎塞。（嘶哑）', '-', '-', '升麻', '[\'升麻\', \'桔梗\', \'川芎\', \'桑白皮\', \'甘草\', \'羌活\', \'马兜铃\']');
INSERT INTO `prescript` VALUES (58846, '51064', '葛根汤', '葛根6克 升麻 秦艽 荆芥 赤芍各3克 苏叶 白芷各2.4克 甘草1.5克 生姜2片', '水煎服。\r\n若无汗而口渴者，为热入阳明之腑，加知母；自汗而口渴者，加石膏、人参；自汗而口不渴者，乃阳明经中风，去苏叶，加桂枝；若春夏之交，惟恐夹温暑之邪，不便用桂枝，加白术4.5克。', '《医学心悟》卷二', '发汗解肌。治阳明经病，目痛，鼻干，唇焦，漱水不欲咽，脉长。', '-', '-', '升麻', '[\'葛根\', \'升麻\', \'秦艽\', \'荆芥\', \'赤芍\', \'苏叶\', \'白芷\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (58847, '51048', '神效瓜蒌散', '大瓜蒌半个（黄熟者帚个，连皮子瓤，重重纸包，火煨，捣烂，取一半）白芷4.5克 玄参6克 升麻L5克 归尾6克 桔梗3克 连翘6克 柴胡3克 青皮3克 天花粉4.5克 穿山甲（炒）3克 川芎2.4克 知母3克 木通3克 木鳖子2个 延胡索0.6克', '上锉一剂。水煎，温服。', '《寿世保元》卷七', '治妇人乳肿作痛，欲成痈毒。', '-', '-', '升麻', '[\'大瓜\', \'半个\', \'黄熟\', \'皮子\', \'重重\', \'纸包\', \'捣烂\', \'一半\', \'白芷\', \'玄参\', \'升麻\', \'克归尾克\', \'桔梗\', \'连翘\', \'柴胡\', \'青皮\', \'天花粉\', \'穿山甲\', \'炒克\', \'川芎\', \'知母\', \'木通\', \'木鳖子\', \'延胡索\']');
INSERT INTO `prescript` VALUES (58848, '51014', '射干鼠粘子汤', '鼠粘子120克（炒，杵）甘草（炙）升麻 射干各30克', '每服9克，用水300毫升。煎至180毫升，去滓温服。', '《小儿痘疹方论》', '宣肺利晒，泻火解毒。治疮疹壮热，大便坚实，或口舌生疮，咽喉肿痛。', '-', '-', '升麻', '[\'粘子克\', \'甘草\', \'升麻\', \'射干\']');
INSERT INTO `prescript` VALUES (58849, '51009', '漏芦汤', '漏芦（去芦头）升麻 大黄（锉，醋炒）黄芩（去黑心）各30克 蓝叶 玄参（黑坚者）各15克', '每服15克，用水600毫升，加竹叶21片，同煮至300毫升，去滓，下芒消末5克，分三次温服。得利则减，未利则加。', '《圣济总录》卷一三五', '治脏腑积热，发为毒肿，夜间疼痛。', '-', '-', '升麻', '[\'漏芦\', \'芦头\', \'升麻\', \'大黄\', \'黄芩\', \'黑心\', \'蓝叶\', \'玄参\', \'坚者\']');
INSERT INTO `prescript` VALUES (58850, '51008', '补阴益气煎', '人参3～9克 当归6～9克 山药（酒炒）6～9克 熟地9～15克或30～60克 陈皮3克 炙甘草3克 升麻0.9～1.5克 柴胡3～6克', '用水400毫升，加生姜3～7片，煎至320毫升，空腹时温服。', '《景岳全书》卷五十一', '主劳倦伤阴，精不化气，或阴虚内乏，以致外感不解，寒热痰疟，或防虚便结不通。\r\n火浮子上者，去升麻，无外邪者，去柴胡。', '-', '-', '升麻', '[\'人参\', \'当归\', \'克山\', \'药酒\', \'熟地\', \'陈皮克\', \'甘草\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58851, '51004', '知母石膏汤', '知母9克 石膏12克 葛根 萎蕤各6克 甘草 黄芩 升麻 人参 杏仁 羌活 防风各4.5克', '用水300毫升，煎至150毫升，去滓温服。取汗。', '《伤寒总病论》卷五', '治风温之病，因发汗后，身体不恶寒而反恶热，无下证者。', '-', '-', '升麻', '[\'知母\', \'石膏\', \'葛根\', \'萎蕤\', \'甘草\', \'黄芩\', \'升麻\', \'人参\', \'杏仁\', \'羌活\', \'防风\']');
INSERT INTO `prescript` VALUES (58852, '50953', '益智和中汤', '肉桂0.3克 桂枝1.2克 牡丹皮 柴胡 葛根 益智仁 半夏各1.5克 当归身 炙甘草 黄耆 升麻各3克 白芍药4.5克 干姜少许', '水450毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '温中祛寒，益气升阳。治脾胃虚寒，肠辩下血，或血色紫黑，腹部冷痛，得热物熨之则减轻，右关脉弦，按之无力者。', '-', '-', '升麻', '[\'肉桂\', \'桂枝\', \'牡丹皮\', \'柴胡\', \'葛根\', \'益智仁\', \'半夏\', \'当归\', \'甘草\', \'黄耆\', \'升麻\', \'各克白\', \'芍药\', \'干姜\', \'少许\']');
INSERT INTO `prescript` VALUES (58853, '50950', '升阳补气汤', '厚朴（姜制）1.5克 升麻 羌活 白芍药 独活 防风 甘草（炙）泽泻各3克 生地黄4.5克 柴胡7.5克', '每服15克，加生姜3片，大枣2枚，用水300毫升，煎至150毫升，去滓，空腹时大温服。\r\n腹胀，加厚朴；腹中似硬，加砂仁。', '《内外伤辨》卷中', '治饮食不时，饥饱劳役，胃气不足，脾气下溜，气短无力，不耐寒热，饭后昏闷，怠情困倦，五心烦热。', '-', '-', '升麻', '[\'厚朴\', \'姜制克\', \'升麻\', \'羌活\', \'芍药\', \'独活\', \'防风\', \'甘草\', \'泽泻\', \'生地黄\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58854, '50905', '泻荣汤', '连翘 升麻各1.8克 桔梗1.5克 生黄芩 生地黄各1.2克 黄耆 苏木 黄连 地龙 全蝎 当归各0.9克 白豆蔻 人参各0.6克 甘草0.45克 梧桐泪0.3克 麝香少许 桃仁3个 虻虫（去翅、足，炒）3个 水蛭3个（炒令烟尽）', '上锉如麻豆大，除连翘、梧桐泪、白豆蔻另为细末，麝香、虻虫、水蛭三味同为细末，都作一服。水300毫升，酒150毫升，入连翘，煎至150毫升，去滓，再入白豆蔻三味并麝香等，再煎至100毫升，稍热，早饭后、午前服之。', '《兰室秘藏》卷下', '治疠风，满面连头极痒，眉毛脱落。', '服药期间，忌酒、湿面、生冷、硬物。', '-', '升麻', '[\'连翘\', \'升麻\', \'桔梗\', \'克生\', \'黄芩\', \'生地黄\', \'黄耆\', \'苏木\', \'黄连\', \'全蝎\', \'当归\', \'白豆蔻\', \'人参\', \'甘草\', \'梧桐\', \'泪克\', \'麝香\', \'少许\', \'桃仁\', \'炒个\', \'水蛭\', \'令烟\']');
INSERT INTO `prescript` VALUES (58855, '50897', '丽泽通气汤', '黄耆12克 苍术 羌活 独活 防风 升麻 葛根各9克 炙甘草6克 川椒 白芷各3克', '每服15克，加生姜3片，枣2枚，葱白10厘米，同煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷上', '益气升阳，祛风散寒。治肺气不足，外感风寒，鼻塞不闻香臭。', '服药期间，忌食一切冷物，及风寒凉处坐，卧、行、立。', '-', '升麻', '[\'黄耆克\', \'苍术\', \'羌活\', \'独活\', \'防风\', \'升麻\', \'葛根\', \'甘草\', \'克川椒\', \'白芷\']');
INSERT INTO `prescript` VALUES (58856, '50876', '漏芦汤', '漏芦 连翘 白蔹 芒消 甘草各0.8克 大黄3克 升麻 枳实 麻黄 黄芩各1.2克', '用水300毫升，煎取100毫升。儿生一日至七日，取20毫升，分三服；八日至十五日，取30毫升，分三服；十六日至二十日，取40毫升，分三服；二十日至三十日，取60毫升，分三服；三十日至四十日，取100毫升，分三服。', '《备急千金要方》卷五', '治小儿热毒痈疽，丹毒，疮疖，并用于预防时行疮痘。', '-', '-', '升麻', '[\'漏芦\', \'连翘\', \'白蔹\', \'芒消\', \'甘草\', \'大黄\', \'升麻\', \'枳实\', \'麻黄\', \'黄芩\']');
INSERT INTO `prescript` VALUES (58857, '50858', '加减补中益气汤', '人参6克 黄耆6克 广皮3克 炙甘草3克 归身6克 炒白芍9克 防风1.5克 升麻0.9克', '用水800毫升，煮取300毫升，分三次温服。', '《温病条辨》卷二', '补气升阳。治气虚下陷，门户不藏，下利不止。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'克广皮克\', \'甘草\', \'克归\', \'身克\', \'白芍\', \'防风\', \'升麻\']');
INSERT INTO `prescript` VALUES (58858, '50850', '人参益气汤', '黄耆4克 生甘草 人参各15克 白芍药9克 柴胡7.5克 炙甘草 升麻各6克 五味子140个', '每服用水300毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治热伤元气，两手指麻木，四肢困倦，怠惰嗜卧。', '-', '-', '升麻', '[\'黄耆克\', \'生甘草\', \'人参\', \'各克白\', \'芍药\', \'柴胡\', \'甘草\', \'升麻\', \'五味子\']');
