INSERT INTO `prescript` VALUES (58860, '50832', '芍药汤', '赤芍药 犀角（镑）木通（锉）石膏（碎）升麻各60克 甘草（生，锉）朴消 玄参 麦门冬（去心，焙）各30克', '每服6克，用水220毫升，煎至180毫升，去滓温服，不计时候。', '《圣济总录》卷一二九', '治胃脘蓄热，结聚成痈。', '-', '-', '升麻', '[\'芍药\', \'犀角\', \'木通\', \'石膏\', \'升麻\', \'甘草\', \'朴消\', \'玄参\', \'麦门冬\']');
INSERT INTO `prescript` VALUES (58861, '50776', '蕤仁丸', '蕤仁90克 车前子 黄连（去须）各60克 麦门冬（去心，焙）23克 青葙子（汤浸）黄芩（去黑心）秦艽（去苗）生地黄 羚羊角末 防风（去叉）各45克 人参 天门冬（焙，去心）丹砂 升麻 苦参（炒）地肤子 菊花 玄参（炒）羌活（去芦）决明子（炒）地骨皮 甘草（炙）各38克', '每服20丸，加至30丸，食后煎百合汤送下。', '《奇效良方》卷五十', '治内外障眼。', '-', '-', '升麻', '[\'蕤仁\', \'车前子\', \'黄连\', \'麦门冬\', \'心焙克\', \'青葙子\', \'汤浸\', \'黄芩\', \'黑心\', \'秦艽\', \'去苗\', \'生地黄\', \'羚羊角\', \'防风\', \'人参\', \'天门冬\', \'丹砂\', \'升麻\', \'苦参\', \'地肤子\', \'菊花\', \'玄参\', \'羌活\', \'去芦\', \'决明子\', \'地骨皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (58862, '50765', '芍药汤', '芍药 防风（去叉）麻黄（去根、节，先煎，掠去沫，焙干用）各22.5克 葛根（锉）30克 黄芩（去黑心）防己 桂（去粗皮）各15克 干姜（炮裂）30克 白术 人参 独活（去芦头）芎藭 竹沥（旋入）升麻 牛膝（去苗，锉，微炒）石膏（碎）陈橘皮（烫去白，焙）羚羊角（镑屑）五加皮（炙）各15克', '每用12克，以水400毫升，煎取200毫升，去滓，入竹沥60毫升，微煎，空腹时温服，一日三次。', '《圣济总录》卷九', '治中风半身不遂。', '-', '-', '升麻', '[\'芍药\', \'防风\', \'麻黄\', \'根节\', \'掠去\', \'焙干\', \'葛根\', \'锉克\', \'黄芩\', \'黑心\', \'防己\', \'桂去\', \'粗皮\', \'干姜\', \'炮裂克\', \'白术\', \'人参\', \'独活\', \'芦头\', \'竹沥\', \'旋入\', \'升麻\', \'牛膝\', \'石膏\', \'橘皮\', \'羚羊角\', \'五加皮\']');
INSERT INTO `prescript` VALUES (58863, '50729', '补中汤', '升麻 柴胡 当归各0.6克 神曲0.9克（炒）泽泻1.2克 大麦芽面 苍术各1.5克 黄耆7.5克 炙甘草2.4克 红花少许 五味子20个', '上药哎咀，分作二服。用水300毫升，煎取150毫升，去滓，空腹时服。', '《兰室秘藏》卷下', '主面黄，汗多，目赤，四肢沉重，食欲不振，腹中时痛，咳嗽，两手寸脉短，右手脉弦细兼涩，关脉虚。', '-', '-', '升麻', '[\'升麻\', \'柴胡\', \'当归\', \'神曲\', \'泽泻\', \'大麦芽\', \'苍术\', \'黄耆\', \'甘草\', \'红花\', \'少许\', \'五味子\']');
INSERT INTO `prescript` VALUES (58864, '50697', '导气汤', '黄耆24克 甘草18克 青皮12克 升麻 柴胡 当归梢 泽泻各6克 橘皮3克 红花3克 五味子120个', '每服用水400毫升，煎至200毫升，去滓，空腹时热服。', '《兰室秘藏》卷下', '主两腿麻木沉重。', '-', '-', '升麻', '[\'黄耆克\', \'甘草\', \'青皮\', \'升麻\', \'柴胡\', \'当归\', \'泽泻\', \'橘皮\', \'红花\', \'五味子\']');
INSERT INTO `prescript` VALUES (58865, '50694', '托里透脓汤', '人参 白术 穿山甲（炒，研）白芷各3克 升麻 甘草节各1.5克 当归6克 生黄耆9克 皂角刺4.5克 青皮15克（炒）', '用水600毫升，煎至200毫升。病在上部，先饮煮酒适量，后热服此药；病在下部，先服药，后饮酒；疮在中部，药内对酒热服。', '《医宗金鉴》卷六十三', '治痈疽已成未溃。', '-', '-', '升麻', '[\'人参\', \'白术\', \'穿山甲\', \'炒研\', \'白芷\', \'升麻\', \'甘草\', \'当归\', \'克生\', \'黄耆克\', \'皂角刺\', \'青皮\']');
INSERT INTO `prescript` VALUES (58866, '50688', '天麻黄膏汤', '天麻 芍药 神曲（炒）羌活（肢节不痛 去之）茯苓各0.9克 人参 黄连各1.2克 当归1.5克 黄耆 甘草 升麻 葛根· 黄柏 苍术各1.8克 泽泻2.1克 柴胡2.7克', '上药哎咀，作一服。用水450毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治表有风证，因连日酣饮，其证复来，右口角并眼颇有侧视，及左手左脚腿麻木疼痛。', '-', '-', '升麻', '[\'天麻\', \'芍药\', \'神曲\', \'羌活\', \'肢节\', \'不痛\', \'茯苓\', \'人参\', \'黄连\', \'当归\', \'黄耆\', \'甘草\', \'升麻\', \'葛根\', \'黄柏\', \'苍术\', \'泽泻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (58867, '50685', '麻黄赤芍汤', '麻黄 赤芍各3克 防风 荆芥 羌活 独活 白芷 苍术 威灵仙 片芩 枳实 桔梗 葛根 川芎各1.5克 甘草 归尾 升麻各0.9克', '水煎服。', '《医学入门》卷七', '治温热流注，肢节肿痛。', '-', '-', '升麻', '[\'麻黄\', \'赤芍\', \'防风\', \'荆芥\', \'羌活\', \'独活\', \'白芷\', \'苍术\', \'威灵仙\', \'片芩\', \'枳实\', \'桔梗\', \'葛根\', \'川芎\', \'甘草\', \'归尾\', \'升麻\']');
INSERT INTO `prescript` VALUES (58868, '50682', '紫雪', '黄金3千克 寒水石1.5千克，石膏1.5千克 磁石1.5千克 滑石1.5千克 玄参500克 羚羊角150克（屑）犀角150克（屑）升麻270克 沉香150克 丁子香30克 青木香150克 甘草250克', '病人强壮者，一服3克，当利热毒；老弱人或热毒微者，一服1.5克。', '《外台秘要》卷十八引《苏恭方》', '清热开窍，镇痉安神。治温热病，热邪内陷心包。高热烦躁，神昏谵语，抽搐痉殿，口渴唇焦，尿赤便闭，以及小儿热盛惊厥。', '-', '-', '升麻', '[\'黄金\', \'寒水石\', \'石膏\', \'磁石\', \'滑石\', \'玄参\', \'羚羊角\', \'克屑\', \'犀角\', \'克屑\', \'升麻\', \'沉香\', \'克丁子\', \'香克\', \'青木香\', \'甘草\']');
