INSERT INTO `prescript` VALUES (58870, '50581', '济川煎', '当归9～15克 牛膝6克 肉苁蓉（酒洗去咸）6～9克 泽泻4.5克 升麻2～3克 枳壳3克', '用水220毫升，煎至160～180毫升，空腹时温服。\r\n如气虚者，加人参；肾阴亦虚者，加熟地；虚甚者，枳壳可减去之。', '《景岳全书》卷五十一', '温肾益精，润肠通便。治老年肾虚，大便秘结，小便清长，腰酸足软，背冷畏寒。', '-', '-', '升麻', '[\'当归\', \'牛膝\', \'肉苁蓉\', \'酒洗\', \'泽泻\', \'升麻\', \'枳壳\']');
INSERT INTO `prescript` VALUES (58871, '50572', '百部丸', '百部根90克 升麻15克 桂心 五味子 甘草 紫菀 干姜各30克', '每服3克，食后、临卧前开水送下。', '《备急千金要方》卷十八', '治久新咳嗽，喘息有音，时吐脓血，咽中腥臭，气息不通。', '-', '-', '升麻', '[\'百部\', \'根克\', \'升麻\', \'克桂心\', \'五味子\', \'甘草\', \'紫菀\', \'干姜\']');
INSERT INTO `prescript` VALUES (58872, '50569', '和血益气汤', '柴胡 炙草 生甘草 麻黄根各0.9克 酒当归梢1.2克 酒知母 酒汉肪己 羌活各15克 酒生地黄2.1克 升麻3克 杏仁 桃仁各6个 红花少许 酒黄连2.4克 石膏1.8克 酒黄柏3克', '用水600毫升，煎至300毫升，去滓温服。', '《兰室秘藏》卷上', '治口干舌干，小便数，舌上赤脉。', '-', '-', '升麻', '[\'柴胡\', \'炙草\', \'生甘草\', \'麻黄根\', \'各克酒\', \'当归\', \'梢克酒\', \'知母\', \'酒汉肪己\', \'羌活\', \'各克酒\', \'生地黄\', \'升麻\', \'杏仁\', \'桃仁\', \'各个\', \'红花\', \'少许\', \'黄连\', \'石膏\', \'克酒\', \'黄柏\']');
INSERT INTO `prescript` VALUES (58873, '50564', '平胃地榆汤', '苍术3克 升麻3克 黑附子3克（炮）地榆2.1克 陈皮 厚朴 白术 干姜 白茯苓 葛根各1.5克 甘草（炙）益智仁 人参 当归 神曲（炒）白芍药各0.9克', '上十六味，作一服。用水300毫升，加生姜3片，枣子2个，煎至150毫升，去滓，空腹时温服。', '《卫生宝鉴》卷十六', '温中散寒，除湿和胃。治结阴便血。', '-', '-', '升麻', '[\'苍术\', \'升麻\', \'克黑\', \'附子\', \'克炮\', \'地榆\', \'陈皮\', \'厚朴\', \'白术\', \'干姜\', \'白茯苓\', \'葛根\', \'甘草\', \'益智仁\', \'人参\', \'当归\', \'神曲\', \'炒白\', \'芍药\']');
INSERT INTO `prescript` VALUES (58874, '50485', '升气实脏丸', '黄耆（蜜炙）30克 人参（去芦）30克 白术（土炒）60克 白茯苓（去皮）15克 山药（炒）30克 莲肉（去心）30克 芡实30克 升麻（酒炒）15克 柴胡（酒炒）15克 干姜（炒黑）15克 肉豆蔻（面裹煨，捶去油净）15克 粉草（炙）15克 椿树根皮（酒炒二次）120克', '每服6克，用糯米半生半炒煎汤送下。', '《古今医鉴》卷五', '治久泻，元气下陷，脾胃衰惫，大肠滑脱，肛门坠下，日夜无度，饮食不思，米谷不化，汤水直过，烦渴引饮，津液枯竭，肌瘦如柴，寒热互作。', '-', '-', '升麻', '[\'黄耆\', \'蜜炙克\', \'人参\', \'芦克\', \'白术\', \'炒克\', \'白茯苓\', \'去皮\', \'山药\', \'炒克\', \'莲肉\', \'心克\', \'芡实\', \'升麻\', \'炒克\', \'柴胡\', \'炒克\', \'干姜\', \'黑克\', \'肉豆蔻\', \'面裹\', \'油净克粉\', \'草炙克\', \'椿树\', \'根皮\', \'二次\']');
INSERT INTO `prescript` VALUES (58875, '50481', '熟干地黄丸', '熟干地黄（焙干）120克 杏仁（汤，退去皮、尖、双仁，炒，别研）牛膝（去苗叶，酒浸，焙）各45克 苦参（细锉，，焙干）菟丝子（酒浸，焙，捣）肉苁蓉（酒浸，切，炒）黄耆（炙，锉）萆薢（炒）各30克 桂（去粗皮）青木香（生用）各7.5克 诃黎勒（煨热，去核）15克 升麻23克', '每服20～30丸，空腹时用温酒送下。', '《圣济总录》卷一三九', '治远年伤折，忽因风气不和，于旧伤处疼痛不可忍者。', '-', '-', '升麻', '[\'熟干\', \'地黄\', \'焙干\', \'杏仁\', \'退去\', \'皮尖\', \'双仁\', \'别研\', \'牛膝\', \'苗叶酒\', \'苦参\', \'焙干\', \'菟丝子\', \'酒浸\', \'肉苁蓉\', \'浸切\', \'黄耆\', \'各克桂去\', \'粗皮\', \'青木香\', \'生用\', \'各克诃黎勒\', \'去核\', \'升麻\']');
INSERT INTO `prescript` VALUES (58876, '50461', '揩齿散', '细辛 白蒺藜（微炒，去刺）露蜂房（微炙）川升麻 白矾（一半烧令汁尽，一半生用，研令细）黄柏（锉）各15克 槐枝 柳枝（粗者，每茎长6厘米）各3～7茎（烧勿令过火）', '使时先以热盐水漱口三五度后，取药揩齿龈上。微觉痛即止，有津即吐之。', '《太平圣惠方》卷三十四', '治牙齿风疳，出血疼痛，牙齿浮虚者。', '-', '-', '升麻', '[\'细辛\', \'蒺藜\', \'露蜂房\', \'微炙川\', \'升麻\', \'白矾\', \'一半\', \'烧令汁\', \'一半\', \'生用\', \'研令细\', \'黄柏\', \'各克槐枝\', \'柳枝\', \'粗者\', \'厘米\', \'过火\']');
INSERT INTO `prescript` VALUES (58877, '50457', '既济解毒汤', '大黄（酒蒸，大便利勿用）黄连（酒炒）黄芩（酒炒）甘草（炙）桔梗各9克 柴胡 升麻 连翘 当归身各3克', '用水300毫升，煎至150毫升，去滓，食后温服。', '《卫生宝鉴》卷二十三', '泻火解毒，导热下行。治上热，头目赤肿而痛，胸膈烦闷不得安卧，身半以下皆寒，足胫尤甚，大便微秘，脉浮数，按之弦细。', '服药期间，忌酒、湿面、大料物及生冷硬物。', '-', '升麻', '[\'黄酒\', \'蒸大\', \'便利\', \'黄连\', \'黄芩\', \'甘草\', \'桔梗\', \'柴胡\', \'升麻\', \'连翘\', \'当归\', \'身各克\']');
INSERT INTO `prescript` VALUES (58878, '50377', '拈痛汤', '白术4.5克 人参（去芦）苦参（酒炒）升麻（去芦）葛根 苍术各6克 防风（去芦）知母（酒洗）泽泻 猪苓 当归身各9克 炙甘草 黄芩（酒洗）茵陈（酒炒）羌活各15克', '上药哎咀，每服30克，用水600毫升，煎至150毫升，去滓，空腹时服。', '《兰室秘藏》卷中', '治湿热为病，肩背沉重，肢节疼痛，胸膈不利。', '-', '-', '升麻', '[\'白术\', \'人参\', \'去芦\', \'苦参\', \'升麻\', \'去芦\', \'葛根\', \'苍术\', \'防风\', \'去芦\', \'知母\', \'酒洗\', \'泽泻\', \'猪苓\', \'当归\', \'身各克\', \'甘草\', \'黄芩\', \'酒洗\', \'茵陈\', \'羌活\']');
INSERT INTO `prescript` VALUES (58879, '50273', '宣毒发表汤', '升麻 葛根各2.5克 前胡2.5克 桔梗0.6克 枳壳（麸炒）2.5克 荆芥 防风各1.5克 薄荷 甘草各0.6克 木通 连翘 牛蒡子 杏仁 竹叶各2.5克', '水煎服。\r\n大寒，加蜜炙麻黄；天气大热，加黄芩。', '《痘疹活幼至宝》卷终', '透疹解毒，宣肺止咳。治麻疹透发不出，发热咳嗽，烦躁口渴，小便赤者。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'各克前\', \'胡克\', \'桔梗\', \'枳壳\', \'炒克\', \'荆芥\', \'防风\', \'薄荷\', \'甘草\', \'木通\', \'连翘\', \'牛蒡子\', \'杏仁\', \'竹叶\']');
INSERT INTO `prescript` VALUES (58880, '50271', '栀子仁散', '栀子仁（熬黑）30克 白鲜皮 赤芍药 升麻各15克 寒水石 甘草（炙）各9克', '每服3～6克，水煎，调紫草茸末1.5克服之。', '《张氏医通》卷十五', '治痘疹毒盛，色黑便秘。', '-', '-', '升麻', '[\'栀子\', \'仁熬\', \'黑克\', \'白鲜皮\', \'芍药\', \'升麻\', \'寒水石\', \'甘草\']');
INSERT INTO `prescript` VALUES (58881, '50173', '补肝汤', '黄耆2.1克 炙甘草1.5克 升麻 猪苓各1.2克 白茯苓 葛根 人参各0.9克 柴胡 羌活 陈皮 连翘 当归身，黄柏（炒）泽泻 苍术 曲末 知母 防风各0.6克', '用水600毫升，煎至300毫升，去滓，空腹时稍热服。', '《兰室秘藏》卷下', '主前阴冰冷，阴汗，两脚软弱无力。', '服药期间，忌酒、湿面。', '-', '升麻', '[\'黄耆\', \'甘草\', \'升麻\', \'猪苓\', \'白茯苓\', \'葛根\', \'人参\', \'柴胡\', \'羌活\', \'陈皮\', \'连翘\', \'当归\', \'黄柏\', \'泽泻\', \'苍术\', \'曲末\', \'知母\', \'防风\']');
INSERT INTO `prescript` VALUES (58882, '50157', '清阳散火汤', '升麻 白芷 黄芩 石膏 防风 荆芥 当归 鼠粘子 连翘 白蒺藜各6克 甘草1.5克', '水煎，食后服。', '《喉科紫珍集》卷下', '治牙根尽处结肿，连及耳项作痛。', '-', '-', '升麻', '[\'升麻\', \'白芷\', \'黄芩\', \'石膏\', \'防风\', \'荆芥\', \'当归\', \'粘子\', \'连翘\', \'蒺藜\', \'甘草\']');
INSERT INTO `prescript` VALUES (58883, '50098', '栀子升麻汤', '栀子10枚（切碎）升麻4.5克 生地黄25克（切碎用）柴胡（去芦）石膏各7.5克', '每服15克，用水250毫升，煎至200毫升，去滓频服。病不解，更作。', '《类证活人书》卷十七', '清解肌热。治三月间至夏月发伤寒、温热病。虚烦潮热不止。', '-', '-', '升麻', '[\'栀子\', \'切碎\', \'升麻\', \'生地黄\', \'切碎\', \'柴胡\', \'去芦\', \'石膏\']');
INSERT INTO `prescript` VALUES (58884, '50065', '顺气和中汤', '黄耆4.5克 人参3克 甘草（炙）2.1克 白术 陈皮 当归 白芍各1.5克 升麻 柴胡各0.9克 细辛 蔓荆子 川芎各0.6克', '上药哎咀，作一服。用水300毫升，煎至150毫升，去滓，食后温服。', '《卫生宝鉴》卷九', '治气虚头痛，痛不可忍，昼夜不得眠，恶风怕冷，不喜饮食，气短懒言，六脉弦细而微。', '-', '-', '升麻', '[\'黄耆克\', \'人参\', \'甘草\', \'炙克\', \'白术\', \'陈皮\', \'当归\', \'白芍\', \'升麻\', \'柴胡\', \'细辛\', \'蔓荆子\', \'川芎\']');
INSERT INTO `prescript` VALUES (58885, '50045', '栀子仁饮', '栀子仁30克 川升麻90克 大青60克 石膏90克（捣碎）黄芩30克 生地黄60克', '每服15克，用水200毫升，入葱白7寸，豉49粒，煎至100毫升，去津，不计时候温服。', '《太平圣惠方》卷七十四', '治妊娠热病，斑出黑色，小便如血，气急，胎欲落。', '-', '-', '升麻', '[\'栀子\', \'仁克川\', \'升麻\', \'克大青克\', \'石膏\', \'捣碎\', \'黄芩\', \'生地黄\']');
INSERT INTO `prescript` VALUES (58886, '50013', '神效暖脐膏', '肉桂45克（去皮）丹皮24克 黄耆 党参 归身 生地各60克 白芍 苁蓉 附子（炮）木鳖子各30克（去壳）荆芥 防风 麻黄 桂枝 柴胡 前胡 升麻 葛根 苏叶 薄荷 羌活 独活白芷 藁本 川芎 细辛各15克', '-', '《慈禧光绪医方选议》', '镇痛止泻，祛风散寒，温中健胃。暖肚。治感受寒邪，腹痛腹胀，呕吐酸水等。', '-', '-', '升麻', '[\'肉桂\', \'去皮\', \'丹皮\', \'黄耆\', \'党参\', \'归身\', \'生地\', \'白芍\', \'苁蓉\', \'附子\', \'木鳖子\', \'各克去\', \'荆芥\', \'防风\', \'麻黄\', \'桂枝\', \'柴胡\', \'升麻\', \'葛根\', \'苏叶\', \'薄荷\', \'羌活\', \'独活\', \'白芷\', \'藁本\', \'川芎\', \'细辛\']');
INSERT INTO `prescript` VALUES (58887, '49951', '葳蕤丸', '葳蕤 车前子 熟干地黄（焙）各120克 升麻 黄芩（去黑心）秦艽（去苗、土）枳壳（去瓤，麸炒）白茯苓（去黑皮）黄连（去须）独活（去芦头）地骨皮 决明子（微炒）山栀子仁 白槟榔（生，锉）各45克 赤芍药 芎藭各60克 秦皮30克', '每服30丸，食后热水下，每日二次。', '《圣济总录》卷一一一', '治眼生翳膜，疼痛昏涩，视物不明。', '-', '-', '升麻', '[\'葳蕤\', \'车前子\', \'熟干\', \'地黄\', \'升麻\', \'黄芩\', \'黑心\', \'秦艽\', \'苗土\', \'枳壳\', \'白茯苓\', \'黑皮\', \'黄连\', \'独活\', \'芦头\', \'地骨皮\', \'决明子\', \'炒山\', \'栀子\', \'仁白\', \'槟榔\', \'芍药\']');
INSERT INTO `prescript` VALUES (58888, '49935', '和中益胃汤', '苏木0.3克 藁本 益智仁各0.6克 热地黄 炙甘草各0.9克 当归身1.2克 柴胡 升麻各1.5克', '用水300毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治太阴、阳明腹痛，大便或泄或秘，或下鲜血，腹中微痛，胁下急缩，脉缓而洪弦，按之空虚者。', '-', '-', '升麻', '[\'苏木\', \'藁本\', \'益智仁\', \'各克热\', \'地黄\', \'甘草\', \'当归\', \'身克\', \'柴胡\', \'升麻\']');
INSERT INTO `prescript` VALUES (58889, '49888', '葛根汤', '葛根24克 生姜9克 龙胆草 大青叶各1.5克 桂心 甘草（炙）麻黄（去节）各6克 葳蕤3克 芍药 黄芩各6克 石膏（碎）升麻各3克', '以水1升，先煮葛根、麻黄取800毫升，掠去沫后，纳余药，再煎至300毫升，分三服，日二夜一。', '《外台秘要》卷一引《小品方》', '治伤寒三四日不愈，热毒内盛，头痛，壮热来解，身体疼痛者。', '服药期间，忌食海藻、菘菜、生葱。', '-', '升麻', '[\'葛根\', \'生姜\', \'龙胆草\', \'大青叶\', \'各克桂心\', \'甘草\', \'麻黄\', \'去节\', \'葳蕤\', \'芍药\', \'黄芩\', \'石膏\', \'升麻\']');
INSERT INTO `prescript` VALUES (58890, '49837', '栀子仁汤', '栀子仁30克 柴胡45克（去苗）川升麻60克 黄芩60克 赤芍30克 大青30克 石膏60克 知母30克 甘草15克（炙赤，锉）杏仁60克（汤浸，去皮、尖、双仁，麸炒微黄）', '每服12克，以水200毫升，入生姜4克，豉100粒，煎至120毫升，去滓，不计时候，温服。', '《类证活人书》卷十六', '治阳毒伤寒，壮热，百节疼痛。', '-', '-', '升麻', '[\'栀子\', \'仁克\', \'柴胡\', \'克去\', \'苗川\', \'升麻\', \'黄芩\', \'赤芍\', \'克大青克\', \'石膏\', \'知母\', \'甘草\', \'克炙赤\', \'杏仁\', \'克汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (58891, '49821', '立效散', '细辛0.6克 炙甘草0.9克 升麻2.1克 防风3克 草龙胆（酒洗）12克', '用水150毫升，煎至100毫升，去滓，以匙抄在口中，煠痛处，待少时则止。', '《兰室秘藏》卷中', '治牙痛不可忍，及头脑项背痛，微恶寒饮，大恶热饮，小便滑数者。', '-', '-', '升麻', '[\'细辛\', \'甘草\', \'升麻\', \'防风\', \'克草\', \'龙胆\', \'洗克\']');
INSERT INTO `prescript` VALUES (58892, '49820', '加味茯苓汤', '苍术6克 白术9克 茯苓3克 甘草（炙）1.5克 猪苓2.4克 泽泻3克 升麻1.5克 肉桂2.1克 柴胡1.8克 黄芩3克 生草1.5克 当归3克 白芍2.1克', '用水400毫升，煎至200毫升，温服。', '《杏苑生春》卷四', '健脾利湿。治中气不足，水湿壅盛，阳气郁遏，不得上达，以致水泄注下，日夜无度，小便短少，口渴咽干，腹中疼痛，或变成赤白痢疾者。', '-', '-', '升麻', '[\'苍术\', \'白术\', \'茯苓\', \'甘草\', \'炙克\', \'猪苓\', \'泽泻\', \'升麻\', \'肉桂\', \'柴胡\', \'黄芩\', \'克生\', \'草克\', \'当归\', \'白芍\']');
INSERT INTO `prescript` VALUES (58893, '49813', '补中益气汤', '黄耆 甘草（炙）各1.5克 人参（去芦）0.9克 当归身0.6克（酒焙干或晒干）橘皮（不去白）0.6～0.9克 升麻0.6～0.9克 柴胡0.6～0.9克 白术0.9克', '上药哎咀，都作一服。用水300毫升，煎至150毫升，去滓，空腹时稍热服。', '《脾胃论》卷中', '补中益气，升阳举陷，主脾胃气虚，少气懒言，四肢无力，困倦少食，饮食乏味，不耐劳累，动则气短；或气虚发热，气高而喘，身热而烦，渴喜热饮，其脉洪大，按之无力，皮肤不任风寒，而生寒热头痛；或气虚下陷，久泻脱肛。现用于子宫下垂；胃下垂或其它内脏下垂者。\r\n病甚劳役、热甚者，黄耆加至3克，咳嗽者，去人参；腹中痛者，加白芍药1.5克、炙甘草1.5克；若恶热喜寒而腹痛者；再加黄芩0.6～0.9克；恶寒冷痛，加桂心0.3～0.9克；头痛，加蔓荆子0.6～0.9克；痛甚者加川芎0.6克；顶痛、脑痛，加藁本0.9～1.5克。', '阴虚内热者忌服。', '-', '升麻', '[\'黄耆\', \'甘草\', \'人参\', \'芦克\', \'当归\', \'身克酒\', \'焙干\', \'晒干\', \'橘皮\', \'不去\', \'升麻\', \'柴胡\', \'白术\']');
INSERT INTO `prescript` VALUES (58894, '49810', '三黄补血汤', '牡丹皮 黄耆 升麻各3克 当归 柴胡各4.5克 熟地黄 川芎各6克 生地黄9克 白芍药15克', '每服15克，用水600毫升，煎至300毫升，去滓，空腹时稍热服。', '《兰室秘藏》卷中', '补气养血。治亡血血虚。面赤善惊，六脉俱大，按之空虚者。', '-', '-', '升麻', '[\'牡丹皮\', \'黄耆\', \'升麻\', \'当归\', \'柴胡\', \'熟地黄\', \'川芎\', \'生地黄\', \'克白\', \'芍药\']');
INSERT INTO `prescript` VALUES (58895, '49800', '秦艽防风汤', '秦艽 防风 当归身 白术各4.5克 炙甘草 泽泻各1.8克 黄柏1.5克 大黄（煨）橘皮各0.9克 柴胡 升麻各0.6克 桃仁30个 红花少许', '上药锉碎，用水450毫升，煎至150毫升，去滓，空腹时稍热服。', '《兰室秘藏》卷下', '治痔漏，每日大便时发疼痛。', '服药期间，忌房事，避风寒，不吃辛热之物。', '-', '升麻', '[\'秦艽\', \'防风\', \'当归\', \'白术\', \'甘草\', \'泽泻\', \'黄柏\', \'大黄\', \'橘皮\', \'柴胡\', \'升麻\', \'桃仁\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (58896, '49772', '防风浴汤', '防风如克 蒴翟（切）30克 羊桃根90克 石南30克 秦艽30克 川升麻30克 苦参90克 茵芋30克 白蒺藜30克 蛇床子30克 白矾30克 枳壳30克', '用水14升，煎至10升，去滓，于暖室中洗浴，令汗出。', '《太平圣惠方》卷二十四', '祛风，润燥，止痒。主风湿外侵，周身瘙痒不可止。', '-', '-', '升麻', '[\'防风\', \'如克\', \'切克\', \'桃根\', \'石南\', \'秦艽\', \'克川\', \'升麻\', \'苦参\', \'克茵芋\', \'克白\', \'蒺藜\', \'蛇床子\', \'白矾\', \'枳壳\']');
INSERT INTO `prescript` VALUES (58897, '49762', '地肤大黄汤', '地肤草 大黄各9克 知母 黄芩 茯苓 芍药 枳实（炙）升麻 通草 甘草（炙）各6克', '上药用水800毫升，煮取300毫升，分二次服。', '《外台秘要》卷三十三引《小品方》', '清热降火，利水通淋。主妊娠膀胱热盛，小便频数，淋沥不畅，心中烦躁，脐腹胀闷。', '服药期间，忌海藻、菘菜、酸性食物。', '-', '升麻', '[\'地肤\', \'大黄\', \'知母\', \'黄芩\', \'茯苓\', \'芍药\', \'枳实\', \'升麻\', \'通草\', \'甘草\']');
INSERT INTO `prescript` VALUES (58898, '49753', '通幽汤', '炙甘草 红花各0.3克 生地黄 熟地黄各1.5克 升麻 桃仁泥 当归身各3克', '上药用水600毫升，煎至300毫升，去滓，调槟榔细末15克，食前，稍热服之。', '《兰室秘藏》卷下', '养血活血，润燥通幽。治噎膈。幽门不通，逆气上冲，吸门不开，饮食不下，或食入反出，大便燥结。', '-', '-', '升麻', '[\'甘草\', \'红花\', \'生地黄\', \'熟地黄\', \'升麻\', \'桃仁\', \'当归\', \'身各克\']');
INSERT INTO `prescript` VALUES (58899, '49748', '阳毒升麻汤', '升麻15克 犀角屑7.5克 射干7.5克 黄芩7.5克 人参7.5克 甘草7.5克', '用水600毫升，煎至300毫升，去滓，每服150毫升，食顷再服。温覆，手足出汗，汗出则解，不解重作。', '《类证活人书》卷十六', '治伤寒一二日便成阳毒，或吐下之后变成阳毒，腰背疼痛，烦闷不安，面赤狂言，或下利，脉浮大而数，面赤斑斑如绵纹，喉咽痛，下脓血。', '-', '-', '升麻', '[\'升麻\', \'犀角\', \'射干\', \'黄芩\', \'人参\', \'甘草\']');
INSERT INTO `prescript` VALUES (58900, '49745', '升麻膏', '升麻 白薇 漏芦 连翘 芒消 黄芩各60克 蛇衔 枳实各90克 栀子40枚 蒴藋120克', '敷患处，一日三次。', '《备急千金要方》卷二十二', '治丹毒，热疮。', '-', '-', '升麻', '[\'升麻\', \'白薇\', \'漏芦\', \'连翘\', \'芒消\', \'黄芩\', \'各克蛇\', \'枳实\', \'栀子\']');
INSERT INTO `prescript` VALUES (58901, '49719', '黄芩射干汤', '黄芩（去黑心）射干各30克 枳实（去甑，麸炒）半夏（汤洗七遍，去滑，焙）甘草（炙，锉）各23克 升麻45克 桂（去粗皮）38克', '每服15克，用水230毫升，加生姜5片，同煎至180毫升，去滓温服。', '《圣济总录》卷一二四', '治咽喉似有物噎塞。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'射干\', \'枳实\', \'半夏\', \'汤洗\', \'七遍\', \'甘草\', \'升麻\', \'克桂去\', \'粗皮克\']');
INSERT INTO `prescript` VALUES (58902, '49716', '通气防风汤', '防风 羌活 陈皮 人参 甘草各1.5克 藁本 青皮各0.9克 白豆蔻 黄柏各0.6克 升麻 柴胡 黄耆各3克', '上哎咀，都作一服。用水300毫升，煎至150毫升，空腹时去滓温服。\r\n如面白脱色，气短者，不可服。', '《内外伤辨》卷中', '治风热外乘，肺气郁甚，肩背痛，汗出，小便数而少。', '-', '-', '升麻', '[\'防风\', \'羌活\', \'陈皮\', \'人参\', \'甘草\', \'藁本\', \'青皮\', \'白豆蔻\', \'黄柏\', \'升麻\', \'柴胡\', \'黄耆\']');
INSERT INTO `prescript` VALUES (58903, '49712', '升麻葛根汤', '升麻 白芍药 甘草（炙）各300克 葛根450克', '每服9克，用水450毫升，煎取300毫升，去滓稍热服，不计时候，日二三服，以病去身凉为度。小儿量力服之。', '《太平惠民和剂局方》卷二', '辛凉疏表，解肌透疹。治感受时气温疫，头痛发热，肢体烦痛；麻疹初起，发而不透。', '-', '-', '升麻', '[\'升麻\', \'芍药\', \'甘草\', \'葛根\']');
INSERT INTO `prescript` VALUES (58904, '49646', '射干散', '射干30克 赤芍药30克 川升麻60克 杏仁45克（汤浸，去皮、尖、双仁，麸炒微黄）牛蒡子30克 枫香30克 葛根60克（锉）麻黄30克（去根、节）甘草60克（炙微赤，锉）', '每服9克，以水300毫升，煎至180毫升，去滓，不计时候，温服。', '《太平圣惠方》卷三十五', '疏风宣肺，凉血解毒。治风毒上攻，咽喉肿痛，水浆不下。', '-', '-', '升麻', '[\'射干\', \'克赤\', \'芍药\', \'克川\', \'升麻\', \'杏仁\', \'克汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'牛蒡子\', \'枫香\', \'葛根\', \'麻黄\', \'克去\', \'根节\', \'甘草\', \'克炙微\']');
INSERT INTO `prescript` VALUES (58905, '49616', '大蓟散', '大蓟根（洗）犀角（镑）升麻 桑白皮（炙）蒲黄（炒）杏仁（去皮、尖）桔梗（去芦，炒）各30克 甘草（炙）15克', '每服12克，用水230毫升，加姜5片，煎至180毫升，去滓温服，不拘时候。', '《重订严氏济生方》', '主饮啖辛热，热邪伤肺，肺痈吐血。', '-', '-', '升麻', '[\'大蓟\', \'根洗\', \'犀角\', \'升麻\', \'桑白皮\', \'杏仁\', \'皮尖\', \'桔梗\', \'去芦\', \'甘草\', \'炙克\']');
INSERT INTO `prescript` VALUES (58906, '49573', '地肤子汤', '地肤子9克 知母 黄芩 猪苓 瞿麦 枳实 升麻 通草 葵子 海藻各3克', '以水600毫升，煮取200毫升，分温三服。\r\n大便不通者，加大黄9克；房劳肾虚，小腹满痛，脉沉细者，加猪肾1只，先煮猪肾，取猪肾汤煎上药。', '《备急千金要方》卷二十一', '主下焦热结，致患淋证，小便赤涩不利，尿频量少，茎中刺痛，或有血尿。', '-', '-', '升麻', '[\'地肤子\', \'知母\', \'黄芩\', \'猪苓\', \'瞿麦\', \'枳实\', \'升麻\', \'通草\', \'葵子\', \'海藻\']');
