INSERT INTO `prescript` VALUES (58914, '49297', '银甲汤', '金银花24克，连翘15克，升麻15克，红藤24克，蒲公英24克，生鳖甲24克，紫花地丁30克，生蒲黄12克，椿根皮12克，大青叶12克，琥珀末12克，桔梗12克，茵陈13克。', '水煎服，每日1剂，日服3次。', '王渭川方', '清热解毒利湿。主湿热蕴结下焦。', '-', '-', '升麻', '[\'金银花\', \'连翘\', \'升麻\', \'红藤\', \'蒲公英\', \'克生\', \'鳖甲\', \'紫花地丁\', \'克生\', \'黄克椿根\', \'皮克\', \'大青叶\', \'琥珀\', \'末克\', \'桔梗\', \'茵陈\']');
INSERT INTO `prescript` VALUES (58915, '49293', '泽泻汤', '泽泻 升麻 杏仁（汤浸，去皮、尖、双仁，研）决明子（微炒）大黄（锉，炒）黄芩（去黑心）甘草（炙）枳实（去瓤，麸炒）芍药各30克 栀子仁 人参 赤茯苓（去黑皮）黄柏（去粗皮）细辛（去苗叶）白术各15克 柴胡（去苗）120克 桑根白皮（锉，炙）60克 青葙子30克', '每服15克，用水220毫升，入生姜4克（拍破），同煎至150毫升，去滓，入芒消1克，食后、临卧温服，一日二次。', '《圣济总录》卷一一二', '治肝热目赤，视物不清，积年青盲，视不见物。', '-', '-', '升麻', '[\'泽泻\', \'升麻\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁研\', \'决明子\', \'大黄\', \'黄芩\', \'黑心\', \'甘草\', \'枳实\', \'芍药\', \'栀子\', \'人参\', \'茯苓\', \'黑皮\', \'黄柏\', \'粗皮\', \'细辛\', \'苗叶\', \'白术\', \'柴胡\', \'苗克桑根\', \'白皮\', \'锉炙克\', \'青葙子\']');
INSERT INTO `prescript` VALUES (58916, '49203', '麻黄升麻汤', '麻黄7.5克（去节）升麻3.5克 当归3.5克 知母2.5克 黄芩2.5克 萎蕤（一作菖蒲）3克 芍药2克 天门冬2克（去心）桂枝2克（去皮）茯苓2克 甘草2克（炙）石膏3克（碎，绵裹）白术2克 干姜2克', '用水2升，先煮麻黄一二沸，去上沫，入余药，煮取600毫升，去滓，分三次温服，每次相隔约1～2小时。汗出愈。', '《伤寒论》', '治伤寒六七日，大下后，寸脉沉而迟，手足厥逆，下部脉不至，咽喉不利，吐脓血者。', '-', '-', '升麻', '[\'麻黄\', \'克去\', \'升麻\', \'当归\', \'知母\', \'黄芩\', \'萎蕤\', \'一作\', \'菖蒲\', \'芍药\', \'天门冬\', \'克去\', \'桂枝\', \'去皮\', \'茯苓\', \'甘草\', \'石膏\', \'克碎\', \'绵裹\', \'白术\', \'干姜\']');
