INSERT INTO `prescript` VALUES (58942, '48449', '羌活附子汤', '麻黄0.9克（不去根、节）黑附子0.9克 羌活1.5克 苍术1.5克 防风0.6克 黄耆3克 甘草 升麻各0.6克 白芷 白僵蚕 黄柏各0.9克', '上药作一服。用水300毫升，煎至150毫升，去滓，食后温服。\r\n寒嗽，加佛耳草0.9克。', '《东垣试效方》卷五', '治冬月大寒犯脑，脑痛齿亦痛，名曰脑风。', '-', '-', '升麻', '[\'麻黄\', \'克不去\', \'根节\', \'附子\', \'羌活\', \'苍术\', \'防风\', \'黄耆克\', \'甘草\', \'升麻\', \'白芷\', \'白僵蚕\', \'黄柏\']');
INSERT INTO `prescript` VALUES (58943, '48394', '玄参升麻汤', '玄参 升麻 甘草（炙）各15克', '每服15克，用水220毫升，煎至160毫升，去滓服。', '《类证活人书》卷十八', '清热解毒化斑。治伤寒发汗吐下后，毒气不散，表虚里实，热发于外，身斑如锦纹，甚则烦躁谵语；兼治喉闭肿痛。', '-', '-', '升麻', '[\'玄参\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (58944, '48379', '救苦汤', '桔梗 连翘 红花 细辛各0.3克 当归身（夏月减半）炙甘草各1.5克 龙胞草 苍术各2克 黄连 羌活 升麻 柴胡 防风 藁本各3克 知母 生地黄 黄柏 黄芩各4.5克 川芎9克', '每服30克，以水300毫升，煎至150毫升，去滓，食后温服。', '《兰室秘藏》卷上', '治眼暴发赤肿，睑高苦疼。', '-', '-', '升麻', '[\'桔梗\', \'连翘\', \'红花\', \'细辛\', \'当归\', \'身夏月\', \'减半\', \'甘草\', \'各克龙胞\', \'苍术\', \'黄连\', \'羌活\', \'升麻\', \'柴胡\', \'防风\', \'藁本\', \'知母\', \'生地黄\', \'黄柏\', \'黄芩\', \'川芎\']');
INSERT INTO `prescript` VALUES (58945, '48346', '玄参升麻汤', '升麻 黄连各1.5克 黄芩（炒）1.2克 连翘 桔梗各0.9克 鼠粘子 玄参 甘草 白僵蚕各0.6克 防风0.3克', '用水300毫升，煎至210毫升，去滓，稍热噙嗽，时时咽之。', '《卫生宝鉴》卷八', '清热解毒，祛风宜肺。治风热上攻，咽喉中妨闷，会厌后肿，舌赤者。', '-', '-', '升麻', '[\'升麻\', \'黄连\', \'黄芩\', \'炒克\', \'连翘\', \'桔梗\', \'各克鼠\', \'粘子\', \'玄参\', \'甘草\', \'白僵蚕\', \'防风\']');
INSERT INTO `prescript` VALUES (58946, '48327', '甘露膏', '半夏0.6克（汤洗）熟甘草 白豆蔻仁 人参 兰香 升麻 连翘 桔梗各1.5克 生甘草 防风各3克 酒知母4.5克 石膏9克', '每服6克，食后用淡生姜汤送下。', '《兰室秘藏》卷上', '治消渴。饮水极甚，善食而瘦，自汗，大便结燥，小便频数。', '-', '-', '升麻', '[\'半夏\', \'克汤\', \'洗熟\', \'甘草\', \'白豆蔻\', \'人参\', \'兰香\', \'升麻\', \'连翘\', \'桔梗\', \'生甘草\', \'防风\', \'各克酒\', \'知母\', \'石膏\']');
INSERT INTO `prescript` VALUES (58947, '48290', '升麻消毒饮', '当归尾 赤芍药 金银花 连翘（去心）牛蒡子（炒）栀子（生）羌活 白芷 红花 防风 生甘草 升麻 桔梗（小剂各3克，中剂各5克，大剂各6克）', '用水400毫升，煎至320毫升，空腹服。\r\n若疮生头面，减去当归尾、红花。', '《医宗金鉴》卷七十四', '治黄水疮。', '-', '-', '升麻', '[\'当归\', \'芍药\', \'金银花\', \'连翘\', \'去心\', \'牛蒡子\', \'栀子\', \'羌活\', \'白芷\', \'红花\', \'防风\', \'生甘草\', \'升麻\', \'桔梗\', \'小剂\', \'中剂\', \'各克大剂\']');
INSERT INTO `prescript` VALUES (58948, '48193', '茯苓升麻汤', '茯苓赤白各15克 升麻5克 当归6克 川芎3克 苎根10克', '急流水煎服，或调琥珀末6克服更佳。', '《医学心悟》卷三', '治孕妇转脬，小便不通。', '-', '-', '升麻', '[\'茯苓\', \'赤白\', \'升麻\', \'当归\', \'川芎\', \'根克\']');
INSERT INTO `prescript` VALUES (58949, '48095', '四妙汤', '紫草 升麻 糯米各30克 甘草（生）7.5克', '每服6克，用水150毫升，煎至90毫升，去滓，分二次温服。', '《圣济总录》卷一六九', '治小儿麸豆疮欲出，浑身壮热，情绪不乐，不思饮食。', '-', '-', '升麻', '[\'紫草\', \'升麻\', \'糯米\', \'甘草\', \'生克\']');
INSERT INTO `prescript` VALUES (58950, '48039', '麻黄茱萸汤', '麻黄 羌活各1.5克 吴茱萸 黄耆 升麻各0.9克 黄芩 当归 黄柏 藁本各0.6克 川芎 蔓荆子 柴胡 苍术 黄连 半夏各0.3克 细辛少许红花少许', '用水300毫升，煎至150毫升，去滓稍热，食后服。', '《兰室秘藏》卷下', '治胸中痛，头痛，食减少，咽嗌不利，右寸脉弦急。', '-', '-', '升麻', '[\'麻黄\', \'羌活\', \'吴茱萸\', \'黄耆\', \'升麻\', \'黄芩\', \'当归\', \'黄柏\', \'藁本\', \'川芎\', \'蔓荆子\', \'柴胡\', \'苍术\', \'黄连\', \'半夏\', \'细辛\', \'少许\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (58951, '48010', '火郁汤', '升麻 葛根 柴胡 白芍各30克 防风甘草各15克', '每服15克，用水600毫升，入连须葱白3寸，煎至300毫升，去滓，不拘时稍热服。', '《兰室秘藏》卷下', '治火郁于中，五心烦热。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'柴胡\', \'白芍\', \'防风\', \'甘草\']');
INSERT INTO `prescript` VALUES (58952, '47997', '柴胡石膏散', '赤芍药 柴胡（去苗）前胡（去苗）石膏（煅）干葛各1.5千克 升麻750克 黄芩 桑白皮各1.26千克 荆芥穗（去土）1.11千克', '每服6克，用水150毫升，加生姜3片，淡豉10粒，煎至100毫升，去滓，稍热服。小儿分作三服，量大小加减，不拘时候。', '《太平惠民和剂局方》卷二', '解表清里。主时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，心胸烦满，寒热往来，痰盛咳嗽，涕唾稠粘。', '-', '-', '升麻', '[\'芍药\', \'柴胡\', \'苗前\', \'胡去\', \'石膏\', \'干葛\', \'升麻\', \'黄芩\', \'桑白皮\', \'荆芥\']');
INSERT INTO `prescript` VALUES (58953, '47983', '地黄汤', '生地黄汁200毫升 川升麻 白蔹 栀子仁 黄连 黄柏 当归 赤芍 射干 大黄 甘草（生用）各10克', '用水2升，煎至1.2升，去滓，入地黄汁，搅令微温，以故帛纳汤内，蘸药拓于肿处，一日数次。', '《太平圣惠方》卷六十二', '主发背成疮溃烂者。', '-', '-', '升麻', '[\'生地黄\', \'升麻\', \'白蔹\', \'栀子\', \'黄连\', \'黄柏\', \'当归\', \'赤芍\', \'射干\', \'大黄\', \'甘草\', \'生用\']');
INSERT INTO `prescript` VALUES (58954, '47892', '滋阴清胃丸', '当归（酒洗）生地黄（酒洗）牡丹皮（去骨）栀子仁（盐水炒）各30克 软石膏（煅，醋淬）60克 黄连（酒炒）知母 葛粉 防风各21克 升麻 白芷各15克 生甘草节12克', '每服100丸，晚上用米汤送下。', '《万病回春》卷五', '治阳明经血热，上下牙床红烂，肉缩齿龈露者。', '-', '-', '升麻', '[\'当归\', \'酒洗\', \'生地黄\', \'酒洗\', \'牡丹皮\', \'栀子\', \'盐水\', \'各克软\', \'石膏\', \'淬克\', \'黄连\', \'知母\', \'葛粉\', \'防风\', \'升麻\', \'白芷\', \'生甘草\', \'节克\']');
INSERT INTO `prescript` VALUES (58955, '47867', '普济消毒饮子', '黄芩 黄连各15克 橘红（去白）玄参 生甘草各6克 连翘 黍粘子 板蓝根 马勃各3克 白僵蚕（炒）2.1克 升麻2.1克 柴胡6克 桔梗6克（一方有人参9克）', '每服15克，用水300毫升，煎至150毫升，去滓，稍热，时时服之。\r\n食后如大便硬，加酒煨大黄3～6克以利之；肿势甚者，宜砭刺之。', '《东垣试效方》卷九', '清热解毒，疏风散邪。治风热疫毒上攻，致患大头瘟，恶寒发热，头面红肿焮痛，目不能开，咽喉不利，舌干口燥，舌红苔白或兼黄，脉浮数有力。现用于急性腮腺炎、急性扁桃体炎、颌下腺炎、头面部蜂窝组织炎等。', '-', '-', '升麻', '[\'黄芩\', \'黄连\', \'橘红\', \'玄参\', \'生甘草\', \'连翘\', \'粘子\', \'板蓝根\', \'马勃\', \'白僵蚕\', \'炒克\', \'升麻\', \'柴胡\', \'桔梗\', \'一方\', \'人参\']');
INSERT INTO `prescript` VALUES (58956, '47825', '断根丸', '紫花地丁草 蜂房（煅）乳香 没药 升麻各9克', '每服15克，酒调下。', '《疮疡经验全书》卷三', '主梅毒，日夜痛，不能行动。', '-', '-', '升麻', '[\'紫花地丁\', \'蜂房\', \'乳香\', \'没药\', \'升麻\']');
INSERT INTO `prescript` VALUES (58957, '47747', '紫雪散', '犀角（镑）羚羊角（镑）石膏 寒水石 升麻各30克 元参60克 甘草（生）24克 沉香（锉）木香（锉）各15克', '大人每用3克，小儿0.6克，十岁者1.5克，徐徐咽之。或用淡竹叶、灯心煎汤化服。咽喉肿痛，吹患处。', '《医宗金鉴》卷六十六', '清心脾积热，解毒。治重腭，心脾有热，上腭生疮，形如梅子，外无寒热，内时作烦；舌疔，心脾火毒，舌生紫疱，其形如豆，坚硬寒热，疼痛应心，咽喉肿痛。', '-', '-', '升麻', '[\'犀角\', \'羚羊角\', \'石膏\', \'寒水石\', \'升麻\', \'各克元\', \'参克\', \'甘草\', \'生克\', \'沉香\', \'木香\']');
INSERT INTO `prescript` VALUES (58958, '47719', '益气转胃方', '黄芪30克，升麻10克，酒大黄6克，枳壳6克，怀牛膝30克，甘草3克。', '水煎服，每日1剂，日服2次。', '林泽田方', '益气升降，温中健胃。主脾胃虚弱，升降失常。', '-', '-', '升麻', '[\'黄芪\', \'升麻\', \'克酒\', \'大黄\', \'枳壳\', \'怀牛膝\', \'甘草\']');
INSERT INTO `prescript` VALUES (58959, '47669', '贝齿散', '贝齿22.5克 黄耆22.5克 当归22.5克 赤芍22.克 生干地黄22.5克 黄连22.5克（去须）川升麻22.5克 桂心22.5克 犀角12克 甘草15克（生、锉）', '不计时候，以温水调下6克。', '《太平圣惠方》卷六十二', '主发背溃后，脓水不尽。', '-', '-', '升麻', '[\'贝齿克\', \'黄耆克\', \'当归\', \'赤芍\', \'克生\', \'地黄\', \'黄连\', \'克去\', \'须川\', \'升麻\', \'克桂心克\', \'犀角\', \'甘草\', \'克生\']');
INSERT INTO `prescript` VALUES (58960, '47641', '黄耆补胃汤', '黄耆 柴胡 当归身 益智 橘皮各9克 升麻6克 炙甘草6克 红花少许', '用水400毫升，煎至200毫升，去滓。空腹时温服。', '《兰室秘藏》卷下', '益气升阳，调补脾胃。治大便一日三四次，溏而不多，有时作泄，腹中鸣，小便黄。', '-', '-', '升麻', '[\'黄耆\', \'柴胡\', \'当归\', \'益智\', \'橘皮\', \'升麻\', \'甘草\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (58961, '47637', '木香顺气汤', '木香0.9克 厚朴（姜制）1.2克 青皮（去白）陈皮 益智仁 白茯苓（去皮）泽泻 干生姜 半夏（汤洗）吴茱萸（汤洗）各0.6克 当归1.5克 升麻 柴胡各0.3克 草豆蔻（面襄煨，去皮）0.9克 苍术（泔浸）0.9克', '上哎咀，都作一服。用水600毫升，煎至300毫升，去滓，大温服，空腹时。', '《医学发明》卷四', '治浊气在上，则生腆胀。', '服药期间，忌生冷硬物及怒。', '-', '升麻', '[\'木香\', \'厚朴\', \'姜制克\', \'青皮\', \'陈皮\', \'益智仁\', \'白茯苓\', \'去皮\', \'泽泻\', \'生姜\', \'半夏\', \'汤洗\', \'吴茱萸\', \'汤洗\', \'当归\', \'升麻\', \'柴胡\', \'草豆蔻\', \'面襄\', \'去皮\', \'苍术\', \'泔浸克\']');
INSERT INTO `prescript` VALUES (58962, '47567', '清暑益气汤', '黄耆 苍术（泔浸，去皮）升麻各3克 人参（去芦）泽泻 神曲（炒黄）橘皮 白术各15克 麦门冬（去心）当归身 炙甘草各0.9克 青皮（去白）0.9克 黄柏（酒洗，去皮）0.6～0.9克 葛根0.6克 五味子9枚', '上药哎咀。用水300毫升，煎至150毫升，去滓，空腹时温服。\r\n脾胃不足者，少用升麻，少加柴胡；中满者，去甘草；咳甚者，去人参；口咽干者，加干葛；汗少者，黄耆减五分；心下痞者，少加黄连。', '《脾胃论》卷中', '清暑化湿，益气生津。治平素气阴俱虚，又感暑湿，或暑湿耗伤气阴，身热而烦，四肢困倦，精神短少，胸满气促，肢体沉痛，口渴自汗，大便溏薄，小便短赤，苔腻，脉虚。', '-', '-', '升麻', '[\'黄耆\', \'苍术\', \'泔浸\', \'去皮\', \'升麻\', \'人参\', \'去芦\', \'泽泻\', \'神曲\', \'橘皮\', \'白术\', \'麦门冬\', \'去心\', \'当归\', \'甘草\', \'青皮\', \'白克\', \'黄柏\', \'酒洗\', \'去皮\', \'葛根\', \'五味子\']');
INSERT INTO `prescript` VALUES (58963, '47557', '均药', '栀子21克（炒）薄荷叶30克 黄连30克 升麻9克 鸡内金（炙黄，11.5克）', '吹患处。', '《喉科紫珍集》卷下', '清热解毒，消肿散结。治咽喉诸症，手术后，患处坚硬，不消不溃者。', '-', '-', '升麻', '[\'栀子\', \'薄荷叶\', \'黄连\', \'升麻\', \'鸡内金\', \'黄克\']');
