INSERT INTO `prescript` VALUES (59011, '46432', '当归附子汤', '当归0.6克 炒盐0.9克 蝎梢 升麻各1.5克 甘草1.8克 柴胡2.1克 黄柏少许（为引用）附子3克 干姜 良姜各3克', '每服15克，用水750毫升，煎至150毫升，去滓，稍热服。亦可研为细末，酒面糊为丸，每服6克。', '《兰室秘藏》卷中', '主赤白带下，脐下冷痛。', '-', '-', '升麻', '[\'当归\', \'盐克蝎\', \'升麻\', \'甘草\', \'柴胡\', \'黄柏\', \'少许\', \'引用\', \'附子\', \'干姜\', \'良姜\']');
INSERT INTO `prescript` VALUES (59012, '46421', '安神汤', '生甘草 炙甘草各6克 防风7.5克 柴胡 升麻 酒生地黄 酒知母各15克 黄耆60克 酒黄柏 羌活各30克', '每服15克，用水400毫升，煎至200毫升，加蔓荆子1.5克、川芎0.9克，再煎至150毫升，去滓，临卧热服。', '《兰室秘藏》卷中', '头痛头眩，眼黑。', '-', '-', '升麻', '[\'生甘草\', \'甘草\', \'防风\', \'柴胡\', \'升麻\', \'生地黄\', \'知母\', \'黄耆克酒\', \'黄柏\', \'羌活\']');
