INSERT INTO `prescript` VALUES (59015, '46328', '水浸鳖甲汤', '鳖甲（九胁者，去裙襕，醋炙）升麻 柴胡（去苗）人参 白茯苓（去黑皮）槟榔（锉）肉豆蔻（去壳）诃黎勒皮 犀角（镑）青橘皮（汤浸，去白，焙）陈橘皮（汤浸，去白，焙）甘草（炙，锉）缩砂仁 茴香子（炒）陈曲（炒）各15克', '上十五味，粗捣筛，每服9克，用水220毫升，浸二日，煎至160毫升，去滓，空腹时细呷，以食压之。', '《圣济总录》卷八十七', '治气劳羸瘦，四肢疼痛，心腹妨闷，不思饮食。', '-', '-', '升麻', '[\'鳖甲\', \'九胁者\', \'升麻\', \'柴胡\', \'去苗\', \'人参\', \'白茯苓\', \'黑皮\', \'槟榔\', \'肉豆蔻\', \'黎勒皮\', \'犀角\', \'橘皮\', \'汤浸\', \'橘皮\', \'汤浸\', \'甘草\', \'砂仁\', \'茴香\', \'陈曲\']');
INSERT INTO `prescript` VALUES (59016, '46285', '千里光明汤', '青木香 黄连 黄柏 黄耆 荆芥 防风 苦参 苍耳子 蛇床子 羌活 升麻 麻黄 甘草各15克 鸡肠草（焙）冬青叶（焙）', '上作一剂。用布包，水煮，于无风处浴洗，凉了又加热。微汗拭干。', '《寿世保元》卷九', '治杨梅疮毒邪陷伏，延溃不能杜绝，手足心皮干枯类似白鹅掌风，筋骨疼痛，并起风块者。', '-', '-', '升麻', '[\'青木香\', \'黄连\', \'黄柏\', \'黄耆\', \'荆芥\', \'防风\', \'苦参\', \'苍耳子\', \'蛇床子\', \'羌活\', \'升麻\', \'麻黄\', \'甘草\', \'各克鸡\', \'肠草\', \'冬青\', \'叶焙\']');
INSERT INTO `prescript` VALUES (59017, '46219', '麝脐散', '牛膝（去芦）十斤，木律四十四两，黄茄（细切）二十个，郁李仁二十两，麝香空皮子（细）一百个。\r\n已上五味，捣碎入罐子内，上用瓦子盖口，留一小窍，用盐泥固济，烧令通赤，即住火取出，以新土罨一伏时取出，后入下项药。\r\n升麻、细辛（去苗），各十斤。\r\n上件为细末。', '每用少许揩患处，须臾温水漱口，临卧更贴少许，咽津亦得妨。', '《宋太平惠民和剂局方》', '治牙齿动摇，风？蚰疼痛，肉宣露，涎血臭气。常服令牙齿坚牢，解骨槽毒气。', '-', '-', '升麻', '[\'牛膝\', \'去芦\', \'十斤\', \'木律\', \'四十四\', \'两黄茄\', \'细切\', \'二十个\', \'郁李仁\', \'二十两\', \'麝香\', \'皮子\', \'一百个\', \'已上\', \'五味\', \'捣碎\', \'罐子\', \'内上\', \'用瓦子\', \'盖口\', \'一小\', \'窍用\', \'固济\', \'烧令通\', \'取出\', \'以新土\', \'一伏时\', \'取出\', \'后入\', \'项药\', \'升麻\', \'细辛\', \'十斤\', \'上件\', \'细末\']');
INSERT INTO `prescript` VALUES (59018, '46088', '牛黄丸', '牛黄15克（细研）龙脑7.5克（细研）天竺黄15克（细研）犀角屑 羚羊角屑 朱砂（细研，水飞过）黄芩 川升麻各30克 甘草7.5克（炙微赤，锉）防风15克（去芦头）麝香3克（细研）真珠15克（细研）', '每服15丸，以温水嚼下，不计时候。', '《太平圣惠方》卷十一', '清热熄风，化痰开窍。治阳毒伤寒，心胸烦闷，恍饱如狂，结热不散。', '-', '-', '升麻', '[\'牛黄\', \'克细研\', \'龙脑\', \'克细研\', \'天竺黄\', \'克细研\', \'犀角\', \'羚羊角\', \'朱砂\', \'细研水\', \'飞过\', \'黄芩\', \'升麻\', \'甘草\', \'克炙微\', \'防风\', \'克去\', \'芦头\', \'麝香\', \'克细研\', \'真珠\', \'克细研\']');
INSERT INTO `prescript` VALUES (59019, '46063', '黄柏汤', '黄柏 黄连 白头翁（一作白蔹）升麻 当归 牡蛎 石榴皮 黄芩 寄生 甘草各6克 犀角 艾叶各3克', '以水600毫升，煮取240毫升，百日至二百日小儿，一服40毫升，二百余日至一岁小儿，一服50毫升。', '《备急千金要方》卷十五', '治小儿夏月外感寒邪，迫使热气内入肠胃，下痢赤白，状如鱼脑，壮热头痛，手足烦。', '-', '-', '升麻', '[\'黄柏\', \'黄连\', \'白头翁\', \'一作\', \'白蔹\', \'升麻\', \'当归\', \'牡蛎\', \'石榴皮\', \'黄芩\', \'寄生\', \'甘草\', \'犀角\', \'艾叶\']');
INSERT INTO `prescript` VALUES (59020, '46056', '清胃散', '生地黄 当归身各0.9克 牡丹皮1.5克 黄连1.8克（如质次，更加O.6克。夏月倍之）升麻3克', '上为细末，都作一服。用水230毫升，煎至150毫升，去滓冷服。', '《脾胃论》卷下', '清胃凉血。治胃中积热，上下牙痛不可忍，牵引头部，满面发热，其齿喜寒恶热；或牙龈红肿，溃烂出血；或唇口腮颊肿痛，口气臭热，口舌干燥，舌红苔黄，脉滑大而数。现用于三叉神经痛、口腔炎、牙周炎属于胃火上炎者。', '-', '-', '升麻', '[\'生地黄\', \'当归\', \'身各克\', \'牡丹皮\', \'黄连\', \'克如质次\', \'更加\', \'克夏\', \'升麻\']');
INSERT INTO `prescript` VALUES (59021, '46004', '羚羊角散', '羚羊角屑、桂心、茯神、麻黄（去根节）、葛根、附子（炮裂.去皮脐）、当归（炒）、酸枣仁、五加皮，各一两。升麻、枳壳（麸炒）、独活，各一两半。', '每服四钱，以水一中盏，入生姜半分，煎至五分，去滓，不拘时服。', '明方贤着《奇效良方》', '治风痱，筋脉缓弱，言语蹇涩。', '-', '-', '升麻', '[\'羚羊角\', \'桂心\', \'茯神\', \'麻黄\', \'根节\', \'葛根\', \'附子\', \'炮裂\', \'去皮\', \'当归\', \'酸枣仁\', \'五加皮\', \'一两\', \'升麻\', \'枳壳\', \'独活\', \'各一\', \'两半\']');
