INSERT INTO `prescript` VALUES (59028, '45746', '玄参升麻汤', '玄参、升麻、甘草（炙）、各三钱。', '上作一服，水二钟，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治伤寒失下，热毒在胃，发斑，甚则烦躁谵语。', '-', '-', '升麻', '[\'玄参\', \'升麻\', \'甘草\', \'各三钱\']');
INSERT INTO `prescript` VALUES (59029, '45727', '补虚款冬花汤', '款冬花22.5克 人参15克 升麻15克 桔梗22.5克（炒）杏仁（汤浸，去皮、尖、双仁，炒）30克 白茯苓（去黑皮）22.5克 甘草（炙，锉）4克 干姜（炮）7.5克 柴胡（去苗）45克 天门冬（去心，焙）15克 鳖甲（去裙襕，醋炙）30克 黄耆（细锉）15克 桑根白皮（锉，炒）22.5克 肉苁蓉（酒浸，去皴皮，炙）30克', '每服15克，用水225毫升，煎至180毫升，去滓，食后温服，一日三次。', '《圣济总录》卷八十六', '主肺劳痰嗽，日渐羸瘦。', '-', '-', '升麻', '[\'款冬花\', \'人参\', \'升麻\', \'桔梗\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'炒克\', \'白茯苓\', \'黑皮\', \'甘草\', \'干姜\', \'炮克\', \'柴胡\', \'苗克\', \'天门冬\', \'心焙克\', \'鳖甲\', \'炙克\', \'黄耆\', \'锉克桑根\', \'白皮\', \'炒克\', \'肉苁蓉\', \'酒浸\', \'皮炙克\']');
INSERT INTO `prescript` VALUES (59030, '45624', '菊花清燥汤', '甘菊花6克 当归 生地 白芍（酒炒）川芎 知母 贝母（去心，研）地骨皮 麦冬（去心）各3克 柴胡 黄芩 升麻 犀角（镑）甘草（生）各1.5克', '上药加竹叶20片，灯心70厘米，用水400毫升，煎至320毫升，食后温服。', '《医宗金鉴》卷六十八', '清热解毒，泻火凉血。治石榴疽。色红焮肿，坚硬疼痛，破翻如榴，寒热如疟。', '-', '-', '升麻', '[\'甘菊花\', \'当归\', \'生地\', \'白芍\', \'川芎\', \'知母\', \'贝母\', \'心研\', \'地骨皮\', \'麦冬\', \'去心\', \'柴胡\', \'黄芩\', \'升麻\', \'犀角\', \'甘草\', \'生各克\']');
INSERT INTO `prescript` VALUES (59031, '45572', '乌梅丸', '乌梅30克（微炒）鳖甲60克（涂醋，炙令黄，去裙襕）川升麻30克 柴胡45克（去苗）甘草20克（生用）麦门冬30克（去心，焙）虎头骨60克（涂酥，炙令黄）天灵盖30克（涂酥，炙令黄）川大黄30克（锉碎，微炒）桃仁30克（汤浸，去皮、尖、双仁，麸炒微黄）', '每于空腹时以粥饮下20丸。', '《太平圣惠方》卷五十二', '治久疟，往来寒热，经年不愈，形体瘦弱；亦治劳疟。', '-', '-', '升麻', '[\'乌梅\', \'克微\', \'鳖甲\', \'克涂\', \'炙令\', \'黄去\', \'升麻\', \'柴胡\', \'克去\', \'甘草\', \'克生用\', \'麦门冬\', \'克去\', \'心焙虎\', \'头骨\', \'克涂\', \'炙令\', \'天灵盖\', \'克涂\', \'炙令\', \'黄川\', \'大黄\', \'桃仁\', \'克汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (59032, '45546', '升麻汤', '升麻30克 苍术30克 荷叶1个（全者）', '上为细末，每服15克，水150毫升，煎100毫升，食后温服。或烧全荷叶1个，研细调煎药服。', '《素问病机气宜保命集》卷下引《太平惠民和剂局方》', '治雷头风。', '-', '-', '升麻', '[\'升麻\', \'苍术\', \'荷叶\', \'全者\']');
INSERT INTO `prescript` VALUES (59033, '45520', '羌活升麻汤', '羌活 升麻 葛根 白芍药 人参 黄芩各3克 黄连 石膏 甘草 生地黄 知母各2.1克', '上药用水300毫升，加生姜3片，大枣1枚，煎至240毫升，温服。', '《古今医统》卷二十五', '治夏令时行瘟热，表里俱热者。', '-', '-', '升麻', '[\'羌活\', \'升麻\', \'葛根\', \'芍药\', \'人参\', \'黄芩\', \'黄连\', \'石膏\', \'甘草\', \'生地黄\', \'知母\']');
INSERT INTO `prescript` VALUES (59034, '45406', '清胃汤', '石膏（煅）12克 黄芩 生地各3克 丹皮4.5克 黄连 升麻各3克', '用水400毫升，煎至320毫升，食后服。', '《医宗金鉴》卷六十五', '清胃泻火。治胃经实热之牙衄，血出如涌，口臭而牙不动。', '-', '-', '升麻', '[\'石膏\', \'煅克\', \'黄芩\', \'生地\', \'丹皮\', \'黄连\', \'升麻\']');
INSERT INTO `prescript` VALUES (59035, '45394', '清胃饮', '当归 生地 黄芩 石膏各3克 升麻1.5克 白芍药 青皮 黄连各2.4克 甘草 牡丹皮各1.2克 栀子仁 苍术各3克 细辛 藿香 荆芥穗各1.8克', '用水400毫升，煎至320毫升，食后缓缓含呷之。', '《古今医统》卷六十四', '治牙床肿痛，出血动摇，因风湿热痰而成者。', '-', '-', '升麻', '[\'当归\', \'生地\', \'黄芩\', \'石膏\', \'升麻\', \'克白\', \'芍药\', \'青皮\', \'黄连\', \'甘草\', \'牡丹皮\', \'栀子\', \'苍术\', \'细辛\', \'藿香\', \'荆芥\']');
INSERT INTO `prescript` VALUES (59036, '45390', '通窍汤', '防风 羌活 藁本 升麻 干葛 川芎 苍术各3克 麻黄 白芷各1.5克 川椒 细辛 甘草各0.9克', '上药加生姜3片，葱白1根，水煎，热服。', '《古今医鉴》卷九', '散寒通窍。治外感风寒，鼻塞声重流涕。', '-', '-', '升麻', '[\'防风\', \'羌活\', \'藁本\', \'升麻\', \'干葛\', \'川芎\', \'苍术\', \'麻黄\', \'白芷\', \'各克川椒\', \'细辛\', \'甘草\']');
INSERT INTO `prescript` VALUES (59037, '45373', '玉龙膏', '瓜蒌（大者一个.去皮），黄蜡一两半，白芷（净拣）半两，麻油（清真者）六两，麝香（研）一钱，松脂（研）一钱半，零香、藿香，各一两；杏仁（去皮.尖）、升麻黄蓍、赤芍药、白芨、白蔹、甘草（净拣），各一分。', '每用少许，薄摊绢帛上贴。\r\n若头面风癣痒，疮肿疼痛，并涂磨令热，频频用之。\r\n如耳鼻中肉铃，用纸拈子每日点之，至一月即愈。\r\n如治灸疮及小儿瘤疮，涂之兼灭瘢痕，神效。', '《宋太平惠民和剂局方》', '摩风止痛，消肿化毒。治一切伤折疮肿。', '-', '上以油浸七日，却比出油，先炼令香熟，放冷入诸药，慢火煎黄色，用绢滤去渣。入银、石锅内，入蜡并麝香、松脂，熬少时，以瓷盒器盛。', '升麻', '[\'大者\', \'一个\', \'去皮\', \'黄蜡\', \'两半\', \'白芷\', \'半两\', \'麻油\', \'清真\', \'六两\', \'麝香\', \'研一钱\', \'松脂\', \'研一钱\', \'半零香\', \'藿香\', \'一两\', \'杏仁\', \'皮尖\', \'麻黄\', \'芍药\', \'白芨\', \'白蔹\', \'甘草\', \'一分\']');
INSERT INTO `prescript` VALUES (59038, '45333', '紫雪', '石膏、寒水石、磁石、滑石，已上四味各三斤，捣碎，水一斛，煮至四斗，去滓入下项：\r\n犀角屑、羚羊角屑、青木香（捣碎）、沉香（捣碎），各五两；玄参（洗.焙.捣碎）、升麻，各一斤；甘草（炒）八两，丁香一两（捣碎）。\r\n已上八味入前药汁中再煮，取一斗五升，去滓，入下项：\r\n朴硝（精者）十斤，硝石四升（如阙.芒硝亦得.每升重七两七钱半）。\r\n已上二味入前药汁中，微火上煎，柳木篦搅不住手，候有七升，投在木盆中，半日欲凝，入下项：\r\n麝香当门子一两二钱半（研）、朱砂（飞研）三两。\r\n已上二味入前药中，搅调令匀，寒之二日', '每服一钱或二钱，用冷水调下，大人、小儿临时以意加减，食后服。', '《宋太平惠民和剂局方》', '疗脚气，毒遍内外，烦热不解，口中生疮，狂易叫走，瘴疫毒疠，卒死温疟，五尸五疰，心腹诸疾，及解诸热药毒发，邪热卒黄等，并解蛊毒鬼魅，野道热毒。\r\n又治小儿惊俌百病。', '-', '-', '升麻', '[\'石膏\', \'寒水石\', \'磁石\', \'滑石\', \'已上\', \'四味\', \'三斤\', \'捣碎\', \'水一\', \'四斗\', \'去滓入\', \'下项\', \'犀角\', \'羚羊角\', \'青木香\', \'捣碎\', \'沉香\', \'捣碎\', \'五两\', \'玄参\', \'捣碎\', \'升麻\', \'一斤\', \'甘草\', \'八两\', \'丁香\', \'一两\', \'捣碎\', \'已上\', \'八味\', \'入前\', \'药汁\', \'一斗\', \'五升\', \'去滓入\', \'下项\', \'朴硝\', \'精者\', \'十斤\', \'硝石\', \'四升\', \'芒硝\', \'每升重\', \'七两\', \'七钱\', \'二味\', \'入前\', \'药汁\', \'微火\', \'柳木\', \'不住\', \'手候\', \'有七升\', \'投在\', \'木盆\', \'半日\', \'凝入\', \'下项\', \'麝香\', \'门子\', \'一两\', \'二钱\', \'半研\', \'朱砂\', \'飞研\', \'三两\', \'已上\', \'二味\', \'入前\', \'药中\', \'调令\', \'匀寒\', \'之二\']');
INSERT INTO `prescript` VALUES (59039, '45281', '麦门冬散', '小草（去心）、黄连（去须）、升麻（去粗皮）、犀角屑、甘草（炙）、枳壳（去瓤.炒黄）、黄芩、大青（去根），各半两；芒硝一两，麦门冬（去心）三分。', '每服三钱，水一盏，煎至七分，食后温服。', '《宋太平惠民和剂局方》', '治丈夫、妇人蕴积邪热，心胸烦闷，咽干口燥，睡卧不安，或大、小肠不利，口舌生疮，并皆治之。', '-', '上为细末。', '升麻', '[\'小草\', \'去心\', \'黄连\', \'升麻\', \'粗皮\', \'犀角\', \'甘草\', \'枳壳\', \'黄芩\', \'大青去\', \'半两\', \'芒硝\', \'一两\', \'麦门冬\', \'去心\', \'三分\']');
INSERT INTO `prescript` VALUES (59040, '45280', '栀子膏', '栀子仁30克 川升麻30克 犀角屑22克 蛇衔39克 兰叶60克（切）生地黄60克 黄芩30克', '涂故帛上，贴患处。', '《太平圣惠方》卷九十', '治小儿热毒疮。', '-', '-', '升麻', '[\'栀子\', \'仁克川\', \'升麻\', \'犀角\', \'克兰\', \'叶克切\', \'生地黄\', \'黄芩\']');
INSERT INTO `prescript` VALUES (59041, '45265', '升麻葛根汤', '升麻、白芍药、甘草（炙），各十两。葛根十五两。', '每服三钱，用水一盏半，煎取一中盏，去滓，稍热服，不计时候，日二、三服，以病气去，身清凉为度。小儿量力服之。', '《宋太平惠民和剂局方》', '冶大人、小儿时气温疫，头痛发热，肢体烦疼；及疮疹已发及未发，疑贰之间，并宜服之。', '-', '上为粗末。', '升麻', '[\'升麻\', \'芍药\', \'甘草\', \'十两\', \'葛根\', \'十五两\']');
INSERT INTO `prescript` VALUES (59042, '45226', '升麻和气饮', '干姜、熟枳壳，各半钱；干葛、熟苍术、桔梗、升麻，各一两；当归、熟半夏、茯苓、白芷，各二钱；陈皮、甘草，各一两半；芍药七钱半，大黄（蒸）半两。', '每服四大钱，水一盏半，姜三片，灯心十五茎，煎至七分，去渣，食前服。', '《宋太平惠民和剂局方》', '治疮疥发于四肢，臀髀痛痒不常，甚至憎寒发热，攻刺疼痛，浸淫浮肿。\r\n又癞风入脏，阴下湿痒，耳鸣眼痛，皆治之。', '-', '上为散。', '升麻', '[\'干姜\', \'枳壳\', \'各半\', \'干葛熟\', \'苍术\', \'桔梗\', \'升麻\', \'一两\', \'当归\', \'半夏\', \'茯苓\', \'白芷\', \'各二钱\', \'陈皮\', \'甘草\', \'各一\', \'两半\', \'芍药\', \'七钱\', \'大黄\', \'半两\']');
INSERT INTO `prescript` VALUES (59043, '45214', '百解散', '干葛75克 升麻 赤芍药各60克 黄芩30克 麻黄22.5克 薄桂（去粗皮）7，5克 甘草45克', '每服6克，用水150毫升，加生姜3片、葱1根，煎至100毫升，温服。\r\n风热盛者，加薄荷。', '《活幼心书》卷下', '治小儿外感风寒，鼻流清涕，头痛发热，昼轻夜重。', '-', '-', '升麻', '[\'干葛克\', \'升麻\', \'芍药\', \'黄芩\', \'麻黄\', \'克薄桂去\', \'粗皮克\', \'甘草\']');
INSERT INTO `prescript` VALUES (59044, '45144', '五香连翘汤', '沉香（不见火）、乳香（不见火.研）、甘草（生）、舶上青、木香（不见火），各一分；连翘（去蒂）、射干、升麻、桑寄生（无.以升麻代之）、独活（今铺家所卖者.只是宿前胡.或是土当归.不堪用.只用羌活.甚妙）、木通（去节），各三分；丁香（不见火）半两，大黄（蒸）三两，麝香（真者.别研）一钱半。', '每服四大钱，水二盏，煮取一盏以上，去渣，取八分清汁，空心热服。', '《宋太平惠民和剂局方》', '治一切恶核，瘰疠痈疽，恶肿等病。', '-', '上俰咀。', '升麻', '[\'沉香\', \'不见\', \'乳香\', \'不见\', \'火研\', \'甘草\', \'生舶\', \'青木香\', \'不见\', \'一分\', \'连翘\', \'射干\', \'升麻\', \'桑寄生\', \'无以\', \'升麻\', \'代之\', \'独活\', \'今铺\', \'卖者\', \'只是\', \'宿前\', \'或是\', \'当归\', \'不堪\', \'只用\', \'羌活\', \'甚妙\', \'木通\', \'去节\', \'三分\', \'丁香\', \'不见\', \'半两\', \'大黄\', \'三两\', \'麝香\', \'真者\', \'研一钱\']');
INSERT INTO `prescript` VALUES (59045, '45107', '升麻酒', '升麻100g 清酒1000ml', '每次30～50ml，每日2次，将酒温热空腹服用。服后当吐下恶物，勿怪。', '唐孙思邈《千金翼方》', '益气止血。用于时气疫疠，头痛寒热，口疮，喉痛，斑疹不透，中气下陷，久痢脱肛，妇女崩带，子宫下坠等症。主治产后恶血不止气虚，不能摄血，故血下不止。', '-', '1. 把升麻与清酒一同放入锅内，大火煮沸，文火煎至一半；\r\n2. 过滤去渣，装瓶备用。', '升麻', '[\'升麻\', \'清酒\']');
INSERT INTO `prescript` VALUES (59046, '45057', '茵陈蒿大黄汤', '茵陈蒿、山栀仁、柴胡、黄檗（蜜炙）、龙胆草，各半两。黄芩（去黑心）、升麻、大黄（炒），各一两。', '每服五钱，水一盏半，煎至一盏，去滓，早晚食后温服。', '明方贤着《奇效良方》', '治伤寒发黄，面目悉黄，小便赤，宜服。', '-', '-', '升麻', '[\'茵陈蒿\', \'山栀仁\', \'柴胡\', \'黄檗\', \'龙胆草\', \'半两\', \'黄芩\', \'黑心\', \'升麻\', \'大黄\', \'一两\']');
INSERT INTO `prescript` VALUES (59047, '45021', '朱砂丸', '朱砂（水飞.一两） 犀角屑（一两） 羚羊角屑（一两） 人参（去芦.一两） 茯神（去木.一两） 远志（去心.一两） 龙齿（研.一两） 麦门冬（去心.一两半） 铁粉（另研.一两半） 防风（去芦.七钱） 黄芩（七钱） 汉防己（去皮.七钱） 秦艽（去土.七钱） 天麻（七钱） 升麻（七钱） 铅霜（另研.半钱）', '每服二十丸，粳米汤送下。不拘时服。', '明方贤着《奇效良方》', '治心脏中风，手足惊掣，心神狂乱，恍惚烦闷，言语蹇涩，并宜服之。', '-', '上为细末，入研药匀，炼蜜和捣三四百下，丸如梧桐子大。', '升麻', '[\'朱砂\', \'水飞\', \'一两\', \'犀角\', \'一两\', \'羚羊角\', \'一两\', \'人参\', \'去芦\', \'一两\', \'茯神\', \'一两\', \'远志\', \'去心\', \'一两\', \'龙齿\', \'一两\', \'麦门冬\', \'两半\', \'铁粉\', \'研一\', \'两半\', \'防风\', \'芦七钱\', \'黄芩\', \'七钱汉\', \'防己\', \'去皮\', \'七钱\', \'秦艽\', \'土七钱\', \'天麻\', \'七钱\', \'升麻\', \'七钱\', \'研半钱\']');
INSERT INTO `prescript` VALUES (59048, '44989', '清肺饮子', '白芍药1.5克 人参 升麻 柴胡各1.2克 天门冬 麦门冬（去心）各0。9克 陈皮0.75克 甘草（生）黄芩 黄柏 甘草（炙）各0.6克', '上药哎咀，作一服。用水300毫升，煎至150毫升，去滓，食后温服。\r\n汗多，加黄耆1.5克。', '《卫生宝鉴》卷八', '治暑热伤气耗阴。', '-', '-', '升麻', '[\'芍药\', \'人参\', \'升麻\', \'柴胡\', \'天门冬\', \'麦门冬\', \'去心\', \'陈皮克\', \'甘草\', \'黄芩\', \'黄柏\', \'甘草\']');
INSERT INTO `prescript` VALUES (59049, '44916', '龟柏丸', '龟版60克 侧柏叶45克 芍药45克 椿根皮23克 升麻 香附各15克', '用四物汤加白术、黄连、陈皮、甘草、生姜煎汤送下。', '《医学入门》卷七', '治便血久而致虚，腰脚软痛。并治麻风疮痒见血。', '-', '-', '升麻', '[\'龟版克\', \'侧柏叶\', \'芍药\', \'克椿根\', \'皮克\', \'升麻\', \'香附\']');
INSERT INTO `prescript` VALUES (59050, '44818', '加减升麻葛根汤', '川升麻1.5克 生甘草1.5克 连翘壳6克 炙僵蚕9克 粉葛根4.5克 苦桔梗3克 金银花9克 干荷叶1角 薄荷叶2.4克 京赤芍6克 净蝉衣2.4克 陈莱菔9克', '水煎服。', '《喉痧症治概要》', '治痧麻虽布，而头面鼻独无，身热泄泻，咽痛不腐者。', '-', '-', '升麻', '[\'升麻\', \'生甘草\', \'连翘\', \'壳克\', \'僵蚕\', \'克粉\', \'葛根\', \'克苦\', \'桔梗\', \'金银花\', \'克干\', \'荷叶\', \'薄荷叶\', \'克京\', \'赤芍\', \'克净\', \'蝉衣\', \'莱菔\']');
INSERT INTO `prescript` VALUES (59051, '44725', '丹砂散', '朱砂22.5克（细研，水飞过）犀角屑15克 天竹黄15克 秦艽15克（去苗）白鲜皮15克 沙参15克（去芦头）寒水石30克 麦门冬60克 （去心，焙干）马牙消15克（研入）川升麻15克 甘草15克（炙微赤，锉）龙脑3克（研入）', '每服3克，以温水调下，不计时候。', '《太平圣惠方》卷二十三', '疏风清热，养阴润肺。主风热，心肺壅滞，时多烦闷。', '-', '-', '升麻', '[\'朱砂\', \'克细\', \'研水\', \'飞过\', \'犀角\', \'天竹黄\', \'秦艽\', \'克去\', \'白鲜皮\', \'沙参\', \'克去\', \'芦头\', \'寒水石\', \'麦门冬\', \'克去\', \'焙干\', \'马牙\', \'消克研\', \'入川\', \'升麻\', \'甘草\', \'克炙微\', \'龙脑\', \'研入\']');
INSERT INTO `prescript` VALUES (59052, '44675', '蓝叶散', '白芷 柴胡 知母 杏仁（去皮）川芎 赤芍药 生地黄 川升麻 干葛 生甘草各7.5克 烂石膏 栀子仁各3.75克 蓝叶（日干）7.5克', '每次4.5克，水煎服。\r\n热甚，加黄芩、玄参。', '《仁斋直指》卷二十四', '治诸丹发热赤肿。', '-', '-', '升麻', '[\'白芷\', \'柴胡\', \'知母\', \'杏仁\', \'去皮\', \'川芎\', \'芍药\', \'生地黄\', \'升麻\', \'干葛\', \'生甘草\', \'各克烂\', \'石膏\', \'栀子\', \'蓝叶日\', \'干克\']');
INSERT INTO `prescript` VALUES (59053, '44664', '上二黄丸', '黄芩60克 黄连（去须，酒浸）30克 升麻 柴胡各9克 甘草6克', '每服50～70丸，空腹用温开水送下。', '《内外伤辨》卷下', '治热食伤中，胸脘痞闷，兀兀欲吐，烦乱不安。', '-', '-', '升麻', '[\'黄芩\', \'黄连\', \'浸克\', \'升麻\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (59054, '44613', '樗根白皮丸', '樗根白皮21克 白术 枳实（面炒）茯苓 柴胡 升麻各6克 黄柏（盐水炒）知母（盐水炒）牡蛎（煅）各9克 韭子（炒）30克 芍药（炒）15克', '每服50丸，空腹时用盐汤送下。', '《古今医鉴》卷八', '主湿热伤脾之遗精。', '-', '-', '升麻', '[\'白皮\', \'白术\', \'枳实\', \'茯苓\', \'柴胡\', \'升麻\', \'黄柏\', \'盐水\', \'知母\', \'盐水\', \'牡蛎\', \'韭子\', \'炒克\', \'芍药\', \'炒克\']');
INSERT INTO `prescript` VALUES (59055, '44575', '导滞通幽汤', '当归、桃仁泥，各一钱半。生地黄、熟地黄，各二钱。升麻，一钱。红花、甘草，各半钱。', '上作一服，水二钟，煎至一钟，去滓，调槟榔细末二钱，稍温服。', '明方贤着《奇效良方》', '治大便难，幽门不通上冲，吸门不开噎塞，不便燥闭，气不得下。治在幽门，以此润之。', '-', '-', '升麻', '[\'当归\', \'桃仁\', \'一钱\', \'生地黄\', \'熟地黄\', \'各二钱\', \'升麻\', \'一钱\', \'红花\', \'甘草\', \'各半\']');
INSERT INTO `prescript` VALUES (59056, '44554', '千金漏芦汤', '漏芦（去芦）、麻黄（去根.节）升麻、赤芍药（生）、黄芩（去皮）、甘草（生）、白敛（净洗）、白芨（去须）、枳壳（米泔浸一宿.去白），各四两；生大黄一十三两。', '每服四钱，水二盏，煎至一盏，纱帛滤去渣，空心，食前热服，以快利为度。病人更自量，增损服之，立效。\r\n若发背、痈疽已成脓者，当排脓，服之，直至脓尽。', '《宋太平惠民和剂局方》', '治痈疽发背，丹毒恶肿，时行热毒，发作赤色，瘰倁初发，头目赤痛，暴生障翳，吹奶肿痛。\r\n一切无名恶疮，虽觉所苦细微，不可轻慢，急服此药，并皆内消，更不成脓。', '-', '上俰咀。', '升麻', '[\'漏芦\', \'去芦\', \'麻黄\', \'根节\', \'升麻\', \'芍药\', \'黄芩\', \'去皮\', \'甘草\', \'生白敛\', \'白芨\', \'枳壳\', \'泔浸\', \'一宿\', \'四两\', \'大黄\', \'一十三\']');
INSERT INTO `prescript` VALUES (59057, '44546', '羚羊角散', '羚羊角（镑）、黄芩、升麻、甘草（炙）、车前子，各十两；栀子仁、草龙胆，各五两；决明子二十两。', '每一钱，食后温热水调下，日进三服，小儿可服半钱。', '《宋太平惠民和剂局方》', '治大人、小儿一切风热毒，上攻眼目，暴发赤肿，或生疮疼痛，隐涩羞明。', '-', '上为末。', '升麻', '[\'羚羊角\', \'黄芩\', \'升麻\', \'甘草\', \'车前子\', \'十两\', \'栀子\', \'仁草\', \'龙胆\', \'五两\', \'决明子\', \'二十两\']');
INSERT INTO `prescript` VALUES (59058, '44542', '当归润燥汤', '当归、熟地黄、生地黄、大黄、桃仁泥、麻仁、甘草，各一钱。升麻，二钱。红花，半钱。', '上作一服，水二钟，煎至一钟，空心热服。', '明方贤着《奇效良方》', '润燥。', '-', '-', '升麻', '[\'当归\', \'熟地黄\', \'生地黄\', \'大黄\', \'桃仁\', \'麻仁\', \'甘草\', \'各一钱\', \'升麻\', \'二钱\', \'红花\', \'半钱\']');
INSERT INTO `prescript` VALUES (59059, '44505', '枳壳人参丸', '枳壳（麸炒.一两半） 人参（一两半） 羌活（一两半） 甘菊花（未开者良.微炒.一两） 防风（去叉.二两） 羚羊角（镑.一两半） 葛根（一两） 熟地黄（焙.二两） 升麻（二两） 黄连（二两） 桂心（一两） 薏苡仁（炒.一两）', '每服二十丸，渐加至三十丸，空心温酒下，日再。', '明方贤着《奇效良方》', '治偏风，手足一边不随，筋骨烦疼。', '-', '上为细末，炼蜜为丸，如梧桐子大。', '升麻', '[\'枳壳\', \'两半\', \'人参\', \'两半\', \'羌活\', \'两半\', \'甘菊花\', \'未开者\', \'良微\', \'一两\', \'防风\', \'二两\', \'羚羊角\', \'两半\', \'葛根\', \'一两\', \'熟地黄\', \'二两\', \'升麻\', \'二两\', \'黄连\', \'二两\', \'桂心\', \'一两\', \'薏苡仁\', \'一两\']');
INSERT INTO `prescript` VALUES (59060, '44501', '加味牛黄散', '牛黄（另研.二钱半） 麝香（另研.二钱半） 犀角屑（二钱半） 羚羊角屑（二钱半） 龙齿（另研.二钱半） 防风（去芦.二钱半） 天麻（二钱半） 独活（去芦.二钱半） 人参（去芦.二钱半） 茯神（去木.二钱半） 川升麻（二钱半） 甘草（炙.二钱半） 白藓皮（二钱半） 远志（去心.二钱半） 天竺黄（另研.二钱半） 朱砂（水飞.半两） 铁粉（另研.半两） 麦门冬（去心.半两）', '上为细末，研匀，每服二钱，煎麦门冬汤调下。不拘时服。', '明方贤着《奇效良方》', '治心脏中风，恍惚恐惧，闷乱不得睡卧，志意不定，言语错乱，并皆治之。', '-', '-', '升麻', '[\'牛黄\', \'研二钱\', \'麝香\', \'研二钱\', \'犀角\', \'羚羊角\', \'龙齿\', \'研二钱\', \'防风\', \'芦二钱\', \'天麻\', \'二钱\', \'独活\', \'芦二钱\', \'人参\', \'芦二钱\', \'茯神\', \'木二钱\', \'半川\', \'升麻\', \'二钱\', \'甘草\', \'炙二钱\', \'半白\', \'皮二钱\', \'远志\', \'心二钱\', \'天竺黄\', \'研二钱\', \'朱砂\', \'水飞\', \'半两\', \'铁粉\', \'另研\', \'半两\', \'麦门冬\', \'去心\', \'半两\']');
INSERT INTO `prescript` VALUES (59061, '44498', '升麻葛根汤', '升麻、芍药、甘草，各二钱半。干葛，三钱。', '上作一服，水二钟，生姜三片，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治大人小儿时气瘟疫，头痛发热，及疮疹已发未发皆可服。', '-', '-', '升麻', '[\'升麻\', \'芍药\', \'甘草\', \'各二钱\', \'半干\', \'葛三钱\']');
INSERT INTO `prescript` VALUES (59062, '44496', '十神汤', '川芎、麻黄（去节）、干葛、紫苏、赤芍药、升麻、白芷、甘草（炙）、陈皮、香附，各一钱半。', '上作一服，水二钟，生姜五片，煎至一钟，不拘时服。', '唐孙思邈《千金翼方》', '治伤寒，时令不正，瘟疫妄行，感冒发热，或欲出疹，不问阴阳，两感风寒，并皆治之。', '-', '-', '升麻', '[\'川芎\', \'麻黄\', \'去节\', \'干葛\', \'紫苏\', \'芍药\', \'升麻\', \'白芷\', \'甘草\', \'陈皮\', \'香附\', \'各一钱\']');
INSERT INTO `prescript` VALUES (59063, '44478', '柴胡升麻汤', '柴胡（去苗）、干葛、荆芥（去梗）、赤芍药、石膏，各一钱半。前胡（去苗）、升麻、桑白皮、黄芩，各一钱。', '上作一服，水二钟，生姜三片，豆豉二十粒，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，咳嗽涕唾稠黏。', '-', '-', '升麻', '[\'柴胡\', \'苗干葛\', \'荆芥\', \'芍药\', \'石膏\', \'各一钱\', \'半前\', \'胡去\', \'升麻\', \'桑白皮\', \'黄芩\', \'各一钱\']');
INSERT INTO `prescript` VALUES (59064, '44374', '柴胡石膏散', '赤芍药、柴胡（去苗）、前胡（去苗）、石膏（煅）、干葛，各五十两。升麻二十五两，黄芩、桑白皮，各三十七两半。荆芥穗（去土）三十七两。', '每服二钱，水一盏，入生姜三片，豉十馀粒，同煎七分，去滓，稍热服。\r\n小儿分作三服，更量大小加减，不计时候。', '《宋太平惠民和剂局方》', '治时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，心胸如满，寒热往来，痰实咳嗽，涕唾稠粘。', '-', '上为粗末。', '升麻', '[\'芍药\', \'柴胡\', \'苗前\', \'胡去\', \'石膏\', \'干葛\', \'五十两\', \'升麻\', \'二十五两\', \'黄芩\', \'桑白皮\', \'三十七\', \'两半\', \'荆芥\', \'三十七\']');
INSERT INTO `prescript` VALUES (59065, '44335', '定神琥珀丸', '琥珀（研）、珍珠（研）、牛黄（研）、天竺黄（研）、铁粉（研）、龙齿（研），各一两。腻粉（研）、犀角（镑）、甘草（炙.锉）、龙胆、升麻、露蜂房（微炙）、丹砂（研）、防风（去叉）、黄芩（去黑心）、麦门冬（去心.焙）、白芍药、钩藤、人参、远志（去心）、知母（焙）、天门冬（去心.焙）、茯神（去木），各三分。干蝎（酒炒.一两半）、麝香（研.一分）、金箔（一百片）、银箔（一百片.与金箔丹砂同研.断星）、石菖蒲（九节者去须.米泔浸.切焙.三分）。', '每服二十丸，食后临卧煎甜竹叶汤下，日二夜一。', '明方贤着《奇效良方》', '治心中风，发动不知，渐成癫痫，惊悸恍惚。', '-', '上除别研药外为末，入研者同研，令断星，炼蜜和捣三百杵，丸如梧桐子大。', '升麻', '[\'琥珀\', \'珍珠\', \'牛黄\', \'天竺黄\', \'铁粉\', \'龙齿\', \'研各\', \'一两\', \'粉研\', \'犀角\', \'甘草\', \'龙胆\', \'升麻\', \'露蜂房\', \'丹砂\', \'防风\', \'黄芩\', \'黑心\', \'麦门冬\', \'心焙白\', \'芍药\', \'钩藤\', \'人参\', \'远志\', \'去心\', \'知母\', \'天门冬\', \'茯神\', \'三分\', \'干蝎酒\', \'两半\', \'麝香\', \'一分\', \'金箔\', \'一百\', \'银箔\', \'一百\', \'金箔\', \'丹砂\', \'同研\', \'断星\', \'石菖蒲\', \'九节\', \'浸切\', \'三分\']');
INSERT INTO `prescript` VALUES (59066, '44302', '紫雪', '石膏144g 寒水石144g 滑石144g 磁石144g 玄参48g 木香15g 沉香15g 升麻48g 甘草24g 丁香3g 芒硝（制）480g 硝石（精制）96g 水牛角浓缩粉9g 羚羊角4.5g 麝香3.6g 朱砂9g', '口服，一次1.5～3g，一日2次；周岁小儿一次0.3g，五岁以内小儿每增一岁，递增0.3g，一日1次；五岁以上小儿酌情服用。', '《中国药典》2015版', '清热解毒，止痉开窍。用于热病，高热烦躁，神昏谵语，惊风抽搐，斑疹吐衄，尿赤便秘。', '孕妇禁用。', '上十六味，石膏、寒水石、滑石、磁石砸成小块，加水煎煮三次。玄参、木香、沉香、升麻、甘草、丁香用石膏等煎液煎煮三次，合并煎液，滤过，滤液浓缩成膏；芒硝、硝石粉碎，兑入膏中，混匀，干燥，粉碎成中粉或细粉；羚羊角锉研成细粉；朱砂水飞成极细粉；将水牛角浓缩粉、麝香研细，与上述粉末配研，过筛，混匀，即得。', '升麻', '[\'石膏\', \'寒水石\', \'滑石\', \'磁石\', \'玄参\', \'木香\', \'沉香\', \'升麻\', \'甘草\', \'丁香\', \'芒硝\', \'硝石\', \'精制\', \'水牛角\', \'浓缩\', \'羚羊角\', \'麝香\', \'朱砂\']');
INSERT INTO `prescript` VALUES (59067, '44284', '红雪', '川朴消5千克 羚羊角屑90克 川升麻90克 黄芩90克 枳壳60克（麸炒微黄，去瓤）赤芍药60克 人参60克（去芦头）淡竹叶60克 甘草60克（生用）木香60克 槟榔60克 葛根45克 大青45克 桑根白皮45克 蓝叶45克 木通45克 栀子45克 朱砂30克（细研）苏枋90克（捶碎）麝香15克（细研）', '每服3～6克，以新汲水调下。', '《太平圣惠方》卷九十五', '清热解毒，开窍安神，解热破积。治伤寒狂躁，温瘴脚气，湿热黄疸，头昏目昏，鼻口生疮，喉痹重舌，肠痈。', '-', '-', '升麻', '[\'川朴消\', \'羚羊角\', \'克川\', \'升麻\', \'黄芩\', \'枳壳\', \'克麸\', \'微黄\', \'芍药\', \'人参\', \'克去\', \'芦头\', \'淡竹叶\', \'甘草\', \'克生用\', \'木香\', \'槟榔\', \'葛根\', \'克大青克桑根\', \'白皮\', \'克蓝叶克\', \'木通\', \'栀子\', \'朱砂\', \'克细\', \'研苏枋\', \'麝香\', \'克细研\']');
INSERT INTO `prescript` VALUES (59068, '44191', '神仙百解散', '山茵陈、柴胡（去芦）、前胡（生姜制.炒）、人参、羌活、独活、甘草、苍术（米泔浸.炒）、干葛、白芍药、升麻、防风（去苗）、藿香（去梗）、白术、半夏（姜汁炙），各一两。\r\n立春已后不加减。\r\n立夏已后加柴胡一分，赤茯苓、当归，各半两。\r\n立秋已后减柴胡一分，不用当归、茯苓，只加干姜（炮）、肉桂（去粗皮），各一分，麻黄（去节）半两。\r\n立冬已后并无加减。\r\n* 一方无当归，有黄芩（去芦）半两。', '每服三钱，水一盏半，姜三片，枣二个，煎至一盏，热服，不计时候，并进二服。\r\n如要表散，加葱白三寸，淡豆豉三十粒，同煎服，以衣被盖覆，汗出而愈。', '《宋太平惠民和剂局方》', '治伤寒遍身疼痛，百节拘急，头目昏痛，肢体劳倦，壮热憎寒，神志不爽；感冒瘟疫瘴气。常服辟瘟疫，治劳倦。', '-', '上为细末。', '升麻', '[\'茵陈\', \'柴胡\', \'芦前\', \'生姜\', \'人参\', \'羌活\', \'独活\', \'甘草\', \'苍术\', \'泔浸\', \'干葛白\', \'芍药\', \'升麻\', \'防风\', \'去苗\', \'藿香\', \'白术\', \'半夏\', \'姜汁\', \'两立\', \'加减\', \'立夏\', \'后加\', \'柴胡\', \'一分\', \'茯苓\', \'当归\', \'各半\', \'两立\', \'柴胡\', \'一分\', \'不用\', \'当归\', \'茯苓\', \'只加\', \'干姜\', \'肉桂\', \'粗皮\', \'一分\', \'麻黄\', \'去节\', \'半两\', \'立冬\', \'已后\', \'加减\', \'一方\', \'当归\', \'黄芩\', \'去芦\', \'半两\']');
INSERT INTO `prescript` VALUES (59069, '44090', '救苦化坚丸', '黄耆3克 人参1克 炙甘草1.5克 真漏芦 升麻各3克 葛根1.5克 连翘3克 牡丹皮1克 当归身 生地黄 熟地黄各1克 白芍药1克 肉桂0.6克 柴胡2.4克 黍粘子1克 羌活3克 独活 防风各1.5克 昆布0.6克 京三棱（煨）0.6克 广茂（煨）1克 益智仁0.6克 大麦蘖面3克 神曲末（炒黄色）0，6克 黄连（去须）1克 黄柏（炒）1克 厚朴9克（姜制）', '每服9克，白汤下。', '《兰室秘藏》卷下', '治瘰疬，马刀挟瘿，从耳下或耳后下颈至肩上，或入缺盆中。', '-', '-', '升麻', '[\'黄耆克\', \'人参\', \'甘草\', \'克真\', \'漏芦\', \'升麻\', \'葛根\', \'连翘\', \'牡丹皮\', \'当归\', \'生地黄\', \'熟地黄\', \'各克白\', \'芍药\', \'肉桂\', \'柴胡\', \'粘子克\', \'羌活\', \'独活\', \'防风\', \'昆布\', \'克京\', \'三棱\', \'克广茂\', \'益智仁\', \'大麦\', \'面克\', \'神曲\', \'黄色\', \'黄连\', \'须克\', \'黄柏\', \'炒克\', \'厚朴\', \'姜制\']');
INSERT INTO `prescript` VALUES (59070, '44085', '柴胡升麻汤', '柴胡（去芦）、前胡（去芦）、干葛、石膏（煅）、赤芍药，各十两。升麻五两，荆芥（去梗）七两半，黄芩（去粗皮）、桑白皮，各六两半。', '每服三大钱，水一盏半，生姜三片，豉十馀粒，同煎一盏，去滓，稍热服，不拘时。小儿更量大小加减。', '《宋太平惠民和剂局方》', '治时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，心胸烦满，寒热往来，痰盛咳嗽，涕唾稠粘。', '-', '上俰咀。', '升麻', '[\'柴胡\', \'芦前\', \'胡去\', \'芦干葛\', \'石膏\', \'芍药\', \'十两\', \'升麻\', \'五两\', \'荆芥\', \'两半\', \'黄芩\', \'粗皮\', \'桑白皮\', \'各六\', \'两半\']');
INSERT INTO `prescript` VALUES (59071, '44079', '升麻泻湿汤', '升麻、生地黄、熟地黄、苍术、青皮、黄栢、当归，各一钱。黄蓍，一钱半。桃仁泥、槐子，各五分。甘草，六分。', '上作一服，水二钟，煎至一钟，食前热服。', '明方贤着《奇效良方》', '治咽膈不通，逆气里急，大便不行。', '-', '-', '升麻', '[\'升麻\', \'生地黄\', \'熟地黄\', \'苍术\', \'青皮\', \'当归\', \'各一钱\', \'一钱\', \'桃仁\', \'泥槐子\', \'五分\', \'甘草\', \'六分\']');
INSERT INTO `prescript` VALUES (59072, '44043', '柏叶散', '柏叶 麻黄（去根节）山栀子（去皮）枳壳（去瓤，锉，麸炒）羌活（去芦）羊肝石 白蒺藜（炒，去角）升麻 子芩（去皮）防风 牛蒡子（隔纸炒）荆芥穗 茺蔚子 大黄（湿纸裹，甑上蒸）各15克 苦参30克 乌蛇1条（酒浸，去皮骨，焙干）', '每服6克，温水调下，一日七八次。', '《普济本事方》卷三引《庞老方》', '治疠风。', '-', '-', '升麻', '[\'柏叶\', \'麻黄\', \'根节\', \'栀子\', \'去皮\', \'枳壳\', \'羌活\', \'去芦\', \'羊肝\', \'石白\', \'蒺藜\', \'炒去\', \'角升\', \'麻子\', \'去皮\', \'防风\', \'牛蒡子\', \'隔纸\', \'荆芥\', \'茺蔚子\', \'大黄\', \'湿纸\', \'苦参\', \'乌蛇条\', \'去皮\', \'焙干\']');
INSERT INTO `prescript` VALUES (59073, '44041', '黄耆汤', '黄耆60克（锉）知母30克 石膏60克 白芍药30克 麦门冬30克（去心）甘草15克（炙微赤，锉）白茯苓30克 桂心30克 川升麻30克 熟干地黄30克 人参30克（去芦头）', '每服12克，用水250毫升，煎至180毫升，去滓温服，每日三至四次。', '《太平圣惠方》卷六十一', '益气阴，去客热。治痈溃后，气血两虚，内热口渴者。', '-', '-', '升麻', '[\'黄耆\', \'知母\', \'石膏\', \'克白\', \'芍药\', \'麦门冬\', \'克去\', \'甘草\', \'克炙微\', \'白茯苓\', \'克桂\', \'心克川\', \'升麻\', \'克熟\', \'地黄\', \'人参\', \'克去\', \'芦头\']');
INSERT INTO `prescript` VALUES (59074, '44007', '十神汤', '川芎、甘草（炙）、麻黄（去根.节）、升麻，各四两。干葛十四两，赤芍药、白芷、陈皮（去瓤）、紫苏（去粗梗）、香附子（杵去毛），各四两。', '每服三大钱，水一盏半，生姜五片，煎至七分，去滓，热服，不以时候。\r\n如发热头痛，加连须葱白三茎。\r\n如中满气实，加枳壳数片同煎服。\r\n虽产妇、婴儿、老人皆可服饵。\r\n如伤寒，不分表、里证，以此导引经络，不致变动，其功效非浅。', '《宋太平惠民和剂局方》', '治时令不正，瘟疫妄行，人多疾病。此药不问阴阳两感，或风寒湿痹，皆可服之。', '-', '上为细末。', '升麻', '[\'川芎\', \'甘草\', \'麻黄\', \'根节\', \'升麻\', \'四两\', \'干葛\', \'十四两\', \'芍药\', \'白芷\', \'陈皮\', \'紫苏\', \'香附子\', \'四两\']');
INSERT INTO `prescript` VALUES (59075, '43992', '牛黄丸', '牛黄（另研）、真珠（另研）、琥珀（另研）、铁粉（另研）、天竺黄（另研）、龙齿（另研）、麝香（另研）、人参（去芦）、龙胆草（去土）、升麻、防风（去叉）、黄芩（去黑心）、钩藤、犀角（镑）、水银、丹砂，各半两。金箔（七十片）、银箔（七十片.于水银丹砂金箔四味同研）、露蜂房（炙.一两）、全蝎（酒炒.一两）。知母、天门冬（去心）、石菖蒲（米泔浸切.酒炒）、白芍药、茯神、麦门冬（去心）、甘草（炙），各一两。', '夜卧及食后，煎新竹叶汤下十五丸至二十丸。', '明方贤着《奇效良方》', '治风邪变成癫痫，时时发动，不知人事，定心神风癫之状，发无常时，每发则仆地吐涎沫，无所觉知。盖由血气皆虚，精神离散，魂魄失守，风邪入于阴经故也。', '-', '上除别研药外，余药捣罗细末，却入另研药，再罗令匀，炼蜜拌和得所，更捣千杵，丸如梧桐子大。', '升麻', '[\'牛黄\', \'另研\', \'真珠\', \'另研\', \'琥珀\', \'另研\', \'铁粉\', \'另研\', \'天竺黄\', \'另研\', \'龙齿\', \'另研\', \'麝香\', \'另研\', \'人参\', \'去芦\', \'龙胆草\', \'升麻\', \'防风\', \'黄芩\', \'黑心\', \'钩藤\', \'犀角\', \'水银\', \'丹砂\', \'半两\', \'金箔\', \'七十\', \'银箔\', \'七十\', \'片于\', \'水银\', \'丹砂\', \'金箔\', \'四味\', \'同研\', \'露蜂房\', \'一两\', \'全蝎\', \'一两\', \'知母\', \'天门冬\', \'去心\', \'石菖蒲\', \'浸切\', \'芍药\', \'茯神\', \'麦门冬\', \'去心\', \'甘草\', \'一两\']');
INSERT INTO `prescript` VALUES (59076, '43987', '清燥汤', '苍术（米泔浸.一钱半） 五味子（十四枚） 黄蓍（一钱） 黄连（一钱） 白术（一钱） 橘皮（一钱） 白茯苓（去皮.一钱） 当归（一钱） 人参（七分） 生地黄（七分） 麦门冬（去心.半钱） 神曲（炒.半钱） 猪苓（半钱） 黄蘖（酒炒.半钱） 甘草（半钱） 泽泻（半钱） 柴胡（三分） 升麻（三分）', '上作一服，水二钟，煎至一钟，食前服。', '明方贤着《奇效良方》', '六七月之间，湿令大行，子能令母实而热旺，湿热相合，而刑庚踏肠，故寒凉救之，燥金受湿热之邪，寒水生化之源，源绝则肾亏，痿厥之病大作，腰以下痿软，瘫痪不能行走，不正，两足欹侧，以此药主之。', '-', '-', '升麻', '[\'苍术\', \'泔浸\', \'一钱\', \'五味子\', \'十四\', \'一钱\', \'黄连\', \'一钱\', \'白术\', \'一钱\', \'橘皮\', \'一钱\', \'白茯苓\', \'去皮\', \'一钱\', \'当归\', \'一钱\', \'人参\', \'七分\', \'生地黄\', \'七分\', \'麦门冬\', \'心半钱\', \'神曲\', \'炒半钱\', \'猪苓\', \'半钱\', \'黄蘖\', \'半钱\', \'甘草\', \'半钱\', \'泽泻\', \'半钱\', \'柴胡\', \'三分\', \'升麻\', \'三分\']');
INSERT INTO `prescript` VALUES (59077, '43961', '阳毒升麻汤', '升麻、犀角、射干，各二钱半。黄芩（二钱），人参、甘草（炙），各一钱。', '上作一服，水二钟，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治阳毒赤斑，狂言咽痛。', '-', '-', '升麻', '[\'升麻\', \'犀角\', \'射干\', \'各二钱\', \'黄芩\', \'二钱\', \'人参\', \'甘草\', \'一钱\']');
INSERT INTO `prescript` VALUES (59078, '43922', '续命汤', '麻黄（去节.先煮掠去沫.焙.一两半） 独活（一两半） 升麻（半两） 葛根（半两） 羚羊角屑（一两） 桂心（一两） 防风（去叉.一两半） 甘草（炙.一两）', '上咀，每服六钱匕，水二盏，浸一宿，明旦煎取一盏，去滓温服，衣覆避外风。', '明方贤着《奇效良方》', '治风痉，口噤不开，身背强直，发如痫状。', '-', '-', '升麻', '[\'麻黄\', \'节先\', \'掠去\', \'两半\', \'独活\', \'两半\', \'升麻\', \'半两\', \'葛根\', \'半两\', \'羚羊角\', \'一两\', \'桂心\', \'一两\', \'防风\', \'两半\', \'甘草\', \'一两\']');
INSERT INTO `prescript` VALUES (59079, '43849', '犀角地黄汤', '犀角（如无升麻代之）、生地黄、牡丹皮、芍药，各一钱半。', '上作一服，水二钟，煎至一钟，食远服。', '明方贤着《奇效良方》', '治血证，大便黑，衂后脉微，发狂发黄当汗下，汗内有淤血。', '-', '-', '升麻', '[\'犀角\', \'升麻\', \'代之\', \'生地黄\', \'牡丹皮\', \'芍药\', \'各一钱\']');
INSERT INTO `prescript` VALUES (59080, '43835', '当归调血汤', '当归7.5克 川芎3克 白芍9克 黄连3克 黄芩3克 升麻1.5克 桃仁（去皮，另研）3克', '水煎，空腹时服。', '《寿世保元》卷三', '主下痢红多。', '-', '-', '升麻', '[\'当归\', \'川芎\', \'白芍\', \'黄连\', \'黄芩\', \'升麻\', \'桃仁\', \'去皮\', \'研克\']');
INSERT INTO `prescript` VALUES (59081, '43797', '除湿羌活汤', '羌活4.5克 防风 升麻 柴胡各3克 藁本 苍术（米泔浸）各6克', '上锉一剂。加生姜3片，水煎服。', '《寿世保元》卷二', '祛风胜湿。主风湿犯表，发热，一身尽痛，脉浮而缓。', '-', '-', '升麻', '[\'羌活\', \'防风\', \'升麻\', \'柴胡\', \'藁本\', \'苍术\', \'泔浸\']');
INSERT INTO `prescript` VALUES (59082, '43725', '防风汤', '秦艽（去苗土）、独活（去芦）、麻黄（去节）、半夏（汤洗七次.切片）、防风（去芦），各二两。升麻、防己、白术、石膏（煅）、芍药（白）、黄芩、甘草、当归（去芦）、远志（去心）、人参（去芦），各一两。', '每服四钱，水二中盏，生姜七、八片，煎至一盏，去滓，取清汁六分，入麝香末少许，食后、临卧带热服。', '《宋太平惠民和剂局方》', '治风虚发热，项背拘急，肢节不遂，恍惚狂言，来去无时，不自觉悟。\r\n亦治脚气缓弱甚效。此药温和，不虚人。', '-', '上粗末，入半夏片令匀。', '升麻', '[\'秦艽\', \'苗土\', \'独活\', \'去芦\', \'麻黄\', \'去节\', \'半夏\', \'汤洗\', \'七次\', \'切片\', \'防风\', \'去芦\', \'二两\', \'升麻\', \'防己\', \'白术\', \'石膏\', \'芍药\', \'黄芩\', \'甘草\', \'当归\', \'去芦\', \'远志\', \'去心\', \'人参\', \'去芦\', \'一两\']');
INSERT INTO `prescript` VALUES (59083, '43723', '栀子仁汤', '栀子仁、赤芍药、大青、知母、升麻、杏仁，各一钱。黄芩（一钱半），石膏（二钱半），柴胡（二钱），甘草（半钱）。', '上作一服，水二钟，生姜五片，豆豉五十粒，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治发斑烦躁，面赤咽痛，潮热。', '-', '-', '升麻', '[\'栀子\', \'仁赤\', \'芍药\', \'大青\', \'知母\', \'升麻\', \'杏仁\', \'各一钱\', \'黄芩\', \'一钱\', \'石膏\', \'二钱\', \'柴胡\', \'二钱\', \'甘草\', \'半钱\']');
INSERT INTO `prescript` VALUES (59084, '43699', '麻黄散', '麻黄（去节.一钱） 白术（一钱） 防风（去芦.一钱） 川芎（一钱） 甘草（炙.一钱） 汉防己（一钱） 当归（去芦.一钱） 人参（去芦.一钱） 羌活（去芦.一钱半） 远志（去心.一钱半） 川升麻（八分） 桂心（半钱） 茯神（去木.一钱半）', '上作一服，水二钟，生姜五片，煎至一钟，入竹沥半盏，再煎一二沸。不拘时服。', '明方贤着《奇效良方》', '治心脏中风，虚寒颤，心惊掣悸，语声混浊，口歪冒昧，好笑，并宜服之。', '-', '-', '升麻', '[\'麻黄\', \'去节\', \'一钱\', \'白术\', \'一钱\', \'防风\', \'芦一钱\', \'川芎\', \'一钱\', \'甘草\', \'一钱汉\', \'防己\', \'一钱\', \'当归\', \'芦一钱\', \'人参\', \'芦一钱\', \'羌活\', \'芦一钱\', \'远志\', \'去心\', \'一钱\', \'半川\', \'升麻\', \'八分\', \'桂心\', \'半钱\', \'茯神\', \'木一钱\']');
INSERT INTO `prescript` VALUES (59085, '43688', '决明子丸', '决明子（半两） 天雄（炮.去皮） 独活（去芦.半两） 天南星（姜制.半两） 川芎（半两） 白术（半两） 升麻（半两） 白附子（炮.去皮.半两） 防风（去芦.半两） 蔓荆子（半两） 当归（去芦.半两） 细辛（去苗.洗.半两） 酸枣仁（半两） 萆薢（酒浸.半两） 犀角屑（半两） 白花蛇肉（酒浸.半两） 白僵蚕（炒.半两） 牛黄（另研.二钱半） 麝香（另研.二钱半） 朱砂（二钱半）', '每服三五十丸，豆淋酒送下。不拘时服。', '明方贤着《奇效良方》', '治肝脏中风，攻及手足，缓弱无力，口眼歪斜，精神不定，行步艰难，并宜服之。', '-', '上为细末，入牛黄等三味，再研和匀，炼蜜为丸，如梧桐子大。', '升麻', '[\'决明子\', \'半两\', \'天雄\', \'去皮\', \'独活\', \'去芦\', \'半两\', \'天南星\', \'姜制\', \'半两\', \'川芎\', \'半两\', \'白术\', \'半两\', \'升麻\', \'半两\', \'白附子\', \'去皮\', \'半两\', \'防风\', \'去芦\', \'半两\', \'蔓荆子\', \'半两\', \'当归\', \'去芦\', \'半两\', \'细辛\', \'苗洗\', \'半两\', \'酸枣仁\', \'半两\', \'半两\', \'犀角\', \'半两\', \'白花蛇\', \'肉酒\', \'半两\', \'白僵蚕\', \'半两\', \'牛黄\', \'研二钱\', \'麝香\', \'研二钱\', \'朱砂\', \'二钱\']');
INSERT INTO `prescript` VALUES (59086, '43652', '小犀角圆', '巴豆二十二枚（去皮.膜.心.炒出油.细研），大黄（蒸.焙）一两一分，犀角三两，黄连（去须）、栀子（去皮）、干蓼蓝、升麻、黄芩、防风（去芦）、人参、当归（去芦）、黄蓍（去苗）、甘草（炙），各一两。', '每服三圆，温汤下，利三、两行，吃冷粥止之，不利，加至四、五圆，初服取快利，后渐减圆数，取微溏泄为度，老小，以意加减，肿消及和润乃止。利下黄水，觉肿处微皱色变，即是消候。一切肿毒皆内消，神验不可论。', '《宋太平惠民和剂局方》', '治肠痈、乳痈、发背，一切毒肿，服之化为水。', '忌热面、蒜、猪肉、芦笋、鱼、海藻、菘菜、生冷、粘食。', '上为细末，入巴豆匀，炼蜜搜和为圆，如梧桐子大。', '升麻', '[\'巴豆\', \'二十二\', \'皮膜\', \'出油\', \'细研\', \'大黄\', \'蒸焙\', \'一两\', \'一分\', \'犀角\', \'三两\', \'黄连\', \'栀子\', \'去皮\', \'蓼蓝\', \'升麻\', \'黄芩\', \'防风\', \'去芦\', \'人参\', \'当归\', \'芦黄\', \'甘草\', \'一两\']');
INSERT INTO `prescript` VALUES (59087, '43634', '防风散', '防风（去叉.三分） 枳壳（麸炒.三分） 沙参（半两） 秦艽（去苗.一两半） 犀角屑（一两） 升麻（一两） 木通（一两） 羌活（一两） 甘草（炙.一两） 茯神（去木.一两） 龙齿（一两） 前胡（一两）', '每服三钱，水一中盏，煎至五分，去滓，入生地黄汁一合，更煎一二沸，不拘时温服。', '明方贤着《奇效良方》', '治热毒风痰壅，头目晕闷，心神不宁。', '-', '-', '升麻', '[\'防风\', \'三分\', \'枳壳\', \'三分\', \'沙参\', \'半两\', \'秦艽\', \'两半\', \'犀角\', \'一两\', \'升麻\', \'一两\', \'木通\', \'一两\', \'羌活\', \'一两\', \'甘草\', \'一两\', \'茯神\', \'一两\', \'龙齿\', \'一两\', \'一两\']');
INSERT INTO `prescript` VALUES (59088, '43581', '桃奴散', '桃奴5枚 甘草7.5克（炙微赤，锉）杏仁20枚（汤浸，去皮、尖，双仁，麸炒微黄）麝香3克 桔梗（去芦头）赤芍药 黄芩 柴胡（去苗）川升麻 川大黄（锉，微炒）鬼臼（去毛）各15克', '每服3克，以水200毫升，煎至l00毫升，去滓，不计时候温服。以利为度。量儿大小，以意加减。', '《太平圣惠方》卷八十三', '治小儿中恶，心腹紧胀疼痛，颜色青黑，大便不通。', '-', '-', '升麻', '[\'桃奴\', \'甘草\', \'克炙微\', \'杏仁\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'麝香\', \'桔梗\', \'芦头\', \'芍药\', \'黄芩\', \'柴胡\', \'苗川\', \'升麻\', \'大黄\', \'鬼臼\']');
INSERT INTO `prescript` VALUES (59089, '43559', '升阳燥湿汤', '高良姜3克，干姜3克，郁李仁3克，甘草3克，陈皮1.5克，黄芪1.5克，白葵花0.9克，柴胡0.9克，升麻0.9克。', '水煎服，每日1剂，日服2次。', '《仁术便览》卷四', '补气升阳，燥湿散寒。主胞宫寒湿。', '-', '-', '升麻', '[\'高良姜\', \'干姜\', \'郁李仁\', \'甘草\', \'陈皮克\', \'黄芪\', \'克白\', \'葵花\', \'柴胡\', \'升麻\']');
INSERT INTO `prescript` VALUES (59090, '43553', '补中益气丸', '炙黄芪200g 党参60g 炙甘草100g 白术（炒）60g 当归60g 升麻60g 柴胡60g 陈皮60g', '口服，水丸一次6g，小蜜丸一次9g，大蜜丸一次1丸，一日2～3次。', '《中国药典》2015版', '补中益气，升阳举陷。用于脾胃虚弱、中气下陷证引起的体倦乏力、食少腹胀、久泻、脱肛、子宫脱垂。', '-', '上八味，粉碎成细粉，过筛，混匀。另取生姜20g、大枣40g加水煎煮二次，滤过。取上述细粉，用煎液泛丸，干燥，制成水丸；或将生姜和大枣的煎液浓缩，每100g粉末加炼蜜100～120g及生姜和大枣的浓缩煎液,制成小蜜丸；或每100g粉末加炼蜜100～120g制成大蜜丸，即得。', '升麻', '[\'黄芪\', \'党参\', \'甘草\', \'白术\', \'当归\', \'升麻\', \'柴胡\', \'陈皮\']');
INSERT INTO `prescript` VALUES (59091, '43413', '内托消毒散', '人参9克 黄耆30克 防风9克 白芷9克 川芎9克 当归9克 桔梗6克 连翘12克 升麻9克 柴胡6克 金银花15克 甘草节6克', '水、酒各150毫升，煎至150毫升，徐徐温服。', '《证治准绳·疡医》卷三', '治发颐有脓不消者。', '-', '-', '升麻', '[\'人参\', \'黄耆克\', \'防风\', \'白芷\', \'川芎\', \'当归\', \'桔梗\', \'连翘\', \'升麻\', \'柴胡\', \'金银花\', \'甘草\', \'节克\']');
INSERT INTO `prescript` VALUES (59092, '43399', '升阳胜湿汤', '柴胡3克，羌活6克，苍术6克，黄芪6克，防风4.5克，升麻4.5克，独活4.5克，当归9克，藁本3克，甘草3克。', '水煎服，每日1剂，日服2次。', '《胎产要决》卷上', '疏风祛湿。主痰湿下注。', '-', '-', '升麻', '[\'柴胡\', \'羌活\', \'苍术\', \'黄芪\', \'防风\', \'升麻\', \'独活\', \'当归\', \'藁本\', \'甘草\']');
INSERT INTO `prescript` VALUES (59093, '43316', '苓麻饮', '白茯苓 赤茯苓各6克 升麻4.5克 当归6克 川芎3克 苎根9克', '水煎服；或调琥珀末6克服，更妙。', '《卫生鸿宝》卷五', '治妊娠转胞，小便不通。', '-', '-', '升麻', '[\'白茯苓\', \'茯苓\', \'升麻\', \'当归\', \'川芎\', \'根克\']');
INSERT INTO `prescript` VALUES (59094, '43258', '椿皮丸', '龟版60克（酥炙）升麻 香附各15克 芍药45克 侧柏叶30克 椿根白皮22.5克', '以四物汤加白术、黄连、甘草、陈皮作末，汤调送下丸药。', '《丹溪心法》卷二', '滋阴养血，清利湿热。主肠风便血，日久血虚。并治麻风、癣疮见于面部。', '-', '-', '升麻', '[\'龟版克\', \'升麻\', \'香附\', \'芍药\', \'侧柏叶\', \'克椿根\', \'白皮\']');
INSERT INTO `prescript` VALUES (59095, '43210', '调中益气汤', '黄耆3克 人参（去芦头，有嗽者去之）甘草 苍术各1.5克 柴胡 橘皮（如腹中气不得运转，更加3克）升麻各0.6克 木香0.3～0.6克', '水600毫升，煎至500毫升，去滓，空腹时带热服之。宁心绝思，药必神效。', '《脾胃论》卷中', '益气健脾，和中祛湿。主肠胃虚弱，湿阻气滞，脘腹胀满，不思饮食，身体倦怠，大便泄泻，肢节烦疼者。', '-', '-', '升麻', '[\'黄耆克\', \'人参\', \'芦头\', \'有嗽\', \'甘草\', \'苍术\', \'柴胡\', \'橘皮\', \'腹中\', \'不得\', \'运转\', \'更加\', \'升麻\', \'木香\']');
INSERT INTO `prescript` VALUES (59096, '43179', '清上泻火汤', '荆芥穗 川芎各0.6克 蔓荆子 当归身 苍术各0.9克 酒黄连 生地黄 藁本 生甘草各1.5克 升麻 防风各2.1克 酒黄柏 炙甘草 黄耆各3克 酒黄芩 酒知母各4.5克 羌活9克 柴胡15克 细辛少许 红花少许', '上药锉碎，分作二服。每服用水300毫升，煎至150毫升，去滓，食后稍热服。', '《兰室秘藏》卷中', '治热厥头痛，得寒则止，遇热则作。', '-', '-', '升麻', '[\'荆芥\', \'川芎\', \'蔓荆子\', \'当归\', \'苍术\', \'各克酒\', \'黄连\', \'生地黄\', \'藁本\', \'生甘草\', \'升麻\', \'防风\', \'各克酒\', \'黄柏\', \'甘草\', \'黄耆\', \'各克酒\', \'黄芩\', \'知母\', \'羌活\', \'柴胡\', \'细辛\', \'少许\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (59097, '43164', '加味补中益气汤', '黄耆（炒）2钱，人参1钱，白术（去芦，炒）2钱，白茯苓2钱，陈皮8分，柴胡4分，升麻3分，白芍（酒炒）1钱5分，当归（酒炒）3钱，萝卜子（炒）3钱，厚朴（姜炒）1钱，甘草（炙）8分，枳实（麸炒）8分。', '加生姜，水煎服。', '《寿世保元》卷三', '脾胃虚弱，治失其宜，元气虚惫，脾胃伤损，肿胀尤甚。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'白术\', \'去芦\', \'白茯苓\', \'陈皮分\', \'柴胡\', \'升麻\', \'白芍\', \'钱分\', \'当归\', \'萝卜\', \'厚朴\', \'甘草\', \'炙分\', \'枳实\', \'炒分\']');
INSERT INTO `prescript` VALUES (59098, '43081', '牢牙散', '羌活30克 草龙胆（酒洗）45克 羊胫骨灰（另研，过罗，取极细末）60克 升麻120克', '卧时掺在牙龈上。', '《兰室秘藏》卷中', '治牙龈肉绽有根，牙疳肿痛，牙齿不长或动摇欲落，牙黄口臭。（牙痛）', '-', '-', '升麻', '[\'羌活\', \'克草\', \'龙胆\', \'洗克羊\', \'胫骨\', \'研过\', \'罗取\', \'细末克\', \'升麻\']');
INSERT INTO `prescript` VALUES (59099, '43062', '加味补中益气汤', '人参1钱，黄耆7分，甘草4分，白术1钱，当归（酒洗）1钱2分，陈皮（去白）1钱，柴胡（去芦）5分，升麻3分，麦冬7分（制，去心），天花粉5分，黄柏7分（酒盐炒），黄芩5分（酒浸）。', '上(口父)咀。用水2钟，煎至1钟，去滓温服。', '《直指附遗》卷九', '动作劳倦。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'甘草\', \'白术\', \'当归\', \'洗钱\', \'陈皮\', \'白钱\', \'柴胡\', \'芦分\', \'升麻\', \'麦冬\', \'分制\', \'天花粉\', \'黄柏\', \'分酒\', \'黄芩\', \'分酒\']');
INSERT INTO `prescript` VALUES (59100, '43059', '中满分消汤', '川乌 泽泻 黄连 人参 青皮 当归 生姜 麻黄 柴胡 干姜 荜澄茄各0.6克 益智仁 半夏 茯苓 木香 升麻各0.9克 黄耆 吴茱萸 厚朴 草豆蔻仁 黄柏各15克', '用水600毫升，煎至300毫升，空腹时热服。', '《兰室秘藏》卷上', '治中满寒胀，寒疝，腹中冷，心下痞，大小便不通，四肢逆冷，食入反出，虚烦躁动，下肢不收。', '服药期间，忌酒、湿面、生冷、油腻等物，戒房事。', '-', '升麻', '[\'川乌\', \'泽泻\', \'黄连\', \'人参\', \'青皮\', \'当归\', \'生姜\', \'麻黄\', \'柴胡\', \'干姜\', \'荜澄茄\', \'益智仁\', \'半夏\', \'茯苓\', \'木香\', \'升麻\', \'黄耆\', \'吴茱萸\', \'厚朴\', \'草豆蔻\', \'黄柏\']');
INSERT INTO `prescript` VALUES (59101, '43046', '辛夷清肺饮', '辛夷1.8克 黄芩 山栀 麦门冬 百合 石膏 知母各3克 甘草1.5克 枇杷叶3片（去毛）升麻0.9克', '上药用水400毫升，煎至320毫升，食后服。外以硇砂散逐日点鼻。', '《外科正宗》卷四', '清肺通窍。治风热郁滞肺经，致生鼻痔。鼻内息肉，初如榴子，渐大下垂，闭塞鼻孔，气不宜通者。', '服药期间及初愈后，应断厚味，戒急暴，省房欲。', '-', '升麻', '[\'辛夷\', \'黄芩\', \'麦门冬\', \'百合\', \'石膏\', \'知母\', \'甘草\', \'枇杷\', \'叶片\', \'升麻\']');
INSERT INTO `prescript` VALUES (59102, '43043', '助阳和血补气汤', '香白芷0.6克 蔓荆子0.9克 炙甘草 当归身（酒洗）柴胡各1.5克 升麻 防风各2.1克 黄耆3克', '上药哎咀，作一服。用水230毫升，煎至150毫升，去滓，临卧时热服。', '《脾胃论》卷下', '治热邪上壅，眼中自睛红，多眵泪，无疼痛而隐涩难开，昏花不明，由于服苦寒药太过者。', '服药期间，须忌风寒及食冷物。', '-', '升麻', '[\'白芷\', \'蔓荆子\', \'甘草\', \'当归\', \'身酒\', \'柴胡\', \'升麻\', \'防风\', \'黄耆克\']');
INSERT INTO `prescript` VALUES (59103, '43037', '黄耆芍药汤', '葛根 羌活各15克 升麻30克 炙甘草60克 白芍药 黄耆各90克', '每服15克，用水300毫升，煎至150毫升，食后服。', '《兰室秘藏》卷中', '治鼻衄血多，面黄，眼涩多眵，手麻木。', '-', '-', '升麻', '[\'葛根\', \'羌活\', \'升麻\', \'甘草\', \'克白\', \'芍药\', \'黄耆\']');
INSERT INTO `prescript` VALUES (59104, '43033', '秦艽羌活汤', '羌活3.6克 秦艽 黄耆各3克 防风2.1克 升麻 炙甘草 麻黄 柴胡各1.5克 藁本0.9克 细辛少许 红花少许', '用水300毫升，煎至150毫升，去滓，空腹时服。', '《兰室秘藏》卷下', '治痔漏成块下垂，痒甚者。', '-', '-', '升麻', '[\'羌活\', \'秦艽\', \'黄耆\', \'防风\', \'升麻\', \'甘草\', \'麻黄\', \'柴胡\', \'藁本\', \'细辛\', \'少许\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (59105, '43000', '竹叶泻经汤', '柴胡 栀子 羌活 升麻 炙草各1.5克 赤芍药 草决明 茯苓 车前子各1.2克 黄芩1.8克 黄连 大黄各1.5克 青竹叶10片 泽泻1.2克', '作一服。用水400毫升，煎至100毫升，食后稍热服。', '《原机启微》卷下', '治漏睛。眼目隐涩，稍觉蚝躁，视物微昏，目痛，内眦开窍如针，按之脓出。', '-', '-', '升麻', '[\'柴胡\', \'栀子\', \'羌活\', \'升麻\', \'炙草\', \'芍药\', \'草决明\', \'茯苓\', \'车前子\', \'黄芩\', \'黄连\', \'大黄\', \'青竹\', \'叶片\', \'泽泻\']');
INSERT INTO `prescript` VALUES (59106, '42968', '散血葛根汤', '干葛 半夏 川芎 防风 羌活 升麻 桔梗各2.4克 白芷 甘草 细辛 苏叶 香附 红花各1.8克', '上药以水400毫升，加葱3根、生姜3片，煎至320毫升，不拘时服。', '《外科正宗》卷三', '疏风理气，活血化瘀。治跌打损伤，瘀血凝滞，结成流注，身发寒热者。', '-', '-', '升麻', '[\'干葛\', \'半夏\', \'川芎\', \'防风\', \'羌活\', \'升麻\', \'桔梗\', \'白芷\', \'甘草\', \'细辛\', \'苏叶\', \'香附\', \'红花\']');
INSERT INTO `prescript` VALUES (59107, '42963', '紫根牡蛎汤', '紫草根15克，牡蛎粉（包煎）15克，当归15克，赤芍9克，川芎6克，银花6克，升麻6克，黄芪6克，甘草3克，大黄适量（后下）。', '水煎服，每日1剂，日服2次。', '《简明中医妇科学》', '清热凉血，解毒泻火。主血热炽盛，火毒伤阴。（乳腺癌）', '-', '-', '升麻', '[\'草根\', \'牡蛎\', \'粉包\', \'当归\', \'赤芍\', \'川芎\', \'银花\', \'升麻\', \'黄芪\', \'甘草\', \'大黄\', \'适量\']');
INSERT INTO `prescript` VALUES (59108, '42948', '五香连翘汤', '沉香 乳香 生甘草 木香各3克 连翘 射干 升麻 独活 桑寄生 木通各9克 丁香15克 大黄30克 麝香5克', '每服12克，用水300毫升，煎至150毫升，去滓，空腹时热服。', '《卫生宝鉴》卷十三', '治瘰疬，痈疽，恶疮肿毒。', '-', '-', '升麻', '[\'沉香\', \'乳香\', \'生甘草\', \'木香\', \'连翘\', \'射干\', \'升麻\', \'独活\', \'桑寄生\', \'木通\', \'丁香\', \'大黄\', \'麝香\']');
INSERT INTO `prescript` VALUES (59109, '42938', '加味补中益气汤', '黄芩1钱，黄耆1钱，柴胡1钱，半夏8分，芍药8分，人参8分，白术8分，当归8分，甘草5分，升麻3分，陈皮6分。', '上作1服。水2钟，加生姜3片，煎8分，空心服。有汗及寒重，加桂枝5分，倍黄耆；热盛，倍柴胡、黄芩；渴，加麦门冬、天花粉。', '《万氏家抄方》卷二', '平素不足，兼以劳役内伤，挟感寒暑，致患疟疾，寒热交作，肢体倦软，乏力少气。', '-', '-', '升麻', '[\'黄芩\', \'黄耆\', \'柴胡\', \'半夏\', \'芍药\', \'人参\', \'白术\', \'当归\', \'甘草\', \'升麻\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (59110, '42899', '清痰利水汤', '黑元参24克，半夏12克，吴茱萸6克，广陈皮6克，胆星10克，泽泻10克，防己10克，石苇10克，黄连6克，柴胡10克，川芎10克，升麻10克，藁本10克，甘草3克，代赭石21克（为引）。', '水煎服，每日1剂，日服2次。', '路际平方', '清痰泻火，祛邪逐瘀。主痰火郁结，邪热内侵。', '-', '-', '升麻', '[\'黑元\', \'参克\', \'半夏\', \'吴茱萸\', \'克广\', \'陈皮克\', \'胆星克\', \'泽泻\', \'防己\', \'石苇\', \'黄连\', \'柴胡\', \'川芎\', \'升麻\', \'藁本\', \'甘草\', \'代赭石\', \'克为\']');
INSERT INTO `prescript` VALUES (59111, '42860', '犀角升麻汤', '犀角（镑）30.3克 升麻30克 防风（去叉股）羌活（去芦）各22克 白芷（不见火）黄芩（去皮）川芎（洗）白附子（炮）各15克 甘草（炙）7.5克', '每服12克，以水220毫升，煎至180毫升，去滓，食后、临卧时服，一日三四次。', '《普济本事方》卷五', '疏风清热，凉血解毒。治风毒侵袭阳明，血凝不行，鼻额间痛，或麻痹不仁，或连口唇、颊车、发际皆痛，不可开口，左额与颊上常如绷急，手触之则痛者。', '-', '-', '升麻', '[\'犀角\', \'升麻\', \'防风\', \'羌活\', \'去芦\', \'白芷\', \'不见\', \'黄芩\', \'去皮\', \'川芎\', \'白附子\', \'甘草\', \'炙克\']');
INSERT INTO `prescript` VALUES (59112, '42833', '生津甘露汤', '升麻1.2克 防风 生甘草 汉防己 生地黄各1.5克 当归身1.8克 柴胡 羌活 炙甘草 黄耆 酒知母 酒黄芩各3克 酒龙胆草 石膏 黄柏各4.5克 红花少许 桃仁5个 杏仁10个', '上哎咀，作为一服。用水300毫升，酒10毫升，煎至150毫升，空腹时稍热服。', '《兰室秘藏》卷上', '清热生津，养血润燥。治中消，能食而瘦，口舌干，自汗，大便结燥，小便频数。', '-', '-', '升麻', '[\'升麻\', \'防风\', \'生甘草\', \'防己\', \'生地黄\', \'当归\', \'身克\', \'柴胡\', \'羌活\', \'甘草\', \'黄耆\', \'知母\', \'黄芩\', \'各克酒\', \'龙胆草\', \'石膏\', \'黄柏\', \'红花\', \'少许\', \'桃仁\', \'杏仁\']');
INSERT INTO `prescript` VALUES (59113, '42786', '牢牙地黄散', '藁本0.6克 生地黄 熟地黄 羌活 防己 人参各0.9克 当归身 益智仁各1.2克 香白芷 黄耆各1.5克 羊胫骨灰 吴茱萸 黄连 麻黄各3克 草豆蔻皮3.6克 升麻4.5克', '先用温水漱口，以药末擦牙。', '《兰室秘藏》卷中', '治脑寒痛及牙痛。', '-', '-', '升麻', '[\'藁本\', \'生地黄\', \'熟地黄\', \'羌活\', \'防己\', \'人参\', \'当归\', \'益智仁\', \'各克香\', \'白芷\', \'黄耆\', \'各克羊\', \'胫骨\', \'吴茱萸\', \'黄连\', \'麻黄\', \'草豆蔻\', \'皮克\', \'升麻\']');
INSERT INTO `prescript` VALUES (59114, '42756', '禹功散', '陈皮 半夏（姜制）赤茯苓 猪苓 泽泻 白术（炒）木通各3克 条芩2.4克 升麻0.9克 甘草0.9克 山栀子（炒）3克', '上锉一剂。用水400毫升，煎至200毫升，不拘时服。得解而止。', '《寿世保元》卷五', '治膀胱有热，小便不通，诸法不能奏效者。', '-', '-', '升麻', '[\'陈皮\', \'半夏\', \'姜制\', \'茯苓\', \'猪苓\', \'泽泻\', \'白术\', \'木通\', \'条芩克\', \'升麻\', \'甘草\', \'克山\', \'栀子\', \'炒克\']');
INSERT INTO `prescript` VALUES (59115, '42737', '秦艽地黄汤', '秦艽 生地 当归 白芍（炒）各4.5克 川芎 防风 荆芥 升麻 白芷 蔓荆子 大力子（蒸）羌活各3克 甘草（炙）1.5克', '水煎服。', '《灵验良方汇编》卷一', '疏风祛湿，活血通络。治风热血燥，筋骨作痛。', '-', '-', '升麻', '[\'秦艽\', \'生地\', \'当归\', \'白芍\', \'川芎\', \'防风\', \'荆芥\', \'升麻\', \'白芷\', \'蔓荆子\', \'大力\', \'子蒸\', \'羌活\', \'甘草\', \'炙克\']');
INSERT INTO `prescript` VALUES (59116, '42664', '羚羊角散', '羚羊角（镑）黄芩 升麻 甘草（炙）车前子各300克 栀子仁 草龙胆各150克 决明子600克', '每服3克，食后温热水调下，日进三服。小儿服1.5克。', '《太平惠民和剂局方》卷七', '治大人小儿一切风热毒邪上攻眼目，暴发赤肿，或生疮疼痛，隐涩羞明。', '-', '-', '升麻', '[\'羚羊角\', \'黄芩\', \'升麻\', \'甘草\', \'车前子\', \'栀子\', \'仁草\', \'龙胆\', \'决明子\']');
INSERT INTO `prescript` VALUES (59117, '42656', '牙疼饮', '石膏12克（研）升麻4，5克 大生地15克 防风 薄荷叶 荆芥穗 前胡 天麻各6克 甘草3克（生）', '水煎，食后热服，避风。\r\n如牙肿，加金银花、羌活各6克。如牙燥，去升麻、前胡，加玄参4.5克；如牙烂，去前胡、天麻，加木通、桅仁各4.5克。', '《外科证洽全书》卷二', '治风火齿痛。', '-', '-', '升麻', '[\'石膏\', \'克研\', \'升麻\', \'大生\', \'地克\', \'防风\', \'薄荷叶\', \'荆芥\', \'穗前\', \'天麻\', \'甘草\', \'克生\']');
INSERT INTO `prescript` VALUES (59118, '42634', '加味槐角丸', '槐角 生地 当归身 黄耆各60克 川芎 阿胶各15克 黄连 条芩 枳壳 秦艽 防风 连翘 地榆 升麻各30克 白芷15克', '每服50丸，加至100丸，空腹时用温酒或米汤送下。', '《丹溪心法附余》卷十一', '治痔漏及肠风下血。', '-', '-', '升麻', '[\'槐角\', \'生地\', \'当归\', \'身黄耆\', \'川芎\', \'阿胶\', \'黄连\', \'条芩\', \'枳壳\', \'秦艽\', \'防风\', \'连翘\', \'地榆\', \'升麻\', \'白芷\']');
INSERT INTO `prescript` VALUES (59119, '42628', '射干丸', '射干 甘草（炙）杏仁（汤浸，去皮、尖及双仁，面炒微黄）各15克 木鳖子 川升麻 川大黄（微炒）各7.5克', '常含丸咽津。', '《奇效良方》卷六十一', '宜肺利咽，泻火解毒。治火毒上攻，肺气不宣，悬痈肿痛，咽喉不利。', '-', '-', '升麻', '[\'射干\', \'甘草\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁面\', \'微黄\', \'木鳖子\', \'升麻\', \'大黄\']');
INSERT INTO `prescript` VALUES (59120, '42602', '生津止渴益水饮', '人参 麦冬 当归 生地各9克 黄耆3克 葛根3克 升麻 炙草各1.2克 茯苓2.4克 五味子15粒', '水煎服。\r\n汗多，加麻黄根3克、浮小麦30克；大便燥，加肉苁蓉4.5克；渴甚，加生脉散。', '《傅青主女科·产后编》卷上', '益气养阴，生津止渴。治产后失血汗多，烦躁，咽干而渴，兼小便不利。', '-', '-', '升麻', '[\'人参\', \'麦冬\', \'当归\', \'生地\', \'黄耆克\', \'葛根\', \'升麻\', \'炙草\', \'茯苓\', \'五味子\']');
INSERT INTO `prescript` VALUES (59121, '42594', '广茂溃坚汤', '广茂 红花 升麻 吴茱萸各0.6克 生甘草 柴胡 泽泻 神曲 青皮 陈皮各0.9克 厚朴（生用）黄芩 黄连 益智仁 草豆蔻仁 当归梢各1.5克 半夏2.1克', '用水700毫升，煎至550毫升，空腹时稍热服。\r\n如渴，加葛根1.2克。', '《兰室秘藏》卷上', '治中满腹胀，内有积聚，坚硬如石，其形如盘，令人不能坐卧，大小便涩滞，上喘气促，面色萎黄，通身虚肿。', '服药期间，忌食酒、醋，湿面。', '-', '升麻', '[\'广茂\', \'红花\', \'升麻\', \'吴茱萸\', \'生甘草\', \'柴胡\', \'泽泻\', \'神曲\', \'青皮\', \'陈皮\', \'厚朴\', \'生用\', \'黄芩\', \'黄连\', \'益智仁\', \'草豆蔻\', \'当归\', \'半夏\']');
INSERT INTO `prescript` VALUES (59122, '42569', '加味解毒汤', '黄芩6克 黄连1.8克 黄柏4.5克 栀子9克 柴胡2.4克 茵陈6克 龙胆草6克 木通6克 滑石9克 升麻1.5克 甘草2.4克', '加灯心，水煎服。\r\n大便实，加大黄6克；目睛黄，倍龙胆草。', '《寿世保元》卷三', '清热渗湿，疏肝利胆。治黄疸。周身如金黄色，小便如浓煮柏汁，服诸药不效者。', '-', '-', '升麻', '[\'黄芩\', \'黄连\', \'黄柏\', \'栀子\', \'柴胡\', \'茵陈\', \'龙胆草\', \'木通\', \'滑石\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (59123, '42538', '生血润肤饮', '川归身（酒洗）生地黄 熟地黄（酒洗）黄耆（蜜炙）各3克 天门冬4，5克 麦门冬（去心）3克 五味子9粒 片芩（去朽，酒洗）1.5克 栝楼仁1.5克 桃仁泥1.5克 酒红花0.3克 升麻0.6克', '上细切，作一服。用水300毫升，煎至150毫升，温服。\r\n如大便结燥，加麻仁、郁李仁各3克。', '《医学正传》卷二', '生血润肤。治燥证。皮肤拆裂，手足枯燥，搔之血出。', '-', '-', '升麻', '[\'川归身\', \'生地黄\', \'熟地黄\', \'酒洗\', \'黄耆\', \'天门冬\', \'麦门冬\', \'心克\', \'五味子\', \'粒片\', \'洗克\', \'栝楼\', \'仁克\', \'桃仁\', \'泥克酒\', \'红花\', \'升麻\']');
INSERT INTO `prescript` VALUES (59124, '42532', '蜀漆汤', '白薇 蜀漆 知母 甘草（炙）苦参 升麻 龙胆各6克 常山 大黄（别渍，后下）各12克 鳖甲（炙）石膏（碎）茯苓 黄芩各9克 香豉9克（裹）独蒜7颗（切）淡竹叶（切）6克', '以水1.2升渍之，加酒适量，合煮取400毫升，去滓，分二次温服。未发前一服，欲至发时又一服。', '《外台秘要》卷五引《救急方》', '治久疟。', '忌肥腻、臊腥、滑物、生冷、人苋、大酢，菘菜、生葱、生菜。', '-', '升麻', '[\'白薇\', \'知母\', \'甘草\', \'苦参\', \'升麻\', \'龙胆\', \'常山\', \'大黄\', \'别渍\', \'鳖甲\', \'石膏\', \'茯苓\', \'黄芩\', \'各克香豉\', \'克裹\', \'独蒜\', \'颗切\', \'淡竹叶\', \'切克\']');
INSERT INTO `prescript` VALUES (59125, '42358', '升阳散火汤', '升麻 葛根 独活 羌活 白芍药 人参 各15克 甘草（炙）柴胡各9克 防风7.5克 甘草（生）6克', '每服15克，用水300毫升，煎至150毫升，去滓，大温服，不拘时。', '《内外伤辨》卷中', '升阳散火。治血虚或胃虚过食冷物，阳气郁遏于脾，肌肤灼热，或骨蒸潮热，扪之烙手。', '服药期间，忌食寒凉之物。', '-', '升麻', '[\'升麻\', \'葛根\', \'独活\', \'羌活\', \'芍药\', \'人参\', \'甘草\', \'柴胡\', \'防风\', \'甘草\', \'生克\']');
INSERT INTO `prescript` VALUES (59126, '42296', '加味益气汤', '黄耆1钱，人参1钱，白术7分，陈皮7分，当归7分，柴胡1钱，升麻3分，黄柏（酒炒）7分，羌活1钱半，防风5分，甘草5分。', '加生姜3片，水煎，热服。冬月，加细辛3分；如热甚，脉滞有力，加黄芩（酒炒）3分。', '《回春》卷二', '素体怯弱，兼之疲劳，感冒伤寒，头痛发热者。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'白术\', \'陈皮分\', \'当归\', \'柴胡\', \'升麻\', \'黄柏\', \'炒分\', \'羌活\', \'钱半\', \'防风\', \'甘草\']');
INSERT INTO `prescript` VALUES (59127, '42264', '虎骨散', '虎骨（酥炙黄）、乌梅肉、赤茯苓（去皮）、肉苁蓉（酒浸.切焙）、人参、甘草（炙）、芍药、鳖甲（去裙襕.醋浸炙）、白术、豆豉（慢火炒）、紫苑（去土）、黄蓍（焙）、常山（炒）、枳壳（去穰.麸炒）、犀角（镑）、知母，以上各一两。当归、升麻、柴胡、桔梗（炒）、前胡（去芦）、桂心（去皮）、木香、天灵盖（酥炙），各三分。桃仁（汤浸.去皮.炒.四十九粒）。', '上为细末，每服二钱匕，温酒调下，空心临卧各一。若为丸，即炼蜜和丸，如梧桐子大，每服二十丸至三十丸，温酒下。', '明方贤着《奇效良方》', '治白虎风，走注疼痛，发歇不定，积年不效者。', '-', '-', '升麻', '[\'虎骨\', \'乌梅\', \'茯苓\', \'去皮\', \'肉苁蓉\', \'浸切\', \'人参\', \'甘草\', \'芍药\', \'鳖甲\', \'白术\', \'豆豉\', \'慢火\', \'紫苑\', \'土黄\', \'常山\', \'枳壳\', \'犀角\', \'知母\', \'以上\', \'一两\', \'当归\', \'升麻\', \'柴胡\', \'桔梗\', \'炒前\', \'胡去\', \'芦桂心\', \'去皮\', \'木香\', \'天灵盖\', \'三分\', \'桃仁\', \'汤浸\', \'去皮\', \'四十九\']');
INSERT INTO `prescript` VALUES (59128, '42255', '芦荟消疳饮', '芦荟 银柴胡 胡黄连 川黄连 牛蒡子 玄参 桔梗 山栀 石膏 薄荷 羚羊角各1.5克 甘草 升麻各0.9克', '上药用水400毫升，淡竹叶10片，煎至210毫升，食后服。', '《外科正宗》卷四', '清热除疳。治小儿走马牙疳，身热气粗，牙龈腐烂，气味作臭，以及穿腮破唇者。', '-', '-', '升麻', '[\'芦荟\', \'银柴胡\', \'胡黄连\', \'黄连\', \'牛蒡子\', \'玄参\', \'桔梗\', \'石膏\', \'薄荷\', \'羚羊角\', \'甘草\', \'升麻\']');
INSERT INTO `prescript` VALUES (59129, '42249', '化斑解毒汤加减', '人中黄30克，石膏30克，连翘15克，升麻15克，知母15克，元参20克，牛蒡子15克，黄连10克，竹叶10克，赤芍10克，甘草10克，荆芥10克，蝉衣6克。', '水煎服，每日1剂，日服2次。', '董智良方', '清热解毒。主漆气辛热，客于肌腠，化热成毒。', '-', '-', '升麻', '[\'人中\', \'黄克\', \'石膏\', \'连翘\', \'升麻\', \'知母\', \'克元\', \'参克\', \'牛蒡子\', \'黄连\', \'竹叶\', \'赤芍\', \'甘草\', \'荆芥\', \'蝉衣\']');
INSERT INTO `prescript` VALUES (59130, '42216', '升举大补汤', '黄耆 白术 陈皮各1.2克 人参6克 炙甘草 升麻各1.2克 当归 熟地黄各6克 麦门冬3克 川芎3克 白芷1.2克 炒黄连0.9克 荆介穗（炒黑）1.2克', '加大枣，水煎服。\r\n汗多，加麻黄根3克，浮小麦9克；大便不通，加肉苁蓉3克。', '《傅青主女科·产后编》卷上', '治产后血崩。', '-', '-', '升麻', '[\'黄耆\', \'白术\', \'陈皮\', \'人参\', \'甘草\', \'升麻\', \'当归\', \'熟地黄\', \'麦门冬\', \'川芎\', \'白芷\', \'黄连\', \'荆介穗\', \'黑克\']');
