INSERT INTO `prescript` VALUES (59132, '42194', '圆明内障升麻汤', '干姜3克 五味子6克 白葆苓9克 防风15克 白芍药18克 柴胡21克 人参 炙甘草 当归身（酒洗）白术 升麻 葛根各30克 黄耆 羌活各45克', '每服15～21克，用水450毫升，煎至300毫升，入黄芩、黄连各6克，同煎数沸，去滓，煎至150毫升，空腹时热服。', '《兰室秘藏》卷上', '治内障初觉，视觉微昏，眼前有黑花，神水变淡绿色；继则视物为二，神水变淡白色；日久则失明，神水变纯白色。', '-', '-', '升麻', '[\'干姜\', \'五味子\', \'克白葆苓\', \'防风\', \'克白\', \'芍药\', \'柴胡\', \'人参\', \'甘草\', \'当归\', \'身酒\', \'白术\', \'升麻\', \'葛根\', \'黄耆\', \'羌活\']');
