INSERT INTO `prescript` VALUES (59149, '41772', '槐花消痔汤', '槐花15克，槐角15克，生地12克，黄连10克，银花12克，黄柏10克，滑石15克，当归12克，升麻6克，柴胡6克，枳壳6克，黄芩10克，甘草3克。', '水煎服，每日1剂，日服2次。', '蒲孝生方', '凉血止血，清热解毒，活血止痛，逐淤消痔。主血热毒火内盛，瘀血浊气下注。', '-', '-', '升麻', '[\'槐花\', \'槐角\', \'生地\', \'黄连\', \'银花\', \'黄柏\', \'滑石\', \'当归\', \'升麻\', \'柴胡\', \'枳壳\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (59150, '41759', '顺经两安汤', '当归15克（酒洗）白芍15克（酒炒）大熟地15克（九蒸）山萸肉6克（蒸）人参9克 白术15克（土炒）麦冬15克（去心）黑芥穗6克 巴戟肉3克（盐水浸）升麻1.2克', '水煎服。', '《傅青主女科》卷上', '治经前大便下血。', '-', '-', '升麻', '[\'当归\', \'克酒\', \'白芍\', \'克酒\', \'熟地\', \'克九蒸\', \'山萸\', \'肉克蒸\', \'人参\', \'白术\', \'克土\', \'麦冬\', \'克去\', \'心黑\', \'芥穗克巴戟\', \'肉克盐\', \'水浸\', \'升麻\']');
INSERT INTO `prescript` VALUES (59151, '41758', '养血润肤饮', '当归9克 热地 生地 黄耆各12克 天冬（去心）麦冬（去心）各6克 升麻 片芩各3克 桃仁泥 红花各2克 天花粉4.5克', '水煎，温服。\r\n如大便燥结，可加大麻仁、郁李仁各9～15克；如风盛痒甚，加明天麻4.5克，同时宜配合外治，如生猪油或鳗鲡油涂沫局部。', '《外科证治全书》', '治面游风，初起面目浮肿，燥痒起皮，如白屑风状，渐渐痒极，延及耳项，有时痛如针刺。现用于皮肤瘙痒症，牛皮癣静止期（血虚风燥型），红皮症等病久血虚风燥而见皮肤干燥、脱屑、瘙痒，舌质红者。', '药后禁食荤腥、鱼、虾、螃蟹或辣椒，生姜等刺激性饮食。', '-', '升麻', '[\'当归\', \'克热地\', \'生地黄\', \'天冬\', \'去心\', \'麦冬\', \'去心\', \'升麻\', \'片芩\', \'桃仁\', \'红花\', \'天花粉\']');
INSERT INTO `prescript` VALUES (59152, '41754', '散肿溃坚汤', '黄芩24克（酒洗，一半炒四遍，一半生用）草龙胆（酒洗，炒四遍）瓜蒌根（锉细，酒洗）黄柏（酒制）酒知母 桔梗 昆布各15克 柴胡 炙甘草 京三棱（酒洗）广茂（酒洗，炒）连翘各10克 葛根 白芍 当归梢 黄连各6克 升麻1.8克', '每服18～21克，用水300毫升，先浸大半日，煎至150毫升，去滓，食后热服。另用半料作细末，炼蜜为丸，如绿豆大。每服100～150丸，用此药汤留一口送下。', '《兰室秘藏》卷下', '疏肝活血，散肿溃坚。治马刀疮结硬如石者。', '-', '-', '升麻', '[\'黄芩\', \'克酒\', \'一半\', \'四遍\', \'一半\', \'生用\', \'龙胆\', \'酒洗\', \'四遍\', \'细酒\', \'黄柏\', \'酒制\', \'知母\', \'桔梗\', \'昆布\', \'柴胡\', \'甘草\', \'三棱\', \'酒洗\', \'广茂酒\', \'连翘\', \'葛根\', \'白芍\', \'当归\', \'黄连\', \'升麻\']');
INSERT INTO `prescript` VALUES (59153, '41732', '加味补中益气汤', '人参1钱，黄耆（蜜炙）1钱，白术（炒）1钱，杜仲（炒）1钱，牛膝1钱，白芍（炒）1钱，甘草（炒）6分，当归（酒浸）8分，升麻3分，陈皮7分，柴胡5分，五味子9粒，黄柏（炒）1钱，枸杞子1钱。', '水煎，空心服。如梦遗，加知母、牡蛎各1钱；腹胀，加半夏、厚朴；咳嗽，加知母、麦冬各8分；泄泻，加肉豆蔻、干姜各7分；呕逆恶心，加藿香、半夏。', '《便览》卷二', '一切中气不足，脾胃弱，下元虚，腰膝软弱，夜有房劳。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'蜜炙钱\', \'白术\', \'杜仲\', \'牛膝\', \'白芍\', \'甘草\', \'炒分\', \'当归\', \'浸分\', \'升麻\', \'陈皮分\', \'柴胡\', \'五味子\', \'黄柏\', \'枸杞子\']');
INSERT INTO `prescript` VALUES (59154, '41719', '升麻鳖甲汤', '升麻28克 当归14克 蜀椒（炒去汗）14克 甘草28克 鳖甲1片（手指大，炙）雄黄7克（研）', '上六味，以水800毫升，煮取200毫升，顿服。', '《金匮要略》卷上', '治阳毒，面赤斑斑如锦纹，咽喉痛，吐脓血。', '-', '-', '升麻', '[\'升麻\', \'当归\', \'克蜀椒\', \'汗克\', \'甘草\', \'鳖甲\', \'手指\', \'雄黄\', \'克研\']');
INSERT INTO `prescript` VALUES (59155, '41604', '茵陈汤', '茵陈12克 黄芩6克 栀子9克 升麻9克 大黄9克 龙胆草6克 枳实6克（炙）柴胡12克', '以水800毫升，煮取400毫升，分三次温服。\r\n若虚羸，加生地黄30克，栀子加至21克，去大黄。', '《外台秘要》卷四引《近效方》', '治黄疸。身面眼悉黄如金色，小便浓如煮黄柏汁者。', '-', '-', '升麻', '[\'茵陈\', \'黄芩\', \'栀子\', \'升麻\', \'大黄\', \'龙胆草\', \'枳实\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59156, '41584', '麻黄升麻汤', '麻黄（去节）75克 升麻38克 黄芩 芍药 甘草（生）石膏（煅）茯苓各30克', '每服12克。用水230毫升，加生姜3片，煎至160毫升，去滓热服。微汗解。', '《三因极一病证方论》卷九', '治伤寒发热，汗下不解，血随气壅，鼻衄者。', '-', '-', '升麻', '[\'麻黄\', \'节克\', \'升麻\', \'黄芩\', \'芍药\', \'甘草\', \'生石膏\', \'茯苓\']');
