INSERT INTO `prescript` VALUES (59158, '41572', '辛梗汤', '人参 干葛各90克 白术 茯苓 甘草（炙）净细辛 桔梗（炒）各60克 柴胡 升麻各23克', '每服6克，用水150毫升，加生姜2片，薄荷叶3片，煎服。', '《医方类聚》卷二六三引《简易方》', '治小儿风热头痛，壮热，鼻涕，咳嗽有痰。', '-', '-', '升麻', '[\'人参\', \'干葛\', \'白术\', \'茯苓\', \'甘草\', \'炙净\', \'细辛\', \'桔梗\', \'柴胡\', \'升麻\']');
