INSERT INTO `prescript` VALUES (59163, '41173', '加味滞下丸', '川黄连（切片，拌好酒，用吴茱萸浸2宿，瓦上炒干，分连、萸各贮，净黄连3两，白痢加茱萸1两，赤痢用湿槐花炒，去槐花）8两，白芍药（酒浸，切片炒）5两，乌梅肉2两，滑石（水飞）6两，炙甘草2两，升麻（绿色者，醋炒）3两，莲肉（去心，炒）6两，白扁豆（炒，去壳）3两，红曲（簸净，炒）5两，干葛2两。', '每服5钱，白汤吞下，饥时服；证重者，日服3次。', '《广笔记》', '痢疾。', '-', '-', '升麻', '[\'黄连\', \'切片\', \'酒用\', \'吴茱萸\', \'浸宿瓦\', \'干分\', \'连萸\', \'贮净\', \'黄连\', \'白痢\', \'茱萸\', \'赤痢\', \'用湿\', \'槐花\', \'炒去\', \'槐花\', \'白芍\', \'药酒\', \'切片\', \'乌梅\', \'滑石\', \'水飞\', \'甘草\', \'升麻\', \'绿色\', \'莲肉\', \'白扁豆\', \'红曲\', \'簸净\', \'两干葛\']');
INSERT INTO `prescript` VALUES (59164, '41138', '发毒攻里五香连翘饮', '沉香1钱，木香1钱，麝香1钱，丁香1钱，乳香2钱，连翘1钱，射干1钱，升麻1钱，桑寄生1钱，独活1钱，甘草1钱，大黄1两5钱，木通2钱。', '水煎，温服。', '《疡科选粹》卷二', '诸疮肿，初觉1-2日，便厥逆咽塞，发寒热。', '-', '-', '升麻', '[\'沉香\', \'木香\', \'麝香\', \'丁香\', \'乳香\', \'连翘\', \'射干\', \'升麻\', \'桑寄生\', \'独活\', \'甘草\', \'大黄\', \'木通\']');
