INSERT INTO `prescript` VALUES (59168, '41045', '加减补阴益气汤', '生地5钱，人参1钱半，黄耆3钱（蜜炙），柴胡5分（盐醋炒黑），白芍1钱半（醋炒），升麻3分（盐醋炒），阿胶3钱（蒲黄灰炒），山药3钱（炒），血余3钱（炒灰），赤石脂3钱（醋炒）。', '水煎，去滓温服。', '《医略六书》卷二十六', '交接出血，脉软数者。', '-', '-', '升麻', '[\'生地\', \'人参\', \'钱半\', \'黄耆\', \'钱蜜\', \'柴胡\', \'分盐\', \'白芍\', \'钱半醋\', \'升麻\', \'分盐\', \'阿胶\', \'黄灰\', \'山药\', \'余钱\', \'赤石脂\']');
INSERT INTO `prescript` VALUES (59169, '40905', '升清流气饮', '升麻3克，青皮6克，黄芪10克，木香3克，苏叶10克，乌药6克，柴胡3克，大腹皮10克，川芎3克，蔓荆子6克，菖蒲3克。', '水煎服，每日1剂，日服2次。', '干祖望方', '调理气机，升清开窍。主气滞窍闭。', '-', '-', '升麻', '[\'升麻\', \'青皮\', \'黄芪\', \'木香\', \'克苏叶克\', \'乌药\', \'柴胡\', \'大腹皮\', \'川芎\', \'蔓荆子\', \'菖蒲\']');
INSERT INTO `prescript` VALUES (59170, '40855', '加味导气汤', '木香3钱，川楝3钱，小茴香4钱，吴茱萸4钱，升麻2钱，巴戟天3钱，葫芦巴3钱，川椒1钱，炮姜2钱。', '水煎，温服。', '《医学探骊集》卷五', '因下部受寒凉而得之疝气，初起少腹微痛，日久生一气管，直通肾囊，不痛其管即空，偶触寒凉，少腹作痛，其管即胀起，复通肾囊。', '-', '-', '升麻', '[\'木香\', \'川楝\', \'小茴香\', \'吴茱萸\', \'升麻\', \'巴戟天\', \'葫芦巴\', \'钱川椒\', \'钱炮\', \'姜钱\']');
