INSERT INTO `prescript` VALUES (59172, '40742', '加味益气汤', '熟地8钱，炙耆5钱，淡附子4钱，归身4钱，白术3钱，菟丝饼3钱，人参不拘，茯苓3钱，升麻7分，川芎1钱5分，净萸肉4钱，肉桂心1钱，煨姜1钱，龙眼肉5个，黑大豆49粒。', '水煎服。', '《胎产秘书》卷下', '产后宗气下陷，脱肛。', '-', '-', '升麻', '[\'熟地\', \'钱炙耆\', \'附子\', \'钱归身\', \'白术\', \'菟丝饼\', \'人参\', \'不拘\', \'茯苓\', \'升麻\', \'川芎\', \'分净萸\', \'肉桂\', \'心钱\', \'姜钱\', \'龙眼肉\', \'黑大\', \'豆粒\']');
INSERT INTO `prescript` VALUES (59173, '40711', '化毒漏芦饮', '漏芦2钱，连翘2钱，元参2钱，牛蒡子2钱，大黄（随证酌用），生甘草8分，犀角1钱（此味不可用升麻代之），黄芩1钱，蓝叶（或青黛亦可）。', '方中蓝叶用量原缺。肿热甚，加芒消。', '《外科医镜》', '喉外生痈。', '-', '-', '升麻', '[\'漏芦\', \'连翘\', \'钱元\', \'参钱\', \'牛蒡子\', \'大黄\', \'随证酌\', \'生甘草\', \'犀角\', \'此味\', \'不可\', \'升麻\', \'代之\', \'黄芩\', \'蓝叶\', \'青黛\', \'亦可\']');
INSERT INTO `prescript` VALUES (59174, '40707', '加减普济消毒饮', '荆芥穗3钱，酒黄芩4钱，马勃3钱，苍术4钱，山栀子3钱，升麻2钱，鼠粘子3钱，桔梗2钱，薄荷3钱，连翘3钱，紫花地丁3钱，独活4钱，甘草2钱。', '水煎，温服。', '《医学探骊集》卷四', '山岚瘴气，客于经络，初病头痛，恶寒身热，脉浮洪而数，随后头项浮肿而痛，甚者面目亦肿而痛，或耳下肿出，而为痄腮，症属大头大行者。', '-', '-', '升麻', '[\'荆芥\', \'黄芩\', \'马勃钱\', \'苍术\', \'钱山\', \'栀子\', \'升麻\', \'钱鼠\', \'粘子\', \'桔梗\', \'薄荷\', \'连翘\', \'紫花地丁\', \'独活\', \'甘草\']');
INSERT INTO `prescript` VALUES (59175, '40700', '加味益气汤', '人参1钱5分，黄耆（蜜炙）1钱5分，白术（蜜炙）1钱5分，甘草（炙）1钱5分，当归2钱，川芎8分，砂仁8分，陈皮8分，酸枣仁（炒）8分，升麻（炒）3分，柴胡3分。', '加生姜3片，大枣2枚，水煎，空心服。', '《叶氏女科》卷二', '养气血。固胎元，预防堕胎。主妇人先经半产，后次有胎，妊娠7月以前。', '-', '-', '升麻', '[\'人参\', \'钱分\', \'黄耆\', \'蜜炙钱分\', \'白术\', \'蜜炙钱分\', \'甘草\', \'炙钱分\', \'当归\', \'川芎\', \'砂仁\', \'陈皮分\', \'酸枣仁\', \'炒分\', \'升麻\', \'炒分\', \'柴胡\']');
