INSERT INTO `prescript` VALUES (59178, '40681', '甘草桔梗升麻汤', '甘草半两，桔梗1两，升麻半两。', '每服2钱，水煎。', '《云歧子保命集》卷下', '小儿斑出欲透，皮肤身热，咽喉不利。', '-', '-', '升麻', '[\'甘草\', \'半两\', \'桔梗\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (59179, '40568', '犀角升麻丸', '犀角45克 升麻30克 羌活30克 防风30克 白附子15克 白芷15克 生地黄30克 川芎15克 红花15克 黄芩15克 甘草（生）7.5克', '每服6克，空腹、临卧时用茶清送下。', '《医宗金鉴》卷六十三', '治雀斑，粉刺，酐黯。', '-', '-', '升麻', '[\'犀角\', \'升麻\', \'羌活\', \'防风\', \'白附子\', \'白芷\', \'生地黄\', \'川芎\', \'红花\', \'黄芩\', \'甘草\', \'生克\']');
