INSERT INTO `prescript` VALUES (59181, '40525', '加味龙麝紫金饼', '生地5钱，玄参5钱，琥珀5钱，犀角（生，镑）5钱，羚羊角（镑）5钱，薄荷5钱，桔梗5钱，升麻5钱，凝水石（煅）5钱，连翘5钱，人参5钱，牙消（另研）5钱，赤茯苓5钱，川芎1两，朱砂（水飞）1两，诃子（去核）4钱，牛黄4钱，冰片4钱，青黛4钱，石膏3钱，麝香少许，金箔百张（为衣）。', '本方方名，据剂型，当作“加味龙麝紫金丸。”', '《证治宝鉴》卷十', '上焦风热，咽喉肿痛，口舌生疮，肺经不清，声音不利，痰涎壅盛。', '-', '-', '升麻', '[\'生地\', \'玄参\', \'琥珀\', \'犀角\', \'生镑\', \'羚羊角\', \'薄荷\', \'桔梗\', \'升麻\', \'钱凝水石\', \'连翘\', \'人参\', \'钱牙消\', \'研钱\', \'茯苓\', \'川芎\', \'朱砂\', \'水飞\', \'诃子\', \'去核\', \'牛黄\', \'冰片\', \'青黛钱\', \'石膏\', \'麝香\', \'少许\', \'金箔\', \'百张\', \'为衣\']');
INSERT INTO `prescript` VALUES (59182, '40482', '二十二味虎睛丸', '虎睛1只（生眼佳，晒干，酒浸令黄色），真珠3钱，蜂房3钱，麻黄2分（去节），钩藤3分，铁精4分，防葵4分，大黄4分，子芩4分，龙齿4分，银屑4分，栀子仁4分，羌活4分，柴胡3分，升麻3分，白鲜皮3分，雷丸（烧令赤）3分，人参3分，细辛1分半，蛇皮5寸（炙），石膏5分，蚱蝉4枚（去翅足，炙）。', '虎晴丸（《普济方》卷三七七）。', '《颅囟经》卷上', '孩子从1岁至大，癫发无时，口出白沫，小便淋沥不利。', '忌生冷、油腻。', '-', '升麻', '[\'虎睛\', \'只生\', \'眼佳\', \'晒干\', \'浸令\', \'黄色\', \'真珠\', \'蜂房\', \'麻黄\', \'分去\', \'钩藤\', \'分铁\', \'精分\', \'防葵\', \'大黄\', \'分子\', \'龙齿\', \'银屑\', \'栀子\', \'仁分\', \'羌活\', \'柴胡\', \'升麻\', \'白鲜皮\', \'雷丸\', \'烧令\', \'赤分\', \'人参\', \'细辛\', \'分半\', \'蛇皮\', \'石膏\', \'蚱蝉\']');
INSERT INTO `prescript` VALUES (59183, '40413', '加减升麻葛根汤', '升麻10g，葛根30g，杭芍10g，荆芥10g，防风10g，甘草6g，浮萍15g。', '-', '《中西医结合皮肤病学》', '解表透疹，清阳明里热。主阳明风热证，怕冷，口干，大便溏稀，全身有瘙痒性风团，丘疹，水疱皮损，脉滑，少力，舌薄黄白苔。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'杭芍\', \'荆芥\', \'防风\', \'甘草\', \'浮萍\']');
