INSERT INTO `prescript` VALUES (59185, '40319', '龙胆汤', '龙胆（去苗，洗）1两，犀角（镑）1两，生地黄（洗，切）1两，麦门冬（去心，生用）3分，升麻（锉）半两，甘草（炙）半两，牡蛎（慢火炒）1两半。', '上(口父)咀。每服4钱匕，水1盏半，煎至8分，去滓，不拘时候温服。', '《圣济总录》卷九十六', '小便赤涩，额上汗出，手足烦热。', '-', '-', '升麻', '[\'龙胆\', \'苗洗\', \'犀角\', \'生地黄\', \'洗切\', \'麦门冬\', \'心生\', \'用分\', \'升麻\', \'半两\', \'甘草\', \'半两\', \'牡蛎\', \'慢火\', \'两半\']');
INSERT INTO `prescript` VALUES (59186, '40304', '加味牛黄散', '牛黄（另研）2钱半，麝香（另研）2钱半，犀角屑2钱半，羚羊角屑2钱半，龙齿（另研）2钱半，防风（去芦）2钱半，天麻2钱半，独活（去芦）2钱半，人参（去芦）2钱半，茯神（去木）2钱半，川升麻2钱半，甘草（炙）2钱半，白鲜皮2钱半，远志（去心）2钱半，天竺黄2钱半（另研）朱砂（水飞）半两，铁粉（另研）半两，麦门冬（去心）半两。', '每服2钱，煎麦门冬汤调下，不拘时服。', '《奇效良方》卷一', '心脏中风，恍惚恐惧，闷乱不得睡卧，志意不定，语言错乱,骨蒸肌热，烦躁，劳热口干；或恍惚恐乱，睡卧不得，志意不定。', '-', '-', '升麻', '[\'牛黄\', \'研钱\', \'麝香\', \'研钱\', \'犀角\', \'羚羊角\', \'龙齿\', \'研钱\', \'防风\', \'芦钱\', \'半天\', \'麻钱\', \'独活\', \'芦钱\', \'人参\', \'芦钱\', \'茯神\', \'木钱\', \'半川\', \'升麻\', \'钱半\', \'甘草\', \'白鲜皮\', \'钱半\', \'远志\', \'心钱\', \'天竺黄\', \'钱半\', \'另研\', \'朱砂\', \'水飞\', \'半两\', \'铁粉\', \'另研\', \'半两\', \'麦门冬\', \'去心\', \'半两\']');
INSERT INTO `prescript` VALUES (59187, '40275', '加味槐花散', '槐花2钱，熟地黄2钱，白术2钱，青皮2钱，荆芥穗2钱，川芎2钱，当归身4分，升麻4分，枳壳（麸炒）5分。', '水煎服。', '《保婴撮要》卷十四', '肠风下血，痔疮肿痛，发热便秘。', '-', '-', '升麻', '[\'槐花\', \'熟地黄\', \'白术\', \'青皮\', \'荆芥\', \'川芎\', \'当归\', \'身分\', \'升麻\', \'枳壳\', \'炒分\']');
INSERT INTO `prescript` VALUES (59188, '40202', '加味二陈汤', '陈皮1钱，半夏1钱，白茯苓1钱，白术1钱，苍术1钱，益智仁（盐水炒）1钱，炙草5分，升麻4分，柴胡7分。', '加生姜为引，水煎服。', '《万氏女科》卷一', '白浊。', '-', '-', '升麻', '[\'陈皮\', \'半夏\', \'白茯苓\', \'白术\', \'苍术\', \'益智仁\', \'盐水\', \'草分\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59189, '40200', '加减三花五子丸', '菊花7钱，蜜蒙花7钱，旋覆花7钱，荆芥7钱，夏枯草7钱，升麻7钱，木贼7钱，枸杞子1两，菟丝子（酒煮）1两，青葙子1两，归须（酒洗）1两，黄芩1两，连翘1两，白茯（去皮）1两，石斛草1两，羌活1两，藁本1两，黄柏1两，知母（盐水炒）1两，防风1两，白芷1两，草决明（炒）8钱，石决明（煅）8钱，蔓荆子8钱，地肤子8钱，甘草6钱。', '每服40丸，盐汤或酒送下。', '《眼科全书》卷六', '五脏风热上攻，肝虚头痛，眼见飞花或生翳障。', '-', '-', '升麻', '[\'菊花\', \'钱蜜\', \'花钱\', \'旋覆花\', \'荆芥\', \'夏枯草\', \'升麻\', \'木贼\', \'枸杞子\', \'菟丝子\', \'青葙子\', \'两归须\', \'黄芩\', \'连翘\', \'两白\', \'去皮\', \'石斛\', \'羌活\', \'藁本\', \'黄柏\', \'知母\', \'盐水\', \'防风\', \'白芷\', \'草决明\', \'石决明\', \'蔓荆子\', \'地肤子\', \'甘草\']');
INSERT INTO `prescript` VALUES (59190, '40159', '加味清胃散', '当归尾2钱，生地黄3钱，牡丹皮3钱，升麻4分，黄连6分，防风1钱5分，荆芥1钱，软石膏3钱。', '水煎服。若牙颧额半边痛者，加防风、羌活、白芷、细辛；若牙龈脱出而出血者，加扁柏叶、黄芩、荆芥、栀子；若虚损人牙痛者，加黄柏、知母、人参、甘草；若满口浮而痛，不能力嚼者，加连翘、元参、芍药；小儿牙疳者，乳母服，加天花粉、元参、白芷；醇酒厚味，唇齿作痛，或牙龈溃烂，连头面颈项作痛者，并加犀角、连翘、甘草；胃气齿痛，加草豆蔻、细辛、防风、羊胫骨灰，去牡丹皮。', '《寿世保元》卷六', '胃经火盛，致牙齿肿痛，上下牙痛，牵引头脑而热，其齿喜冷恶热者;胃中蕴热，中脘作痛，痛后火气发泄，必作寒热乃止；及齿龈肿痛出血。', '-', '-', '升麻', '[\'当归\', \'尾钱\', \'生地黄\', \'牡丹皮\', \'升麻\', \'黄连\', \'防风\', \'钱分\', \'荆芥\', \'钱软\', \'石膏\']');
INSERT INTO `prescript` VALUES (59191, '40057', '龙胆汤', '龙胆1两半，秦艽（去苗土）1两半，升麻1两。', '每服5钱匕，水1盏半，浸药1宿，平旦煎至8分，加黄牛乳5合，再煎至1盏，去滓，空心温服，日2次。取利为度。', '《圣济总录》卷六十', '阴黄。', '-', '-', '升麻', '[\'龙胆\', \'两半\', \'秦艽\', \'苗土\', \'两半\', \'升麻\']');
INSERT INTO `prescript` VALUES (59192, '40049', '分痘汤', '升麻1钱，元参3钱，麦冬3钱，当归2钱，青蒿2钱，生甘草2钱，半夏5分，生地3钱，荆芥1钱。', '水煎服。', '《辨证录》卷十四', '痘已见形，又出一层红斑者，此夹疹痘也；或似斑而非斑，或零星错乱，皆是夹疹之症。', '-', '-', '升麻', '[\'升麻\', \'钱元\', \'参钱\', \'麦冬\', \'当归\', \'青蒿\', \'生甘草\', \'半夏\', \'生地\', \'荆芥\']');
INSERT INTO `prescript` VALUES (59193, '40032', '加味补中益气汤', '人参1钱，黄耆（蜜炙）1钱，白术1钱，白芍药（酒炒）1钱，甘草（炙）1钱，陈皮1钱，当归1钱，升麻5分，柴胡5分，砂仁5分。', '水煎服。', '《济阳纲目》卷七十三', '劳倦饮食损伤元气，或过服寒凉消导之药，致清气下陷，肚腹大痛。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'蜜炙钱\', \'白术\', \'白芍\', \'药酒\', \'甘草\', \'陈皮\', \'当归\', \'升麻\', \'柴胡\', \'砂仁\']');
INSERT INTO `prescript` VALUES (59194, '40010', '加减补中益气汤', '党参3钱，白术1钱，黄耆2钱（蜜炙），炙甘草1钱，当归身2钱（微炒），升麻5分（蜜炙），熟地3钱，乌梅肉3个（去壳），山萸肉2钱（炒）。', '生姜3片，大枣2枚为引。', '《医门八法》卷三', '疝气，小腹胀痛牵连睾丸。', '-', '-', '升麻', '[\'党参\', \'白术\', \'黄耆\', \'钱蜜\', \'甘草\', \'当归\', \'身钱\', \'升麻\', \'分蜜\', \'熟地\', \'乌梅\', \'肉个\', \'山萸\']');
INSERT INTO `prescript` VALUES (59195, '39956', '化毒解毒汤', '升麻4g，石膏15g，连翘子9g，牛蒡子9g，黄连6g，知母6g，元参9g，竹叶7g。', '水煎服。', '《中医皮肤病学简编》', '药物性皮炎。', '-', '-', '升麻', '[\'升麻\', \'石膏\', \'连翘\', \'牛蒡子\', \'黄连\', \'知母\', \'元参\', \'竹叶\']');
INSERT INTO `prescript` VALUES (59196, '39951', '防风汤', '防风（去叉，锉）2两，独活（去芦头）2两，防己2两，秦艽（去苗土）2两，黄耆2两，芍药2两，白术2两，芎2两，远志（去心）2两，石膏（碎）3两，升麻2两，石斛（去根，锉）3两，牛膝（去苗，酒浸，切，焙）2两，丹参2两，陈橘皮（汤浸，去白，焙）1两半，甘草（炙，锉）2两，厚朴（去粗皮，生姜汁炙5遍）2两，天门冬（去心，焙）2两，薏苡仁（炒）2两半，羚羊角（镑屑）2两，五加皮2两，熟干地黄（焙干）1两半，麻黄（去节，先煎，掠去沫，焙干）1两半，地骨皮2两，人参2两，茯神（去木）2两。', '每以6钱匕，水3盏，煎至1盏半，去滓，分温2服，空心、夜卧服之。若觉心中烦热，每煎将熟，别入竹沥1合，更煎3沸温服。', '《圣济总录》卷九', '风痱，四肢不收，言语謇涩。', '-', '-', '升麻', '[\'防风\', \'独活\', \'芦头\', \'防己\', \'秦艽\', \'苗土\', \'两黄耆\', \'芍药\', \'白术\', \'两芎\', \'远志\', \'石膏\', \'升麻\', \'石斛\', \'牛膝\', \'苗酒\', \'浸切\', \'丹参\', \'两陈\', \'橘皮\', \'汤浸\', \'两半\', \'甘草\', \'厚朴\', \'粗皮生\', \'姜汁\', \'炙遍\', \'天门冬\', \'薏苡仁\', \'两半\', \'羚羊角\', \'五加皮\', \'两熟\', \'地黄\', \'焙干\', \'两半\', \'麻黄\', \'节先\', \'掠去\', \'焙干\', \'两半\', \'地骨皮\', \'人参\', \'茯神\']');
INSERT INTO `prescript` VALUES (59197, '39948', '加味安胎饮', '白术（蜜炙）2钱，人参1钱，当归1钱，熟地黄1钱，川芎1钱，白芍1钱，陈皮1钱，苏叶1钱，黄芩（蜜炙）1钱，炙甘草1钱，升麻5分（1方有砂仁）。', '加生姜3片，大枣2个，水1钟半，煎7分服。', '《叶氏女科》卷二', '妊娠6月，脾胃气虚不能承载，致胎作胀，或胀作痛。', '-', '-', '升麻', '[\'白术\', \'蜜炙钱\', \'人参\', \'当归\', \'熟地黄\', \'川芎\', \'白芍\', \'陈皮\', \'苏叶\', \'黄芩\', \'蜜炙钱\', \'甘草\', \'升麻\', \'分方\', \'砂仁\']');
INSERT INTO `prescript` VALUES (59198, '39909', '加减补中益气汤', '党参5钱，炙耆5钱，炙草1钱，归身5钱（炒），升麻1钱（蜜炙），乌梅5个（去核）。', '-', '《医门八法》卷四', '补气，敛肝。主产后血崩，新血暴注，血脱气陷者。', '-', '-', '升麻', '[\'党参\', \'钱炙耆\', \'草钱\', \'归身\', \'升麻\', \'钱蜜\', \'乌梅\', \'去核\']');
INSERT INTO `prescript` VALUES (59199, '39873', '加味解毒散', '玄参3钱，连翘3钱，升麻3钱，芍药3钱，当归3钱，羌活3钱，生地黄3钱，牛蒡子（炒）3钱，茯苓3钱，甘草3钱，金银花5钱，漏芦5钱。', '加味解毒饮（《准绳·幼科》卷三）。', '《保婴撮要》卷十二', '天泡疮，发热作痛。', '-', '-', '升麻', '[\'玄参\', \'连翘\', \'升麻\', \'芍药\', \'当归\', \'羌活\', \'生地黄\', \'牛蒡子\', \'茯苓\', \'甘草\', \'金银\', \'花钱\', \'漏芦\']');
INSERT INTO `prescript` VALUES (59200, '39862', '加味黄芩汤', '黄芩4钱，厚朴3钱，吴茱萸4钱，毛苍术4钱，杭白芍3钱，升麻3钱，车前子4钱（炒），木通3钱，大枣6枚，甘草2钱。', '水煎，温服。赤痢，去车前子，加大黄4钱，服1剂，再加地榆炭3钱，粟壳4钱，服2剂；白痢，去大枣、车前子，加槟榔3钱，炮姜2钱，地榆炭3钱，粟壳4钱。', '《医学探骊集》卷三', '漏底伤寒。伤寒日久，不能忌口，饮冷食凉，触动脾胃，致令脾气虚衰，脾湿下陷，腹痛泄泻。亦治痢疾。', '-', '-', '升麻', '[\'黄芩\', \'厚朴\', \'吴茱萸\', \'苍术\', \'白芍\', \'升麻\', \'车前子\', \'木通\', \'大枣\', \'甘草\']');
INSERT INTO `prescript` VALUES (59201, '39794', '加味消毒犀角饮', '牛蒡子3两，荆芥穗5钱，甘草1两，防风（去芦）7钱半，川升麻7钱半，犀角3钱，麦门冬（去心）5钱，桔梗5钱。', '每服2钱，水煎，温服，时时令呷含下。', '《袖珍小儿》卷八', '毒气壅遏，壮热心烦，疮疹出未匀透，口生疮不能吮乳。', '-', '-', '升麻', '[\'牛蒡子\', \'荆芥\', \'甘草\', \'防风\', \'芦钱\', \'半川\', \'升麻\', \'钱半\', \'犀角\', \'麦门冬\', \'心钱\', \'桔梗\']');
INSERT INTO `prescript` VALUES (59202, '39786', '龙麝聚圣丹', '川芎1两，生地黄半两，犀角屑半两，羚羊角半两，南琥珀半两（研），南玄参半两，桔梗半两，连翘半两，马牙消3钱（研），人参3钱，赤茯苓3钱（去皮），升麻3钱，牛黄3钱（研），麝香3钱（研），脑子（研）3钱，南硼砂（研）1两，铅白霜（研）1钱，朱砂（水飞）半两，金箔（为衣）50片。', '每服1丸，用薄荷汤化下；或新水化服亦得，更或细嚼服，并噙化咽津，皆可，食后、临卧服，日2-3次。', '《御药院方》卷九', '心脾客热，毒气攻冲，咽喉亦肿疼痛，或成喉疖，或结硬不消，愈而复发，经久不愈，或舌本肿胀，满口生疮，饮食难咽。', '-', '-', '升麻', '[\'川芎\', \'生地黄\', \'半两\', \'犀角\', \'半两\', \'羚羊角\', \'半两\', \'琥珀\', \'半两\', \'研南\', \'玄参\', \'半两\', \'桔梗\', \'半两\', \'连翘\', \'半两\', \'马牙\', \'消钱研\', \'人参\', \'茯苓\', \'去皮\', \'升麻\', \'牛黄\', \'钱研\', \'麝香\', \'钱研\', \'脑子\', \'研钱南\', \'硼砂\', \'研两\', \'白霜\', \'研钱\', \'朱砂\', \'水飞\', \'半两\', \'金箔\', \'衣片\']');
INSERT INTO `prescript` VALUES (59203, '39734', '加减补益汤', '黄耆1钱5分，人参1钱5分，归身1钱，炙甘草5分，焦术1钱，陈皮5分，升麻3分，川椒20粒，乌梅2个，吴萸3分，川连3分（均炒）。', '生姜。大枣为引，水煎服。', '《胎产秘书》', '产后阴门发痒。', '-', '-', '升麻', '[\'黄耆\', \'钱分\', \'人参\', \'钱分\', \'归身\', \'甘草\', \'焦术\', \'陈皮分\', \'升麻\', \'分川椒粒\', \'乌梅\', \'吴萸\', \'分川\', \'连分\']');
INSERT INTO `prescript` VALUES (59204, '39657', '化坚汤', '升麻1钱，葛5分，漏芦，牡丹皮3钱，当归3钱，生熟地黄3钱，连翘1钱，黄耆1钱，芍药3钱，桂3钱，柴胡8钱，黍粘1钱，羌活1钱，防风5分，独活5分，昆布、三棱、广术、人参、黄连、陈皮。', '方中自昆布以下用量原缺。腹胀，加朴；气不顺，加木香；便秘，加大黄。', '《脉因证治》卷下', '泻火散结。主瘰疬，瘿瘤。', '-', '-', '升麻', '[\'升麻\', \'钱葛分\', \'漏芦\', \'牡丹皮\', \'当归\', \'钱生\', \'熟地黄\', \'连翘\', \'黄耆\', \'芍药\', \'钱桂钱\', \'柴胡\', \'羌活\', \'防风\', \'独活\', \'昆布\', \'三棱\', \'广术\', \'人参\', \'黄连\', \'陈皮\']');
INSERT INTO `prescript` VALUES (59205, '39596', '加减射干鼠粘汤', '射干1钱1分，山豆根1钱1分，白僵蚕1钱1分，鼠粘子1钱2分，紫草茸1钱2分，紫菀1钱2分，桔梗1钱，石膏1钱，诃子1钱，木通1钱，升麻8分，蝉蜕8分，甘草5分。', '每服4-5钱，水煎，食远服。', '《准绳·幼科》卷六', '痘症，热毒上冲，咽喉肿痛。', '-', '-', '升麻', '[\'射干\', \'钱分\', \'山豆根\', \'钱分\', \'白僵蚕\', \'分鼠\', \'粘子\', \'钱分\', \'紫草茸\', \'钱分\', \'紫菀\', \'钱分\', \'桔梗\', \'石膏\', \'诃子\', \'木通\', \'升麻\', \'蝉蜕\', \'甘草\']');
INSERT INTO `prescript` VALUES (59206, '39569', '加减木香散', '木香2钱半，良姜2钱半，升麻（去腐）2钱半，人参（去芦）2钱半，槟榔2钱半，神曲（炒）2钱，肉豆蔻吴茱萸（泡）半钱，缩砂仁半钱，干姜（炮）半钱，陈皮半钱。', '木香散（《普济方》卷二○八）。宜加白术。', '《卫生宝鉴》卷十六', '飧泄。', '-', '-', '升麻', '[\'木香\', \'钱半良\', \'姜钱\', \'升麻\', \'腐钱\', \'人参\', \'芦钱\', \'槟榔\', \'钱半\', \'神曲\', \'肉豆蔻\', \'吴茱萸\', \'泡半钱\', \'砂仁\', \'半钱\', \'干姜\', \'炮半钱\', \'陈皮\', \'半钱\']');
INSERT INTO `prescript` VALUES (59207, '39566', '车前草汤', '车前草半两，常山（细锉）半两，升麻半两，白粳米半合，豉（炒）半两，甘草（生）半两。', '每用3钱匕，以水1盏半浸药，置于星月下，横一小刀子于药上，五更煎取7分，去滓，分温2服，空腹未发前1服，相次再服。良久得吐，吐定得食浆水粥。', '《圣济总录》卷三十五', '久疟日多，憎寒壮热不止，渴饮水。', '-', '-', '升麻', '[\'车前草\', \'半两\', \'常山\', \'半两\', \'升麻\', \'半两\', \'粳米\', \'半合豉\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59208, '39475', '防风天麻丸', '防风1两（去芦），天麻1两，升麻1两，白附子1两（炮），定风草1两，细辛1两（去苗），川芎1两，人参1两（去芦），丹参1两（去芦），苦参1两（去芦），玄参1两（去芦），紫参1两（去芦），蔓荆子1两，威灵仙1两，穿山甲1两（炒），何首乌1两（另捣为末），蜈蚣1对。', '本方为末，蜜酒调下一钱，名“防风天麻汤”（见《中国医学大辞典》）。', '《准绳·类方》卷五', '疠风癞病。', '宜食淡白粥120日，病人大忌房劳，将息慎口。', '-', '升麻', '[\'防风\', \'两去\', \'天麻\', \'升麻\', \'白附子\', \'两炮定\', \'风草\', \'细辛\', \'两去\', \'川芎\', \'人参\', \'两去\', \'丹参\', \'两去\', \'苦参\', \'两去\', \'玄参\', \'两去\', \'芦紫参\', \'两去\', \'蔓荆子\', \'威灵仙\', \'穿山甲\', \'何首乌\', \'蜈蚣\']');
INSERT INTO `prescript` VALUES (59209, '39474', '地黄汤', '生干地黄（焙）3分，麻黄（去根节，煎，掠去沫，焙）半两，黄芩（去黑心）半两，赤茯苓（去黑皮）半两，升麻半两，龙胆（去土）半两，大黄（锉，炒）半两，黄连（去须）半两，桑根白皮（锉，炒）半两，甘草（炙，锉）1分。', '每服3钱匕，水1盏，煎至7分，去滓温服。以利为度。', '《圣济总录》卷六十五', '咳嗽，大便不通，壅热，口内生疮。', '-', '-', '升麻', '[\'生干\', \'地黄\', \'焙分\', \'麻黄\', \'根节\', \'掠去\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'茯苓\', \'黑皮\', \'半两\', \'升麻\', \'半两\', \'龙胆\', \'半两\', \'大黄\', \'半两\', \'黄连\', \'半两\', \'桑根\', \'白皮\', \'半两\', \'甘草\']');
INSERT INTO `prescript` VALUES (59210, '39466', '防风升麻汤', '防风1钱，升麻1钱，山栀1钱（去壳），麦门冬1钱（去心），木通1钱，甘草节1钱。', '用淡竹叶3片煎，食远服。', '《幼科类萃》卷二十一', '小儿丹瘤赤肿。', '-', '-', '升麻', '[\'防风\', \'升麻\', \'钱山\', \'栀钱\', \'麦门冬\', \'木通\', \'甘草\', \'节钱\']');
INSERT INTO `prescript` VALUES (59211, '39463', '风水散斑汤', '玄参2两，当归2两，荆芥3钱，升麻1钱，生地1两。', '水煎服。', '《辨证录》卷六', '补水散火。主热郁于内，热极生斑，身中如红云一片。', '-', '-', '升麻', '[\'玄参\', \'当归\', \'荆芥\', \'升麻\', \'生地\']');
INSERT INTO `prescript` VALUES (59212, '39389', '二九还元丹', '胡麻8两，苦参8两，荆芥8两，防风2两，羌活2两，升麻2两，独活2两，风藤4两，木通4两，黄柏4两，当归4两，白芷4两，柴胡3两，僵蚕1两5钱，蝉壳1两，川芎1两，蒺藜2两5钱，大风子12两。', '每服50丸，温酒送下，1日3次。', '《解围元薮》卷三', '风疠危笃恶证。', '避风，戒色。', '-', '升麻', '[\'胡麻\', \'苦参\', \'荆芥\', \'防风\', \'羌活\', \'升麻\', \'独活\', \'两风藤\', \'木通\', \'黄柏\', \'当归\', \'白芷\', \'柴胡\', \'僵蚕\', \'川芎\', \'蒺藜\', \'大风子\']');
INSERT INTO `prescript` VALUES (59213, '39318', '白牙散', '白芷7分，升麻1钱，石膏1钱5分，羊胫骨灰2钱，麝香少许。', '先以温水漱口，擦之。', '《兰室秘藏》卷中', '一切牙痛,牙黄黑色。', '-', '-', '升麻', '[\'白芷\', \'升麻\', \'石膏\', \'钱分\', \'胫骨\', \'灰钱\', \'麝香\', \'少许\']');
INSERT INTO `prescript` VALUES (59214, '39311', '白鲜皮汤', '白鲜皮1两半，女萎1两半，防风（去叉）1两半，细辛（去苗叶）1两半，升麻1两半，苍耳（炒）1两半，桂（去粗皮）1两半，附子（炮裂，去皮脐）1两半，五味子1两半，菖蒲（九节者，去须节，米泔浸，切，焙）1两半，蒺藜子（炒，去角）1两半，黄耆（炙，锉）3两。', '每服5钱匕，水1盏半，煎至8分，去滓，食前温服，日2次。', '《圣济总录》卷八', '风腰脚不随，四肢(疒帬)痹，口噤不语，手臂脚膝痿弱颤掉。', '-', '-', '升麻', '[\'白鲜皮\', \'两半\', \'女萎\', \'两半\', \'防风\', \'两半\', \'细辛\', \'苗叶\', \'两半\', \'升麻\', \'两半\', \'苍耳\', \'两半\', \'桂去\', \'粗皮\', \'两半\', \'附子\', \'炮裂\', \'去皮\', \'两半\', \'五味子\', \'两半\', \'菖蒲\', \'九节\', \'节米\', \'浸切\', \'两半\', \'蒺藜\', \'两半\', \'黄耆\']');
INSERT INTO `prescript` VALUES (59215, '39301', '防风汤', '防风2两，麻黄2两，秦艽2两，独活2两，当归1两，远志1两，甘草1两，防己1两，人参1两，黄芩1两，升麻1两，芍药1两，石膏半两，麝香6铢，生姜2两，半夏2两（一方用白术1两）。', '防风散（《证治要诀类方》卷三）。有热，加大黄2两；先有冷心痛疾者，倍当归，加桂心3两，不用大黄。', '《千金》卷七', '肢体虚风，微痉发热，肢节不随，恍惚狂言，来去无时，不自觉悟；亦治脚气缓弱。', '-', '-', '升麻', '[\'防风\', \'麻黄\', \'秦艽\', \'独活\', \'当归\', \'远志\', \'甘草\', \'防己\', \'人参\', \'黄芩\', \'升麻\', \'芍药\', \'石膏\', \'半两\', \'麝香\', \'生姜\', \'半夏\', \'一方\', \'白术\']');
INSERT INTO `prescript` VALUES (59216, '39281', '白芷胃风汤', '白芷2钱5分，升麻2钱5分，葛根8分，苍术（米泔炒）8分，炙草1钱5分，当归1钱5分，草豆蔻4分，柴胡4分，黄柏（炒）4分，藁本4分，羌活4分，蔓荆子3分，白僵蚕3分，麻黄（去节）7分。', '水煎服。', '《外科枢要》卷四', '手足阳明经气虚风热，面目麻木，或牙关紧急，眼目瞤动。', '-', '-', '升麻', '[\'白芷\', \'钱分\', \'升麻\', \'钱分\', \'葛根\', \'苍术\', \'分炙草\', \'钱分\', \'当归\', \'钱分\', \'草豆蔻\', \'柴胡\', \'黄柏\', \'炒分\', \'藁本\', \'羌活\', \'蔓荆子\', \'白僵蚕\', \'麻黄\', \'节分\']');
INSERT INTO `prescript` VALUES (59217, '39209', '归经两安汤', '人参3钱，当归5钱，白芍5钱，熟地5钱，山茱萸2钱，巴戟天1钱，白术5钱，麦冬5钱，荆芥（炒黑）3钱，升麻4分。', '水煎服。', '《辨证录》卷十一', '妇人经入大肠，行经之前一日大便出血。', '-', '-', '升麻', '[\'人参\', \'当归\', \'白芍\', \'熟地\', \'山茱萸\', \'巴戟天\', \'白术\', \'麦冬\', \'荆芥\', \'黑钱\', \'升麻\']');
INSERT INTO `prescript` VALUES (59218, '39145', '六化丹', '犀角（生者）1两，黄芩1两，大黄1两，巴豆半两（去心，去油），升麻1两，当归1两，栀子1两，白蔹1两，甘草1两（炙），天南星1两，黄耆1两半，防风（去芦）1两。', '每服5丸，不拘时候。', '《卫济宝书》卷下', '化风，化热，化毒，化结，化积，化脓为水。主痈疽。', '-', '-', '升麻', '[\'犀角\', \'生者\', \'黄芩\', \'大黄\', \'巴豆\', \'半两\', \'心去\', \'升麻\', \'当归\', \'栀子\', \'白蔹\', \'甘草\', \'天南星\', \'两黄耆\', \'两半\', \'防风\', \'去芦\']');
INSERT INTO `prescript` VALUES (59219, '39014', '开关散', '升麻（取绿色坚实者，酒拌周时，俟润透，晒干，炒黑色用）8钱，台乌（盐水拌透，炒黄色用）8钱，苍术（米泔水润透，炙至白烟起，碗覆存性用）1钱。', '上药用水2碗，煎1碗，隔汤炖热勿冷。令病者仰卧正枕，以洗净新羊毛笔蘸药，使病人吮之。欲吐则任其吐，吐后复吮，至5-6口，当吐痰，不吐药；吮至半碗，并痰不吐；吮完，自能进食。', '《活人方汇编》卷五', '翻胃初起，不拘三脘，迟速吐逆及噎膈之症。', '-', '-', '升麻', '[\'升麻\', \'绿色\', \'坚实\', \'周时\', \'晒干\', \'黑色\', \'用钱\', \'台乌\', \'盐水\', \'黄色\', \'用钱\', \'苍术\', \'米泔水\', \'透炙至\', \'白烟\', \'起碗\', \'覆存性\', \'用钱\']');
INSERT INTO `prescript` VALUES (59220, '39006', '加味清胃散', '黄连（炒）1钱，生地黄1钱，升麻1钱，牡丹皮8分，当归3钱2分，芍药、柴胡。', '水煎服。', '《口齿类要》', '脾胃肝胆经热。', '-', '-', '升麻', '[\'黄连\', \'生地黄\', \'升麻\', \'牡丹皮\', \'当归\', \'钱分\', \'芍药\', \'柴胡\']');
INSERT INTO `prescript` VALUES (59221, '38994', '龙胆汤', '龙胆3分，萎蕤3分，芍药3分，黄芩（去黑心）3分，石膏3分，麻黄（去根节）3分，升麻3分，甘草（炙，锉）3分，葛根（锉）1两，桂（去粗皮）半两，大青半两。', '每服3钱匕，水1盏，加生姜1枣大（拍碎），煎至6分，去滓温服。', '《圣济总录》卷二十三', '伤寒温病后烦躁。', '-', '-', '升麻', '[\'龙胆\', \'分萎\', \'蕤分\', \'芍药\', \'黄芩\', \'黑心\', \'石膏\', \'麻黄\', \'根节\', \'升麻\', \'甘草\', \'葛根\', \'两桂去\', \'粗皮\', \'半两\', \'大青\', \'半两\']');
INSERT INTO `prescript` VALUES (59222, '38959', '加减补中益气汤', '黄耆2钱（炒），人参4钱，白术3钱（土炒），当归1钱，白芍1钱（酒炒），陈皮7分，柴胡5分，升麻3分，黄芩（酒炒）3分，黄连（姜炒）5分，木香3分，砂仁4分，茯苓5分，甘草5分。', '加生姜3片，大枣1枚，水2钟，煎至1钟，温服。人参4钱，服3剂后，每剂只用3钱，又服5剂后，只用2钱；黄耆服至30剂后，浑身不痒去之；升麻服至20剂后去之。', '《鲁府禁方》卷二', '补元气，健脾胃、养心血，平肝火，清湿热，消膨胀。主', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'白术\', \'钱土\', \'当归\', \'白芍\', \'钱酒\', \'陈皮分\', \'柴胡\', \'升麻\', \'黄芩\', \'炒分\', \'黄连\', \'炒分\', \'木香\', \'砂仁\', \'茯苓\', \'甘草\']');
INSERT INTO `prescript` VALUES (59223, '38949', '决明散', '决明子（微炒）1两半，秦皮（锉）1两，甘菊花1两，细辛（去苗叶）3分，羚羊角（镑）1两，赤芍药1两半，麦门冬（去心，焙）1两，升麻1两，黄芩（去黑心）1两，黄连（去须）1两，朴消（研）1两半，甘草（炙）1两。', '本方方名，《普济方》引作\"决明子散\"。', '《圣济总录》卷一一○', '斑疮入眼。', '-', '-', '升麻', '[\'决明子\', \'两半\', \'秦皮\', \'甘菊花\', \'细辛\', \'苗叶分\', \'羚羊角\', \'芍药\', \'两半\', \'麦门冬\', \'升麻\', \'黄芩\', \'黑心\', \'黄连\', \'两朴\', \'消研\', \'两半\', \'甘草\']');
INSERT INTO `prescript` VALUES (59224, '38862', '白药子散', '川升麻1两，白药1两，前胡1两，石膏1两，羚羊角屑、甘草（炙）半两，玄参3分，麦门冬1两半，川朴消2两。', '每服5钱，水1盏，加竹茹1分，煎至5分，去滓温服，不拘时候。', '《鸡峰》卷五', '热病咽喉肿塞连舌根疼痛，及干呕头疼不下食。', '-', '-', '升麻', '[\'升麻\', \'白药\', \'两前\', \'胡两\', \'石膏\', \'羚羊角\', \'甘草\', \'半两\', \'玄参\', \'麦门冬\', \'两半\', \'川朴\', \'消两\']');
INSERT INTO `prescript` VALUES (59225, '38758', '白蒺藜散', '白蒺藜（炒）2两，芎藭2两，山栀子（去皮）1两3钱，防风（去芦）1两3钱，萆薢（炒）2两，羌活（去芦头）2两，白芷（炒）2两，升麻2两，白茯苓（去黑皮）2两半，远志（去心）1两1钱，菖蒲（九节者，泔浸，切，焙干）1两1钱，蔓荆实1两1钱，细辛（去苗叶，微炒）1两1钱，茵芋（去粗茎）1两1钱，芍药1两1钱，麻黄（去根节，煎，掠去沫，焙干）1两1钱，龙骨（刮去土）1两1钱，人参1两1钱，当归（切，焙）1两1钱，桂（去粗皮）1两1钱，白术（微炒）1两，附子（炮裂，去皮脐）3枚，甘草（炙，锉）1两半，桔梗（切，焙', '每服2-3钱匕，用温酒调下，空心。午时前各1服。如口干舌涩，喉里烟生，鼻中卒痛，是佳应也；甚，即煎少许甘草汤解之。', '《普济方》卷一一一引《圣惠》', '大风恶疾，或二年三年眉须堕落者，手足疼闷，骨节烦肿，面色黑，皮肉渐变。', '-', '-', '升麻', '[\'蒺藜\', \'栀子\', \'去皮\', \'防风\', \'去芦\', \'羌活\', \'芦头\', \'白芷\', \'升麻\', \'白茯苓\', \'黑皮\', \'两半\', \'远志\', \'去心\', \'菖蒲\', \'九节\', \'浸切\', \'焙干\', \'荆实\', \'细辛\', \'苗叶微\', \'两钱茵芋\', \'芍药\', \'麻黄\', \'根节\', \'掠去\', \'焙干\', \'龙骨\', \'刮去\', \'土两钱\', \'人参\', \'当归\', \'切焙\', \'两钱桂去\', \'粗皮\', \'白术\', \'附子\', \'炮裂\', \'去皮\', \'脐枚\', \'甘草\', \'两半\', \'桔梗\', \'切焙\']');
INSERT INTO `prescript` VALUES (59226, '38726', '白芷升麻汤', '白芷1钱半，升麻1钱，桔梗1钱，生黄芩3钱，酒黄芩4钱，红花半钱，甘草（炙）半钱。', '上作1服。水2钟，酒半钟，煎至8分，食后服。', '《奇效良方》卷五十四', '臂上生痈,肿痛，右手脉大，未成脓者。', '-', '-', '升麻', '[\'白芷\', \'钱半\', \'升麻\', \'桔梗\', \'钱生\', \'黄芩\', \'钱酒\', \'黄芩\', \'红花\', \'半钱\', \'甘草\', \'炙半钱\']');
INSERT INTO `prescript` VALUES (59227, '38658', '决明子散', '决明子1两，川升麻1两，地骨皮两，柴胡（去苗）1两，葳蕤半两，玄参半两，犀角屑半两，甘草（炙微赤，锉）半两。', '每服4钱，水1钟，煎至6分，去滓，食后温服。', '《普济方》卷八十引《圣惠》', '肝膈热毒，眼生钉翳。', '-', '-', '升麻', '[\'决明子\', \'两川\', \'升麻\', \'地骨皮\', \'柴胡\', \'葳蕤\', \'半两\', \'玄参\', \'半两\', \'犀角\', \'半两\', \'甘草\', \'炙微\', \'半两\']');
INSERT INTO `prescript` VALUES (59228, '38640', '加味益气汤', '黄耆1钱，人参1钱，白术1钱，甘草（炙）1钱，当归1钱，陈皮1钱，升麻5分，柴胡5分，麦门冬（去心）5分，香附5分，羌活5分，防风5分，木香3分，乌药（炮）3分。', '加生姜、大枣，水煎服。', '《济阳纲目》卷八十二', '气虚，十指尽麻，并面目皆麻。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'白术\', \'甘草\', \'当归\', \'陈皮\', \'升麻\', \'柴胡\', \'麦门冬\', \'心分\', \'香附\', \'羌活\', \'防风\', \'木香\', \'乌药\', \'炮分\']');
INSERT INTO `prescript` VALUES (59229, '38611', '加味补中益气汤', '人参3钱，当归身3钱，柴胡2钱，黄耆8钱，升麻1钱半，熟地5钱，玉竹3钱，枸杞4钱，白术3钱，云苓3钱，石斛3钱，甘草1钱，大枣3个，生姜1片。', '水煎服。', '《眼科临症笔记》', '高风障症。二目不赤，不疼，不肿，常觉眩晕，每至日落星出，而无所见。', '-', '-', '升麻', '[\'人参\', \'当归\', \'身钱\', \'柴胡\', \'黄耆\', \'升麻\', \'半熟\', \'玉竹\', \'枸杞\', \'白术\', \'钱云苓\', \'石斛\', \'甘草\', \'大枣\', \'生姜\']');
INSERT INTO `prescript` VALUES (59230, '38553', '龙齿散', '龙齿半两（细研），朱砂1两（细研如粉），牛黄1分（研入），细辛1两，龙脑1分（细研），犀角屑1两，防风1两（去芦头），羌活1两，荆芥1两，枳壳1两（麸炒微黄，去瓤），天竺黄1两（细研），茯神1两，沙参1两（去芦头），天麻1两，川升麻1两，子芩1两，麦门冬1两（去心，焙），羚羊角屑1两，甘草半两（炙微赤，锉），甘菊花半两。', '每服1钱，食后煎竹时汤调下。', '《圣惠》卷四', '心脏风热，心神恍惚，烦躁多惊，不得眠卧。', '-', '-', '升麻', '[\'龙齿\', \'半两\', \'细研\', \'朱砂\', \'两细\', \'研如粉\', \'牛黄\', \'分研入\', \'细辛\', \'龙脑\', \'分细研\', \'犀角\', \'防风\', \'两去\', \'芦头\', \'羌活\', \'荆芥\', \'枳壳\', \'两麸\', \'微黄\', \'天竺黄\', \'两细研\', \'茯神\', \'沙参\', \'两去\', \'芦头\', \'天麻\', \'两川\', \'升麻\', \'两子\', \'麦门冬\', \'两去\', \'羚羊角\', \'甘草\', \'半两\', \'炙微\', \'甘菊花\', \'半两\']');
INSERT INTO `prescript` VALUES (59231, '38489', '加味八珍汤', '八珍汤8钱，黄耆1钱，防风5分，升麻5分。', '水煎服。外以荆芥、藿香、樗皮煎汤熏洗。', '《济阴纲目》卷十四', '产后子肠不收。', '-', '-', '升麻', '[\'八珍\', \'汤钱\', \'黄耆\', \'防风\', \'升麻\']');
INSERT INTO `prescript` VALUES (59232, '38427', '白薇汤', '白薇半两，蜀漆叶半两，常山1两，知母（切，焙）半两，鳖甲（去裙襕醋炙黄）3分，甘草（炙，锉）半两，苦参半两，大黄（生）1两，升麻半两，石膏（碎）3分，豉1合，蒜3枚（独棵者，切），淡竹叶（切）3两，赤茯苓（去黑皮）3分，龙胆半两，黄芩（去黑心）半两。', '每服3钱匕，水1盏，酒半盏，同煎至1盏，去滓，未发前温1服，发时1服，仍以药汁涂手面良。', '《圣济总录》卷三十七', '瘴疟，经百日或一年者。', '-', '-', '升麻', '[\'白薇\', \'半两\', \'漆叶\', \'半两\', \'常山\', \'知母\', \'切焙\', \'半两\', \'鳖甲\', \'黄分\', \'甘草\', \'半两\', \'苦参\', \'半两\', \'大黄\', \'生两\', \'升麻\', \'半两\', \'石膏\', \'碎分豉\', \'合蒜\', \'独棵者\', \'淡竹叶\', \'茯苓\', \'黑皮\', \'龙胆\', \'半两\', \'黄芩\', \'黑心\', \'半两\']');
INSERT INTO `prescript` VALUES (59233, '38426', '加减黄芩汤', '荆芥穗3钱，薄荷3钱，黄芩5钱，车前子4钱（炒），毛苍术4钱，盐泽泻3钱，升麻3钱，木通3钱，粉甘草3钱。', '水煎，温服。', '《医学探骊集》卷三', '伤寒泄泻。', '-', '-', '升麻', '[\'荆芥\', \'薄荷\', \'黄芩\', \'车前子\', \'苍术\', \'泽泻\', \'升麻\', \'木通\', \'钱粉\', \'甘草\']');
INSERT INTO `prescript` VALUES (59234, '38418', '龙齿散', '龙齿1两（细研），犀角屑1两，茯神1两，人参1两（去芦头），牛黄1分（细研），蝉壳1分（微炒），赤石脂1两，黄芩3分，牡蛎粉3分，川升麻3分。', '每服半钱，不拘时候。以荆芥、薄荷汤调下。', '《圣惠》卷八十五', '小儿惊热，心神烦乱，手足缩掣不定。', '-', '-', '升麻', '[\'龙齿\', \'两细研\', \'犀角\', \'茯神\', \'人参\', \'两去\', \'芦头\', \'牛黄\', \'分细\', \'研蝉壳\', \'分微\', \'赤石脂\', \'黄芩\', \'牡蛎\', \'粉分川\', \'升麻\']');
INSERT INTO `prescript` VALUES (59235, '38391', '防己竹沥汤', '防己（锉）1两，麻黄（去根节，先煎，掠去沫，焙）3两，防风2两（去叉），升麻2两，桂2两（去粗皮），芎2两，独活2两（去芦头），羚羊角2两（镑）。', '每服5钱匕，水1盏半，煎至8分，去滓，更入竹沥1合，再煎3-5沸，温服，日2夜1。风若未除，更服防风独活汤。手足逆冷，加生姜3两、白术2两。', '《圣济总录》卷五', '初得中风，四肢不收，心神昏愦，眼不识人，不能言语，已服荆沥汤及防风汤，诸症好转者。', '-', '-', '升麻', '[\'防己\', \'麻黄\', \'根节\', \'掠去\', \'防风\', \'两去\', \'升麻\', \'两桂\', \'两去\', \'粗皮\', \'独活\', \'两去\', \'芦头\', \'羚羊角\', \'两镑\']');
INSERT INTO `prescript` VALUES (59236, '38390', '防风饮', '防风（去叉）1两，龙骨（去土）2两，升麻1两，赤芍药1两，黄芩（去黑心）1两，葛根（锉）1两半，石膏（碎）1两半。', '本方方名，《普济方》引作“防风散”。', '《圣济总录》卷十五', '首风，脑项掣痛。', '-', '-', '升麻', '[\'防风\', \'龙骨\', \'升麻\', \'两赤\', \'芍药\', \'黄芩\', \'黑心\', \'葛根\', \'两半\', \'石膏\', \'两半\']');
INSERT INTO `prescript` VALUES (59237, '38321', '防己汤', '防己（锉）1两，竹沥（旋入）、防风（去叉，锉）1两，升麻1两，桂（去粗皮）1两，麻黄（去根节，先前，掠去沫，焙干）1两半，芎1两，羚羊角（镑）1两。', '每用药12钱匕，以水4盏，煎至2盏，去滓，入竹沥2合，更煎3沸，分3次温服，空心、午时、夜深各1服。常服，加独活1两半；若手足逆冷，加干姜1两（炮裂）。若不即除，更服麻黄防风汤。', '《圣济总录》卷九', '中风，半身不随，口面斜，语不得转。', '-', '-', '升麻', '[\'防己\', \'竹沥\', \'旋入\', \'防风\', \'升麻\', \'两桂去\', \'粗皮\', \'麻黄\', \'根节\', \'先前\', \'掠去\', \'焙干\', \'两半\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (59238, '38302', '半夏丸', '半夏2斤（去皮，河水洗6-7度，完用），白矾1斤（末之），丁香4两，甘草4两，草豆蔻4两，川升麻4两，缩砂4两（粗捣）。', '每服1粒，嚼破，用姜汤送下，或干吃。候60日干，方得服。', '方出《千金》卷五，名见《医部全录》卷四二二', '大人、小儿咳逆上气。', '-', '-', '升麻', '[\'半夏\', \'去皮\', \'河水\', \'完用\', \'白矾\', \'丁香\', \'甘草\', \'草豆蔻\', \'两川\', \'升麻\', \'两缩\']');
INSERT INTO `prescript` VALUES (59239, '38249', '含化龙脑丸', '龙脑1分（细研），川升麻1两，甘草半两（炙微赤，锉），马牙消1两，麝香1分（细研），钟乳粉1两，川大黄半两（炙，碎，微炒），黄耆1两（锉），生地黄5两（取汁）。', '先深针肿结处，散尽毒气，后以绵裹1丸含咽津，不拘时候。以咽喉通利为度。', '《圣惠》卷三十五', '咽喉中有物如弹丸，日数深远，津液难咽、发渴疼痛。', '-', '-', '升麻', '[\'龙脑\', \'分细\', \'研川\', \'升麻\', \'甘草\', \'半两\', \'炙微\', \'马牙消\', \'麝香\', \'分细\', \'研钟\', \'乳粉\', \'两川\', \'大黄\', \'半两\', \'炙碎\', \'黄耆\', \'生地黄\', \'两取\']');
INSERT INTO `prescript` VALUES (59240, '38242', '百伤饮', '干葛3两，净香附2两，升麻（净洗），青皮（去白）1两，陈皮（去白）1两，谷芽（净洗，焙干）1两，麦芽（净洗，焙干）1两，桔梗（锉，炒）1两，紫苏（和根）1两，缩砂仁1两，甘草1两，神曲（炒）1两，赤芍药1两，麻黄（不去根节）7钱半，枳壳（切片，麸炒微焦）7钱半。', '每服2钱，水1盏，煎7分，不拘时候温服；或加生姜、葱白同煎。有积，加水酒曲；热多，添灯心、竹叶。', '《活幼心书》卷下', '感冒风寒邪气，不拘冷热二证。', '慢惊、慢脾不用。', '-', '升麻', '[\'干葛\', \'两净\', \'香附\', \'升麻\', \'净洗\', \'青皮\', \'陈皮\', \'两谷芽\', \'焙干\', \'麦芽\', \'净洗\', \'焙干\', \'桔梗\', \'紫苏\', \'根两缩\', \'砂仁\', \'甘草\', \'神曲\', \'芍药\', \'麻黄\', \'不去\', \'根节\', \'枳壳\', \'切片\', \'焦钱\']');
INSERT INTO `prescript` VALUES (59241, '38238', '决明汤', '决明子1两，升麻1两，枳实（炙）1两，柴胡1两，黄芩1两，芍药1两，栀子14枚，竹叶1升，车前草4升，甘草1两（炙）。', '决明子汤（《圣济总录》卷一○八）。', '《外台》卷二十一引《广济方》', '客热冲眼，赤痛泪出。', '忌海藻，菘莱。', '-', '升麻', '[\'决明子\', \'升麻\', \'枳实\', \'柴胡\', \'黄芩\', \'芍药\', \'栀子\', \'竹叶\', \'车前草\', \'甘草\']');
INSERT INTO `prescript` VALUES (59242, '38175', '加味四君子汤', '人参1钱，白术1钱，茯苓1钱，甘草（炙）1钱，芍药（炒）1钱，升麻1钱。', '水煎服。', '《济阳纲目》卷二十二', '虚泻，饮食入胃不住，完谷不化。', '-', '-', '升麻', '[\'人参\', \'白术\', \'茯苓\', \'甘草\', \'芍药\', \'升麻\']');
INSERT INTO `prescript` VALUES (59243, '38152', '化毒汤', '紫草（嫩者）半两，升麻半两，甘草（炙）半两。', '四妙汤(《圣济总录》卷一六九)、化斑汤(《儒门事亲》卷十二)、化毒散(《寿世保元》卷八)。', '《活人书》卷二十一', '小儿疮痘已出末出。', '-', '-', '升麻', '[\'紫草\', \'嫩者\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59244, '38119', '龙胆丸', '龙胆草2钱，川升麻2钱，苦楝根皮（焙）2钱，防风2钱，赤茯苓2钱，芦荟2钱，油发灰2钱，青黛（干）3钱，黄连（净）3钱。', '龙脑丸（《得效》卷十二）。', '《直指小儿》卷三', '脑疳，头皮光急，满头饼疮，脑热发结，身汗，腮肿囟高。', '-', '-', '升麻', '[\'龙胆草\', \'钱川\', \'升麻\', \'苦楝\', \'根皮\', \'防风\', \'茯苓\', \'芦荟\', \'钱油\', \'发灰钱\', \'青黛干\', \'黄连\', \'净钱\']');
INSERT INTO `prescript` VALUES (59245, '38083', '加味益气汤', '人参（无参者，以淮山药3钱代之，或以时下生条参3钱代之）1钱，当归1钱，甘草（炙）1钱，白术1钱半，陈皮8分，川芎6分，黄耆（蜜炙）2钱，升麻（蜜炒）3分，柴胡（酒炒）3分，石菖蒲6分。', '生姜、大枣为引，水煎服。', '《会约》卷六', '劳苦太过，气虚耳聋，或耳鸣眩运，倦怠。', '-', '-', '升麻', '[\'人参\', \'参者\', \'以淮\', \'山药\', \'钱代\', \'时下\', \'生条\', \'参钱代\', \'当归\', \'甘草\', \'白术\', \'钱半\', \'陈皮分\', \'川芎\', \'分黄耆\', \'蜜炙钱\', \'升麻\', \'炒分\', \'柴胡\', \'炒分\', \'石菖蒲\']');
INSERT INTO `prescript` VALUES (59246, '38048', '加减补中益气汤', '潞党参5钱，炙口耆3钱，炙甘草2钱，炙升麻1钱，当归身3钱（炒），熟地黄3钱，醋白芍2钱，乌梅肉3个（去核）。', '-', '《医门八法》卷三', '年老虚弱之人，气虚不能收摄，小便烦数，滴沥不止。', '-', '-', '升麻', '[\'党参\', \'钱炙口\', \'甘草\', \'升麻\', \'当归\', \'身钱\', \'熟地黄\', \'白芍\', \'乌梅\', \'肉个\', \'去核\']');
INSERT INTO `prescript` VALUES (59247, '38044', '开郁四物汤', '香附米1钱（炒黑），归身1钱，白芍药1钱（酒炒），熟地黄1钱，川芎半钱，黄耆半钱，蒲黄半钱，地榆半钱，人参半钱，白术1钱，升麻3分。', '水煎服。甚者，加棕榈灰为末，酒调服。', '方出《医学正传》卷七，名见《东医宝鉴·内景篇》卷三', '崩漏。', '-', '-', '升麻', '[\'香附\', \'黑归身\', \'芍药\', \'钱酒\', \'熟地黄\', \'川芎\', \'半钱\', \'黄耆\', \'半钱\', \'黄半钱\', \'地榆\', \'半钱\', \'参半\', \'白术\', \'升麻\']');
INSERT INTO `prescript` VALUES (59248, '38038', '加味补益败毒散', '生耆2钱，人参2钱，焦术1钱，炙甘草8分，陈皮1钱，归身2钱，升麻2分，荆芥1钱，净银花2钱，肉桂5分，防风1钱，乳香（去油）1钱。', '水煎服。', '《胎产秘书》卷下', '湿热下陷，阴门生疮。', '-', '-', '升麻', '[\'生耆\', \'人参\', \'焦术\', \'甘草\', \'陈皮\', \'归身\', \'升麻\', \'荆芥\', \'钱净\', \'花钱\', \'肉桂\', \'防风\', \'乳香\', \'油钱\']');
INSERT INTO `prescript` VALUES (59249, '38036', '归连汤', '升麻2两，黄连2两，大黄2两，川芎2两，羚羊角2两，红花2两，归尾2两，甘草2两，黄芩3两，金银花3两。', '水煎服。余者可纳芒消再煎，涂肿处。', '《诚书》卷十五', '丹毒初发，血热毒盛。', '-', '-', '升麻', '[\'升麻\', \'黄连\', \'大黄\', \'川芎\', \'羚羊角\', \'红花\', \'两归尾\', \'甘草\', \'黄芩\', \'金银花\']');
