INSERT INTO `prescript` VALUES (59253, '37871', '地骨皮散', '地骨皮1两，川升麻1两，玄参1两，甘草（炙微赤，锉）1两，防风（去芦头）1两，黄芩1两，赤茯苓2两，羌活3分，桑根白皮2两（锉），决明子2两半，石膏2两，柴胡2两半（去苗）。', '每服4钱，以水1中盏，加生姜半分，淡竹叶2-7片，黑豆50粒，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝壅毒气上攻，眼睛赤涩疼痛，心躁体热。', '-', '-', '升麻', '[\'地骨皮\', \'两川\', \'升麻\', \'玄参\', \'甘草\', \'炙微\', \'防风\', \'芦头\', \'黄芩\', \'两赤\', \'茯苓\', \'羌活\', \'分桑根\', \'白皮\', \'决明子\', \'两半\', \'石膏\', \'柴胡\', \'两半\', \'去苗\']');
