INSERT INTO `prescript` VALUES (59260, '37563', '甘草酒', '甘草（炙）半两，升麻半两，沉香（锉）半两，麝香（别研）半两，豉1两半。', '每服5钱匕，酒1盏半，煎至8分，去滓，早、晚食前各1服。其滓热敷肿上。甚者取豉半升、栀子仁14枚，葵菜2两，3味用水2升半，煎至1升，滤去滓，温分3服，空心、日午、晚间，服尽为度。', '《圣济总录》卷一三六', '毒气肿，当头上如刺痛。', '-', '-', '升麻', '[\'甘草\', \'半两\', \'升麻\', \'半两\', \'沉香\', \'半两\', \'麝香\', \'别研\', \'半两\', \'两半\']');
