INSERT INTO `prescript` VALUES (59294, '36425', '安神复元汤', '黄耆（蜜炙）1钱5分，人参1钱5分，当归（酒洗）1钱5分，柴胡1钱，升麻5分，黄连（酒炒）1钱，黄芩（酒炒）1钱，黄柏（酒炒）3钱，知母1钱，防风1钱，蔓荆子7分，麦门冬1钱，茯神1钱，酸枣（炒）1钱5分，川芎1钱，甘草5分，甘枸杞子1钱5分。', '上锉1剂，加龙眼肉3枚，水煎服。', '《寿世保元》卷六', '思虑烦心而神散，精脱于下，真阴不上泥丸，而气不聚，耳鸣耳重听，及耳内痒。', '-', '-', '升麻', '[\'黄耆\', \'蜜炙钱分\', \'人参\', \'钱分\', \'当归\', \'洗钱\', \'柴胡\', \'升麻\', \'黄连\', \'黄芩\', \'黄柏\', \'知母\', \'防风\', \'蔓荆子\', \'麦门冬\', \'茯神\', \'酸枣\', \'炒钱分\', \'川芎\', \'甘草\', \'分甘\', \'枸杞子\', \'钱分\']');
