INSERT INTO `prescript` VALUES (59298, '36374', '白花蛇酒', '白花蛇（1条，温水洗净，头尾各去3寸，酒浸，去骨刺，取净肉）1两，全蝎（炒）1钱，当归1钱，防风1钱，羌活1钱，独活5钱，白芷5钱，天麻5钱，赤芍药5钱，甘草5钱，升麻5钱。', '每温饮数杯，常令相续。', '《本草纲目》卷四十三引《濒湖集简方》', '诸风无新久，手足缓弱，口眼斜，语言謇涩；或筋脉挛急，肌肉顽痹，皮肤燥痒，骨节疼痛；或生恶疮、疥、癞。', '-', '-', '升麻', '[\'白花蛇\', \'温水\', \'洗净\', \'头尾\', \'各去\', \'浸去\', \'骨刺\', \'取净肉\', \'全蝎\', \'当归\', \'防风\', \'羌活\', \'独活\', \'白芷\', \'天麻\', \'芍药\', \'甘草\', \'升麻\']');
