INSERT INTO `prescript` VALUES (59305, '36191', '白鲜皮散', '白鲜皮半两，子芩半两，川升麻半两，玄参半两，白蒺藜半两（微炒，去刺），桔梗半两（去芦头），防风半两（去芦头），前胡半两（去芦头），百合半两，甘草半两（炙微赤，锉），栀子仁半两，马牙消1两，麦门冬1两半（去心，焙），茯神半两。', '每服2钱，食后以薄荷汤调下。', '《圣惠》卷六十四', '遍身热毒疮，及皮肤瘙痒，烦躁;热苑四肢，肿实不散，令人气壅。', '-', '-', '升麻', '[\'白鲜皮\', \'半两\', \'子芩\', \'半两\', \'升麻\', \'半两\', \'玄参\', \'半两\', \'蒺藜\', \'半两\', \'桔梗\', \'半两\', \'芦头\', \'防风\', \'半两\', \'去芦\', \'头前\', \'半两\', \'芦头\', \'百合\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'栀子\', \'半两\', \'马牙消\', \'麦门冬\', \'两半\', \'茯神\', \'半两\']');
INSERT INTO `prescript` VALUES (59306, '36117', '加减益胃升阳渗湿汤', '人参3钱，白术5钱，黄耆3钱，茯苓1钱，益智仁1钱，苍术1钱，泽泻1钱，附子5分，炮姜5分，炙甘草5分，升麻5分，防风5分。', '水煎服。', '《观聚方》卷一引《赤水医案》', '脾虚不运，脉沉微，脾泄不止，日夜十二三行，面色黄白带青，两颐浮肿，四肢亦浮，小水不能独利，利必与大便并行，肠鸣四肢冷，口不渴，饮食大减，口唇龈内皆白者。', '-', '-', '升麻', '[\'人参\', \'白术\', \'黄耆\', \'茯苓\', \'益智仁\', \'苍术\', \'泽泻\', \'附子\', \'分炮\', \'姜分\', \'甘草\', \'升麻\', \'防风\']');
INSERT INTO `prescript` VALUES (59307, '36115', '加减参苓白术散', '泡参3钱，扁豆4钱，焦术3钱，茯苓3钱，茅苍术1钱半，砂仁1钱，炙升麻1钱，广皮2钱。', '水煎服。', '《中医妇科治疗学》', '补气升阳。主妊娠数月，因脾虚气弱，消化不良，食少腹胀，大便不实，下肢肿胀，气短神疲，面色萎黄，舌淡口和，苔白滑，脉濡而虚者。', '-', '-', '升麻', '[\'参钱\', \'扁豆\', \'焦术\', \'茯苓\', \'钱茅\', \'苍术\', \'钱半\', \'砂仁\', \'升麻\', \'钱广皮\']');
INSERT INTO `prescript` VALUES (59308, '36078', '决明子丸', '决明子半两，天雄半两（炮），犀角屑半两，天南星半两（炮裂），白花蛇肉半两（酥拌，微炙），独活半两，川芎半两，白附子半两（炮），川升麻半两，白术半两，白僵蚕半两（微炒），防风半两，蔓荆子半两，当归半两（微炒），细辛半两，酸枣仁半两（微炒），萆薢半两，牛黄1分，朱砂1分，麝香1分。', '每服20丸，以豆淋酒送下。', '《医方类聚》卷二十引《神巧万全方》', '肝脏中风，攻手足，缓弱无力，口眼斜，精神不定，行走艰难。', '-', '-', '升麻', '[\'决明子\', \'半两\', \'天雄\', \'半两\', \'犀角\', \'半两\', \'天南星\', \'半两\', \'炮裂\', \'白花蛇\', \'半两\', \'独活\', \'半两\', \'川芎\', \'半两\', \'白附子\', \'半两\', \'炮川\', \'升麻\', \'半两\', \'白术\', \'半两\', \'白僵蚕\', \'半两\', \'防风\', \'半两\', \'蔓荆子\', \'半两\', \'当归\', \'半两\', \'细辛\', \'半两\', \'酸枣仁\', \'半两\', \'半两\', \'牛黄\', \'朱砂\', \'麝香\']');
