INSERT INTO `prescript` VALUES (59318, '35873', '加味补中益气汤', '人参1钱，黄耆1钱，当归1钱，白术1钱，陈皮5分，甘草5分，升麻5分，柴胡5分，防风5分，白芷5分，川芎5分。', '加生姜、大枣，水煎服。', '《医统》卷五十五', '中气不足，卫气不舒，致患搔痒。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'当归\', \'白术\', \'陈皮分\', \'甘草\', \'升麻\', \'柴胡\', \'防风\', \'白芷\', \'川芎\']');
INSERT INTO `prescript` VALUES (59319, '35856', '贝齿散', '贝齿3分，黄耆3分（锉），当归3分，赤芍药3分，生干地黄3分，黄连3分（去须），川升麻3分，桂心3分，犀角屑1分半，甘草半两（生锉）。', '每服2钱，以温水调下，不拘时候。', '《圣惠》卷六十二', '排脓止痛。主发背溃后，脓水不尽。', '-', '-', '升麻', '[\'贝齿分\', \'黄耆\', \'当归\', \'分赤\', \'芍药\', \'分生\', \'地黄\', \'黄连\', \'分去\', \'须川\', \'升麻\', \'分桂心分\', \'犀角\', \'分半\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59320, '35831', '补中益气汤', '黄耆1钱5分，甘草（炙）1钱，人参1钱，当归1钱，白术1钱，升麻3分，柴胡3分，陈皮3分，麦门冬6分，五味子（炒）5分。', '水2钟，加生姜3片，大枣2枚，煎1钟，空心热服。', '《外科正宗》卷一', '疮疡元气不足，四肢倦怠，口干发热，饮食无味，或饮食失节，或劳倦身热，脉洪大而无力，或头痛而恶寒，或声高而喘，身热而烦。', '-', '-', '升麻', '[\'黄耆\', \'钱分\', \'甘草\', \'人参\', \'当归\', \'白术\', \'升麻\', \'柴胡\', \'陈皮分\', \'麦门冬\', \'五味子\', \'炒分\']');
